INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6251','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院保安服务招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院保安服务招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;盐城市第一人民医院南北两个院区的保安服务合同均相继到期，现我院南北两个院区对全权管理的安全管理服务工作，各自进行招标，本次招标依据《中华人民共和国政府采购法》、《中华人民共和国招标投标法》等国家相关法律法规，在国内的保安服务公司进行公开招标，欢迎有能力的公司参加投标。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、招标项目名称：南北两个院区各自的保安服务</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、人员要求：南区保安员50名，北区保安员60名，男性，身高165厘米以上，年龄20-45周岁，文化程度不低于高中毕业，持有保安人员工作证件。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、服务期限：两年</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、服务范围：维护院区内公共安全秩序服务，包括安全管理、消防管理、车辆管理服务等。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、投标方资格要求：</span></p><ol class=\" list-paddingleft-2\"><li><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">具有行业资质证书（二级以上证书，含二级）。</span></p></li><li><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">具备独立法人的资格。</span></p></li><li><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">具有良好的商业信誉和管理经验。</span></p></li><li><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">具有独立承担民事责任的能力。</span></p></li><li><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">具有履行合同所必需的设备和专业技术能力。</span></p></li><li><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">参加此项投标活动前三年内，没有违法记录。</span></p></li><li><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">保安公司对保安员要进行岗前培训，并有严格的管理及检查考核制度。</span></p></li><li><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">投标方不得将整体管理责任转让和转包第三方，不接受联合体投标。</span></p></li></ol><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、质量要求：符合国家《保安服务管理条例》和《物业管理条例》等相关保安管理要求，达到国家该行业相关服务标准和合同约定的相关要求，并随时接受我院的检查、监督。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">七、承包方式：全包的方式，即承包总费用中含保安服务人员的工资和福利、伙食费、劳保、各项社会劳动及特殊工种保险项目，以及在服务过程中所需的低值易耗品等所有均包含在承包费用中。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">八、报名时间:2019年4月15日--2019年4月22日</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp;上午：8：00--11:30&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;下午：14:00--17:30</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp;星期六下午和星期日全天休息</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">九、报名地址：盐城市第一人民医院北区招标采购中心办公室（盐城市亭湖区毓龙路166号）&nbsp;联系人：郑蓉&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;联系电话：0515-88507786</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">十、报名须知：凡具备资格要求的投标人报名时，需携带企业营业执照副本、资质证书、法定代表人身份证明、保安服务许可证等复印件，上述文件必须加盖公章。</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2019-04-15\',\n  \'timestamp\' => \'2019-04-15\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'5434aa8d30fa2491\',\n  \'views\' => \'218\',\n  \'verifier\' => \'ycyyo\',\n)','1654844803');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6252','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院北区新生儿病房、ICU2净化空调机组板式换热器更','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院北区新生儿病房、ICU2净化空调机组板式换热器更换项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院北区新生儿病房、ICU2净化空调机组板式换热器更换项目，资金已落实，招标人为盐城市第一人民医院，现对该工程进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、工程概况</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.1&nbsp;工程名称：盐城市第一人民医院北区新生儿病房、ICU2净化空调机组板式换热器更换项目；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.2地点：盐城市第一人民医院北区；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.3招标内容：北区新生儿病房、ICU2净化空调机组板式换热器更换、机组清洗、阀门更换等项目，具体内容详见招标人要求和工程量清单；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.4工期要求：15日历天，具体开工日期以招标人的通知；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.5发包方式：包工包料。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.6工程质量符合国家及行业规范合格标准。质保期一年。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、投标人应当具备的主要资格条件：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.1投标人具备建设行政主管部门核发的机电工程施工总承包或建筑机电安装工程专业承包三级（含）以上施工资质或营业执照经营范围包含空调维修等相关内容。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.2自2016年1月1日以来（以合同签订日期为准），投标人须承担过一项净化空调机组维修或安装项目类似业绩（投标时须提供业绩合同原件）。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.3投标时须授权技术人员参加本项目谈判。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、本工程采用资格后审。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4、投标报名及招标文件的获取</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件（标明联系人、联系电话及邮箱）到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2019年4月12日至2019年4月17日。联系人：胡荣艳，联系电话：0515-68037830，13815581495。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5.本项目采用竞争性谈判方式。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;招标人：盐城市第一人民医院</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;日&nbsp;&nbsp;期：2019年4月12日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2019-04-12\',\n  \'timestamp\' => \'2019-04-12\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'249c0c51c68e96bf\',\n  \'views\' => \'403\',\n  \'verifier\' => \'ycyyo\',\n)','1654844803');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6253','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院南区冷却塔维修项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院南区冷却塔维修项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院南区冷却塔维修项目，资金已落实，招标人为盐城市第一人民医院，现对该工程进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、工程概况</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.1 工程名称：盐城市第一人民医院南区冷却塔维修项目；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.2地点：盐城市第一人民医院南区；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.3招标内容：东区3组喷雾无风机冷却塔水流喷管及涡流喷嘴更换；西区2组喷雾无风机冷却塔风扇叶片风叶、水流喷管及涡流喷嘴等更换，具体内容详见招标人要求和工程量清单；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.4工期要求：20日历天，具体开工日期以招标人的通知；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.5发包方式：包工包料。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.6工程质量符合行业标准。质保期两年。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2 投标人应当具备的主要资格条件：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.1投标人具备建设行政主管部门核发的机电工程施工总承包三级（含）以上施工资质或建筑机电安装工程专业承包三级（含）以上施工资质或营业执照经营范围包含：冷却塔安装、维修等相关内容。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.2自2016年1月1日以来（以合同签订日期为准），投标人须承担过一项冷却塔安装或维修项目类似业绩（投标时须提供业绩合同原件）。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.3投标时须授权技术人员参加本项目谈判。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、本工程采用资格后审。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.投标报名及招标文件的获取</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件（标明联系人、联系电话及邮箱）到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2019年4月11日至2019年4月16日。联系人：胡荣艳，联系电话：0515-68037830，13815581495。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5.本项目采用竞争性谈判方式。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; 招标人：盐城市第一人民医院</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 日 &nbsp;期：2019年4月11日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2019-04-11\',\n  \'timestamp\' => \'2019-04-11\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'01184316794d8eb2\',\n  \'views\' => \'481\',\n  \'verifier\' => \'ycyyo\',\n)','1654844804');
