INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6277','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院北区停车管理系统建设招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院北区停车管理系统建设招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、盐城市第一人民医院北区停车管理系统建设，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.项目概况与招标内容</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.1项目名称：盐城市第一人民医院北区停车管理系统建设。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.2项目地点：盐城市第一人民医院北区</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.3招标内容：北区停车管理系统（车牌抓拍系统）建设，实现车辆的信息采集，后台与市公安局“320”系统车辆信息数据等平台联网对接，依托市公安局大数据中心支撑，实现自动采集、自动比对、自动研判、自动预警，对进出医院的车辆进行有效管控。具体详见招标人清单。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.&nbsp;投标人资格要求</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）投标人具有建设行政主管部门核发的电子与智能化工程专业承包二级及以上资质的独立法人，并具有有效的安全生产许可证。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（2）业绩要求：投标人自2016年1月1日以来（以合同签订日期为准），承担过一项类似的停车管理系统（车牌抓拍系统）建设业绩（投标时须提供业绩合同原件）。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.&nbsp;投标报名及招标文件的获取</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2019年1月4日至2019年1月9日。联系人：胡荣艳，0515-68087830；手机：13815581495。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5.&nbsp;本项目采用竞争性谈判方式。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;招标人：盐城市第一人民医院</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;时间：2019年1月4日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2019-01-04\',\n  \'timestamp\' => \'2019-01-04\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'2e2f33d75043add0\',\n  \'views\' => \'397\',\n  \'verifier\' => \'ycyyo\',\n)','1654844817');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6278','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院宣传制作品采购项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院宣传制作品采购项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">1、盐城市第一人民医院宣传制作品采购项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">2、项目概况与招标内容</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 3em; text-align: left;\"><span style=\"font-size: 20px;\"><span style=\"font-family: 宋体;\">2.1项目名称：</span><span style=\"font-family: 宋体; letter-spacing: 0px;\">盐城市第一人民医院宣传制作品采购项目</span></span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">2.2服务内容：院内宣传品制作、安装，包括（不限于）标识标牌、制度牌、喷绘、材料设计印刷、门牌、医护介绍、横幅、席卡、大活动的背景布置等。</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">2.3服务期：1年，具体开始时间以招标人的通知为准。</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">3、投标人资格要求</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">3.1投标人为营业执照经营范围须包含户外（或室外）标识（或标牌）制作或安装等相关内容的独立法人，信誉良好。</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">3.2投标人须拟派1名专业设计师。</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">3.3业绩要求：自2016年1月1日以来（以合同或协议书的签订日期为准），投标人具有为政府或事业单位或银行、移动、电信、联通、邮政等类型客户连续3年以上宣传制作品采购类似服务业绩[投标时须提供业绩合同（或协议书）原件和订单（或发票）等证明材料复印件加盖单位公章]。</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">4、投标报名及招标文件的获取</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; margin-top: 7px; margin-bottom: 7px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2019年1月4日至2019年1月9日。联系人：胡荣艳，0515-68087830；手机：13815581495。</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">5、评标办法：本项目采用“综合评估法”。</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 32px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 48px; line-height: 3em; text-align: left;\"><span style=\"font-family: 宋体; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 48px; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;招标人：盐城市第一人民医院</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-align: right; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2019年1月4日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2019-01-04\',\n  \'timestamp\' => \'2019-01-04\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'e901d02f2a0dc7e3\',\n  \'views\' => \'553\',\n  \'verifier\' => \'ycyyo\',\n)','1654844818');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6279','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院污水处理站运营值守托管项目招标公告（三次）','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院污水处理站运营值守托管项目招标公告（三次）\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、盐城市第一人民医院污水处理站运营值守托管项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、项目概况与招标内容</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.1项目名称：盐城市第一人民医院污水处理站运营值守托管项目</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.2项目地点：盐城市第一人民医院北区、南区、五官科、紫薇门诊及广济医院</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.3招标内容：污水处理站运营值守托管，主要包含但不限于为污水处理站配备合格的操作人员，保证污水处理站正常运行和污水排放达标。具体内容详见招标人提供的服务要求。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.4服务期：一年，具体开始时间以招标人的通知为准。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、投标人资格要求</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.1投标人经营范围包含污水处理等相关内容的独立法人资格。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.2投标人须配备医院北区、南区、五官科医院、紫薇门诊及广济医院，目前暂定7人（具体人员招标人有权调整实际用工人数），其中至少有2人须持有污水（废水）处理职业资格证或污水（废水）处理工培训证书（投标时须提供证书原件）。具体各个院区的人员数量及分布，可根据招标人的要求进行合理分配或调整，投标人需无条件服从。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.3投标人自2016年1月1日以来（时间以合同签订日期为准），承担过一项污水处理运营值守托管业绩（投标时须提供委托合同原件）。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4、投标报名及招标文件的获取</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2019年1月3日至2019年1月8日。联系人：胡荣艳，0515-68087830，13815581495。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5、评标办法：本项目采用“综合评估法”。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标人：盐城市第一人民医院</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2019年1月3日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2019-01-03\',\n  \'timestamp\' => \'2019-01-03\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'b597805ca412ca0c\',\n  \'views\' => \'864\',\n  \'verifier\' => \'ycyyo\',\n)','1654844818');
