INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6307','5','cms','item','add','0','859','','ycyyo','添加内容 | 中标结果公示','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'中标结果公示\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">因医院实际工作需要，于2018年10月19日14时30分进行项目评标工作，经评标小组评审，中标结果如下：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、北区佳能DR机房装饰改造工程 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：盐城明飞装饰工程有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中 标 价：12万元</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、北区佳能DR机房改造防辐射工程</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：宜兴市日森防辐射设备有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中 标 价：5.2万元</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、北区佳能DR机房改造加固工程</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：盐城市华泽工程加固改造有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中 标 价：6.6万元</span></p><p></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监察室联系，电话0515-88508906。</span></p><p></p><p></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;盐城市第一人民医院</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2018年10月19日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2018-10-19\',\n  \'timestamp\' => \'2018-10-19\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'d37f6640d03373e7\',\n  \'views\' => \'993\',\n  \'verifier\' => \'ycyyo\',\n)','1654844832');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6308','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院广济医院手术室净化及装饰工程中标结果公示','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院广济医院手术室净化及装饰工程中标结果公示\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background: white; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">盐城市第一人民医院广济医院手术室净化及装饰工程，招标评标工作已经结束，经评标委员会评审，结果如下：</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体; font-size: 20px; text-indent: 2em;\"><img src=\"http://localhost/upload/images/2019/10/b34fe953a80708d7.png\" title=\"image.png\" alt=\"image.png\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"font-family: 宋体; font-size: 20px;\">以上内容公示时间为2018年10月19日至2018年10月23日，对公示结果如有异议，请于2018年10月23日前向招标人或招标代理机构提出（联系人：盐城市第一人民医院，联系电话：0515-88508965）。</span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; text-align: right; background: white; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\"></span></p><p style=\"font-family: 宋体, verdana; font-size: 14px; white-space: normal; text-align: