INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6327','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院南区机房汽水换热器 维修改造招标文件','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院南区机房汽水换热器 维修改造招标文件\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">根据医院实际工作需要，我院南区机房汽水换热器需维修，现邀请潜在投标人参加投标。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、项目概况</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、工程名称：盐城市第一人民医院南区机房汽水换热器维修改造；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、建设地点：盐城市第一人民医院南区（人民南路66号）；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、招标内容：盐城市第一人民医院南区机房汽水换热器维修改造，具体内容详见招标人提供的图纸和工程量清单；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4、工期要求：20日历天，具体开工日期以招标人的书面通知。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、投标申请人应具备的资格条件</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、投标人须是具有建设行政主管部门核发的建筑机电安装工程专业承包三级及以上资质的独立法人，并具有有效的安全生产许证；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、投标人须具有中华人民共和国特种设备安装改造维修许可证（压力容器）或中华人民共和国特种设备制造许可证（压力容器）。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、报名方式和招标文件的获取。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">公告发布后，请符合上述资质条件的投标人携带单位介绍信到江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号8楼809室，联系人：胡荣艳，联系电话：0515-88351862）投标报名并领取招标文件，投标报名截止时间2018年8月20日至2018年8 月24日。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、本项目采用竞争性谈判方式。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">具体详见招标文件。</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2018年8月20日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2018-08-20\',\n  \'timestamp\' => \'2018-08-20\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'7ae93e32e04a8633\',\n  \'views\' => \'314\',\n  \'verifier\' => \'ycyyo\',\n)','1654844843');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6328','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院（南区）护理员服务招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院（南区）护理员服务招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">根据医院实际工作需要，我院南区护理员服务项目须组织招标，现邀请潜在投标人参加投标。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、项目概况</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、项目名称：盐城市第一人民医院（南区）护理员服务。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、服务地点：盐城市第一人民医院南区。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、招标内容：一对一护理员、团队式护理员服务（详见招标文件）。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4、服务周期：服务期限为二年，从合同签订之日起计算。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、投标申请人应具备的资格条件</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、投标人必须为中华人民共和国境内注册的独立法人，且营业执照经营范围须包含病员陪护等相关内容；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、投标人必须具有相关项目管理经验，自2015年1月1日以来，至少承担过一项二级及以上医院护理员服务业绩（投标时须提供业绩合同原件）；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、项目经理必须为投标人正式员工，男性年龄在55周岁（含）以下或女性年龄在50周岁（含）以下，且持有建设行政主管部门核发的《全国物业管理从业人员岗位证书》或房产主管部门核发的《项目经理岗位证书》，近两年内没有被物业主管部门处罚记录。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4、投标时须提供项目经理自2018年2月～2018年6月连续六个月在本单位向投标人注册所在地人社部门交纳的社会保险证明原件（如提供的是养老保险手册，须附有效期内的缴费清单原件；如提供的是人社部门证明原件，须注明项目经理姓名和缴费起止期间；也可提供印有企业注册所在地的社会保险管理中心参保缴费证明电子专用章的社保缴费清单网络打印件和人员社保卡号和参保缴费证明验证码）。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、评标办法：本项目采用“综合评估法”</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、报名方式和招标文件的获取。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">公告发布后，请符合上述资质条件的投标人携带单位介绍信到江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号8楼809室，联系人：胡荣艳，联系电话：0515-88351862）投标报名并领取招标文件，投标报名截止时间2018年8月20日至2018年8 月24日。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">具体详见招标文件。</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2018年8月20日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2018-08-20\',\n  \'timestamp\' => \'2018-08-20\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'79a812f62434939b\',\n  \'views\' => \'813\',\n  \'verifier\' => \'ycyyo\',\n)','1654844844');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6329','5','cms','item','add','0','859','','ycyyo','添加内容 | 中标结果公示','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'中标结果公示\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px;\"><span style=\"font-family: 宋体, SimSun;\">因医院实际工作需要，于2018年8月18日10时进行招标的项目，经评</span><span style=\"font-family: 宋体, SimSun;\">标小组评审，中标结果如下：</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、北区卫生间改造项目 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：一标段 江苏力邦装饰有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二标段 盐城明飞装饰工程有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中 标 价：一标段11万元，二标段1.83万元</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、（南区）冷冻机组维保项目</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：江苏冰洋制冷空调有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中 标 价：27.5万元</span></p><p></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监察室联系，电话0515-88508906。</span></p><p></p><p></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;盐城市第一人民医院</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2018年8月18日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2018-08-18\',\n  \'timestamp\' => \'2018-08-18\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'1d867bb1625bbcf9\',\n  \'views\' => \'724\',\n  \'verifier\' => \'ycyyo\',\n)','1654844844');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6330','5','cms','item','add','0','859','','ycyyo','添加内容 | 市委第四巡察组巡察公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'市委第四巡察组巡察公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin-top: 0px; margin-bottom: 0px; text-align: center; line-height: 37px;\"><br /></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">根据市委同意和巡察工作领导小组安排，市委第四巡察组对盐城市卫生和计划生育委员会党委开展巡察，并对盐城市第一人民医院党委、盐城市中医院党委、盐城市第二人民医院党委、盐城市妇幼保健院党委、盐城市疾病预防控制中心党委、盐城市卫生监督所党总支等党组织开展延伸巡察。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">一、巡察时间</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2018年8月16日-9月30日。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">二、巡察内容</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">主要围绕党的政治建设、思想建设、组织建设、作风建设、纪律建设和夺取反腐败斗争压倒性胜利等方面，对巡察对象维护习近平总书记核心地位、维护党中央权威和集中统一领导情况，学习贯彻习近平新时代中国特色社会主义思想情况，选人用人和基层党组织建设情况，执行中央八项规定精神和整治“四风”情况，党规党纪执行情况，领导干部廉洁自律和整治群众身边腐败问题情况，以及十二届省委巡视整改情况、市委前三轮巡察发现共性问题整改情况等进行监督。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">根据工作职责，市委巡察组主要受理反映被巡察党组织领导班子及其成员和关键岗位党员、干部问题的来信来电来访，重点是关于违反政治纪律、组织纪律、廉洁纪律、群众纪律、工作纪律、生活纪律等方面的举报和反映。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">其他不属于巡察受理范围的信访问题，将按规定由被巡察单位或有关部门认真处理。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">三、反映渠道</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">市委第四巡察组电话：13912508642（电话受理时间：2018年8月16日-9月30日期间9:00-17:30）</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">电子邮箱：swxcb11@126.com</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">邮政信箱：江苏省盐城市A1810号邮政信箱。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">邮政编码：224002</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">同时，在盐城市亭湖区开放大道28号瀛洲宾馆3012室设置信访接待处。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">欢迎广大干部群众实事求是反映情况，提出意见和建议，并对市委巡察组工作人员进行监督。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">特此公告。</span></p><p><br /></p><p><br /></p><p style=\"text-indent: 2em; text-align: right; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">&nbsp; &nbsp;中共盐城市委第四巡察组</span></p><p style=\"text-indent: 2em; text-align: right; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2018年8月16日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2018-08-16\',\n  \'timestamp\' => \'2018-08-16\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'c58b787ff27d610d\',\n  \'views\' => \'612\',\n  \'verifier\' => \'ycyyo\',\n)','1654844844');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6331','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院北区急诊楼一层卫生间改造项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院北区急诊楼一层卫生间改造项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、盐城市第一人民医院北区急诊楼一层卫生间改造项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.项目概况与招标内容</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.1项目名称：盐城市第一人民医院北区急诊楼一层卫生间改造项目。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.2项目地点：盐城市第一人民医院北区。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.3招标内容：北区急诊楼一层公共卫生间改造项目，具体详见招标人提供的工程量清单。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.投标人资格要求</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.1投标人须具有有效的建筑装修装饰工程专业承包二级及以上施工资质的独立法人，并具有有效的安全生产许可证。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.投标报名及招标文件的获取</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼809室）投标报名并领取招标文件，投标报名时间2018年8月8日至2018年8月13日。联系人：胡荣艳，0515-88351862。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5.&nbsp;本项目采用竞争性谈判方式。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标人：盐城市第一人民医院</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">时间：2018年8月8日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2018-08-08\',\n  \'timestamp\' => \'2018-08-08\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'02ad5c147b9d76c7\',\n  \'views\' => \'749\',\n  \'verifier\' => \'ycyyo\',\n)','1654844845');
