INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6369','5','cms','item','add','0','859','','ycyyo','添加内容 | 北区日常维修招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'北区日常维修招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院北区零星维修项目</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标公告</span></p><p></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、盐城市第一人民医院北区零星维修项目，资金已落实，招标人盐城市第一人民医院现拟对该项目进行招标，诚邀潜在的投标人参加投标。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托负责本工程招标事宜。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.项目概况与招标内容</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.1项目名称： 盐城市第一人民医院北区零星维修</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.2服务地点：盐城市第一人民医院北区。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.3招标内容：神经内科二病区楼梯贴磁砖；普外科二病区37、38床两间VIP病房油漆维修粉刷及儿科病区卫生间木门更换等项目。具体内容详见招标人提供的工程量清单。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, S