INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6376','0','cms','item','add','0','859','','宣传处','添加内容(前台) | 盐城市第一人民医院DSA设备预评价、控制效果评价和环评服务','/index.php/cms/item-list-category-859-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/u.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院DSA设备预评价、控制效果评价和环评服务项目竞争性谈判采购公告\',\n  \'title_color\' => \'\',\n  \'html\' => \'1\',\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'timestamp\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'summary\' => \'\',\n  \'sub_title\' => \'\',\n  \'verify_frame\' => \'\',\n  \'custom_a\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 28pt; text-indent: 23.5pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市第一人民医院<span>DSA</span>设备预评价、控制效果评价和环评服务项目，资金已落实，采购人盐城市第一人民医院现对该项目进行采购，欢迎潜在申请人参加。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 28pt; text-indent: 23.5pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">一、江苏仁禾中衡工程咨询房地产估价有限公司受采购人委托具体负责本项目的采购事宜。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 28pt; text-indent: 23.5pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">二、项目概况</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 28pt; text-indent: 23.5pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目名称：盐城市第一人民医院<span>DSA</span>设备预评价、控制效果评价和环评服务项目。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目内容：盐城市第一人民医院<span>DSA</span>设备预评价、控制效果评价和环评服务包括但不限于：</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">（<span>1</span>）卫生：放射诊疗设备预评价和控制效果评价（含批复）、办理放射诊疗许可证；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">（<span>2</span>）环保：办理环境影响报告表（含批复）及环保竣工验收、办理辐射安全许可证；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">（<span>3</span>）对放射诊疗设备和场所（包括卫生和环保）进行质量控制检测和放射工作场所职业病危害因素检测；采购人保留对上述暂定项目内容适当调整的权利。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">质量要求</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">符合国家规范及行业标准，形成书面报告，并通过卫生、环保等监管部门的验收，并取得相关证书。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">服务期：设备安装验收合格条件具备后<span>60</span>日历天内完成委托任务。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.5</span><span style=\"font-family: 宋体; font-size: 12pt;\">预算价：约<span>9.8</span>万元。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 28pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">三、申请人应当具备的主要资格条件</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 28pt; text-indent: 23.5pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">3.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">申请人是具有独立法人资格的单位，营业执照须具有相关经营范围，能独立承担民事责任；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 28pt; text-indent: 23.5pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">3.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">申请人具备职业卫生（放射卫生）技术服务机构乙级及以上资质；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 28pt; text-indent: 23.5pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">3.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">申请人资质需经省级及以上卫生主管部门和环保主管部门认可；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 28pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">3.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">本项目不接受联合体。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 28pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">3.5</span><span style=\"font-family: 宋体; font-size: 12pt;\">财务要求：申请人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">3.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">信誉要求：未被“信用中国”网站（<span>www.creditchina.gov.cn</span>）列入失信被执行人、重大税收违法案件当事人名单、政府采购严重失信行为记录名单。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 28pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">四、竞争性谈判文件获取方式</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">凡符合本采购公告要求的申请人，其授权代表于<span>2022</span>年<span>6</span>月<span>13</span>日至<span>2022</span>年<span>6</spa