INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6395','0','cms','item','add','0','859','','宣传处','添加内容 | 盐城市第一人民医院北区妇产科门诊改造工程  招标公告','/index.php/cms/item-list-category-859-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院北区妇产科门诊改造工程  招标公告\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-indent:21.0pt;line-height:24.0pt;\r\nbackground:white\"><span style=\"font-size:10.5pt;font-family:宋体;color:black\">盐城市第一人民医院北区妇产科门诊改造工程已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;color:black\">一、</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">盐城市招标代理有限公司</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">受招标人委托具体负责本项目的招标事宜。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;color:black\">二、符合本项目招标公告资格要求的投标的申请人均可参加该项目投标。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.1pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><b><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">三、项目概况：</span></b></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">1</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">、工程名称：盐城市第一人民医院北区妇产科门诊改造工程。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">2</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">、工程地点：盐城市毓龙西路<span>166</span>号。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">3</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">、招标规模：工程造价约<span>56.8</span>万元。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">4</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">、招标范围及内容：盐城市第一人民医院北区妇产科门诊改造工程</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">的施工</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">，具体详见图纸、工程量清单和现场情况，招标人保留对上述工程规模进行适当调整的权利。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">5</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">、工期要求：<span>20</span>日历天，具体开工日期以招标人的通知为准。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">6</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">、质量标准：符合国家“合格”标准，质保期<span>2</span>年。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.1pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><b><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">四、投标人资格要求：</span></b></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">1</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">、投标人资质要求：投标人须具有建设行政主管部门核发的建筑装修装饰工程专业承包二级及以上施工资质的独立法人，并取得有效的安全生产许可证。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">2</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">、项目负责人的资格要求：项目负责人必须是投标人本单位正式职工，已取得建筑工程专业二级及以上建造师执业注册证书，同时具有《建筑施工企业项目负责人安全生产考核合格证》（<span>B</span>类）。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">3</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">、本次招标不接受联合体投标。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">4</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">、财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<span> </span></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">5</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">、信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">6</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">、投标申请人须保证项目负责人及代理人均为本单位的正式职工，并确保从投标截止之日当月向前连续<span>6</span>个月均已在本单位缴纳养老保险（例：如开标日期为<span>2020</span>年<span>7</span>月的某一天，则投标单位需保证以上人员<span>2020</span>年<span>1-6</span>月养老保险均在本单位缴纳），投标时无需提供缴费记录证明，中标候选人公示前、异议（投诉）提出相关单位需提供缴费记录给招标人核验。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">7</span><span style=\"font-size:10.5pt;font-family:宋体;color:black\">、我方承诺中标的项目经理，不因任何情形而更换，且出勤率不少于<span>80%</span>，如发生更换或出勤率少于<span>80%</span>，除接受招标文件约定的处罚外，自愿另行接受合同价<span>2%</span>的扣款。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.1pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><b><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">五、招标文件获取：</span></b></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;color:black\">请有意参与投标</span><span style=\"font-size:10.5pt;font-family:宋体\">的申请人将单位介绍信或委托书（自行注明联系方式及邮箱）原件扫描件于<b><span>2022</span>年<span>6</span>月<span>23</span>日<span>18:00</span>时</b>前发送到</span><span style=\"font-size:10.5pt;\r\nfont-family:宋体\">2719215359</span><span style=\"font-size:10.5pt;\r\nfont-family:宋体\">@qq.com</span><span style=\"font-size:10.5pt;font-family:宋体\">邮箱进行报名，报名费跟财务联系，财务电话<span>0515-89803566</span>、<span>13921809379</span><span style=\"color:black\">，逾期不再受理。</span></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.1pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><b><span style=\"font-size:10.5pt;font-family:宋体;\r\ncolor:black\">六、投标保证金</span></b></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;color:black\">本项目投标保证金为人民币<span>5000</span>元，具体详见招标文件。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;color:black\">七、本项目资格审查办法采用<b>资格后审</b>；评标办法采用<b>综合评估法</b>，具体详见招标文件。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体;color:black\">八、本项目严禁挂靠，一经核实挂靠，将被取消投标、中标资格，并按相关规定进行处罚，直至建议有关部门吊销资质证书。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.1pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><b><span style=\"font-size:10.5pt;font-family:宋体\">九、联系方式</span></b></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体\">招标人：盐城市第一人民医院</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:21.0pt;\r\nline-height:23.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-family:宋体\">地<span>&nbsp; </span>址：盐城市人民南路<span>66</span>号</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:24.0pt;line-height:23.0pt;punctuation-wrap:simple;\r\ntext-autospace:none\"><span style=\"font-size:10.5pt;font-family:宋体\">联系人：凌工</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;te