INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6401','0','cms','item','add','0','859','','宣传处','添加内容(前台) | 盐城市儿童医院体检中心周边场地硬化招标公告','/index.php/cms/item-list-category-859-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/u.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市儿童医院体检中心周边场地硬化招标公告\',\n  \'title_color\' => \'\',\n  \'html\' => \'1\',\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'timestamp\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'summary\' => \'\',\n  \'sub_title\' => \'\',\n  \'verify_frame\' => \'\',\n  \'custom_a\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市儿童医院体检中心周边场地硬化资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">1</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、工程概况<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></b></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目名称：盐城市儿童医院体检中心周边场地硬化</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目地点：盐城市儿童医院院内</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标内容：儿童医院体检中心周边场地平整和硬化。具体内容详见招标人提供的工程量清单。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">质量标准：符合国家“合格”标准，质保期<span>1</span>年。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.5 </span><span style=\"font-family: 宋体; font-size: 12pt;\">工期要求：<span>10</span>日历天，具体开工日期以招标人的通知。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标规模：工程造价约<u><span> 45</span></u>万元。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.7</span><span style=\"font-family: 宋体; font-size: 12pt;\">发包方式：包工包料。</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">2 </span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">投标人应当具备的主要资格条件：</span></b></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人须具有有效的行政主管部门核发的</span><span style=\"font-family: 宋体; font-size: 12pt;\">建筑工程施工总承包三级及以上资质或市政公用工程施工总承包三级及以上资质，并取得安全生产许可证。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目负责人须具有建筑工程专业二级及以上建造师资格和持有安全生产考核合格证（<span>B</span>类）或注册专业为市政公用工程专业二级及以上建造师资格证书和安全生产考核合格证（<span>B</span>类）。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">项目负责人从本工程投标文件递交截止之日起必须满足下列条件：</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">（<span>1</span>）项目负责人不得同时在两个或者两个以上单位受聘或者执业特指以下三种情形：</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">① 同时在两个及以上单位签订劳动合同或交纳社会保险；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">② 将本人执（职）业资格证书同时注册在两个及以上单位；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">③ 项目负责人担任其他公司法定代表人。 </span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">（<span>2</span>）项目负责人是非变更后无在建工程，或项目负责人是变更后无在建工程（必须原合同工期已满且变更备案之日已满<span>6</span>个月），或因非承包方原因致使工程项目停工或因故不能按期开工、且已办理了项目负责人解锁手续，或项目负责人有在建工程，但该在建工程与本次招标的工程属于同一工程项目、同一项目批文、同一施工地点分段发包或分期施工的情况且总的工程规模在项目负责人执业范围之内。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">项目负责人投标截止时不得同时在其他建设工程项目中担任现场管理人员、不在本项目兼任本项目的其它岗位。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">在建工程：处于中标结果公告（直接发包的项目以网上合同备案或归集为准）到合同约定的工程全部完成且竣工验收合格期间的工程。竣工验收证明是指由建设单位（或监理）组织工程建设各方验收合格，\r\n并签署相应的单位工程质量竣工验收记录或者分部工程质量验收记录等验收文件。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人须保证项目负责人及代理人均为本单位的正式职工，并确保从投标截止之日当月向前连续<span>6</span>个月均已在本单位缴纳养老保险（例：如开标日期为<span>2020</span>年<span>7</span>月的某一天，则投标单位需保证以上人员<span>2020</span>年<span>1-6</span>月养老保险均在本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">2.4</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">截止<span>2022</span>年<span>5</span>月在盐城市第一人民医院存在已中标工程项目，且投标人未按合同工期完成并通过招标人验收的，本次不得报名参与本单位招标项目。</span></b></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">&nbsp;&nbsp; 2.5</span><span style=\"font-family: 宋体; font-size: 12pt;\">其他要求<span><br>\r\n&nbsp;&nbsp; 2.5.1</span>财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<span>&nbsp;<br>\r\n&nbsp;&nbsp; 2.5.2</span>信誉要求：①投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.6 </span><span style=\"font-family: 宋体; font-size: 12pt;\">本次招标不接受联合体投标。</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 21pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">3</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、投标报名及招标文件的获取</span></b></p>\r\n<p style=\\\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\\\'><span style=\"background: white;\">请符合上述资质条件的投标人携带单位介绍信到江苏双清工程造价咨询有限公司投标报名并领取招标文件，投标报名时间</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">6 </span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">21</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">时</span><span style=\"background: white; font-family: 宋体;\">30</span><span style=\"background: white;\">分至</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">6</span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">25</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">18</span><span style=\"background: white;\">时。联系人：张旭良；手机：</span><span style=\"background: white; font-family: 宋体;\">0515-88200706,13515130964</span><span style=\"background: white;\">。</span></p>\r\n<p style=\\\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\\\'><span style=\"background: white; font-family: 宋体;\">4</span><span style=\"background: white;\">、</span><b>本项目采用“资格后审”，“综合评分”</b><span style=\"background: white;\">。</span></p>\r\n<p style=\\\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\\\'><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p>\r\n<p style=\\\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\\\'><span style=\"background: white; font-family: 宋体;\">6</span><span style=\"background: white;\">、招投标监督管理机构及投诉电话：盐城市第一人民医院监督审查室，联系电话</span><span style=\"background: white; font-family: 宋体;\">0515-66696810</span><span style=\"background: white;\">。</span></p>\r\n<p style=\\\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"color: black; font-family: 宋体;\">招标人：盐城市第一人民医院</span></p>\r\n<p style=\\\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"color: black; font-family: 宋体;\">地<span>&nbsp; </span>址：毓龙西路<span>166</span>号</span></p>\r\n<p style=\\\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"color: black; font-family: 宋体;\">联系人：金工</span></p>\r\n<p style=\\\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"color: black; font-family: 宋体;\">电<span>&nbsp; </span>话：</span><span style=\"font-family: 宋体;\">0515-83578932</span></p>\r\n<p style=\\\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"color: black; font-family: 宋体;\">招标代理机构：</span>江苏双清工程造价咨询有限公司<span style=\"color: black; font-family: 宋体;\"> </span></p>\r\n<p style=\\\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"color: black; font-family: 宋体;\">地址：</span>盐城市紫薇广场<span style=\"font-family: 宋体;\">C</span>区<span style=\"font-family: 宋体;\">7</span>楼<span style=\"color: black; font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></p>\r\n<p style=\\\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"color: black; font-family: 宋体;\">联系人：</span>尤鸿望、张旭良<span style=\"color: black; font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></p>\r\n<p style=\\\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"color: black; font-family: 宋体;\">电话：</span><span style=\"font-family: 宋体;\">0515-88200316</span>、<span style=\"font-family: 宋体;\">13515130964</span><span style=\"color: black; font-family: 宋体;\">&nbsp; </span></p>\',\n  ),\n  \'views\' => \'\',\n  \'url\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'action\' => \'add\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'attachment_hash\' => \'7dacfe7e155b5b1f\',\n  \'verify\' => 1,\n