INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6409','0','cms','item','add','0','859','','宣传处','添加内容(前台) | 盐城市儿童医院成人健康管理中心家具采购及安装项目招标公告','/index.php/cms/item-list-category-859-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/u.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市儿童医院成人健康管理中心家具采购及安装项目招标公告\',\n  \'title_color\' => \'\',\n  \'html\' => \'1\',\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'timestamp\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'summary\' => \'\',\n  \'sub_title\' => \'\',\n  \'verify_frame\' => \'\',\n  \'custom_a\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">盐城市儿童医院成人健康管理中心家具采购及安装项目已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-size: 12pt;\">一、</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">江苏双清工程造价咨询有限公司受招标人委托具体负责本项目的招标事宜。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-size: 12pt;\">二、</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">符合本项目招标公告资格要求的投标的申请人均可参加该项目投标。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">三、项目概况</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-size: 12pt;\">1</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">、项目名称：盐城市儿童医院成人健康管理中心家具采购及安装项目</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-size: 12pt;\">2</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">、工程地点：</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">盐城市儿童医院院内</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-size: 12pt;\">3</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">、工程规模：约</span><span style=\"line-height: 1.5; font-size: 12pt;\">28.3</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">万元。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-size: 12pt;\">4</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">、</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">质量标准：符合国家“合格”标准，质保期<span>2</span>年。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-size: 12pt;\">5</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">、供货要求：</span><span style=\"line-height: 1.5; font-size: 12pt;\">20</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">日历天</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">，具体开工日期以招标人的通知为准</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">四、标段划分及招标内容</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-size: 12pt;\">1</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">、招标内容：盐城市儿童医院成人健康管理中心家具采购及安装项目，具体数量及规格等要求详见工程量清单，招标人保留对上述工程规模进行适当调整的权利。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">五、投标人主要资格要求</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-size: 12pt;\">1</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">、投标人资质要求：投标申请人必须是在中国境内注册，</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">具有有效的含家具生产或家具销售经营范围的营业执照，</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">有独立完成本项目所有工作的能力的独立法人。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2</span><span style=\"font-family: 宋体; font-size: 12pt;\">、本次招标不接受联合体投标。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">3</span><span style=\"font-family: 宋体; font-size: 12pt;\">、财务要求：投标人应具有独立订</span><span style=\"font-family: 宋体; font-size: 12pt;\">立合同的能力，未处于财产被接管、冻结和破产状态。<span>&nbsp;</span></span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">4</span><span style=\"font-family: 宋体; font-size: 12pt;\">、信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 1.5; text-indent: 24pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">5</span><span style=\"line-height: 1.5; font-family: 宋体; font-size: 12pt;\">、投标申请人须保证委托代理人、项目负责人均为本单位的正式职工，并确保从投标截止之日当月向前连续<span>6</span>个月均已在本单位缴纳养老保险（例：如开标日期为<span>2020</span>年<span>7</span>月的某一天，则投标单位需保证以上人员<span>2020</span>年<span>1-6</span>月养老保险均在本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。</span></p>\r\n<p styl