INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2009','article','938','3','admin789',' 许准','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/13_17/f396eb8719464fbb.jpg.thumb.jpg','','','6','男，新生儿科主任，主任医师，本科学历。从事儿科临床工作20余年，具有丰富的临床经验，熟练掌握儿科领域的理论知识，儿科疾病的诊断和操作，擅长新生儿疾病、儿童哮喘的诊治。参与儿童唇腭裂术后病理性语言的研究，获盐城市科技进步二等奖。','','','','','','1','admin789','0','1626167743','1626167743','1626167743','1626167743','1','','','82','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','新生儿疾病的诊治','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2010','article','938','3','admin789',' 胥飞','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/13_17/9bdead5938c29473.jpg.thumb.jpg','','','6','男，儿科副主任，主任医师，本科学历。任江苏省医学会儿科分会委员、小儿神经学组成员、小儿风湿免疫学组成员、江苏省医院协会儿童医院分会委员、江苏省抗癫痫协会常务理事、江苏省夜遗尿协作组委员、盐城市医学会儿科分会副主委、兼职副教授。从事儿科临床30余年，熟练掌握儿科领域的理论知识、临床各种疾病的诊疗和操作，有丰富的临床经验尤其对小儿咳喘、发热、惊厥等擅长。以第一作者在核心期刊发表论文10余篇..','','','','','','1','admin789','0','1626167809','1626167809','1626167809','1626167809','1','','','145','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','肺炎，哮喘，风湿免疫，脑炎，癫痫等。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2011','article','939','3','admin789',' 许丹','','0','副主任医师','<!--#p8_attach#-->/cms/item/2021_07/13_17/52cd314583f21078.jpg','','','6','妇产科副主任医师，硕士研究生毕业于南通大学妇科肿瘤专业，在职博士就读于广西医科大学妇科肿瘤专业。曾在上海复旦大学附属妇产科医院、美国克利夫兰医学中心交流学习普通妇科及妇科肿瘤诊治。从事妇产科临床工作13年，擅长高危妊娠诊治、产后出血抢救、普通妇科炎症疾病、妇科内分泌治疗、妇科肿瘤预防诊治等。在国内核心期刊以第一作者发表文章6篇，参与省级、市级课题2项。','','','','','','1','admin789','0','1626167971','1626167971','1626167971','1626167971','1','','','0','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','高危妊娠诊治、产后出血抢救、普通妇科炎症疾病、妇科内分泌治疗、妇科肿瘤预防诊治等。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2012','article','939','3','admin789','李艳','','0','副主任医师','<!--#p8_attach#-->/cms/item/2021_07/13_17/db1a7fb72ff849d8.jpg','','','6','女，妇产科副主任医师，博士在读。从事妇产科专业临床及基础研究十余年，擅长妇科肿瘤及生殖内分泌疾病等的诊治、产科危急重症如妊娠期高血压疾病、妊娠期糖尿病、凶险性前置胎盘、羊水栓塞等的处理。熟练完成各种妇产科手术，包括新式剖宫产，腹膜外剖宫产，经阴道手术，妇科恶性肿瘤分期手术以及腹腔镜、宫腔镜手术等。主要研究方向：妇科恶性肿瘤，高危妊娠，生殖内分泌疾病。','','','','','','1','admin789','0','1626168041','1626168041','1626168041','1626168041','1','','','0','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','妇科肿瘤及生殖内分泌疾病等的诊治、产科危急重症如妊娠期高血压疾病、妊娠期糖尿病、凶险性前置胎盘、羊水栓塞等的处理。','','','','');
