INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2035','article','940','2','admin5','宋俊彪','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/13_17/bf3c2003309f8515.JPG','','','6','男，主任医师，本科学历。从事肿瘤内科专业近30年，重视对各种恶性化综合治疗及个体化化疗，对肺癌，乳腺癌，食道癌，直肠癌，胃癌等常见肿瘤的化疗有深入的研究。在省级以上杂志发表论文9篇；获“江苏省十大医德标兵”称号。','','','','','','1','admin5','0','1626169203','1626169203','1626169203','1626169203','1','','','0','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','肺癌、乳腺癌、食道癌、直肠癌、胃癌等规范化、合理化治疗。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2036','article','940','2','admin5','严必忠','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/13_17/1a4e6d72c554a924.JPG','','','6','男，主任医师，本科学历。从事肿瘤内科治疗近30年，主攻各种晚期癌症，对恶性淋巴瘤，食道癌有深入的研究。擅长盖诺持续输注联合顺铂和5-Pu治疗食道癌，盖诺持续输注联合顺铂治疗转移性乳腺癌，草酸铂，足叶乙，尿嘧啶+CF联合治疗胃癌以及NDP+LV+5-FU联合治疗晚期食道癌。在各级杂志发表医学论文数篇。','','','','','','1','admin5','0','1626169333','1626169333','1626169333','1626169333','1','','','60','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','各种恶性肿瘤病的内科治疗及综合治疗，肺癌、乳腺癌、消化道肿瘤的诊疗。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2037','article','940','2','admin5','胡平','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/13_17/39bc72812bc0deaf.JPG','','','6','女，主任医师，本科学历。从事肿瘤内科临床工作多年，积累了丰富的临床治疗经验，研究方向：非小细胞肺癌的综合治疗、晚期乳腺癌联合化疗方案和内分泌治疗及动脉超声介入治疗。在省级以上杂志发表医学论文10余篇，主持科研项目1项。','','','','','','1','admin5','0','1626169384','1626169384','1626169384','1626169384','1','','','60','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','食道、胃、结、直肠癌放化疗、综合治疗，乳腺癌辅助化疗及联合医学靶向治疗，内分泌治疗，癌痛的规范化治疗。','','','','');
