INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2044','article','940','2','admin5','陈平','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/13_17/7fca3b385b2c59f5.jpg.thumb.jpg','','','6','女，肿瘤内科主任、主任医师，本科学历，兼职副教授。任江苏省化疗与生物治疗委员会委员、江苏省抗癌协会化疗专业委员会委员、江苏省中西医结合学会委员、盐城市医学会专业委员会主任委员。熟练掌握各种常见肿瘤的诊断和治疗，对各种恶性肿瘤的诊断与治疗均能得心应手，在各类刊物上发表论文10余篇，SCI收录两篇，最高影响因子2.9；获市科技进步二等奖1项，三等奖1项，获新技术引进奖一等奖1项，省卫生厅立项1次。','','','','','','1','admin5','0','1626169586','1626169586','1626169586','1626169586','1','','','186','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','肺癌，乳腺癌，胃癌、淋巴瘤等的诊治。','','','','');
