INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2101','article','948','2','admin5','陈波','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_10/f95be52ea92fb1d8.JPG','','','6','男，主任医师，本科学历。从事感染科专业三十年，熟练掌握感染性疾病病人的诊治，具有丰富临床经验，对慢性乙型肝炎抗病毒治疗及耐药后的处理有深入的研究、掌握腹水浓缩还输技术。发表20多篇医学论文，其中在国家级杂志发表2篇。','','','','','','1','admin5','0','1626231098','1626231098','1626231098','1626231098','1','','','64','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','慢乙肝、肝硬化腹水、肝癌、伤寒、出血热、重症肝炎、发热待查、腹水待查的诊断与治疗，对慢性乙型肝炎抗病毒治疗及耐药后的处理有深入的研究。','','','','');
