INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2105','article','948','2','admin5','陈奇','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_10/8de20e1f0ce68ad5.JPG','','','6','男，感染科主任，主任医师，本科学历，学士学位。江苏省中医药学会感染病分会委员、江苏省医学会临床流行病学分会委员、江苏省医学会热带病与寄生虫病分会委员、中西医结合学会肝病分会常务委员、盐城市中西医结合学会理事、盐城市医学会感染病分会常务副主任委员。1985年毕业于南通医学院后即从事感染性疾病防治工作，熟练掌握感染病专业的各种常见疾病的诊断 治疗及腹水浓缩还输、人工肝支持系统技术。在省级以..','','','','','','1','admin5','0','1626231271','1626231271','1626231271','1626231271','1','','','65','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','重症肝炎、肝硬化、乙型肝炎抗病毒治疗及耐药后的挽救治疗；肝癌、自身免疫性肝病、药物性肝炎、酒精性和非酒精性脂肪性肝病的诊断和治疗以及新发传染病的防治。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2106','article','949','2','admin5','祁素英','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_10/974d5e4bbffefbc1.JPG','','','6','女，主任医师，本科学历，南通大学兼职教授。任盐城市医学会皮肤性病分会副主任委员。从事皮肤科工作30年余，熟练掌握皮肤科专业理论知识及操作技术，具有丰富的临床经验，对大疱及疱疹性皮肤病、血管炎、变态反应性皮肤病、皮肤肿瘤及部分性病等诊治有独特的见解及治疗经验。以第一作者在省级以上杂志刊物上发表论文多篇，获市自然科学优秀论文成果评选二等奖1次。','','','','','','1','admin5','0','1626231337','1626231337','1626231337','1626231337','1','','','194','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','结缔组织病、大疱及疱疹性皮肤病、血管炎、白癜风、银屑病、皮炎、湿疹、荨麻疹、尖锐湿疣、皮肤肿瘤等疾病的诊治及二氧化碳激光和皮肤科的小手术等。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2107','article','949','2','admin5','秦萍萍','','0','副主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_10/410af52dff308816.JPG','','','6','女，副主任医师，本科学历，南通大学在职硕士学位。任盐城市医学会皮肤性病专业委员会秘书长，南通大学、徐州医学院兼职讲师。从事皮肤性病、激光美容专业数年，熟练掌握皮肤科专业的理论知识，对临床各种疾病的诊治具有丰富的临床经验，能娴熟掌握皮肤科多种操作技术，如皮肤美容激光、皮肤外科、皮肤病理等。在各级专业期刊发表文章数篇，近年获市科技进步二等奖一项。','','','','','','1','admin5','0','1626231386','1626231386','1626231386','1626231386','1','','','147','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','丘疹鳞屑性皮肤病，大疱及疱疹性皮肤病、色素性疾病、皮肤血管瘤、皮肤肿瘤及性病等疾病的诊断与治疗；美容激光、皮肤病理、皮肤外科等各项诊疗技术。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2108','article','949','2','admin5','吕东','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_10/8a082ce69ab9b434.JPG','','','6','男，皮肤科主任，主任医师，本科学历。任江苏省医学会皮肤性病分会委员、中国医师协会激光美容专业委员会委员、江苏省中西医结合学会皮肤性病专业委员会常委、盐城市医学会皮肤性病专业委员会主任委员、盐城市中西医结合学会皮肤性病分会主任委员、南通大学医学院兼职教授。“111工程”重点医学人才。从事医学临床工作30年，有较扎实的理论基础与操作技能，熟练处理皮肤科常见病与多发病，对疑难皮肤疾病的治疗和...','','','','','','1','admin5','0','1626231435','1626231435','1626231435','1626231435','1','','','130','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','过敏性皮肤病、结缔组织疾病、大疱性疾病、重症药疹等疾病的治疗；对激光美容、皮肤外科、皮肤病理有一定的研究。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2109','article','950','2','admin5','胥 柯','','0','副主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_10/26586aab31dcaeb2.JPG','','','6','内分泌科副主任医师，医学硕士。主要擅长于诊断和治疗糖尿病及其各种急慢性并发症，对于内分泌其他疾病如甲状腺疾病，骨质疏松，肾上腺及垂体疾病的诊治也有丰富的临床经验。在省级以上杂志发表多篇学术论文。','','','','','','1','admin5','0','1626231515','1626231515','1626231515','1626231515','1','','','0','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','诊断和治疗糖尿病及其各种急慢性并发症。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2110','article','950','2','admin5','郝兰香','','0','副主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_10/5160827f9b909e2a.JPG','','','6','女，医学博士，副主任医师。盐城市创新团队带头人，医学青年人才，江苏省医师协会青春期医学专业委员会委员、江苏省医学会内分泌分会青年委员、江苏省医学会糖尿病分会微血管病学组成员、盐城市内分泌学会委员。擅长矮小症、性早熟、性发育延迟、肥胖症、糖尿病、肾上腺、甲状腺等疾病的诊治。主持并参与盐城市级项目多项，发表SCI论文8篇。','','','','','','1','admin5','0','1626231556','1626231556','1626231556','1626231556','1','','','0','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','糖尿病及其并发症；甲状腺疾病；垂体疾病、肾上腺疾病；性腺疾病及骨质疏松症等疾病的诊断与治疗，对肥胖病、矮小症、性早熟、垂体肿瘤等疾病有较深入的研究。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2111','article','950','2','admin5','边德志','','0','副主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_10/a01d30e44d03faf4.JPG','','','6','男，医学博士，副主任医师；从事内分泌专业工作10余年，在内分泌临床、教学、科研积累了丰富的经验；参与完成省级科研项目3项，发表论文约20篇，其中SCI收录文章3篇。','','','','','','1','admin5','0','1626231598','1626231598','1626231598','1626231598','1','','','91','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','糖尿病及其并发症、甲状腺疾病、高脂血症、身材矮小、发育异常、垂体疾病、嗜铬细胞瘤、库欣综合征、骨质疏松症、痛风、继发性高血压及其他内分泌与代谢疾病的诊断及治疗。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2112','article','950','2','admin5','仇颖','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/c6bf52338d3b460b.JPG','','','6','女，主任医师，硕士研究生学历。任盐城市内分泌学会委员、盐城市骨质疏松与骨矿物盐学会委员。从事内分泌临床工作20余年，具有丰富的临床经验。','','','','','','1','admin5','0','1626231652','1626231652','1626231652','1626231652','1','','','105','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','糖尿病及其并发症、甲状腺疾病、垂体疾病、性腺疾病、痛风、肥胖症及血脂代谢紊乱等疾病的诊治。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2113','article','950','2','admin5','任向东','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/6feeea250a67712d.JPG','','','6','男，主任医师，本科学历。任盐城市内分泌学会副主任委员、盐城市骨质疏松与骨矿物盐学会副主任委员。从事内分泌临床工作20余年，熟练掌握垂体、甲状腺、糖尿病、肾上腺、性腺疾病的诊治，擅长甲状腺功能亢进、甲亢危象、甲状腺功能减退、甲状腺炎、糖尿病的强化治疗、口服降糖药的治疗、胰岛素泵的应用、肾上腺疾病、更年期综合症、骨质疏松症的诊治。在省级以上刊物发表论文10余篇。','','','','','','1','admin5','0','1626231692','1626231692','1626231692','1626231692','1','','','180','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','糖尿病及其并发症、甲状腺疾病、垂体疾病、性腺疾病等的诊治。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2114','article','950','2','admin5','邹艾桂','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/77efb89c94b2e264.JPG','','','6','女，主任医师，本科学历，南通大学兼职教授。从事内分泌临床工作多年，对内分泌科危重、疑难病例、少见病的诊治具有一定的临床经验。同时开展对内分泌肥胖症及糖尿病足，更年期综合症进行心理治疗等。具有深厚的临床治疗和较高的科研学术水平。','','','','','','1','admin5','0','1626231732','1626231732','1626231732','1626231732','1','','','67','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','难治性甲亢突眼、糖尿病足、各类关节疾病、肥胖症、不明原因浮肿的诊治及更年期综合症的综合治疗。','','','','');
