INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2127','article','952','2','admin5','仇玉臣','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/9dec6eb719b2841c.JPG','','','6','男，主任医师，学士学位。任盐城市医学会老年学组副主任委员,盐城市医学会骨质疏松分会副主任委员。从业25年来，潜心研究老年医学、中老年保健，有扎实的理论基础与操作技能，熟练处理老年人常见病与多发病，对疑难疾病的治疗和危重疾病的抢救有较丰富的经验，对中老年保健有独到见解。获盐城市科技进步一等奖1次。','','','','','','1','admin5','0','1626233176','1626233176','1626233176','1626233176','1','','','46','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','老年科糖尿病、高血压、冠心病、脑卒中等疾病的诊断治疗','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2128','article','952','2','admin5','周建平','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/7f13c2e88fab4b2f.JPG','','','6','男，老年医学科主任，主任医师，学士学位.江苏省医学会老年医学分会委员、盐城市医学会老年医学分会主任委员。从事老年医学临床工作20余年，有扎实的理论基础与操作技能，对老年人糖尿病、高血压、冠心病、脑血管病等疾病的诊治具有丰富的临床经验，对老年人疑难疾病的治疗和危重疾病的抢救具有丰富的经验。在各级杂志发表论文30余篇；获盐城市科技成果一等奖等多项奖项。2013年度被盐城市政府评为名医生。','','','','','','1','admin5','0','1626233218','1626233218','1626233218','1626233218','1','','','43','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','老年人糖尿病、高血压、冠心病、脑血管病等疾病的诊治','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2129','article','953','2','admin5','王继东','','0','副主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/5dab275c2792a945.JPG','','','6','男，中医科副主任中医师，本科学历。善于运用中医整体观念脉诊进行辩证脑梗死、颈椎病、肩周炎、腰椎间盘突出症、强直性脊柱炎、高血压、慢性萎缩性胃炎等，先后再省级刊物发表医学论文5篇。','','','','','','1','admin5','0','1626233274','1626233274','1626233274','1626233274','1','','','112','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','运用中医整体观念脉诊进行辩证脑梗死、颈椎病、肩周炎等','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2130','article','953','2','admin5','杨林','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/f6b2ac954a00aa75.JPG','','','6','男，主任中医师。任江苏省中医学会肺系分会委员，盐城市中医学会副会长，盐城市中医学会内科分会副主任委员，是盐城市名中医、盐城市名老中医专家学术经验继承指导老师。从医40余年，专攻中医内科，发明了“清胆合剂”、“残石净合剂”，对系统性红斑狼疮、慢性肾炎、紫癜性肾炎的治疗效果显著。获市科技进步一等奖1项；分别获盐城市政府、江苏省中医药科技进步二等奖3项。在省级以上杂志发表论文80余篇。','','','','','','1','admin5','0','1626233318','1626233318','1626233318','1626233318','1','','','104','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','血栓闭塞性脉管炎、下肢静脉血栓形成、慢性肾炎、紫癜性肾炎、系统性红斑狼疮、术后肝内胆管结石等疑难病的治疗','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2131','article','953','2','admin5','徐晓军','','0','主任中医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/d7faffb0ccbc83ae.JPG','','','6','男，本科学历，主任中医师。主要从事中西医结合治疗老年病及内科疑难病（如高血压、脑卒中、冠心病、慢性胃肠炎、2型糖尿病等）的研究工作。在省级以上刊物发表学术论文7篇，参加《剔瘀通络法治疗下肢深静脉血栓的临床研究》的科研工作，获盐城市2011年度科技进步二等奖。','','','','','','1','admin5','0','1626233366','1626233366','1626233366','1626233366','1','','','72','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','中西医结合治疗慢性消化系统疾病（如慢性胃炎、慢性肠炎、胃肠功能紊乱等）及中医辨证治疗心脑血管疾病（如高血压病、脑卒中等）','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2132','article','953','2','admin5','周翠红','','0','主任中医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/51befeb1c606eb7d.JPG','','','6','女，本科学历，主任中医师。主要从事中西医结合治疗老年病及内科疑难病（如脑梗塞、冠心病、2型糖尿病、紫癜性肾炎、原发性血小板减少症、系统性红色斑狼疮等）的研究工作。在省级以上刊物发表学术论文10篇，参加《重叠疏凿疗法治疗血栓闭塞性脉管炎的临床研究》的科研工作，获盐城市2007年度科技进步一等奖。','','','','','','1','admin5','0','1626233405','1626233405','1626233405','1626233405','1','','','72','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','中西医结合治疗周围血管病（如血栓闭塞性脉管炎、下肢深静脉血栓形成、动脉硬化性闭塞症等）','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2133','article','953','2','admin5','徐萍','','0','主任中医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/1422188d919d12aa.JPG','','','6','女，主任中医师，本科学历，南通大学医学院兼职副教授。从事中医及中西医结合妇科临床工作20余年，师从沪上妇科名家朱南孙、沈仲理教授，擅长运用中医药治疗妇科多发病、常见病。对功能失调性子宫出血、子宫内膜异位症、卵巢囊肿、痤疮、黄褐斑等疾病的治疗积累了较为丰富的临床经验，开设了具有中医特色的妇科专科门诊。在省级以上刊物发表医学论文10余篇。','','','','','','1','admin5','0','1626233447','1626233447','1626233447','1626233447','1','','','53','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','多囊卵巢综合征、卵巢早衰、不孕症、子宫腺肌增生、月经失调、子宫肌瘤、围绝经期综合征，乳腺增生症的诊治','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2134','article','953','2','admin5','瞿立武','','0','主任中医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/65416c500c158be0.JPG','','','6','男，本科学历，主任中医师，盐城市中医药学会理事。先后在省级以上刊物发表医学论文10余篇。参与《重叠疏凿疗法治疗血栓闭塞性脉管炎临床研究》的科研工作，获得2007年度市级科技进步一等奖。','','','','','','1','admin5','0','1626233485','1626233485','1626233485','1626233485','1','','','82','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','运用中西医结合治疗心脑血管疾病，如冠心病、心绞痛、脑血管意外、多种原因引起的心力衰竭、血栓闭塞性脉管炎、糖尿病所致血管病变等杂病','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2135','article','954','2','admin5','还剑东','','0','副主任按摩医师','<!--#p8_attach#-->/cms/item/2021_07/14_11/2afc05db79bff156.JPG','','','6','副主任医疗按摩师。兼任中国足底反射区健康法研究会会员，中华预防学会足部健康法专业委员会，江苏反射反射保健行业协会理事，江苏省中医学会盲人推拿分会理事，具有丰富的理论功底和治疗手法。擅长于治疗颈椎病、肩周炎、腰椎间盘突出症、失眠等推拿适应症。7篇论文在省级刊物上发表，任《足部药物与推拿》等主任副主编。','','','','','','1','admin5','0','1626233538','1626233538','1626233538','1626233538','1','','','0','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','治疗颈椎病、肩周炎、腰椎间盘突出症、失眠等推拿适应症','','','','');
