INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2180','article','957','2','admin5','张标','','0','','<!--#p8_attach#-->/cms/item/2021_07/14_14/1daf695a7a5f87c5.JPG','','','6','男，普通外科主任医师，医学博士。中国第14期援助南美洲圭亚那医疗队队员。从事普通外科临床工作20余年，熟练掌握普通外科专业理论知识，对普外科各类疑难复杂的疾病有丰富的诊治以及手术经验，尤其擅长肝胆、胃肠道肿瘤的微创手术治疗及综合治疗。','','','','','','1','admin5','0','1626245503','1626245503','1626245503','1626245503','1','','','0','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','肝胆、胃肠道肿瘤的微创手术治疗及综合治疗。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2181','article','957','2','admin5','范仁根','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_14/8c70182c87042d15.JPG','','','6','男，主任医师，普外科医学博士，硕士生导师，兼职副教授，盐城市重点医学人才，江苏省333工程重点培养人才，江苏省普外科学组青年委员，2014年赴德国学习研修。研究方向为肝胆外科，对普外科急危重、疑难病例的处理有丰富的临床经验。目前在SCI收录期刊发表医学论文7篇，另在核心期刊、省级期刊发表医学论文20余篇。主持盐城市科研课题三项。有较强的临床工作能力和科研、教学能力。','','','','','','1','admin5','0','1626245558','1626245558','1626245558','1626245558','1','','','1','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','肝胆胰脾疾病、后腹膜肿瘤、胃肠道肿瘤、甲状腺乳房肿瘤、各种疝的手术治疗。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2182','article','957','2','admin5','孙大林','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_14/8060ca14ca61deee.JPG','','','6','男，主任医师，本科学历，南通大学副教授。任中华医学会普外科分会委员、盐城市肝移植工程核心成员。从医二十多年，具有丰富的临床工作经验，深入研究解剖学，将正常解剖、变异解剖、病理解剖灵活应用于工作实践中。手术大胆细腻，成功完成数百例高难度的精品手术。手术谱涉及甲状腺、乳腺、肝脏、胆道、胰腺、脾脏、胃肠、腹膜后等全科领域，尤其在普外科重危疑难疾病诊治方面有独到建树。','','','','','','1','admin5','0','1626245591','1626245591','1626245591','1626245591','1','','','0','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','普外科腹腔镜手术。','','','','');
