INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2233','article','963','2','admin5','仇旭','','0','副主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_16/c8c2c15e2c43e218.JPG','','','6','男，副主任医师，学士学位。任盐城市医学会口腔医学专业分会委员，南通大学兼职讲师。从事口腔专业近20年，在《中外医疗》、《中国现代药物应用》等专业期刊上发表论文多篇；获盐城市科技进步奖三等奖一项。','','','','','','1','admin5','0','1626250196','1626250196','1626250196','1626250196','1','','','0','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','口腔颌面外科、口腔内科、口腔美容、口腔颌面肿块的诊治、复杂牙、阻生牙、多生牙的拔除，口腔颌面外伤急救、牙外伤、脱位、松动牙复位固定，牙周病、口腔黏膜病治疗等。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2234','article','963','2','admin5','陈斯军','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_16/b233cd3115113305.JPG','','','6','男，主任医师，本科学历。现为江苏省医学会美容医师协会美容学组委员，江苏省口腔医学会修复分会委员及盐城市口腔医学会委员。从事口腔专业近20多年，在省级以上刊物发表论文10余篇，获市科技进步奖三等奖1项，市医学新技术引进奖二等奖1项。','','','','','','1','admin5','0','1626250236','1626250236','1626250236','1626250236','1','','','49','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','固定义齿、活动义齿的修复、儿童及成人的错牙合 矫治。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2235','article','963','2','admin5','浦路明','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_16/614003c86de3334d.JPG','','','6','女，主任医师，本科学历。任江苏省口腔医学会正畸分会委员，盐城市口腔医学会委员，兼职副教授。长期从事口腔正畸修复工作，熟练掌握口腔正畸修复领域的理论知识。各种疾病的诊治和操作，具有丰富的临床经验，常规开展儿童青少年和成人的牙颌畸形治疗。在《口腔医学》等省级权威医学期刊物发表多篇专业论文；Twin-Block功能矫治器获得盐城市科技进步三等奖及盐城市新技术引进二等奖。','','','','','','1','admin5','0','1626250275','1626250275','1626250275','1626250275','1','','','71','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','儿童、成人的牙牙合 畸形治疗、成人骨性正畸的非手术治疗；全口义齿、摘局部义齿、烤瓷冠桥等修复治疗。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2236','article','963','2','admin5','葛建华','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_16/aa53c3ba6d243050.JPG','','','6','女，主任医师，本科学历。任江苏省医疗美容主诊医师专家库成员、南通大学兼职教授。从事口腔科工作40年，对口腔内科和外科的疾病诊治有丰富的临床经验。在各级杂志发表医学论文多篇。','','','','','','1','admin5','0','1626250309','1626250309','1626250309','1626250309','1','','','89','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','唇裂、腭裂畸形的手术整复、口腔颌面部良恶性肿瘤的手术治疗、口腔颌面部外伤的急救及缺损的修复重建、微钛板固定、口腔黏膜的疑难病症的诊治、多生牙及埋伏牙的诊断及手术、复杂牙的根管治疗。','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2237','article','963','2','admin5','华春清','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_16/4da602eb3d7e7a7d.JPG','','','6','男，主任医师，硕士研究生学历。任盐城市医学会口腔分会委员、南通大学兼职副教授，盐城市“135”医学重点人才。对口腔专业各方面的理论知识有较好的掌握和实际运用能力，临床经验较为丰富，重点研究方向是口腔种植修复，再植牙的诊断及治疗等。在核心刊物上发表学术论文10余篇；主持承担市级科研项目1项，参与市级科研项目3项，获二等奖1项，三等奖2项。','','','','','','1','admin5','0','1626250342','1626250342','1626250342','1626250342','1','','','80','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','口腔颌面部各种良恶性肿瘤治疗、上下颌骨骨折的治疗及各种微小钛板固定、单双侧唇腭裂整复、多生牙埋伏牙的诊断治疗、骨埋伏阻生牙拔除术、复杂牙根管治疗及其根管再治疗术等。','','','','');
