INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`url`,`attributes`,`summary`,`source`,`author`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`) VALUES ('2247','article','964','2','admin5','吴华新','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_16/8889f9394885d267.JPG','','','6','男，主任医师，学士学位。任盐城市眼科学会副主任委员、江苏省防盲指导组专家组成员，兼职教授。从事眼科临床工作近30年，有较丰富的临床工作经验，对眼科疑难病的诊治颇有心得，尤其是青光眼的早期诊断和治疗，可熟练开展白内障、青光眼、眼眶内肿瘤摘除、玻璃体切割、眼科美容整形等多种眼科手术。发表眼科论文20余篇，主编《白内障与防盲》一书，并参与编写《现代眼科学》《泪器病学》等专著。','','','','','','1','admin5','0','1626250823','1626250823','1626250823','1626250823','1','','','53','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','泪器病，青光眼，晶体病的诊治。','','','','');
