INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2263','3085','1','','','一、盐城市儿童医院一期改造工程保健楼工程监理服务已经相关部门批准建设。工程所需资金自筹，已落实。项目已具备招标条件，现对该本工程进行公开招标。本次招标对投标报名人的资格审查采用资格后审方法确定合格的投标人。 二、盐城市招标代理有限公司受招标人委托具体','172.16.100.2','172.16.100.2','1632703689','<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体\">一、盐城市儿童医院一期改造工程保健楼工程监理服务已经相关部门批准建设。工程所需资金自筹，已落实。项目已具备招标条件，现对该本工程进行公开招标。本次招标对投标报名人的资格审查采用资格后审方法确定合格的投标人。<span><br>\r\n&nbsp;&nbsp;&nbsp; </span>二、盐城市招标代理有限公司受招标人委托具体负责本工程的招标事宜。<span><br>\r\n&nbsp;&nbsp;&nbsp; </span>三、项目概况</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体\">1</span><span style=\"font-size:12.0pt;font-family:宋体\">、项目名称：盐城市儿童医院一期改造工程保健楼工程监理服务。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体\">2</span><span style=\"font-size:12.0pt;font-family:宋体\">、工程地点：盐城市毓龙西路<span>34</span>号</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">3</span><span style=\"font-size:12.0pt;font-family:宋体\">、工程规模：本工程建筑面积约<span>5440</span>平方米。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">4</span><span style=\"font-size:12.0pt;font-family:宋体\">、</span><span style=\"font-size:12.0pt;font-family:宋体\">监理服务期限</span><span style=\"font-size:\r\n12.0pt;font-family:宋体\">：</span><span style=\"font-size:12.0pt;font-family:宋体\">与工程施工同步，并服从工程建设进度要求。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">5</span><span style=\"font-size:12.0pt;font-family:宋体\">、质量要求：</span><span style=\"font-size:12.0pt;font-family:宋体\">合格。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:12.0pt;\r\nfont-family:宋体\">四、招标内容</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:12.0pt;\r\nfont-family:宋体\">盐城市儿童医院一期改造工程保健楼工程监理服务（包括监理的工程范围和监理的阶段范围）。监理的工程范围主要指盐城市儿童医院一期改造工程保健楼工程的监理，具体以招标人提供的施工图纸和监理合同确定的范围为准；阶段范围主要指建设工程施工阶段、保修阶段的监理服务。施工阶段监理服务内容：对建设工程质量、安全、造价、进度进行控制，对合同、信息进行管理，对工程建设相关方的关系进行协调，并履行建设工程安全生产管理法定职责的服务活动。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:12.0pt;\r\nfont-family:宋体\">五、投标人主要资格要求<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';margin-right:-5.35pt;text-align:left;\r\ntext-indent:24.0pt;line-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:12.0pt;font-family:宋体\">1</span><span style=\"font-size:12.0pt;font-family:宋体\">、投标人</span><span style=\"font-size:12.0pt;\r\nfont-family:宋体\">资质条件：投标人须具有建设行政主管部门核发的工程监理综合资质或具有房屋建筑工程监理专业丙级及以上资质的独立法人。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:宋体;text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt\">2</span><span style=\"font-size:\r\n12.0pt\">、本工程项目监理机构主要管理人员是指总监理工程师、专业监理工程师、监理员等。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">2.1 </span><span style=\"font-size:12.0pt;font-family:\r\n宋体\">项目管理（含监理）</span><span style=\"font-size:12.0pt;font-family:宋体\">组主要管理人员配备要求如下：</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">2.1.1</span><span style=\"font-size:12.0pt;font-family:\r\n宋体\">总监理工程师资格要求：总监理工程师必须为投标人本单位的在职职工，具有注册专业为房屋建筑工程的国家注册监理工程师资格。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体\">2.1.2 </span><span style=\"font-size:12.0pt;font-family:宋体\">总监理工程师自本项目投标文件递交截止之日起无在监工程。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">2.1.3 </span><span style=\"font-size:12.0pt;font-family:\r\n宋体\">监理部组成人员配备满足项目需求。</span><span style=\"font-size:12.0pt;font-family:宋体\">项目监理机构其他主要管理人员要求：</span><span style=\"font-size:12.0pt;font-family:宋体\">项目监理机构主要管理人员配备须合理，人员配备不得低于江苏省住房和城乡建设厅公告【<span>2017</span>】第<span>35</span>号文件要求。并确保根据项目需求随时增加人员，满足项目监理服务要求。专业监理工程师必须符合在监工程监理规划监理人员进退场计</span><span style=\"font-size:12.0pt;font-family:宋体\">划要求。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体\">3</span><span style=\"font-size:12.0pt;font-family:宋体\">、</span><span style=\"font-size:12.0pt;\r\nfont-family:宋体\">投标申请人须保证代理人、</span><span style=\"font-size:12.0pt;font-family:\r\n宋体\">本工程</span><span style=\"font-size:12.0pt;font-family:宋体\">投标</span><span style=\"font-size:12.0pt;font-family:宋体\">监理服务机构主要管理人员</span><span style=\"font-size:12.0pt;font-family:宋体\">均为本单位的正式职工，</span><span style=\"font-size:12.0pt;font-family:宋体\">并确保从投标截止日当月向前连续<span>6</span>个月均连续在本单位缴纳养老保险（例：如开标日期为<span>2020</span>年<span>7</span>月的某一天，则投标单位需保证以上人员<span>2020</span>年<span>1-6</span>月养老保险均在本单位缴纳）。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">4</span><span style=\"font-size:12.0pt;font-family:宋体\">、</span><span style=\"font-size:12.0pt;font-family:宋体\">招标人不接受联合体投标。招标人坚决谢绝挂靠单位参与本工程的投标。如在招投标过程中发现以他人名义投标的，没收其全部投标保证金，如中标的将取消其中标资格并按相关法律法规予以处罚。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">5</span><span style=\"font-size:12.0pt;font-family:宋体\">、</span><span style=\"font-size:12.0pt;font-family:宋体\">财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">6</span><span style=\"font-size:12.0pt;font-family:宋体\">、信誉要求：投标人没有被国家、江苏省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">7</span><span style=\"font-size:12.0pt;font-family:宋体\">、本次招标不接受联合体投标。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体\">8</span><span style=\"font-size:12.0pt;font-family:宋体\">、符合相关法律、法规规定的其他要求。</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">六、投标保证金：</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">本项目投标保证金为人民币叁仟元整，具体交纳方式详见招标文件约定。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">八、本项目评标办法采用“</span><span style=\"font-size:12.0pt;font-family:宋体\">综合评估法</span><span style=\"font-size:\r\n12.0pt;font-family:宋体;color:black\">”。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">九、投标报名及招标文件领取时间和地点</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">请符合要求的潜在投标人于</span><span style=\"font-size:12.0pt;font-family:宋体;color:red\">2021</span><span style=\"font-size:12.0pt;font-family:宋体;color:red\">年<span>9</span>月<span>27</span>日至<span>2021</span>年<span>10</span>月 <span>4</span>日</span><span style=\"font-size:12.0pt;font-family:宋体;\r\ncolor:black\">（每日上午<span>8</span>：<span>30</span>时至<span>12</span>：<span>00</span>时，下午<span>14</span>：<span>30</span>时至<span>18</span>：<span>00</span>时）携带单位介绍信原件（或授权委托书原件，注明联系人电话及邮箱）至</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">盐城市招标代理有限公司盐城市钱江财富广场A座4F行政服务部（联系人：张工\r\n&nbsp;0515-89803566）</span><span style=\"font-size:12.0pt;font-family:宋体;\r\ncolor:black\">报名并领取招标文件。<span>&nbsp;&nbsp;</span></span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">十、联系方式</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">招标人：盐城市第一人民医院</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">地址：盐城市毓龙西路<span>34</span>号</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">联系人：金仁甫</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">联系电话：<span>0515-66698027&nbsp;</span></span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">招标代理机构：</span><span style=\"font-size:12.0pt;font-family:宋体\">盐城市招标代理有限公司</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">地址：</span><span style=\"font-size:12.0pt;font-family:宋体\">盐城市钱江财富广场A座4F</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">联系人：</span><span style=\"font-size:12.0pt;font-family:宋体\">吴满霞</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">联系电话：</span><span style=\"font-size:12.0pt;font-family:宋体\">0515-89803566&nbsp; 15950298580</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2264','3086','1','','/ewebeditor/uploadfile/image/2021/09/27/20210927094337170_s.jpg','2021年09月22日2021年新入职护士在经过了一个月紧锣密鼓岗前培训，在医院19个培训基地的共同努力下，圆满的完成了教学任务，全部人员通过了岗前培训，正式成为盐城市第一人民医院护理队伍的一员。今天，在医院进行岗前培训结业典礼，既是感谢所有的带教老师，也是展示我','172.16.100.2','172.16.100.2','1632707206','<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:21.0pt\"><span>2021</span><span style=\"font-family:宋体\">年</span><span>09</span><span style=\"font-family:宋体\">月</span><span>22</span><span style=\"font-family:宋体\">日，</span><span>2021</span><span style=\"font-family:宋体\">年新入职护士在经过了一个月紧锣密鼓岗前培训，在医院</span><span>19</span><span style=\"font-family:宋体\">个培训基地的共同努力下，圆满的完成了教学任务，全部人员通过了岗前培训，正式成为盐城市第一人民医院护理队伍的一员。今天，在医院进行岗前培训结业典礼，既是感谢所有的带教老师，也是展示我们医院年轻护士的风采。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:21.0pt\"><span style=\"font-family:宋体\"><br>\r\n</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:21.0pt\"><span style=\"font-family:宋体\"><a href=\"/ewebeditor/uploadfile/image/2021/09/27/20210927094337170.jpg\" target=\"_blank\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;<img src=\"/ewebeditor/uploadfile/image/2021/09/27/20210927094337170_s.jpg\" border=\"0\"></a><br>\r\n</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\'