INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2344','3167','1','','<!--#p8_attach#-->/cms/item/2021_12/24_11/c6d5e4c363bea22f.jpg.thumb.jpg','急性致死性肺栓塞（FPE）是指以心脏骤停、休克、晕厥、严重呼吸困难及难以纠正的低氧血症为表现的疾病，是急性肺栓塞（PTE）最严重的类型，也是心脏骤停的常见原因之一。据统计，FPE可导致4.1%的肺栓塞病人发生心脏骤停，死亡率高达65%-95%。','172.16.100.2','172.16.100.2','1630379657','<p><span style=\"\">急性致死性肺栓塞（FPE）是指以心脏骤停、休克、晕厥、严重呼吸困难及难以纠正的低氧血症为表现的疾病，是急性肺栓塞（PTE）最严重的类型，也是心脏骤停的常见原因之一。据统计，FPE可导致4.1%的肺栓塞病人发生心脏骤停，死亡率高达65%-95%。</span></p>','','','','','');
