INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2352','3175','1','','<!--#p8_attach#-->/cms/item/2021_12/24_11/bc226cae644b803a.jpg.thumb.jpg','不忘初心谋发展，砥砺奋进谱新篇。11月7日，盐城市第一人民医院隆重举办2021年创新发展暨专科联盟成立大会。','172.16.100.2','172.16.100.2','1636342014','<p><span style=\"\">不忘初心谋发展，砥砺奋进谱新篇。11月7日，盐城市第一人民医院隆重举办2021年创新发展暨专科联盟成立大会。</span></p>','','','','','');
