INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2482','3306','1','','/ewebeditor/uploadfile/image/2022/06/10/20220610105214855001.png','为进一步增强党员干部职工廉洁自律意识，根据市纪委监委、市卫健委及医院“510”廉政教育活动部署，5月27日，盐城市一院组织党员干部90余人赴东台参观廉政文化教育专线，接受廉政实境教育。在黄海森林公园党性教育基地，全体党员同志重温入党誓词，唱响《歌唱祖国》，切','172.16.1.2','172.16.1.2','1654012800','<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">为进一步增强党员干部职工廉洁自律意识，根据市纪委监委、市卫健委及医院“<span>510</span>”廉政教育活动部署，<span>5</span>月<span>27</span>日，盐城市一院组织党员干部<span>90</span>余人赴东台参观廉政文化教育专线，接受廉政实境教育。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">在黄海森林公园党性教育基地，全体党员同志重温入党誓词，唱响《歌唱祖国》，切身感受生命激情，重温峥嵘岁月，提高党性修养。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">在黄海森林公园党风廉政教育基地，通过图文和实物相结合的方式，聆听廉政故事，让大家现场感悟“艰苦奋斗、科学求真、守正创新、绿色发展”的黄海林工精神。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">此次廉政文化实境教育，是一次深刻的学习、是一次心灵的洗礼、更是一次思想的熏陶。大家纷纷表示，在今后工作、学习、生活中，将不断加强自身党性锻炼，遵守党章党纪，廉洁行医，认真履职尽责服务好患者，做一名德技双馨的医务人员。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\"><br>\r\n</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: center; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\"><img width=\"553\" height=\"311\" align=\"center\" src=\"/ewebeditor/uploadfile/image/2022/06/10/20220610105214855001.png\"></span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">&nbsp;</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">&nbsp;</span></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2485','3309','1','','','盐城市第一人民医院为满足临床使用需要，更好地为患者提供医疗服务，经医院院研究同意，拟对医院《出生医学证明》档案整理服务项目进行招标，具体要求以招标文件为准。欢迎符合资格要求的供应商前来报名参与。一、招标内容《出生医学证明》档案整理服务项目，具体情况详','172.16.1.2','172.16.1.2','1604419200','<p><br /></p><p><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">盐城市第一人民医院为满足临床使用需要，更好地为患者提供医疗服务，经医院院研究同意，拟对医院《出生医学证明》档案整理服务项目进行招标，具体要求以招标文件为准。欢迎符合资格要求的供应商前来报名参与。<br />一、招标内容<br />《出生医学证明》档案整理服务项目，具体情况详见“项目需求”等招标人要求。<br />二、资质要求<br />报名单位必须具备《中华人民共和国政府采购法》第22条所规定的条件外，还须具备如下条件：<br />1、在国内市场监督管理部门注册，具有承担本次招标内容能力的独立的法人资格（以营业执照或市场监督管理部门备案材料为准）；<br />2、投标人至少四名项目成员具有档案资格上岗证（提供近半年社保证明，参与该项目人员需与投标文件提供人员保持一致）；<br />3、投标人具有综合档案馆或同类项目案例；<br />4、参加政府采购活动前三年内，没有重大违法记录；<br />5、本项目不接受联合体投标。<br />三、报名时间<br />2020年11月4日至2020年11月10日；<br />北京时间：上午08:30 — 11:30，下午14:30 — 18:00。<br />四、报名地点<br />盐城市青年西路8号805室（江苏仁禾中衡工程咨询房地产估价有限公司）。<br />五、报名需提交材料<br />1、企业营业执照(副本)的复印件；<br />2、投标人提供至少四名项目成员档案资格上岗证及近半年社保证明（参与该项目人员需与投标文件提供人员保持一致）；<br />3、投标人提供综合档案馆或同类项目案例资料，如合同复印件等；<br />4、法定代表人直接参与报名的，需提供法定代表人身份证原件，同时提供复印件；委托代理人参与报名的，需提供法定代表人授权委托书、委托代理人身份证原件，同时提供法定代表人以及委托代理人身份证复印件；<br />5、提供信用查询记录（登录信用中国：www.creditchina.gov.cn，查询信用记录并截图）；<br />6、封面注明投标公司名称、投标人电话、邮箱。<br />注：以上报名材料请加盖投标单位红章、按顺序装订成册（概不退还）。<br />六、招标文件的获取<br />在报名后的3个工作日内，将招标文件通过邮件发送给投标报名人，注意查收（不另行电话通知），报名单位根据其做好标书等待开标。<br />七、开标时间、地点：另行通知。<br />八、联系方式<br />联系科室：盐城市第一人民医院主院区（南区）门诊4楼招标采购中心<br />联系人：张老师<br />联系电话：0515-66696855<br />联系地址：盐城市人民南路66号<br />邮编：224000<br /><br />招标代理公司：江苏仁禾中衡工程咨询房地产估价有限公司<br />联系人：胡女士<br />联系电话：13815581495<br />联系地址：盐城市青年西路8号805室<br />邮编：224006<br /><br />盐城市第一人民医院<br />2020年11月4日<br /><br /></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2486','3310','1','','','一、盐城市第一人民医院广济医院UPS采购及安装项目已具备招标条件，诚邀潜在的投标人参加本项目的投标工作。二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。三、项目概况3.1项目名称：盐城市第一人民医院广济医院UPS采购及安装3.2建','172.16.1.2','172.16.1.2','1604419200','<p><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">一、盐城市第一人民医院广济医院UPS采购及安装项目已具备招标条件，诚邀潜在的投标人参加本项目的投标工作。<br />二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。<br />三、项目概况<br />3.1项目名称：盐城市第一人民医院广济医院UPS采购及安装<br />3.2建设地点：盐城市大庆中路52号。<br />3.3招标范围：盐城市第一人民医院广济医院UPS采购、安装及调试，具体内容详见招标人提供的设计图纸和工程量清单，招标人保留对上述招标范围进行适当调整的权利。<br />3.4工期要求：15日历天，具体开工时间以招标人书面通知为准。<br />3.5质量标准：符合国家“合格”标准，并通过相关部门验收合格。<br />四、投标申请人应当具备的主要资格条件：<br />4.1投标人具有建设行政主管部门核发的电子与智能化工程二级及以上专业承包资质，同时具备独立法人资格和有效的安全生产许证。<br />4.2项目负责人资格要求<br />项目负责人具有建筑工程或机电安装工程专业二级及以上建造师资格（投标时须提供建造师注册证书或临时执业证书）和持有安全生产考核合格证（B类）。<br />4.4财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<br />4.5信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。<br />4.6委托代理人为投标人本单位正式职工，从投标截止日当月向前连续6个月均在投标人本单位缴纳养老保险（例：如投标截止日期为2020年10月*日，则为2020年4-9月养老保险均在投标人本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。<br />五、投标保证金：<br />本项目投标保证金为人民币贰仟圆整，具体交纳方式详见招标文件约定。<br />六、本项目资格审查采用“资格后审”办法。<br />七、本项目评标办法采用“合理低价法”。<br />八、招标文件领取时间和地点<br />请符合要求的潜在投标人于2020年11月4日至2020年11月8日（每日上午8：30时至12：00时，下午14：30时至18：00时）携带单位介绍信原件（或授权委托书原件，注明联系人电话及邮箱）至江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号805室）报名并领取招标文件。 &nbsp;<br />招标人：盐城市第一人民医院<br />地址：盐城市毓龙西路166号<br />联系人：金仁甫<br />联系电话：66698027<br /><br />招标代理机构：江苏仁禾中衡工程咨询房地产估价有限公司<br />地址：盐城市青年西路8号<br />联系人：刘永杰、胡荣艳<br />联系电话：0515-69057406、13626222271<br />日期：2020年11月4日<br /><br /></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2487','3311','1','','','一、盐城市儿童医院配电房改造工程已具备招标条件，诚邀潜在的投标人参加本项目的投标工作。二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。三、项目概况3.1项目名称：盐城市儿童医院配电房改造工程3.2建设地点：盐城市毓龙西路6号','172.16.1.2','172.16.1.2','1604419200','<p style=\"text-indent: 2em;\"><br /></p><p><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">一、盐城市儿童医院配电房改造工程已具备招标条件，诚邀潜在的投标人参加本项目的投标工作。<br />二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。<br />三、项目概况<br />3.1项目名称：盐城市儿童医院配电房改造工程<br />3.2建设地点：盐城市毓龙西路6号。<br />3.3招标范围：盐城市儿童医院高压配电房搬迁改造工程施工，具体内容详见招标人提供的设计图纸和工程量清单，招标人保留对上述招标范围进行适当调整的权利。<br />3.4工期要求：15日历天，具体开工时间以招标人书面通知为准。<br />3.5质量标准：符合国家“合格”标准。<br />四、投标申请人应当具备的主要资格条件：<br />4.1投标人须是具有建设行政主管部门颁发的建筑工程施工总承包三级及以上施工资质的独立法人，同时具有有效的安全生产许可证。<br />4.2项目负责人资格要求<br />项目负责人具有建筑工程专业二级及以上建造师资格,并持有安全生产考核合格证（B类）。<br />4.3财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<br />4.4信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。<br />4.5委托代理人为投标人本单位正式职工，从投标截止日当月向前连续6个月均在投标人本单位缴纳养老保险（例：如投标截止日期为2020年10月*日，则为2020年4-9月养老保险均在投标人本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。<br />4.6本次招标工程招标人一律不接受联合体投标报名。<br />五、投标保证金：具体金额及交纳方式详见招标文件约定。<br />六、本项目资格审查采用“资格后审”办法。<br />七、本项目评标办法采用“合理低价法”。<br />八、招标文件领取时间和地点<br />请符合要求的潜在投标人于2020年11月4日至2020年11月8日（每日上午8：30时至12：00时，下午14：30时至18：00时）携带单位介绍信原件（或授权委托书原件，注明联系人电话及邮箱）至江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号805室）报名并领取招标文件。 &nbsp;<br />&nbsp;<br />招标人：盐城市第一人民医院<br />地址：盐城市毓龙西路6号<br />联系人：金仁甫<br />联系电话：0515-66698027<br />&nbsp;<br />招标代理机构：江苏仁禾中衡工程咨询房地产估价有限公司<br />地址：盐城市青年西路8号<br />联系人：刘永杰、胡荣艳<br />联系电话：0515-69057406、13626222271<br />日期：2020年11月4日</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2488','3312','1','','','一、招标条件本招标项目 盐城市儿童医院一期工程 已经相关部门批准建设，招标人为 盐城市第一人民医院，工程所需资金来源 国有资金，100% 。项目已具备招标条件，现对该项目的工程结算审核及零星工程招标代理服务 进行招标。二、项目概况1、项目名称：盐城市儿童医院一','172.16.1.2','172.16.1.2','1604073600','<p><br /></p><p><br /><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">一、招标条件<br />本招标项目 盐城市儿童医院一期工程 已经相关部门批准建设，招标人为 盐城市第一人民医院，工程所需资金来源 国有资金，100% 。项目已具备招标条件，现对该项目的工程结算审核及零星工程招标代理服务 进行招标。<br />二、项目概况<br />1、项目名称：盐城市儿童医院一期工程结算审核及零星工程招标代理服务。<br />2、服务地点：盐城市第一人民医院、盐城市儿童医院。<br />3、工程规模：本项目工程总造价约4000万元。<br />4、标段划分及招标内容<br />本次招标项目为一个标段，招标内容为盐城市儿童医院一期工程结算审核及零星工程招标代理服务。 内容如下：<br />4.1 工程结算审核服务：工程竣工结算的编制与审核；与工程造价业务有关的咨询服务等。<br />4.2招标代理服务的主要工作内容（包含但不限于）：向招标人提供工程策划和代理咨询建议；负责拟定发包方案，并报业主认可；发布招标公告（发出投标邀请书）；编制资格预审文件，组织接收投标人报名，审查潜在投标人资格，协助业主确定潜在投标人（如有资格预审项目）；编制招标文件(含编制招标工程量清单、标底、招标控制价)并对其合法性、准确性和严密性负责；组织现场踏勘和答疑；组织开标、评标；处理招标过程中出现的质疑、投诉等事项；草拟工程合同；编制招投标情况书面报告，招投标资料整理归档；及时、主动、有效、稳妥的跟踪解决招标后可能存在的各种问题，减少投诉。<br />三、投标人资格要求<br />1、投标人必须为具有工程造价咨询甲级资质的独立法人，近三年未因业务质量问题和违法违规行为受到相关部门处理处罚。<br />2、项目组拟派人员的要求：<br />2.1项目组拟派人员至少包括（但不限于）以下人员：<br />①项目负责人1名，必须是本单位在岗在职的注册造价工程师，年龄不超过55周岁，并具备工程建设类中级及以上职称。<br />②土建、安装专业的造价工程师各1名，必须是本单位在岗在职的注册造价工程师。<br />③除上述①、②项人员外，其他人员必须是投标人本单位在岗在职的员工，人员数量、专业根据项目服务需要配备。<br />④工程审计服务与招标代理服务人员互不兼职。<br />2.2 项目组组成人员驻场要求：<br />项目负责人必须常驻现场，须接受招标人的日常考核和值班安排。<br />3、投标人须保证委托代理人及投标项目负责人、投标项目组成员均为本单位的正式职工，并确保从2020 年1 月开始至投标截止之日当月至少连续6个月均已在本单位缴纳养老保险（例：如开标日期为2020年10月的某一天，则投标单位需保证以上人员2020年2月以后任意连续6月养老保险均在本单位缴纳）。投标时无需提供养老保险证明材料。<br />4、招标人不接受联合体投标。<br />5、招标人坚决谢绝挂靠单位参与本工程的投标。如在招投标过程中发现以他人名义投标的，没收其全部投标保证金，如中标的将取消其中标资格并按相关法律法规予以处罚。<br />6、财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<br />7、信誉要求：<br />7.1 投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。<br />7.2 投标人不得存在：近3年内有行贿犯罪行为且被记录，或者法定代表人有行贿犯罪记录且自记录之日起未超过5年的情形。<br />7.3 投标人被人民法院列为失信被执行人的信息在“信用中国”、“信用江苏”网站上被公布的，自载明的发布之日开始，直至撤销或者更正信息中载明的发布时间的期间，本项目的招标人拒绝其投标，资格审查不予通过，不得被确定为中标候选人、中标人。在一次招投标活动中，资格预审申请人、投标人或者中标候选人因正被列为失信被执行人，导致其资格预审不通过或者被取消中标候选人资格、中标人资格的，不因其之后失信信息被撤销或更正而改变已经作出的决定。 <br />7.4 投标人作为失信联合惩戒对象，被采取依法限制参与建设工程招投标惩戒措施的，被有关部门推送在“信用中国”、“信用江苏”、“信用盐城”相关网站公示且在有效期内的，本项目的招标人拒绝其投标，资格审查不予通过，不得被确定为中标候选人、中标人。在一次招投标活动中，资格预审申请人、投标人或者中标候选人因正被列为联合惩戒对象，导致其资格预审不通过或者被取消中标候选人资格、中标人资格的，不因其之后失信信息被撤销或更正而改变已经作出的决定。<br />7.5 投标人应保证符合上述信誉要求，如被证明违反，除按照招标文件处理，还将被视为不诚信行为，由相关部门按规定处理。<br />8、根据不相容岗位职责分离原则，“盐城市儿童医院一期招标代理服务单位”的服务单位不得参与本次投标。<br />四、评标办法<br />本项目资格审查方法采用“资格后审”合格制；本项目评标办法采用“综合评估法”。 具体详见招标文件。<br />五、招标文件的获取<br />1、请符合资格条件的投标人于2020年10月31日至2020年11月4日，上午8：30时至12：00时，下午14：30时至18：00时，携带投标申请人单位介绍信或授权委托书原件（注明联系人、联系方式及邮箱）及本人身份证原件购买并领取招标文件等材料,逾期领取的不予接收。<br />2、领取地点：江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）。<br />3、联系电话： 刘永杰，0515-69057406；手机：13626222271 。<br />六、本工程严禁挂靠、转包，一经核实挂靠、转包的，将被取消投标、中标资格，并按相关规定进行处罚，直至建议有关部门吊销资质证书。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 招标人：盐城市第一人民医院&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 时间：2020年10月31日 &nbsp;<br /><br /></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2489','3313','1','','','1、盐城市第一人民医院主院区3#DSA手术室改造工程，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2、项目概况与招标内容2.1项目名称：盐城市第一人民医院主院区3#DSA手术室改造工程。2.2项目地点：盐城市第一人民医院主院区','172.16.1.2','172.16.1.2','1604073600','<p style=\"text-indent: 0em;\" dir=\"ltr\"><span style=\"font-size: 18px; font-family: 宋体, SimSun;\"><br /></span></p><p style=\"text-indent: 0em;\" dir=\"ltr\"><span style=\"font-size: 18px; font-family: 宋体, SimSun;\">1、盐城市第一人民医院主院区3#DSA手术室改造工程，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。<br />2、项目概况与招标内容<br />2.1项目名称：盐城市第一人民医院主院区3#DSA手术室改造工程。<br />2.2项目地点：盐城市第一人民医院主院区（人民南路66号）。<br />2.3招标内容：盐城市第一人民医院主院区3#DSA手术室改造工程,具体内容详见招标人提供的工程量清单。<br />2.4发包方式：包工包料。<br />3、投标人资格要求<br />3.1投标人须是同时具有建筑机电安装工程专业承包三级及以上资质和建筑智能化工程专业承包三级及以上资质（或建筑智能化工程设计与施工二级及以上资质）的独立法人，并具有有效的安全生产许可证。<br />3.2拟派的项目经理必须具有贰级及以注册建造师证书（专业为机电安装工程），并具有安全生产B类证，且从本工程投标文件递交截止之日起无在建工程。（提供项目经理建造师证书、安全生产考核合格证B类）。<br />3.3项目经理、委托代理人为投标人本单位正式职工，从投标截止日当月向前连续6个月均在投标人本单位缴纳养老保险（例：如投标截止日期为2020年10月*日，则为2020年4-9月养老保险均在投标人本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。<br />4、投标报名及招标文件的获取<br />请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话及邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）投标报名，投标报名时间2020年10月31日8时至2020年11月4日18时。联系人：刘永杰，0515-69057406；手机：13626222271。<br />5、本项目采用竞争性谈判方式。<br />6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。<br />7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。<br />其余内容详见招标文件。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; 招标人：盐城市第一人民医院<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 时间：2020年10月31日</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2490','3314','1','','','因医院实际工作需要，于2020年10月22日进行招标的项目，经评标小组评审，中标结果如下：一、盐城市第一人民医院北区6号楼2F西侧病房呼叫系统更换等项目中标单位：江苏中灿机电工程有限公司中标价：49500元二、盐城市第一人民医院北区发热门诊CT机房改造项目中标单位：宜','172.16.1.2','172.16.1.2','1603296000','<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">因医院实际工作需要，于2020年10月22日进行招标的项目，经评标小组评审，中标结果如下：<br />一、盐城市第一人民医院北区6号楼2F西侧病房呼叫系统更换等项目<br />中标单位：江苏中灿机电工程有限公司<br />中标价：49500元<br />二、盐城市第一人民医院北区发热门诊CT机房改造项目<br />中标单位：宜兴市日森防辐射设备有限公司<br />中标价：183000元<br />三、盐城市第一人民医院北区放疗楼等处中央空调管道维修更换等项目<br />中标单位：江苏诚锐暖通设备有限公司<br />中标价：44000元<br />四、盐城市第一人民医院发电机组维保项目<br />中标单位：泰州东康机电工程有限公司<br />中标价：124800元<br />为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监察室联系，电话0515-66696810。<br />&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; 盐城市第一人民医院 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2020年10月22日</span><br /><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2491','3315','1','','','各医疗设备生产企业、经营企业：盐城市儿童医院由盐城市第一人民医院代为建设，拟采购一批医疗设备（设备清单见附件1），项目资金已落实，将于近期开展市场调研，欢迎符合条件的医疗设备生产企业、经营企业前来参与。有意愿参与本次市场调研的医疗设备生产企业、经营企','172.16.1.2','172.16.1.2','1603296000','<p style=\"line-height: 16px;\"><br /></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">各医疗设备生产企业、经营企业：</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">盐城市儿童医院由盐城市第一人民医院代</span><span style=\"color:#333333; font-family:\">为</span><span style=\"color:#333333; font-family:\">建</span><span style=\"color:#333333; font-family:\">设</span><span style=\"color:#333333; font-family:\">，拟采购一批医疗设备（设备清单见附件</span><span style=\"color:#333333; font-family:\">1</span><span style=\"color:#333333; font-family:\">），项目资金已落实，将于近期开展市场调研，欢迎符合条件的医疗设备生产企业、经营企业前来参与。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">有意愿参与本次市场调研的医疗设备生产企业、经营企业请于20</span><span style=\"color:#333333; font-family:\">20</span><span style=\"color:#333333; font-family:\">年10月 </span><span style=\"color:#333333; font-family:\">31</span><span style=\"color:#333333; font-family:\">日前</span><span style=\"color:#333333; font-family:\">将相关材料发送至邮箱：</span><a href=\"mailto:ycyysbc@qq.com。\"><span style=\"color:#0000ff; font-family:\">ycyysbc@qq.com。</span></a></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">现场咨询会时间另行通知。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24.1pt; \"><span style=\"color:#333333; font-family:\">报名须知：</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">1.仅允许产品生产厂家或取得生产厂家授权期限不少于一年的区域级代理经销商参与本</span><span style=\"color:#333333; font-family:\">次</span><span style=\"color:#333333; font-family:\">设备采购的咨询、调研、招标等活动。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">2.具有独立承担民事责任的能力。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">3.具有履行合同所必须具备的设备和专业知识。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">4.具有良好的商业信誉。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">5.近3年内在经营活动中无严重不良记录。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">6.按要求填写《盐城市第一人民医院设备咨询报名表》（见附件2），并上传医疗器械注册证、医疗器械经营许可证、销售代表授权书、产品销售授权书原件扫描件。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">联系人：李老师</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">联系电话：0515-88508946</span></p><p style=\"margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; widows:0; \"><span style=\"font-family:\">&nbsp;</span></p><p style=\"line-height: 16px;\"><a style=\"font-size:12px; color:#0066cc;\" href=\"http://localhost/upload/files/2020/10/a3e0ecdacfd9708e.xls\" title=\"附件1.xls\">附件1.xls</a></p><p style=\"line-height: 16px;\"><a style=\"font-size:12px; color:#0066cc;\" href=\"http://localhost/upload/files/2020/10/27678a6cd7df89e9.docx\" title=\"附件2.docx\">附件2.docx</a></p><p><br /></p>','','','','','');
