INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2564','3388','1','','','为了确保医用信息化建设的网络安全，按照“公平，公正，公开”的原则，对我院急需采购一批网络安全设备与软件，现进行市场调研，欢迎有资质的公司前来报名，现将有关事项公告如下：一、本次招标采购目录1、网络安全设备与软件 基本要求：按三级等保2.0版，信息网络安全','172.16.1.2','172.16.1.2','1590163200','<p style=\"text-indent:28px\"><span style=\"font-size: 18px;\"><span style=\"font-family: 宋体;\">为了确保医用信息化建设的网络安全，按照“公平，公正，公开</span>”<span style=\"font-family: 宋体;\">的原则，对我院急需采购一批网络安全设备与软件，现进行市场调研，欢迎有资质的公司前来报名，现将有关事项公告如下：</span></span></p><p style=\"text-indent:28px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p><span style=\"font-family: 宋体; font-size: 18px;\">一、本次招标采购目录</span></p><p><span style=\"font-size: 18px;\">1<span style=\"font-size: 18px; font-family: 宋体;\">、网络安全设备与软件</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px; font-family: 宋体;\">基本要求：按三级等保</span>2.0<span style=\"font-size: 18px; font-family: 宋体;\">版，信息网络安全需要增加安全设备（含态势感知系统、防火墙、漏洞扫描、入侵检测系统、病毒库升级费）。</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p><span style=\"font-size: 18px;\">2<span style=\"font-size: 18px; font-family: 宋体;\">、信息化终端安全管理系统</span>&nbsp;&nbsp; </span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px; font-family: 宋体;\">基本要求：对医院所有信息终端进行准入、监管。</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p><span style=\"font-size: 18px;\">&nbsp;</span></p><p><span style=\"font-family: 宋体; font-size: 18px;\">二、供应商资格要求</span></p><p><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51);\">1、具备独立法人的资格</span></p><p><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51);\">2、具有良好的商业信誉</span></p><p><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51);\">3、具有独立承担民事责任的能力</span></p><p><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51);\">4、须符合信息化政府采购资质</span></p><p><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51);\">5、具有履行合同所必需的设备和专业技术能力</span></p><p><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51);\">6、近三年内在经营活动中无严重不良记录</span></p><p><span style=\"font-size: 18px;\">&nbsp;</span></p><p><span style=\"font-family: 宋体; font-size: 18px;\">三、报名</span></p><p><span style=\"font-size: 18px;\">1<span style=\"font-size: 18px; font-family: 宋体;\">、报名方式：邮箱报名</span></span></p><p><span style=\"font-size: 18px;\">2<span style=\"font-size: 18px; font-family: 宋体;\">、截至时间：</span>2020<span style=\"font-size: 18px; font-family: 宋体;\">年</span>5<span style=\"font-size: 18px; font-family: 宋体;\">月</span>25<span style=\"font-size: 18px; font-family: 宋体;\">日</span>&nbsp;&nbsp; 18<span style=\"font-size: 18px; font-family: 宋体;\">：</span>00<span style=\"font-size: 18px; font-family: 宋体;\">时</span></span></p><p><span style=\"font-size: 18px;\">3<span style=\"font-size: 18px; font-family: 宋体;\">、报名邮箱：</span><span style=\"font-size: 18px; font-family: 等线;\">ycyysbc@qq.com</span></span></p><p><span style=\"font-size: 18px;\">&nbsp;</span></p><p><span style=\"font-family: 宋体; font-size: 18px;\">四、报名须知</span></p><p><span style=\"font-family: 宋体; font-size: 18px;\">凡具备资格要求的供应商报名时，须将企业营业执照正本、资质证书、法定代表人身份证明、授权委托书等原件扫描件发送邮箱。</span></p><p><span style=\"font-size: 18px;\">&nbsp;</span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px; font-family: 宋体;\">五、本次招标项目联系人：李伟</span>&nbsp;&nbsp;&nbsp; <span style=\"font-size: 18px; font-family: 宋体;\">联系电话：</span>0515-88508946&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 0515-83578946</span></p><p><span style=\"font-size: 18px;\">&nbsp;</span></p><p><span style=\"font-family: 宋体; font-size: 18px;\">六．盐城市第一人民医院咨询报名表（下载）</span></p><p style=\"line-height: 16px;\"><a style=\"color: rgb(0, 102, 204); font-size: 18px; text-decoration: underline;\" href=\"http://localhost/upload/files/2020/5/a75a2afc6e2e96af.docx\" title=\"盐城市第一人民医院设备咨询报名表.docx\"><span style=\"font-size: 18px;\">盐城市第一人民医院设备咨询报名表.docx</span></a></p><p><br /></p>','','','','','');
