INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6496','0','cms','item','add','0','859','','宣传处','添加内容(前台) | 盐城市第一人民医院消防零星整改项目招标公告','/index.php/cms/item-list-category-859-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/u.php/cms/item-add?cid=859&model=article&type=2\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院消防零星整改项目招标公告\',\n  \'title_color\' => \'\',\n  \'html\' => \'1\',\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'timestamp\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'summary\' => \'\',\n  \'sub_title\' => \'\',\n  \'verify_frame\' => \'\',\n  \'custom_a\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市第一人民医院消防零星整改项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">1</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、工程概况<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></b></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目名称：盐城市第一人民医院消防零星整改项目</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目地点：盐城市第一人民医院</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标内容：</span><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市第一人民医院消防零星整改项目施工</span><span style=\"font-family: 宋体; font-size: 12pt;\">。具体内容详见招标人提供的工程量清单。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">质量标准：符合国家“合格”标准。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.5</span><span style=\"font-family: 宋体; font-size: 12pt;\">工期要求：<span>15</span>日历天，具体开工日期以招标人的通知。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标规模：工程造价约<u><span>8.93</span></u>万元。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.7</span><span style=\"font-family: 宋体; font-size: 12pt;\">发包方式：包工包料。</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">2 </span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">投标人应当具备的主要资格条件：</span></b></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 22pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人须具有有效的建筑装修装饰工程专业承包二级及以上施工资质的独立法人，并具有有效的安全生产许可证。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">、项目负责人资格类别和等级<span>:</span>项目负责人须提供建筑工程专业二级及以上建造师注册证书和安全生产考核合格证（<span>B</span>类）。项目负责人从本工程投标文件递交截止之日起必须满足下列条件：</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">（<span>1</span>）项目负责人不得同时在两个或者两个以上单位受聘或者执业特指以下三种情形：</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">① 同时在两个及以上单位签订劳动合同或缴纳社会保险；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">② 将本人执（职）业资格证书同时注册在两个及以上单位；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">③ 项目负责人担任其他公司法定代表人。 </span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">（<span>2</span>）项目负责人是非变更后无在建工程，或项目负责人是变更后无在建工程（必须原合同工期已满且变更备案之日已满<span>6</span>个月），或因非承包方原因致使工程项目停工或因故不能按期开工、且已办理了项目负责人解锁手续，或项目负责人有在建工程，但该在建工程与本次招标的工程属于同一工程项目、同一项目批文、同一施工地点分段发包或分期施工的情况且总的工程规模在项目负责人执业范围之内。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">项目负责人投标截止时不得同时在其他建设工程项目中担任现场管理人员、不在本项目兼任本项目的其它岗位。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">在建工程：处于中标结果公告（直接发包的项目以网上合同备案或归集为准）到合同约定的工程全部完成且竣工验收合格期间的工程。竣工验收证明是指由建设单位（或监理）组织工程建设各方验收合格，并签署相应的单位工程质量竣工验收记录或者分部工程质量验收记录等验收文件。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人须保证项目负责人及代理人均为本单位的正式职工，并确保从投标截止之日当月向前连续<span>6</span>个月均已在本单位缴纳养老保险（例：如开标日期为<span>2020</span>年<span>7</span>月的某一天，则投标单位需保证以上人员<span>2020</span>年<span>1-6</span>月养老保险均在本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">&nbsp;&nbsp; 2.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">其他要求</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">&nbsp;&nbsp; 2.4.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<span>&nbsp;</span></span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">&nbsp;&nbsp; 2.4.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">信誉要求：①投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.5 </span><span style=\"font-family: 宋体; font-size: 12pt;\">本次招标不接受联合体投标。</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 21pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">3</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、投标报名及招标文件的获取</span></b></p>\r\n<p style=\\\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\\\'><span style=\"background: white;\">请符合上述资质条件的投标人携带单位介绍信到江苏双清工程造价咨询有限公司投标报名并领取招标文件，投标报名时间</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">7</span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">26</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">时</span><span style=\"background: white; font-family: 宋体;\">30</span><span style=\"background: white;\">分至</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">1</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">18</span><span style=\"background: white;\">时。联系人：张旭良；手机：</span><span style=\"background: white; font-family: 宋体;\">0515-88200706,13515130964</span>，逾期不再受理<span style=\"background: white;\">。</span></p>\r\n<p style=\\\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\\\'><span style=\"background: white; font-family: 宋体;\">4</span><span style=\"background: white;\">、</span><b>本项目采用“资格后审”，“综合评估法”</b><span style=\"background: white;\">。</span></p>\r\n<p style=\\\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\\\'><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p>\r\n<p style=\\\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\\\'><span style=\"background: white; font-family: 宋体;\">6</span><span style=\"background: white;\">、招投标监督管理机构及投诉电话：盐城市第一人民医院监督审查室，联系电话</span><span style=\"background: white; font-family: 宋体;\">0515-66696810</span><span style=\"background: white;\">。</span></p>\r\n<p style=\\\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"color: black; font-family: 宋体;\">招标人：盐城市第一人民医院</span></p>\r\n<p style=\\\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"color: black; font-family: 宋体;\">地<span>&nbsp; </span>址：人民南路<span>66</span>号</span></p>\',\n  ),\n  \'views\' => \'\',\n  \'url\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'action\' => \'add\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'attachment_hash\' => \'00ed00e257f73280\',\n  \'verify\' => 1,\n  \'verifier\' => \'宣传处\',\n)','1658825243');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6497','0','cms','item','add','0','859','','宣传处','添加内容(前台) | 盐城市第一人民医院变配电设备维修保养、试验检测等项目','/index.php/cms/item-list-category-859-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/u.php/cms/item-add?cid=859&model=article&type=2\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院变配电设备维修保养、试验检测等项目\',\n  \'title_color\' => \'\',\n  \'html\' => \'1\',\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'timestamp\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'summary\' => \'\',\n  \'sub_title\' => \'\',\n  \'verify_frame\' => \'\',\n  \'custom_a\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\\\'background: white; margin: 0cm 0cm 0pt; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char;\\\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">盐城市第一人民医院变配电设备维修保养、试验检测等项目</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">一、</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">盐城市招标代理有限公司</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">受招标人委托具体负责本项目的招标事宜。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">二、符合本项目招标公告资格要求的投标的申请人均可参加该项目投标。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><b><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">三、项目概况：</span></b></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">1</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">、工程名称：</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">盐城市第一人民医院变配电设备维修保养、试验检测等项目</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">2</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">、工程地点：</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">盐城市第一人民医院主院区（人民南路<span>66</span>号）、北区（毓龙西路<span>166</span>号）、儿童医院（毓龙西路<span>6</span>号）、五官科医院（南城河路<span>12</span>号）</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">3</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">、招标规模：工程造价约<span>28.5</span>万元。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">4</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">、招标范围及内容：</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">医院四个院区高低压变配电设备维修保养、试验检测等项目。具体内容详见招标人的要求和工程量清单</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">，招标人保留对上述工程规模进行适当调整的权利。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体;\">5</span><span style=\"font-family: 宋体;\">、工期要求：</span><span style=\"font-family: 宋体;\">30</span><span style=\"font-family: 宋体;\">日历天，具体开工日期以招标人的通知</span><span style=\"font-family: 宋体;\">。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">6</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">、质量标准：</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">符合国家及行业合格标准，确保医院变配电设备安全稳定运行。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><b><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">四、投标人资格要求：</span></b></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; vertical-align: baseline; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体;\">1</span><span style=\"font-family: 宋体;\">、投标人具有建设行政主管部门核发的电力工程施工总承包或输（送）变电工程专业承包三级及以上资质的独立法人，并持有有效的安全生产许可证。</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; vertical-align: baseline; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体;\">2</span><span style=\"font-family: 宋体;\">、投标人具有国家电监会派出机构颁发的承装（试、修）类均为五级及以上的电力设施许可证。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">3</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">、本次招标不接受联合体投标。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">4</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">、财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<span> </span></span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">5</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">、信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">6</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">、投标申请人须保证项目负责人及代理人均为本单位的正式职工，并确保从投标截止之日当月向前连续<span>6</span>个月均已在本单位缴纳养老保险（例：如开标日期为<span>2020</span>年<span>7</span>月的某一天，则投标单位需保证以上人员<span>2020</span>年<span>1-6</span>月养老保险均在本单位缴纳），投标时无需提供缴费记录证明，中标候选人公示前、异议（投诉）提出相关单位需提供缴费记录给招标人核验。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><b><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">五、招标文件获取：</span></b></p>\r\n<p align=\"left\" style=\\\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体;\">1</span><span style=\"font-family: 宋体;\">、获取时间：<span>2022</span>年<span>7</span>月<span>26</span>日至<span>2022</span>年<span>8</span>月<span>1</span>日，每日<span>8:30 </span>时至<span>18</span>：<span>00</span>时（北京时间<span>,</span>下同）。</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体;\">2</span><span style=\"font-family: 宋体;\">、获取方法：凡有意参加本次招标的投标人，须在获取招标文件截止时间前，携带单位介绍信（加盖公章、注明联系人、联系方式及邮箱）至盐城市招标代理有限公司行政服务部报名，领取招标文件。</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体;\">3</span><span style=\"font-family: 宋体;\">、招标文件获取联系人：张工，联系号码：<span>0515-89803566</span>、<span>13921809379</span>。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><b><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">六、投标保证金</span></b></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">本项目投标保证金为人民币<span>3000</span>元，具体详见招标文件。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">七、本项目资格审查办法采用<b>资格后审</b>；评标办法采用<b>综合评估法</b>，具体详见招标文件。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">八、本项目严禁挂靠，一经核实挂靠，将被取消投标、中标资格，并按相关规定进行处罚，直至建议有关部门吊销资质证书。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><b><span style=\"font-family: 宋体; font-size: 10.5pt;\">九、联系方式</span></b></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">招标人：盐城市第一人民医院</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体;\">地<span>&nbsp; </span>址：盐城市人民南路<span>66</span>号</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">联系人：</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">费工</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\\\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">电<span>&nbsp; </span>话：</span><span style=\"font-family: 宋体;