INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6579','0','cms','item','add','0','841','','宣传处','添加内容(前台) | 盐城市急危重症学术会议成功举办','/index.php/cms/item-list-category-841-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/u.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市急危重症学术会议成功举办\',\n  \'title_color\' => \'\',\n  \'html\' => \'1\',\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'timestamp\' => \'\',\n  \'source\' => \'重症医学科\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'summary\' => \'\',\n  \'sub_title\' => \'\',\n  \'verify_frame\' => \'\',\n  \'custom_a\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\\\'margin: 0cm 0cm 8pt; text-align: justify; line-height: 1.07; text-indent: 28pt; font-family: \"Arial\",\"sans-serif\"; font-size: 10pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.07; font-family: 仿宋,仿宋_GB2312; font-size: 14pt;\">急重症医学经过近<span>20</span>年的高速发展，在与新冠疫情的斗争中大放异彩，是降低危重患者死亡率、赢得救治时机的有利武器。随着盐城市第一人民医院在本区域率先开展<span>ECMO</span>技术，体外生命支持技术<span>ECMO</span>的实践，救治了大量急危重症患者。经过近年来的临床经验积累与不断探索，盐城市一院已形成了相对成熟的专业体系与组织架构，积累了丰富的临床治疗与护理经验。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 8pt; text-align: justify; line-height: 1.07; text-indent: 28pt; font-family: \"Arial\",\"sans-serif\"; font-size: 10pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"line-height: 1.07; font-family: 仿宋,仿宋_GB2312; font-size: 14pt;\">为促进我市急危重症医学的发展，加强急危重症人才队伍建设，盐城市第一人民医院于<span>2022</span>年<span>7</span>月<span>31</span>日成功举办盐城市急危重症学术会议暨<span>ECMO</span>技术项目学习班，特别邀请江苏省人民医院陈旭峰教授、武正山教授及盐城市急救中心胥伟华主任作专题讲座，聚焦急危重症科最新指南解读、<span>ECMO</span>新技术发展；盐城市一院重症医学科陈兰平、陈建军、卢仲谦、周亮亮、何应丰、姚洁等医学专家针对<span>ECMO</span>的病理生理、启动、抗凝、并发症预防、撤机、容量管理等流程开展了生动的授课。大会吸引了急诊与危重症相关专业的医护人员踊跃参加，线上、线下超过<span>500</span>余人参会学习。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 8pt; text-align: center; line-height: 1.07; font-family: \"Arial\",\"sans-serif\"; font-size: 10pt;\\\'><span style=\"line-height: 1.07; font-family: 仿宋,仿宋_GB2312; font-size: 14pt;\"><img width=\"542\" height=\"310\" align=\"center\" alt=\"5f4a8753d24dba58fa66d311de053b1\" src=\"/ewebeditor/uploadfile/image/2022/08/02/20220802102055366001.jpg\"></span></p>\r\n<p style=\\\'margin: 0cm 0cm 8pt; text-align: center; line-height: 1.07; font-family: \"Arial\",\"sans-serif\"; font-size: 10pt;\\\'><span style=\"line-height: 1.07; font-family: 仿宋,仿宋_GB2312; font-size: 14pt;\">△盐城市一院党委书记查文章致辞。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 8pt; text-align: center; line-height: 1.07; font-family: \"Arial\",\"sans-serif\"; font-size: 10pt;\\\'><span style=\"line-height: 1.07; font-family: 仿宋,仿宋_GB2312; font-size: 14pt;\"><img width=\"553\" height=\"311\" align=\"center\" alt=\"52b57614b2661d67c23485c428573c3\" src=\"/ewebeditor/uploadfile/image/2022/08/02/20220802102055573002.jpg\"></span></p>\r\n<p style=\\\'margin: 0cm 0cm 8pt; text-align: center; line-height: 1.07; font-family: \"Arial\",\"sans-serif\"; font-size: 10pt;\\\'><span style=\"line-height: 1.07; font-family: 仿宋,仿宋_GB2312; font-size: 14pt;\">△江苏省人民医院陈旭峰教授作专题讲座。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 8pt; text-align: center; line-height: 1.07; font-family: \"Arial\",\"sans-serif\"; font-size: 10pt;\\\'><span style=\"line-height: 1.07; font-family: 仿宋,仿宋_GB2312; font-size: 14pt;\"><img width=\"540\" height=\"281\" align=\"center\" alt=\"5494d0c5f7440c3f710298f03ab198b\" src=\"/ewebeditor/uploadfile/image/2022/08/02/20220802102055621003.jpg\"></span></p>\r\n<p style=\\\'margin: 0cm 0cm 8pt; text-align: center; line-height: 1.07; font-family: \"Arial\",\"sans-serif\"; font-size: 10pt;\\\'><span style=\"line-height: 1.07; font-family: 仿宋,仿宋_GB2312; font-size: 14pt;\">△盐城市急救中心胥伟华教授作专题讲座。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 