INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6581','0','cms','item','add','0','841','','宣传处','添加内容 | 慰问消防中队 致敬烈火英雄 ——盐城市一院开展庆“八一”走访慰问活','/index.php/cms/item-list-category-841-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'慰问消防中队 致敬烈火英雄 ——盐城市一院开展庆“八一”走访慰问活动\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'2022-08-01 16:29:50\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">在“八一”建军节来临之际，盐城市第一人民医院党委书记查文章率队前往盐城消防支队特勤中队开展走访慰问活动，向在酷暑高温天气坚守岗位的消防战员们送去慰问品以及节日的问候和祝福。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">座谈会上市一院党委书记查文章代表盐城市一院向盐城消防支队特勤中队全体人员致以崇高的敬意。盐城消防支队特勤中队始终以忠诚为魂、以执着为念，特别是在防火灭火、抢险救灾、应急处突及为群众解难纾困等工作中不辱使命、扎实工作，无私奉献，充分发扬了政治坚定，顽强拼搏的工作精神。盐城市第一人民医院将认真学习消防救援官兵对党忠诚、奉献担当、竭诚为民的精神，以更加饱满的热情投入到工作中去，为盐阜人民健康作出新贡献。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;tex