INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6682','0','cms','item','add','0','858','','宣传处','添加内容(前台) | 盐城市第一人民医院2022年公开招聘编外护理专业技术人员公','/index.php/cms/item-list-category-858-page-4','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/u.php/cms/item-add?cid=858&model=article&type=2\',\n  \'cid\' => \'858\',\n  \'title\' => \'盐城市第一人民医院2022年公开招聘编外护理专业技术人员公告\',\n  \'title_color\' => \'\',\n  \'html\' => \'1\',\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'timestamp\' => \'\',\n  \'source\' => \'人事处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'summary\' => \'\',\n  \'sub_title\' => \'\',\n  \'verify_frame\' => \'\',\n  \'custom_a\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<div>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">盐城市第一人民医院为国家三级甲等综合性医院，承担医疗、教学、科研、康复、急救等公立医院服务职能。为加强医院专业技术人员队伍建设，现面向社会公开招聘编外护理专业技术人员</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>55</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">名。根据《江苏省事业单位公开招聘人员办法》（苏办发〔</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2020</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">〕</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>9</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">号）等规定，现将有关事项公告如下：</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 30pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正黑体_GBK; font-size: 15pt;\">一、招聘岗位</span></p>\r\n<p style=\\\'background: white; text-align: justify; line-height: 24pt; text-indent: 20.25pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-left: 0cm; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">本次公开招聘相关信息在盐城市卫生健康委员会网站</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>(https://wsj.yancheng.gov.cn/)</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">及盐城市第一人民医院网站（</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>http://www.ycsyy.com/</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">）上发布，具体招聘岗位、数量、资格条件等详见《盐城市第一人民医院</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年公开招聘编外护理专业技术人员岗位表》（附件</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>1</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">，以下简称《招聘岗位表》）。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正黑体_GBK; font-size: 16pt;\">二、资格条件</span></p>\r\n<p style=\\\'background: white; text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-left: 0cm; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>1.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">具有中华人民共和国国籍；</span></p>\r\n<p style=\\\'background: white; text-align: justify; line-height: 24pt; text-indent: 20.25pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-left: 0cm; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>&nbsp; 2.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">遵守中华人民共和国宪法和法律，拥护中国共产党领导和社会主义制度；</span></p>\r\n<p style=\\\'background: white; text-align: justify; line-height: 24pt; text-indent: 20.25pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-left: 0cm; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>&nbsp; 3.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">品具有良好的品行；</span></p>\r\n<p style=\\\'background: white; text-align: justify; line-height: 24pt; text-indent: 20.25pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-left: 0cm; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>&nbsp; 4.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">身心健康，能适应岗位要求；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>5.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">招聘对象为国家计划内招收的大专及以上学历毕业生，年龄在</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>30</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">周岁及以下（</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>1991</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>8</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">月</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>25</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">日及以后出生），对年龄有特殊要求的，以《招聘岗位表》中注明的为准，年龄计算方法不变；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>6.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">具备《招聘岗位表》中岗位要求的资格条件：</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">①资格条件中的“</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年毕业生”，指在</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年毕业并已取得学历（学位）证书，且仍无工作单位的人员。其中，能够提供《毕业生就业推荐表》（原件）的普通高校毕业生，取得学历（学位）证书的日期可放宽至</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>12</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">月</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>31</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">日；国（境）外同期毕业人员，取得学历（学位）证书的日期可适当放宽，但须在</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>12</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">月</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>31</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">日前完成教育部留学服务中心学历认证。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2020</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年和</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2021</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年普通高校毕业生，若仍未落实工作单位，其档案关系仍保留在原毕业学校，或保留在各级毕业生就业主管部门（毕业生就业指导服务中心）、人才交流服务机构和公共就业服务机构的，以及国（境）外同期毕业且已完成学历认证但仍未落实工作单位的人员，可应聘面向</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年毕业生岗位。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">参加基层服务项目的人员，如参加服务项目前无工作经历，服务期满且考核合格后</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年内的，可应聘面向</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年毕业生岗位。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">以普通高校应届毕业生应征入伍服义务兵的人员，退役后</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>1</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年内的，可应聘面向</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年毕业生岗位。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">②资格条件中“专业”的设置目录为《江苏省</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年度考试录用公务员专业参考目录》。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>7.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">本次公开招聘没有户籍限制</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>;</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>8.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">取得祖国大陆普通高校学历的台湾学生和取得祖国大陆承认学历的其他台湾居民应聘时按国家和江苏省的有关规定执行。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>9.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">有下列情形之一的，不得应聘：</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">（</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>1</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">）现役军人和普通高校在读非</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年毕业生；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">（</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">）尚未解除纪律处分或者正在接受纪律审查的人员、刑事处罚期限未满或者涉嫌违法犯罪正在接受调查的人员；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">（</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>3</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">）应聘人员与事业单位工作人员有夫妻关系、直系血亲关系、三代以内旁系血亲关系、近姻亲及其他须回避的亲属关系的，不得应聘具有直接上下级领导关系的管理岗位，以及《事业单位人事管理回避规定》要求回避的岗位；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">（</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>4</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">）依据《江苏省事业单位公开招聘人员办法》（苏办发〔</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2020</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">〕</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>9</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">号）被聘用到江苏省地方各类事业单位服务期未满的人员；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">（</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>5</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">）国家和省另有规定不得应聘的人员。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正黑体_GBK; font-size: 16pt;\">三、报名安排</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>1.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">报名时间、方式、地点</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">报名时间：</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>8</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">月</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>31</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">日—</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>9</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">月</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">日，上午</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>8:00-11:30</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">，下午</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2:30-5:30</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">报名方式：现场报名。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">报名地点：盐城市人民南路</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>66</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">号门诊楼四楼（盐城市第一人民医院主院区人事处）。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">报名注意事项及资格审查</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">（</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>1</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">）应聘人员在规定时间内到报名地点报名。应聘人员只能选择一个岗位报名，报名时填写《盐城市第一人民医院</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年公开招聘编外护理专业技术人员报名表》</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>(</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">附件</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2)</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">，在《报名表》上粘贴本人近期一寸免冠正面彩照</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>3</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">张，并提交下列材料（以下材料均要求为原件，并提供复印件一份）：</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">①身份证；②学历、学位证书，其中未取得毕业证书的</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年毕业生须提供所在院校出具的省教育厅统一印制的《毕业生双向选择就业推荐表》和在校成绩单；</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2020</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年、</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2021</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年毕业生应聘面向</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年毕业生岗位的还需提供原始的《毕业生就业协议书》、《“</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年毕业生”身份承诺书》</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>(</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">附件</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>3)</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">；社会人员还需提供护士执业证书等材料；③应聘人员报考面向</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2022</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年毕业生岗位的还需提供高中毕业证书；④外省院校毕业生需提供有效期内的学信网学历认证和国家计划内统招证明材料</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>(</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">如大学录取通知书、省教委验证的录用花名册等</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>)</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">（</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">）本次招聘收取考试费</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>100</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">元。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">（</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>3</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">）资格审查：应聘人员须使用在有效期内的第二代居民身份证进行报名，报名与考试使用的身份证必须一致。应聘人员报名时，由医院人事处会同监督审查室依据报名条件进行资格审查并收取相关材料。报名期间，如对审查意见有异议的，请及时与招聘单位或其主管部门陈述申辩，报名时间截止时，未提出陈述申辩的，视为没有异议。面试、实践操作技能考核前将对拟进入此环节人员进行现场资格复审（复审时提供所有报名材料原件）。</span></p>\r\n<p style=\\\'text-align: justify; line-height: 23pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-left: 0cm; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">（</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>4</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">）</span><span style=\"font-family: 方正仿宋_GBK; font-size: 15pt;\">招聘岗位数与通过资格审核人数达不到开考比例的，相应核减岗位招聘计划数，直至取消该岗位招聘计划。报考被取消岗位的人员，可在规定时间内改报其他符合条件的岗位。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">（</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>5</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">）应聘人员对所填写的报名信息和提交报名材料的真实性、准确性、完整性、有效性负责。资格审查工作贯穿于招聘工作全过程。凡有弄虚作假者，一经查实，取消应聘资格。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">应聘人员应认真阅读疫情防控须知（附件</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>4</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">），按目前疫情防控要求参加此次应聘。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正黑体_GBK; font-size: 16pt;\">四、考试科目、时间及实施办法</span></p>\r\n<p style=\\\'text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-left: 0cm; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">考试采取笔试、面试及实践技能操作考核相结合的办法进行。</span></p>\r\n<p style=\\\'text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-left: 0cm; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>1.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">笔试：内容为护理岗位所需专业知识，笔试总分</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>100</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">分，设</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>60</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">分合格线。笔试具体时间、地点另行通知。</span></p>\r\n<p style=\\\'text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-left: 0cm; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">确定面试、实践技能操作考核人选：在笔试成绩合格人员中，按照招聘岗位</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>1:2</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">的比例从高分到低分确定参加面试、实践技能操作考核的人员（含末位同分者），不足</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>1:2</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">的按照实际人数确定面试人选。在上述范围内的应聘人员，以及其他排名较前的人员，请在成绩公布后保持联系方式畅通，以便招聘单位通知资格复审或递补，联系不到者视为自动放弃。</span></p>\r\n<p style=\\\'text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-left: 0cm; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>3.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">资格复审：面试、实践技能操作考核前，医院对相关人选进行资格复审，资格复审时必须提供报名材料原件。凡考生提供的证明材料影响资格复审结果的，取消其面试及实践技能操作考核资格，并在报考同岗位的笔试合格人员中从高分到低分依次递补。资格复审的具体时间、地点另行通知。</span></p>\r\n<p style=\\\'background: white; margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>4.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">面试：主要考察应聘者的综合分析能力、沟通协调能力、应变能力、语言表达能力、仪表风度等，面试方式为结构化面试。面试总分</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>100</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">分，设</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>60</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">分合格线，低于合格线者不予录用。面试成绩当场公布。面试时间、地点另行通知。</span></p>\r\n<p style=\\\'text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-left: 0cm; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>5. </span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">实践操作技能考核：考核内容为应聘岗位所需专业技能，考核总分</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>100</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">分，设</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>60</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">分合格线，低于合格线者不予录用。考核成绩当场公布。考试时，考生需自备白色工作服（无学校标识）、护士帽、口罩等物品。考核时间、地点另行通知。</span></p>\r\n<p style=\\\'background: white; margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>6.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">总成绩计算方式：总成绩采取百分制。考试结束后，根据应聘者笔试、面试、实践操作技能考核得分情况，按笔试占</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>40%</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">、面试占</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>40%</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">、实践操作技能考核占</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>20%</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">的比例计算考试总成绩。成绩均保留两位小数，第三位小数按“四舍五入”办法处理。</span></p>\r\n<p style=\\\'background: white; margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">笔试、面试、实践操作技能考核均由招聘单位具体组织实施。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正黑体_GBK; font-size: 16pt;\">五、聘用</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">考试结束后，在考试合格人员中，按照总成绩从高分到低分顺序，根据招聘岗位拟招聘人数，</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>1:1</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">确定参加体检人员。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">若总成绩相同，笔试成绩高者优先，若笔试成绩仍相同，面试成绩高者优先，如面试成绩仍相同，对成绩相同的人员组织加试（加试时间、方式、地点另行通知）。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">体检标准按照最新的《公务员录用体检通用标准（试行）》执行。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">体检结束后，招聘单位对体检合格人员组织考察（考察参照江苏省公务员录用考察标准执行），并根据考察和体检结果，研究确定拟聘用人员名单。因个人放弃、体检、考察不合格等原因出现招聘岗位空缺时，在该岗位的考试合格人员中，按总成绩从高分到低分依次递补（成绩相同取舍办法同前）。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">拟聘用人员名单将在市卫健委、市一院网站和医院事务公示栏内公示不少于</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>7</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">个工作日，接受社会监督。拟聘用人员名单公示后，应聘人员如无正当理由放弃聘用资格的，招聘单位可以在名单公示结束后的</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>1</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年内取消其再次应聘本单位的资格。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">对公示无异议人员，招聘单位按规定为其办理有关聘用手续，与拟聘用人员签订聘用合同，约定试用期。试用期满考核合格，予以定岗定级；考核不合格者，取消聘用资格，解除聘用合同；未能取得当年执业护士资格的，解除劳动关系。拟聘用人员与招聘单位订立</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>3</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年以上（含试用期）聘用合同的，除依法依规解除聘用合同外，应当在招聘单位最低服务</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>3</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">年（含试用期）。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正黑体_GBK; font-size: 16pt;\">六、纪律监督</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>1.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">招聘工作坚持公开、公平、公正的原则，全过程接受市卫健委机关党委（机关纪委）、招聘单位纪检监察部门的监督。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">本次招聘按《江苏省事业单位公开招聘人员办法》（苏办发〔</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>2020</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">〕</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>9</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">号）第七章第三十六条规定实行回避。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>3.</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">本公告最终解释权归盐城市第一人民医院。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">咨询电话：</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>0515-66696806(</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">市第一人民医院人事处</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>)</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">；</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">监督电话：</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>0515-66696810(</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">市第一人民医院监督审查室</span><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>)</span><span style=\"background: white; font-family: 方正仿宋_GBK; font-size: 16pt;\">。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'>&nbsp;</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\\\'background: white; font-family: \"Times New Roman\",\"serif\"; font-size: 16pt;\\\'><span style=\"font-size: 14pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><img border=\"0\" src=\"/ewebeditor/sysimage/icon16/doc.gif\"><a target=\"_blank\" href=\"/ewebeditor/uploadfile/file/2022/08/25/20220825175747333.doc\"><span style=\"font-size: 14pt;\">附件1.盐城市第一人民医院2022年公开招聘编外护理专业技术人员岗位表.doc</span></a></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><img border=\"0\" src=\"/ewebeditor/sysimage/icon16/doc.gif\"><a target=\"_blank\" href=\"/ewebeditor/uploadfile/file/2022/08/25/20220825175758029.doc\"><span style=\"font-size: 14pt;\">附件2.盐城市第一人民医院2022年公开招聘编外护理专业技术人员报名表.doc</span></a></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 34.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><img border=\"0\" src=\"/ewebeditor/sysimage/icon16/doc.gif\"><a target=\"_blank\" href=\"/ewebeditor/uploadfile/file/2022/08/25/20220825175807867.doc\"><span style=\"font-size: 14pt;\">附件3.“2022年毕业生”身份承诺书.doc</span></a></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n