INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1398','2210','1','','<!--#p8_attach#-->/cms/item/2021_07/14_15/b292d16523c6948f.JPG','男，泌尿外科微创诊疗中心暨首都医科大学泌尿外科研究所盐城分中心主任，主任医师，本科学历。任江苏省中西医结合学会泌尿外科专业委员会常务委员、省医学会男科学分会委员、省医学会泌尿外科学分会结石学组委员、省中医药学会泌尿外科委员、生殖医学专业委员等。江苏省“333工程”高层次人才、盐城市新世纪“111工程”高层次人才、盐城市“兴卫工程”重点医学人才。从事泌尿外科临','192.168.162.110','192.168.162.110','1626249134','男，泌尿外科微创诊疗中心暨首都医科大学泌尿外科研究所盐城分中心主任，主任医师，本科学历。任江苏省中西医结合学会泌尿外科专业委员会常务委员、省医学会男科学分会委员、省医学会泌尿外科学分会结石学组委员、省中医药学会泌尿外科委员、生殖医学专业委员等。江苏省&ldquo;333工程&rdquo;高层次人才、盐城市新世纪&ldquo;111工程&rdquo;高层次人才、盐城市&ldquo;兴卫工程&rdquo;重点医学人才。从事泌尿外科临床工作20余年，有较扎实的理论基础与操作技能，对疑难疾病的治疗和危重疾病的抢救有较丰富的经验。正常开展国内先进的根治性膀胱全切除&ldquo;T&rdquo;型原位回肠新膀胱术及泌尿外科腹腔镜肾癌、肾盂癌、前列腺癌根治，肾上腺肿瘤切除，经皮肾镜、输尿管镜碎石，经尿道前列腺、膀胱肿瘤电切等微创手术千余例。以第一作者或通讯作者在国内外核心期刊发表学术论文10余篇；获盐城市科学技术进步奖一等奖1项、江苏省医学新技术引进二等奖1项、盐城市医学新技术引进一等奖2项，华夏医学奖三等奖。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1399','2211','1','','<!--#p8_attach#-->/cms/item/2021_07/14_15/ae867082a16faa11.JPG','男，泌尿外科主任，主任医师，本科学历，南通大学兼职教授。任江苏省医学会泌尿外科分会委员、江苏省医学会男科学分会委员、盐城市泌尿外科分会主任委员、盐城市外科学会理事、盐城市知识分子联谊会理事。1996年在盐城地区率先开展肾脏移植手术，填补了盐城市器官移植的空白，在泌尿系结石、肿瘤、创伤、先天畸形、感染、性功能障碍等领域均有较高造诣。在省级以上期刊发表论著20余','192.168.162.110','192.168.162.110','1626249229','<p>男，泌尿外科主任，主任医师，本科学历，南通大学兼职教授。任江苏省医学会泌尿外科分会委员、江苏省医学会男科学分会委员、盐城市泌尿外科分会主任委员、盐城市外科学会理事、盐城市知识分子联谊会理事。1996年在盐城地区率先开展肾脏移植手术，填补了盐城市器官移植的空白，在泌尿系结石、肿瘤、创伤、先天畸形、感染、性功能障碍等领域均有较高造诣。在省级以上期刊发表论著20余篇，其中SCI收录5篇，获国家自然科学基金项目1项、盐城市医学新技术引进奖7项、市科技进步奖1项、获&ldquo;盐城市十佳医务工作者&rdquo;荣誉称号。</p>\r\n','','','','','');
