INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1836','2656','1','','','盐城市第一人民医院主院区智能停车场管理系统改造及维保项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。1、项目概况1.1 项目名称：盐城市第一人民医院主院区智能停车场管理系统改造及维保项目；1.2维保地点：盐城市第一人','192.168.162.110','192.168.162.110','1627284388','<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:24.0pt;\r\nline-height:22.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:12.0pt;font-family:宋体\">盐城市第一人民医院主院区智能停车场管理系统改造及维保项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:24.1pt;\r\nline-height:22.0pt;layout-grid-mode:char\" align=\"left\"><b><span style=\"font-size:\r\n12.0pt;font-family:宋体\">1</span></b><b><span style=\"font-size:12.0pt;font-family:\r\n宋体\">、项目概况</span></b></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:24.0pt;\r\nline-height:22.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">1.1 </span><span style=\"font-size:12.0pt;font-family:\r\n宋体\">项目名称：盐城市第一人民医院主院区智能停车场管理系统改造及维保项目；</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-indent:24.0pt;line-height:22.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">1.2</span><span style=\"font-size:12.0pt;font-family:宋体\">维保地点：盐城市第一人民医院主院区；</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-indent:24.0pt;line-height:22.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">1.3</span><span style=\"font-size:12.0pt;font-family:宋体\">招标内容：主院区智能停车场车场管理系统改造及维保，主要包含但不限于收费系统、反向寻车系统，更换所有进出口道闸<span>10</span>台、进出口道闸<span>10</span>套系统增加车道监控、增加<span>4</span>台自助缴费机、硬盘刻录机（配硬盘、交换机）及配件更换维保等。具体详见招标人项目需求。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:24.0pt;\r\nline-height:22.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">1.4</span><span style=\"font-size:12.0pt;font-family:\r\n宋体\">服务期限：智能停车场管理系统改造及维保服务期限三年，具体维保开始日期以书面合同签订日期为准。维保期满经院方考核合格后可根据相关法律法规续签合同。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:24.1pt;\r\nline-height:22.0pt;layout-grid-mode:char\" align=\"left\"><b><span style=\"font-size:\r\n12.0pt;font-family:宋体\">2 </span></b><b><span style=\"font-size:12.0pt;\r\nfont-family:宋体\">投标人应当具备的主要资格条件：</span></b></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:24.0pt;\r\nline-height:22.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">2.1</span><span style=\"font-size:12.0pt;font-family:\r\n宋体\">投标人须是具有建设行政主管部门核发的电子与智能化工程二级及以上专业承包资质的独立法人或智能停车管理设备制造商。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:24.1pt;\r\nline-height:22.0pt;layout-grid-mode:char\" align=\"left\"><b><span style=\"font-size:\r\n12.0pt;font-family:宋体\">3</span></b><b><span style=\"font-size:12.0pt;font-family:\r\n宋体\">、投标报名及招标文件的获取</span></b></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:22.0pt;\r\nbackground:white\"><span style=\"font-family:宋体;background:white\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路<span>8</span>号<span>8</span>楼<span>805</span>室）投标报名并领取招标文件，投标报名时间<span>2021</span>年<span>7</span>月<span>26</span>日<span>8</span>时至<span>2021</span>年<span>7</span>月<span>30</span>日<span>18</span>时。联系人：刘永杰，<span>0515-69057406</span>；手机：<span>13626222271</span>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:22.0pt;\r\nbackground:white\"><span style=\"font-family:宋体;background:white\">4</span><span style=\"font-family:宋体;background:white\">、本项目采用“综合评估法”。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:22.0pt;\r\nbackground:white\"><span style=\"font-family:宋体;background:white\">5</span><span style=\"font-family:宋体;background:white\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:22.0pt;\r\nbackground:white\"><span style=\"font-family:宋体;background:white\">6</span><span style=\"font-family:宋体;background:white\">、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话<span>0515-66696810</span>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:22.0pt;\r\nbackground:white\"><span style=\"font-family:宋体;background:white\">其余内容详见招标文件。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:right;background:white\" align=\"right\"><span style=\"font-family:宋体;background:white\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:right;background:white\" align=\"right\"><span style=\"font-family:宋体;background:white\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:right;background:white\" align=\"right\"><span style=\"font-family:宋体;background:white\">&nbsp;</span><span style=\"font-family:\r\n宋体;background:white\">招标人：盐城市第一人民医院</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:right;background:white;word-break:break-all\" align=\"right\"><span style=\"font-family:宋体;background:white\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span><span style=\"font-family:宋体;background:white\">时间：<span>2021</span>年<span>7</span>月<span>26</span>日 </span></p>','','','','','');
