INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1997','2817','1','','','盐城市第一人民医院主院区净化空调系统运行及维保服务项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。1、项目概况与招标内容1.1项目名称：盐城市第一人民医院主院区净化空调系统运行及维保服务项目。1.2项目地点：盐城','192.168.162.110','192.168.162.110','1614096000','<p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><a name=\"_Hlk60902728\"></a><span style=\"font-family:宋体;color:black;background:white\">盐城市第一人民医院主院区净化空调系统运行及维保服务项目</span><span style=\"font-family:宋体;color:black;background:white\">，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">1</span><span style=\"font-family:宋体;color:black;background:white\">、项目概况与招标内容</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">1.1</span><span style=\"font-family:宋体;color:black;background:white\">项目名称：盐城市第一人民医院主院区净化空调系统运行及维保服务项目。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">1.2</span><span style=\"font-family:宋体;color:black;background:white\">项目地点：</span><span style=\"font-family:宋体;background:white\">盐城市第一人民医院主院区（人民南路66号）</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">1.3</span><span style=\"font-family:宋体;color:black;background:white\">项目简要说明：盐城市第一人民医院主院区净化空调系统运行及维保服务项目，主要包含但不限于：（1）净化空调系统（风系统、水系统、净化机组、排风机组等）；强电系统（净化空调强电系统、照明、插座、配电柜、隔离变压器等）；弱电系统（净化空调弱电控制系统：包括DDC控制器、上位机、各类传感器、保护装置、执行器、数模转换器以及显示操作面板等；监控系统：包括监控主机、电源、摄像头、显示器、线路等；呼叫系统：包括电源、主机、喇叭、分机、麦克风等）。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">（2）建筑装饰（顶板、墙面、地板、防撞带、踢脚线、检修口等）、各类门（自动门、手推移门、手拉门、门禁等）；医用气体系统（设备带、气体吊塔等）以及洁净室内的保温、保冷库、器械柜、药品柜、写字板、传递窗、观察窗、洗手盆、洗手池、盐水挂构、水龙头等与净化工程相关范围内的一切工作的日常巡检，维修、维护保养工作。具体内容详见招标人提供的服务要求。</span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-size:16px;font-family:宋体\">1.4</span><span style=\"font-size:16px;font-family:宋体;color:black;background:white\">服务期：1年。</span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-size:16px;font-family:宋体;color:black;background:white\">1.5</span><span style=\"font-size:16px;font-family:宋体;color:black;background:white\">服务地点：盐城市第一人民医院主院区。</span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-size:16px;font-family:宋体;color:black;background:white\">1.6</span><span style=\"font-size:16px;font-family:宋体;color:black;background:white\">本项目设定最高限价为<span style=\"text-decoration:underline;\">28</span>万元。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">2</span><span style=\"font-family:宋体;color:black;background:white\">、投标人资格要求</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">2.1</span><span style=\"font-family:宋体;color:black;background:white\">投标人须是具备建筑机电安装工程专业承包三级及以上资质及电子与智能化工程专业承包二级及以上资质的独立法人。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">2.2</span><span style=\"font-family:宋体;color:black\">未被</span><span style=\"font-family:宋体;color:black\">“</span><span style=\"font-family:宋体;color:black\">信用中国</span><span style=\"font-family:宋体;color:black\">”</span><span style=\"font-family:宋体;color:black\">网站（</span><span style=\"font-family:\'&color:black\">www.creditchina.gov.cn</span><span style=\"fo