INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2009','2829','1','','http://www1.ycsyy.com/upload/images/2020/9/7a7e60704550992f.png','根据2020年8月12日发布的《盐城市第一人民医院2020年公开招聘医学人才公告》，目前部分招聘岗位报名人数严重不足，未达到开考比例。为确保盐城市儿童医院正常有序运转，经研究决定，对上述公告中未达到开考比例的19个岗位发布补充公告，进行补充报名，具体事项安排如下','192.168.162.110','192.168.162.110','1600272000','<p style=\"\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 21px;\">根据2020年8月12日发布的《盐城市第一人民医院2020年公开招聘医学人才公告》，目前部分招聘岗位报名人数严重不足，未达到开考比例。为确保盐城市儿童医院正常有序运转，经研究决定，对上述公告中未达到开考比例的19个岗位发布补充公告，进行补充报名，具体事项安排如下：</span></p><p style=\"\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 21px;\">1</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 21px;\">、补充报名时间：2020年9月21日至9月25日，上午8:00—11:30，下午2:30—5:30。</span></p><p style=\"\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 20px;\">2</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 20px;\">、调整后的招聘岗位表详见附件。</span></p><p style=\"\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 20px;\">3</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 20px;\">、《</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 21px;\">盐城市第一人民医院2020年公开招聘医学人才公告</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 20px;\">》中的其他事项保持不变，按原定的程序和要求执行。</span></p><p style=\"\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 20px;\">特此公告。</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-size: 20px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: \"Times New Roman\";\"></span></span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-size: 20px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: \"Times New Roman\";\"></span></span></p><p style=\"\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 20px;\"></span><a href=\"http://wsj.yancheng.gov.cn/module/download/downfile.jsp?classid=0&amp;filename=158466b6abd145d6b869165a2e53c923.xls\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; background-color: transparent; text-decoration: none; transition: all 0.2s ease-out 0s; color: rgb(51, 51, 51);\"><img src=\"http://www1.ycsyy.com/upload/images/2020/9/7a7e60704550992f.png\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; vertical-align: middle; border: currentcolor; max-width: 100%;\" />附件：盐城市第一人民医院2020年公开招聘医学人才岗位表(补报名).xls</a><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 21px;\"></span></p><p style=\"\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: 仿宋_GB2312; font-size: 21px;\"></span></p><p style=\"\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 21px;\"><span class=\"Apple-converted-space\"> </span></span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 21px;\">盐城市第一人民医院</span></p><p style=\"\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体; font-size: 20px;\"> <span class=\"Apple-converted-space\">               </span></span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: 仿宋; font-size: 20px;\">2020</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: 仿宋; font-size: 20px;\">年9月17日</span></p><p><br /></p>','','','','','');
