INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2049','2869','1','','http://www1.ycsyy.com/upload/images/2020/11/ac503b3ff7f87285.jpg','连日来，党的十九届五中全会的召开在盐城市一院引发积极反响。院党委多次召开专题会议，及时传达全会精神，积极部署贯彻落实。各党支部组织党员干部利用学习强国APP、智慧党建等平台，通过电视、报刊等各类媒体，组织召开专题会议，及时学习全会文件，结合医院工作实际','192.168.162.110','192.168.162.110','1605110400','<p><br /></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">连日来，党的十九届五中全会的召开在盐城市一院引发积极反响。院党委多次召开专题会议，及时传达全会精神，积极部署贯彻落实。各党支部组织党员干部利用学习强国APP、智慧党建等平台，通过电视、报刊等各类媒体，组织召开专题会议，及时学习全会文件，结合医院工作实际，深入贯彻落实全会精神。11月11日下午，市一院党委中心组召开十九届五中全会专题学习，集体学习了《中国共产党第十九届中央委员会第五次全体会议公报》，传达了11月2日市委关于迅速学习宣传和贯彻党的十九届五中全会精神的工作要求。<br /> 盐城市第一人民医院党委书记查文章主持会议并提出要求，按照中央部署认真学习十九届五中全会的精神，结合医院工作实际，要做到坚定不移讲政治，坚持维护习近平总书记党中央的核心、全党的核心地位，坚决维护党中央权威和集中统一领导，全面推动十四五规划，建立区域性高质量医疗中心。要坚定不移谋发展，小步就是落后，不进步就是退步。要做到强技术、强服务、强内涵。要坚定不移以人民为中心，改革服务理念。要坚定不移加强作风建设，讲规矩讲纪律，从自身做起，从“小切口做大事”，真正做到“三优三满意”。<br /> 按照十九届五中全会的精神要求，盐城市第一人民医院党委委员、副院长嵇友林，盐城市第一人民医院党委委员、纪委书记卞学梅，盐城市第一人民医院党委委员、副院长李奇志，盐城市第一人民医院党委委员、副院长李自强等院党委班子成员在会上分享了学习心得。</span> <br /></p><p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2020/11/ac503b3ff7f87285.jpg\" title=\"c8c23d69ae5118f8036fb8434208c03.jpg\" alt=\"c8c23d69ae5118f8036fb8434208c03.jpg\" /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2050','2870','1','','http://www1.ycsyy.com/upload/images/2020/9/3c3e449fb456e0e9.jpg','2020年9月30日下午，盐城市第一人民医院机关第一党支部以“ 严纪律、守规矩、强作风、树形象”为主题，组织全体党员、发展对象等四十余名同志进行作风建设突出问题专项整治集体约谈提醒，学习了《江苏省纪委通报7起违反中央八项规定精神典型问题》，以案为鉴，开展国庆','192.168.162.110','192.168.162.110','1601395200','<p style=\"background-color:#ffffff; line-height:18pt; margin-bottom:11.25pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:21pt; \"><span style=\"background-color:#ffffff; color:#333333; font-family:\">2020</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">年</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">9</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">月</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">30</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">日</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">下午，</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">盐城市第一人民医院机关第一党支部</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">以</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">“ </span><span style=\"background-color:#ffffff; color:#333333; font-family:\">严纪律、守规矩、强作风、树形象</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">”</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">为主题，</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">组织全体党员、发展对象等四十余名同志</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">进行作风建设突出问题专项整治集体约谈提醒，学习了《江苏省纪委通报</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">7</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">起违反中央八项规定精神典型问题》，以案为鉴，开展国庆中秋双节前廉政警示教育。会上，全体党员同志</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">重点学习了《盐城市第一人民医院作风建设突出问题专项整治实施方案》《关于进一步推行餐饮节约制止餐饮浪费行为的意见》《盐城市第一人民医院</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">2020</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">年医疗行业作风建设工作专项行动实施方案》等</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">作风行风建设工作要求</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">，</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">传达</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">学习习近平总书记在全国抗击新冠肺炎疫情表彰大会上的重要讲话</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">、《习近平谈治国理政》（第三卷）等内容</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">，并对党支部工作进行</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">了</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">具体</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">的</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">安排。</span></p><p style=\"background-color:#ffffff; line-height:18pt; margin-bottom:11.25pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:21pt; \"><span style=\"background-color:#ffffff; color:#333333; font-family:\">最后</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">，</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">全体党员观看了党建电教片《领航新时代》及微纪录片《见证初心和使命的</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">“</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">十一书</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">”</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">》选集。</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">党员同志们表示，从革命先烈、抗疫英雄的英雄事迹，到脱贫攻坚的模范典型，体现了不同年代共产党人的初心使命，充分地证明了中国共产党人的初心和使命，就是为中国人民谋幸福，为中华民族谋复兴。作为医院行政部门的共产党员，将以严格</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">的标准要求自己，自觉守纪律、讲规矩、强作风、树形象，以积极地姿态投入工作，服务临床一线，为</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">推动医院高质量发展</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">，建设高水平区域医疗中心作出应有的贡献</span><span style=\"background-color:#ffffff; color:#333333; font-family:\">。</span></p><p><span style=\"background-color:#ffffff; color:#333333; font-family:\"><img src=\"http://www1.ycsyy.com/upload/images/2020/9/3c3e449fb456e0e9.jpg\" style=\"border: 0; max-width: 100%\" /></span></p>','','','','','');
