INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2052','2872','1','','','点击下载：盐城市第一人民医院2019年度市级部门决算公开.pdf','192.168.162.110','192.168.162.110','1595433600','<p style=\"line-height: 16px; text-align: center;\"><span style=\"font-size: 18px;\">点击下载：</span><a href=\"http://www1.ycsyy.com/upload/files/2020/7/38a6a2b9b869c0d2.pdf\" target=\"_self\" title=\"2019年度市一院部门决算公开报告 -_上网.pdf\" textvalue=\"盐城市第一人民医院2019年度市级部门决算公开.pdf\" style=\"font-size: 18px; text-decoration: underline;\"><span style=\"font-size: 18px;\">盐城市第一人民医院2019年度市级部门决算公开.pdf</span></a></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2053','2873','1','','','根据《中华人民共和国环境影响评价法》、《环境影响评价技术导则 总纲》、《环境影响评价公众参与办法》的相关规定，对《盐城市儿童医院二期工程项目环境影响评价报告书》工作相关信息，以便广大公众在环境保护方面提出更多的意见和建议。一、建设项目名称及范围项目名','192.168.162.110','192.168.162.110','1595260800','<p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-family: 仿宋;\">根据《中华人民共和国环境影响评价法》、《环境影响评价技术导则</span> <span style=\"font-family: 仿宋;\">总纲》、《环境影响评价公众参与办法》的相关规定，对《盐城市儿童医院二期工程项目环境影响评价报告书》工作相关信息，以便广大公众在环境保护方面提出更多的意见和建议。</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">一、建设项目名称及范围</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">项目名称：盐城市儿童医院二期工程项目</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">项目地址：盐城市亭湖区毓龙路北侧，迎宾北路东侧地块</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">项目建设内容及规模：二期工程总建筑面积约</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">70000</span><span style=\"font-size: 16px; font-family: 仿宋;\">平方米，其中：地上建筑面积</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">50000</span><span style=\"font-size: 16px; font-family: 仿宋;\">平方米，地下建筑面积</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">20000</span><span style=\"font-size: 16px; font-family: 仿宋;\">平方米，项目总投资</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">50000</span><span style=\"font-size: 16px; font-family: 仿宋;\">万元，医院床位设置</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">500</span><span style=\"font-size: 16px; font-family: 仿宋;\">张，停车位</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">300</span><span style=\"font-size: 16px; font-family: 仿宋;\">辆。</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">二、建设单位名称及联系方式</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">建设单位：盐城市第一人民医院</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">单位地址：江苏省盐城市毓龙西路</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">166</span><span style=\"font-size: 16px; font-family: 仿宋;\">号</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">联系人：霍主任</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">联系电话：</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">0515-66698027</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">三、承担评价工作的环境影响评价机构名称和联系方式</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">环评单位：江苏南大环保科技有限公司</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">单位地址：江苏省南京经济技术开发区恒竞路</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">27</span><span style=\"font-size: 16px; font-family: 仿宋;\">号</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">联系人：王工</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">联系电话：</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">025-68568026</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">电子邮箱：</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">wanghj@nuep.com.cn</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">四、环境影响评价工作的工作程序和主要工作内容：</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">本次环境影响评价工作按照《中华人民共和国环境保护法》、《中华人民共和国环境影响评价法》、《环境影响评价技术导则</span> <span style=\"font-size: 16px; font-family: 仿宋;\">总纲》等相关法律、条例、技术规范开展。</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">本次评价主要工作内容包括：总则、建设项目概况、建设项目工程分析、环境现状调查分析、环境影响预测与评价、污染防治措施、环境经济损益分析、环境管理与监测计划、评价结论。</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">五、征求公众意见的主要事项：</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">1</span><span style=\"font-size: 16px; font-family: 仿宋;\">）您是否知道</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">/</span><span style=\"font-size: 16px; font-family: 仿宋;\">了解该区域；</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">2</span><span style=\"font-size: 16px; font-family: 仿宋;\">）您认为是否有必要开展本次环境影响评价工作；</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">3</span><span style=\"font-size: 16px; font-family: 仿宋;\">）您对区域目前的建设持何态度，并简要说明原因；</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">4</span><span style=\"font-size: 16px; font-family: 仿宋;\">）根据您掌握的情况，认为目前区域对环境质量造成的危害</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">/</span><span style=\"font-size: 16px; font-family: 仿宋;\">影响方面及程度；</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">5</span><span style=\"font-size: 16px; font-family: 仿宋;\">）您对该区域环保方面有何建议和要求；</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">6</span><span style=\"font-size: 16px; font-family: 仿宋;\">）从环保角度出发，您对区域今后发展持何种态度，并简要说明原因。</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">六、公众提出意见的方式和途径</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">公众提交意见时，应当提供有效的联系方式，鼓励采用实名方式提交意见并提供常住地址。</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">公众可以通过电话等方式，在规定时间内将填写的公众意见表提交建设单位，反映与环境保护有关的意见和建议。</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">在环境影响报告书征求意见稿编制过程中，公众均可向建设单位提出与环境影响评价相关的意见。</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">七、公众意见表的网络链接：</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">见附件</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">八、提交公众意见表的方式和途径：</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">自本公告之日起</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">10</span><span style=\"font-size: 16px; font-family: 仿宋;\">个工作日内，公众可向建设单位电子邮箱提交电子版公众意见表，或将纸质版公众意见表邮寄或送至建设单位联系地址。</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">九、公示时间：</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">自本公示信息发布之日起十个工作日。</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">附件：</span><a href=\"http://www1.ycsyy.com/upload/files/2020/7/5877a570ac90660.docx\" title=\"建设项目环境影响评价公众意见表(盐城市儿童医院二期工程)20200721.docx\" style=\"color: rgb(0, 102, 204); font-size: 18px; font-family: 宋体, SimSun; text-decoration: underline;\"><span style=\"font-family: 宋体, SimSun;\">建设项目环境影响评价公众意见表(盐城市儿童医院二期工程)20200721.docx</span></a></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 16px; font-family: 仿宋;\"><br /></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: \"Times New Roman\", serif; font-size: 18px;\"></span></p><p style=\"text-align:right;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\"> </span><span style=\"font-size: 16px; font-family: 仿宋;\">盐城市第一人民医院</span></span></p><p style=\"text-align:right;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">2020</span><span style=\"font-size: 16px; font-family: 仿宋;\">年</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">7</span><span style=\"font-size: 16px; font-family: 仿宋;\">月</span><span style=\"font-size: 16px; font-family: \"Times New Roman\", serif;\">21</span><span style=\"font-size: 16px; font-family: 仿宋;\">日</span></span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2054','2874','1','','http://www1.ycsyy.com/upload/images/2020/2/46941ac3248722c7.png','第一部分　部门概况一、单位主要职能。盐城市第一人民医院，系南京大学附属盐城第一人民医院、南通大学第四附属医院、徐州医科大学附属临床医院、江苏医药职业学院第一附属医院、第一临床学院，创建于1948年，经过近七十年的发展，现已成为一所集医疗、教学、科研、康复','192.168.162.110','192.168.162.110','1581696000','<p style=\"margin-top: auto; margin-bottom: auto; text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><strong><span style=\"font-family: 宋体, SimSun;\">第一部分　部门概况</span></strong></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 19px; font-family: 黑体;\">一、单位主要职能。</span></span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院，系南京大学附属盐城第一人民医院、南通大学第四附属医院、徐州医科大学附属临床医院、江苏医药职业学院第一附属医院、第一临床学院，创建于1948年，经过近七十年的发展，现已成为一所集医疗、教学、科研、康复、急救为一体的国家三级甲等综合性医院。</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">医院共设三个院区：北区（毓龙西路166号）、南区（人民南路66号）、五官科院区（南城河路12号）；医院现有总占地面积20.73万平方米，建筑面积36万平方米；资产总值35.95亿元；目前实际开放床位3100张，职工3200人。</span></p><p style=\"margin-bottom: 0px; text-align: justify; text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">医院设置临床科室34个、医技科室23个、护理单元103个、职能处室24个，拥有省级临床重点专科14个、市级医学重点学科18个、市级临床重点专科30个、全国综合医院示范中医科1个、通过国家药物临床试验认证机构认证科室13个。医院同时拥有省区级胸痛、卒中、创伤、孕产妇急危重症救治中心，省级紧急医学救援基地医院，设有全市的急救中心、临床检验中心、医学特检中心、糖尿病防治中心、心脏介入中心和微创中心等医学中心。</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">医院有南通大学、徐州医科大学硕士培养点18个，博士培养点1个；有博士生导师1名，硕士生导师35名，副教授11名，兼职教授、副教授176名；医院现有医学博士97名，医学硕士553名；高级职称477人；被确认为省“333”工程人才12名、省“科教强卫”工程人才8名、市“515”人才11名。</span></p><p style=\"margin-bottom: 0px; text-align: justify; text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">医院拥有万元以上医疗设备近4000台件，医疗设备总额8.3亿元，先进的大型设备有进口西门子64层PET—CT 1台、核磁共振5台、炫速双源CT2台、高档螺旋CT6台、高能直线加速器3台、SPE CT 2台，医院同时拥有全球领先的德国轨道物流系统和全市唯一的贝克曼实验室自动化流水线系统，有集成度较高的覆盖全院的信息系统。</span></p><p style=\"line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">医院加强对外协作，与国内外多家知名医院、学科和医学院校建立了友好合作关系。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 19px; font-family: 黑体;\">二、2020年度部门主要工作任务及目标。</span></span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">1、2020年财务工作计划</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">年门诊量≥145万人次；</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">年住院工作量≥14.50万人次；</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">药占比≤35%；</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">卫生材料收入占医疗收入比≤17%；</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">检查、检验收入占医疗收入比≤20%；</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">医院总收入＞26.25亿，医院总支出＜25.75亿；</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2、严格执行财务、物价、医保、税务、工商等政策、法律法规。</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">3、实行全面预算管理，药品、卫材、人员等支出实施预算管理，预算管理符合率达96%；门急诊、住院病人均次费用控制在规定指标内。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">4、寻求新的经济增长点：大力开展新技术新项目，努力提高新设备的使用率；扩大病人数量：改善就诊环境，向上和大医院联营，向下和社区结对。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">5、调整收入、支出结构：提高业务收入中技术劳务性收入的比例，降低药品收入占医药收入的比重，降低卫生材料支出占医药收入的比重；适度提高人员支出占业务支出的比重，降低药品、卫材、管理费用支出占业务支出的比重。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">6、控制费用：控制每出院病人药品费用及材料费用，控制医保费用，控制应收账款和应付账款及贷款规模。</span></p><p style=\"background: white; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">7、总体规划，做好与城南医院一体化管理。建立统一的财务管理，做好成本核算、绩效考核分配。</span></p><p style=\"margin-top: auto; margin-bottom: auto; text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"margin-top: auto; margin-bottom: auto; text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><strong><span style=\"font-size: 21px; font-family: 宋体;\">第二部分　<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">2020</span>部门预算表</span></strong></span></p><p style=\"margin-top: auto; margin-bottom: auto; text-indent: 42px; line-height: 3em;\"><span style=\"color: red; font-family: 宋体, SimSun; font-size: 20px;\">详见附件</span></p><p style=\"margin-top: auto; margin-bottom: auto; text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><strong><span style=\"font-size: 21px; font-family: 宋体;\">第三部分　2020部门预算情况说明</span></strong></span></p><p style=\"text-indent: 47px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、收支预算总表情况说明</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本表反映部门年度总体收支预算情况。根据《盐城市财政局关于批复2020年市级部门预算的通知》（盐财预〔2019〕4号）填列。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2020年度收入、支出预算总计<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">525032.06</span>万元，与上年相比收、支预算总计各增加<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">28064</span>万元，增长<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">11.97</span>%。主要原因是原城南医院并入我院后业务收入、业务支出等较上年大幅增长。其中：</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（一）收入预算总计<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">262516.03</span>万元。包括：</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1．财政拨款收入预算总计<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">5297.76</span>万元。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一般公共预算收入预算<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">5297.76</span>万元，与上年相比减少<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">1935.20</span>万元，增长<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">-26.76</span>%。主要原因是城南医院4亿贷款贴息5年到期，减少财政贴息拨款收入2000万元。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2．其他资金收入预算总计<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">257218.27</span>万元。与上年相比增加<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">29999.20</span>万元，增长<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">13.20</span>%。主要原因是城南医院并入我院后业务收入较2019年有较大幅度增长。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（二）支出预算总计<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">262516.03</span>万元。包括：</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1．卫生健康（类）支出<span style=\"text-decoration: underline; font-family: 宋体, SimSun; font-size: 20px;\">262516.03</span>万元，主要用于人员经费、卫生材料、药品费用等支出。与上年相比增加<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">28064</span>万元，增长<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">11.97</span>%。主要原因是人员经费、卫生材料、药品费用等大幅增加。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、收入预算情况说明</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本年收入预算合计<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">262516.03</span>万元，其中：</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一般公共预算收入<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">5297.76</span>万元，占<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">2.02</span>%；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其他资金<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">257218.27</span>万元，占<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">97.98</span>%；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><em><span style=\"font-size: 19px; font-family: 宋体;\">（可用饼图显示本年收入结构图）</span></em></span></p><p style=\"text-indent: 37px; line-height: 3em; text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2020/2/46941ac3248722c7.png\" title=\"image.png\" alt=\"image.png\" /></p><p style=\"text-indent: 37px; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">图1：收入预算图</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、支出预算情况说明</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本年支出预算合计<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">262516.03</span>万元，其中：</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">基本支出<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">257998.03</span>万元，占<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">98.28</span>%；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">项目支出<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">3558</span>万元，占<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">1.36</span>%；</span></p><p style=\"margin-left: 7px; text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其他支出<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">960</span>万元，占<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">0.56</span>%</span></p><p style=\"margin-left: 7px; text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><em><span style=\"font-size: 19px; font-family: 宋体;\">（可用饼图显示本年支出结构图）</span></em></span></p><p style=\"margin-left: 7px; text-indent: 28px; line-height: 3em; text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2020/2/6a232f9fa2eeb0f4.png\" title=\"image.png\" alt=\"image.png\" /></p><p style=\"margin-left: 7px; text-align: center; text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">图2：支出预算图</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、财政拨款收支预算总表情况说明</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2020年度财政拨款收、支总预算<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">10595.52</span>万元。与上年相比，财政拨款收、支总计各减少<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">1935.20</span>万元，增长<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">-26.76</span>%。主要原因是城南医院4亿贷款贴息5年到期，减少财政贴息拨款2000万元。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、财政拨款支出预算表情况说明</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2020年财政拨款预算支出<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">5297.76</span>万元，占本年支出合计的<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">2.02</span>%。与上年相比减少<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">1935.20</span>万元，增长<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">-26.76</span>%。主要原因是城南医院4亿贷款贴息5年到期，减少财政贴息拨款支出2000万元。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、财政拨款基本支出预算表情况说明</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2020年度财政拨款基本支出预算<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">1739.76</span>万元，其中：人员经费<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">1739.76</span>万元。主要包括：基本工资、津贴补贴、奖金、社会保障缴费、伙食补助费、绩效工资、其他工资福利支出、离休费、退休费、抚恤金、生活补助、医疗费、奖励金、住房公积金、提租补贴、其他对个人和家庭的补助支出。</span></p><p style=\"margin-top: auto; margin-bottom: auto; text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><strong><span style=\"font-size: 19px; font-family: 宋体;\">第四部分　名词解释</span></strong></span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、财政拨款：指由一般公共预算、政府性基金预算安排的财政拨款数。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、一般公共预算：包括公共财政拨款（补助）资金、专项收入。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、财政专户管理资金：包括专户管理行政事业性收费（主要是教育收费）、其他非税收入。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、其他资金：包括事业收入、经营收入、其他收入等。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、基本支出：包括人员经费、商品和服务支出（定额）。其中，人员经费包括工资福利支出、对个人和家庭的补助。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、项目支出：包括编入部门预算的单位发展项目、市直发展项目支出安排数等。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">七、“三公”经费：指市级部门用一般公共预算财政拨款安排的因公出国（境）费、公务用车购置及运行费和公务接待费。其中，因公出国（境）费指单位公务出国（境）的住宿费、旅费、伙食补助费、杂费、培训费等支出；公务用车购置及运行费指单位公务用车购置费及租用费、燃料费、维修费、过路过桥费、保险费、安全奖励费用等支出；公务接待费指单位按规定开支的各类公务接待（含外宾接待）支出。</span></p><p style=\"line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"line-height: 16px; text-indent: 2em;\"><a style=\"color: rgb(0, 102, 204); text-decoration: underline; font-family: 宋体, SimSun; font-size: 20px;\" href=\"http://www1.ycsyy.com/upload/files/2020/2/ce479b27b33eae6.xls\" title=\"盐城市第一人民医院2020年部门预算公开表.xls\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院2020年部门预算公开表.xls</span></a></p><p style=\"line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span><br /></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2055','2875','1','','','12月25日下午，我院组织召开廉政风险防控“学、议、行”工作专题点评会，对重点部门、关键岗位的廉政风险点排查、防控工作进行分析评议。会上，药学部、设备处、医务处的负责同志分别就本部门贯彻落实9月30日全市医疗机构高值耗材专项整治暨卫生健康系统廉政风险防控“','192.168.162.110','192.168.162.110','1577289600','<p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 21px;\">12月25日下午，我院组织召开廉政风险防控“学、议、行”工作专题点评会，对重点部门、关键岗位的廉政风险点排查、防控工作进行分析评议。会上，药学部、设备处、医务处的负责同志分别就本部门贯彻落实9月30日全市医疗机构高值耗材专项整治暨卫生健康系统廉政风险防控“学、议、行”部署推进会的精神，开展廉政风险防控工作在会上作了交流发言，李明祥、柏宏坚、嵇友林等分管院领导分别进行了点评。出席会议的市纪委监委派驻纪检监察组、市卫健委领导予以现场指导，充分肯定了三部门的风险排查及防控工作，并就我院下一步廉政风险防控工作提出要求。会议开得很成功，取得了预期的效果，为全院面上风险防控工作的扎实推进提供了指导意义。</span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2056','2876','1','','','进一步改善医疗服务是“不忘初心、牢记使命”主题教育活动的重要一环。找差距、抓落实，查找出病员和家属不满意的问题立行立改，切实改善群众看病就医体验，努力提升群众获得感和满意度是医院工作永恒的主题。12月18日，医院在主院区门诊四楼会议室召开四季度病员工休座','192.168.162.110','192.168.162.110','1576771200','<p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">进一步改善医疗服务是“不忘初心、牢记使命”主题教育活动的重要一环。找差距、抓落实，查找出病员和家属不满意的问题立行立改，切实改善群众看病就医体验，努力提升群众获得感和满意度是医院工作永恒的主题。</span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">12月18日，医院在主院区门诊四楼会议室召开四季度病员工休座谈会。主院区内科病区16名病员（家属）代表以及医院行风办、护理部、医务处、总务处、财务处、药学部、医保办等职能处室负责人参加会议。</span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会上，医院行风办工作人员向大家介绍了医院基本概况，并邀请病员（家属）对医院各项工作进行现场评议，排查医院医疗服务中群众不满意、不方便、不放心的环节。与会人员纷纷表示，医院环境优雅舒适，医护人员服务态度好、医疗技术精、医德医风佳。他们还点名表扬了吉亚军、张由美、张扣东、卞伟纲、周晓宁、戴夕超、周欢欢、周芬、郑洁、周君珺、徐海艳、沈丽丽等医护人员。同时，对医院各项工作提出6条意见和建议。相关科室责任人员在现场对部分问题进行解答，对于需要多部门协调解决的问题，行风办随即汇总整理，迅速落实整改措施。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2057','2877','1','','','2019年12月19日，市一院14名行政处室副科级干部经过领导干部选拔任用程序上任。院党委、纪委通过任前廉政谈话、廉政知识考试、警示案例教育、签订廉洁从政承诺书、发放廉洁从政口袋书等“五个一”形式，在干部走上新岗位前吹廉风、划红线，进一步筑牢新任干部拒腐防变的','192.168.162.110','192.168.162.110','1576771200','<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2019年12月19日，市一院14名行政处室副科级干部经过领导干部选拔任用程序上任。院党委、纪委通过任前廉政谈话、廉政知识考试、警示案例教育、签订廉洁从政承诺书、发放廉洁从政口袋书等“五个一”形式，在干部走上新岗位前吹廉风、划红线，进一步筑牢新任干部拒腐防变的思想道德和纪法防线。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">纪委书记卞学梅在谈话中对新上岗的同志提出了五点要求。一是要坚决贯彻执行党的路线方针政策和上级决策部署，严守党的政治纪律、组织纪律和工作纪律，始终做到讲党性、守规矩、做表率。二是要严格遵守国家法律法规、医院各项规定制度，不徇私枉法、不违规办事，始终做到依法行政、按制度办事。三是要严格落实中央“八项规定”精神，始终保持求真务实、谦虚谨慎、勤俭节约的良好作风。四是要模范遵守中央和省、市关于廉洁自律的各项规定，增强防腐防变意识。五是要履职尽责，切实履行“一岗双责”，顾全大局，带好团队，勤奋工作，认真落实职责范围内的廉政建设工作。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">新上任的同志纷纷表示，一定不辜负组织嘱托，不忘初心，牢记使命，严以律己，廉洁从政，做一个让组织放心、群众满意的好干部。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">市一院对新任干部进行廉政教育，已经常态化。这既是履行干部选拔要求的必经程序，也是强化党风廉政建设的一项重要举措，充分体现了党组织对干部的关心和爱护。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2058','2878','1','','','12月5日，盐城市第一人民医院与耀州区人民医院对口协作管理会议正式召开。会议宣布了耀州区人民医院新的领导班子。2019年6月，在苏陕协作的大背景下，盐城市第一人民医院与耀州区人民医院正式建立对口支援关系，盐城市一院前后选派2批次共计10位专家默默扎根耀州，潜心','192.168.162.110','192.168.162.110','1575561600','<p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">12月5日，盐城市第一人民医院与耀州区人民医院对口协作管理会议正式召开。会议宣布了耀州区人民医院新的领导班子。2019年6月，在苏陕协作的大背景下，盐城市第一人民医院与耀州区人民医院正式建立对口支援关系，盐城市一院前后选派2批次共计10位专家默默扎根耀州，潜心“传帮带”，积极开展新技术，取得了10多项医疗技术新突破，填补了多项技术空白。这次两家医院对口协作管理，是医改新形势的要求，更是加强苏陕对口支援，推动健康扶贫工作，助力耀州百姓健康的一件大事，对推动耀州卫生健康事业发展、提高医疗服务水平具有重要的意义。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2059','2879','1','','','11月21日，南京大学和盐城市人民政府举行合作签约仪式，南京大学医学院附属盐城第一医院正式揭牌。盐城市第一人民医院与南京大学医学院将在学科建设、医疗技术、人才培养、科研交流和医院管理等方面紧密合作，更好地承担服务全市百姓健康的使命和责任。盐城市委书记、市','192.168.162.110','192.168.162.110','1574352000','<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">11月21日，南京大学和盐城市人民政府举行合作签约仪式，南京大学医学院附属盐城第一医院正式揭牌。盐城市第一人民医院与南京大学医学院将在学科建设、医疗技术、人才培养、科研交流和医院管理等方面紧密合作，更好地承担服务全市百姓健康的使命和责任。盐城市委书记、市人大常委会主任戴源，盐城市委副书记、市长曹路宝，江苏省教育厅副厅长王成斌，江苏省卫生健康委副巡视员祁爱平，南京大学党委常委、副校长、医学院院长张峻峰共同为“南京大学医学院附属盐城第一医院”揭牌。盐城市副市长葛启发、南京大学、盐南高新区和市有关部门及单位负责同志、盐城市第一人民医院领导班子等出席签约揭牌仪式。盐城市委副书记、市长曹路宝，南京大学党委常委、副校长、医学院院长张峻峰，江苏省卫生健康委副巡视员祁爱平，江苏省教育厅副厅长王成斌分别致辞。南京大学党委常委、副校长、医学院院长张峻峰和盐城市政府副市长葛启发代表双方签约。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2060','2880','1','','','11月21日下午，韩国延世大学世福兰斯医院李炳奭院长一行6人来盐，就盐城市第一人民医院与韩国延世大学世福兰斯医院建立合作医院关系举行揭牌仪式。盐城市人大常委会副主任周键云和韩国延世大学世福兰斯医院院长李炳奭为盐城市第一人民医院与韩国延世大学世福兰斯医院合','192.168.162.110','192.168.162.110','1574352000','<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">11月21日下午，韩国延世大学世福兰斯医院李炳奭院长一行6人来盐，就盐城市第一人民医院与韩国延世大学世福兰斯医院建立合作医院关系举行揭牌仪式。盐城市人大常委会副主任周键云和韩国延世大学世福兰斯医院院长李炳奭为盐城市第一人民医院与韩国延世大学世福兰斯医院合作医院揭牌。盐城市第一人民医院院长查文章和韩国延世大学世福兰斯医院院长李炳奭签署合作协议。盐城市卫生健康委员会主任徐杰在讲话中指出，韩国延世大学世福兰斯医院在很多学科领域方面技术领先，与我市医疗机构有很大的合作空间，特别是与市第一人民医院有广泛的合作基础，在医院管理交流、人才培养、技术开发上都可以进行合作，建立多学科、多层次交流机制，努力搭建优势学科创新平台，促进医疗技术水平进步。目前，我市正在实施“健康盐城”建设，努力提高全市的医疗健康水平。市委、市政府十分重视中韩卫生健康产业的合作，市卫健委将全力支持盐城医疗界与韩国延世大学医院的合作，为其在盐城的发展提供一切便利条件。盐城市第一人民医院党委书记张慧娟和韩国延世大学世福兰斯医院院长李炳奭分别致辞。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2061','2881','1','','','11月14日是“联合国糖尿病日”，今年的主题是“家庭与糖尿病”，旨在号召糖尿病患者家属与糖尿病患者一起抗击糖尿病，养成良好的生活习惯。11月14日上午，盐城市第一人民医院内分泌科携手临床营养科、药剂科的专家和志愿者走进盐南高新区新都街道社区卫生服务中心为就诊','192.168.162.110','192.168.162.110','1573747200','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">11月14日是“联合国糖尿病日”，今年的主题是“家庭与糖尿病”，旨在号召糖尿病患者家属与糖尿病患者一起抗击糖尿病，养成良好的生活习惯。11月14日上午，盐城市第一人民医院内分泌科携手临床营养科、药剂科的专家和志愿者走进盐南高新区新都街道社区卫生服务中心为就诊患者义诊宣传。11月14下午，盐城市一院临床营养科主任医师赵桐为食堂厨师及配餐员讲解《中国糖尿病膳食指南(2017)》。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"></span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2062','2882','1','','','11月7日-8日，由南通大学第二附属医院、盐城市医院协会主办，盐城市第一人民医院承办的国家级继续教育项目‑---休克规范化诊疗新进展学习班暨第二届盐城市急诊急救医学论坛、2019年盐城市急诊质量管理专业委员会年会在盐城市港府洲际酒店隆重举行。本次会议开幕式由盐城','192.168.162.110','192.168.162.110','1573228800','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">11月7日-8日，由南通大学第二附属医院、盐城市医院协会主办，盐城市第一人民医院承办的国家级继续教育项目‑---休克规范化诊疗新进展学习班暨第二届盐城市急诊急救医学论坛、2019年盐城市急诊质量管理专业委员会年会在盐城市港府洲际酒店隆重举行。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">本次会议开幕式由盐城市第一人民医院急诊科副主任陈茂刚主持。会议开幕式上，盐城市第一人民医院查文章院长、盐城市中医药管理局陶惠副局长等领导分别作重要讲话，描绘出盐城市第一人民医院急诊科未来发展的美好蓝图，为盐城市急诊学科的建设和发展指明了前进方向。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">本次会议参会人员达150人次，会议现场学术氛围浓厚，讲题内容精彩纷呈，会场井然有序，参会代表听课认真，讨论热烈。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2063','2883','1','','','为进一步落实国家卫生健康委发布的《国家卫生健康委关于进一步加强医疗机构感染预防和控制工作的通知》和《江苏省三级综合医院评审标准实施细则》中的要求，10月份，盐城市一院分阶段开展医院感染防控知识的全员培训与考核工作。全院各临床科室（病区）根据通知要求，积','192.168.162.110','192.168.162.110','1572796800','<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为进一步落实国家卫生健康委发布的《国家卫生健康委关于进一步加强医疗机构感染预防和控制工作的通知》和《江苏省三级综合医院评审标准实施细则》中的要求，10月份，盐城市一院分阶段开展医院感染防控知识的全员培训与考核工作。全院各临床科室（病区）根据通知要求，积极组织医务人员参加了培训学习，在感染防控相关基础知识，包括医院感染基本概念、标准预防概念、手卫生、消毒隔离、抗菌药物管理、无菌操作、安全注射、医疗废物分类、多重耐药菌防控等方面，利用“问卷星”VIP系统等培训考核平台进行考核。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2064','2884','1','','','为充分调动我院广大医务和科研人员从事科研活动的积极性，挖掘我院的科研潜力，发现和培养优秀科研人才，今年，我院出台下发了《盐城市第一人民医院培育基金管理办法（试行）》,并于10月底正式启动2019年度盐城市第一人民医院院内培育基金申报工作。设立培育基金项目是','192.168.162.110','192.168.162.110','1572710400','<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为充分调动我院广大医务和科研人员从事科研活动的积极性，挖掘我院的科研潜力，发现和培养优秀科研人才，今年，我院出台下发了《盐城市第一人民医院培育基金管理办法（试行）》,并于10月底正式启动2019年度盐城市第一人民医院院内培育基金申报工作。设立培育基金项目是为促进学科发展，人才成长，分设重点项目和青年项目，坚持“个人申请，专家评审，择优资助，宁缺毋滥”的原则，每年在财务预算中单独列支一定额度的科研培育基金、专款专用。院科技处负责统一管理培育基金项目，包括组织申报、评审、立项、实施、检查、协调和验收等工作。设立培育基金项目将进一步提升我院国家级项目申报的数量和质量，鼓励开展国家级项目的前期研究工作，促进医院与科研院所的合作，为申报国家级项目提供基础研究积累，实现各类国家级项目立项数的突破。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 19px; font-family: 仿宋;\"></span></strong></span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2065','2885','1','','','11月1日，由盐城市第一人民医院结成的全省首家市县镇三级紧密型医联体——响水县港城医院投入运营，为陈家港镇及周边地区居民提供高水平的医疗服务。开业当天，盐城市第一人民医院院长查文章带领骨科、耳鼻喉科、心血管内科等12个专科的专家，为患者提供免费的诊疗服务','192.168.162.110','192.168.162.110','1572624000','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">11月1日，由盐城市第一人民医院结成的全省首家市县镇三级紧密型医联体——响水县港城医院投入运营，为陈家港镇及周边地区居民提供高水平的医疗服务。开业当天，盐城市第一人民医院院长查文章带领骨科、耳鼻喉科、心血管内科等12个专科的专家，为患者提供免费的诊疗服务、健康咨询、康复指导等。此次义诊共接诊患者700余人，发放健康宣教手册500份，免费测血糖130人次、测血压225人次。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2066','2886','1','','','10月22日下午，南京大学党委常委、副校长、医学院院长张峻峰率团赴盐城市第一人民医院考察调研，南京大学医学院党政领导班子、盐城市人民政府副市长葛启发、盐城市卫生健康委员会主任徐杰、盐城市第一人民医院领导班子等陪同考察，双方就深化合作事宜开展交流讨论，达成','192.168.162.110','192.168.162.110','1571760000','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">10月22日下午，南京大学党委常委、副校长、医学院院长张峻峰率团赴盐城市第一人民医院考察调研，南京大学医学院党政领导班子、盐城市人民政府副市长葛启发、盐城市卫生健康委员会主任徐杰、盐城市第一人民医院领导班子等陪同考察，双方就深化合作事宜开展交流讨论，达成广泛共识。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">张峻峰一行先后参观了盐城市第一人民医院临床技能培训中心等临床、教学、科研场地与设施，并与相关负责同志进行了交流座谈。座谈会上，盐城市第一人民医院院长查文章首先汇报了医院基本情况、教学工作及双方合作方案。盐城市卫健委主任徐杰简要介绍了盐城市医疗卫生发展建设情况。他强调，盐城市近年来正大力推进医疗卫生事业建设，南京大学与盐城市第一人民医院携手合作是盐城市医疗卫生事业发展的重要契机，希望通过与南京大学的深度合作，进一步完善医院人才队伍建设、学科建设和体制机制建设，全力提升盐城市医疗服务水平。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">张峻峰对盐城市委市政府的大力支持以及盐城市第一人民医院为开展合作所做的前期准备工作表示感谢。他指出，以临床为中心，教学与科研辅助的“一体两翼”格局，将有助于医院向更高水平发展。南大医学院要与盐城市第一人民医院在前期共酌基础上，“做实、做快、做好”相关合作事宜，在科研合作平台、人才培养机制和临床诊疗体系等方面进行深度探索，全面落实体制机制建设，通过互派人员开展学习交流、共同筹划科研年会等一系列举措，加强人才队伍方面的合作，加快实现医院科研水平的提升。张峻峰强调，双方的合作对南京大学来说是一个新的起点，也是南京大学服务国家、服务江苏、服务盐城的一个方向，医学院更应当以此为契机，不忘初心、牢记使命，以全新的战略高度、全局高度、历史高度谋发展，为早日实现习近平总书记提出的“第一个南大”的宏伟目标作出应有的贡献。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2067','2887','1','','','为迎接全球洗手日的到来，将手卫生的基本理念和服务宗旨充分展现在医院的每一个角落，10月15日,盐城市第一人民医院开展手卫生宣传日活动，在全院人员中普及手卫生知识，提高全员手卫生依从性与正确性，进而预防与控制医院感染的发生，保障医疗质量及患者的安全。2019年','192.168.162.110','192.168.162.110','1571155200','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为迎接全球洗手日的到来，将手卫生的基本理念和服务宗旨充分展现在医院的每一个角落，10月15日,盐城市第一人民医院开展手卫生宣传日活动，在全院人员中普及手卫生知识，提高全员手卫生依从性与正确性，进而预防与控制医院感染的发生，保障医疗质量及患者的安全。2019年手卫生宣传日活动的主题是:“养成良好卫生习惯，从正确洗手做起”。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本次活动由院感染管理处、院办、医务处、护理部、宣传处、信息处等科室联合举办。活动内容和形式丰富多彩，在多个院区设置宣传活动，现场运用多媒体、宣传画等方式进行手卫生知识宣传；邀请医院手卫生表演组现场表演一场活力动感的手卫生操；医务人员执行手卫生规范签名承诺活动；开展手卫生相关知识培训及有奖知识竞答活动。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2068','2888','1','','','为贯彻落实9月30日全市医疗机构高值医用耗材专项整治暨卫生健康系统廉政风险防控“学、议、行”活动部署推进会精神，进一步推动高值医用耗材专项整治工作，不断深化医院廉政风险防控工作，10月9日下午，市一院召开高值医用耗材专项整治暨廉政风险防控“学、议、行”活动','192.168.162.110','192.168.162.110','1570636800','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为贯彻落实9月30日全市医疗机构高值医用耗材专项整治暨卫生健康系统廉政风险防控“学、议、行”活动部署推进会精神，进一步推动高值医用耗材专项整治工作，不断深化医院廉政风险防控工作，10月9日下午，市一院召开高值医用耗材专项整治暨廉政风险防控“学、议、行”活动部署动员会议。院领导班子全体成员、中层干部、护士长、主诊医师、副主诊医师参加了此次会议。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2069','2889','1','','','为隆重庆祝中华人民共和国成立70周年，弘扬医院光荣传统，展示医院发展成果，表达一院人热爱党、热爱祖国、热爱人民的高尚情怀，9月29日下午，盐城市第一人民医院用一场精彩的文艺演出，向祖国母亲70周年华诞献礼。文艺演出设置了三个篇章，精心编排的舞蹈、倾情献唱的','192.168.162.110','192.168.162.110','1569772800','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为隆重庆祝中华人民共和国成立70周年，弘扬医院光荣传统，展示医院发展成果，表达一院人热爱党、热爱祖国、热爱人民的高尚情怀，9月29日下午，盐城市第一人民医院用一场精彩的文艺演出，向祖国母亲70周年华诞献礼。文艺演出设置了三个篇章，精心编排的舞蹈、倾情献唱的金曲、生动有趣的小品和情景剧、百人合唱团等节目精彩纷呈，为中华人民共和国成立70周年呈上了一份特殊的礼物。演出中还举行了“最美白衣天使”颁奖仪式，表彰了一批在医疗、教学、科研、急救等方面取得丰硕成果，在平凡的岗位上作出不平凡业绩的先进、楷模。盐城市政协副主席、市卫健委副主任孙长春，盐城市委宣传部副部长、市文明办主任周柏青，盐城市卫健委主任徐杰，盐城市一院党委书记张慧娟，盐城市一院院长查文章为“最美白衣天使”代表颁奖。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2070','2890','1','','','9月19日下午，盐城市第一人民医院召开“不忘初心、牢记使命”主题教育动员部署大会。院领导班子全体成员，各党支部书记、支委、党小组长、党建联络员，部分党员代表参会。会上，盐城市第一人民医院院长查文章主持会议, 盐城市第一人民医院党委书记张慧娟动员讲话，盐城','192.168.162.110','192.168.162.110','1568908800','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">9月19日下午，盐城市第一人民医院召开“不忘初心、牢记使命”主题教育动员部署大会。院领导班子全体成员，各党支部书记、支委、党小组长、党建联络员，部分党员代表参会。会上，盐城市第一人民医院院长查文章主持会议, 盐城市第一人民医院党委书记张慧娟动员讲话，盐城市卫生健康委员会主任、委主题教育第一指导组组长徐杰讲话。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">徐杰主任在讲话中强调，根据中央精神和省市委、委党委部署，市直卫生健康系统“不忘初心、牢记使命”主题教育已经全面启动。开展“不忘初心、牢记使命”主题教育，一要紧扣主题主线，强化统筹安排；二要坚持以上率下，强化领导示范；三是抓牢基层基础，强化支部引领；四是立足见底见效，强化问题整改；五是秉持从严从实，强化整体推进。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2071','2891','1','','','为隆重庆祝中华人民共和国成立70周年，大力弘扬以爱国主义为核心的伟大民族精神，盐城市第一人民医院把开展“不忘初心、牢记使命”主题教育与开展以“我和我的祖国”为主题的宣传教育活动紧密结合起来，向中华人民共和国成立70周年献礼。9月18日，盐城市第一人民医院组','192.168.162.110','192.168.162.110','1568822400','<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为隆重庆祝中华人民共和国成立70周年，大力弘扬以爱国主义为核心的伟大民族精神，盐城市第一人民医院把开展“不忘初心、牢记使命”主题教育与开展以“我和我的祖国”为主题的宣传教育活动紧密结合起来，向中华人民共和国成立70周年献礼。9月18日，盐城市第一人民医院组织一批医学博士、专家组成“心连心”博士专家团赴响水县人民医院开展义诊、讲学、查房、手术、会诊等走基层活动。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">今年7月，盐城市第一人民医院与响水县人民医院举行医联体签约仪式，在医疗技术、医疗服务、人才培养、重点科室建设等方面开展合作共建达成协议。此次盐城市第一人民医院“心连心”博士专家团走基层（响水站）活动共接诊450人次，进一步推进了医联体深度合作，提升了基层医疗服务能力，让老百姓获得更加优质的医疗服务和健康保障。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2072','2892','1','','','9月12日，盐城市第一人民医院重症医学科独立开展了盐城地区首例ECMO（体外膜肺氧合）治疗技术，为救治重症患者争取了宝贵的时间和机会，开启了“重生”之门。ECMO是一门顶尖体外生命支持技术，它代表一个医院，甚至一个地区、一个国家的危重症急救水平。盐城市第一人民','192.168.162.110','192.168.162.110','1568304000','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">9月12日，盐城市第一人民医院重症医学科独立开展了盐城地区首例ECMO（体外膜肺氧合）治疗技术，为救治重症患者争取了宝贵的时间和机会，开启了“重生”之门。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">ECMO是一门顶尖体外生命支持技术，它代表一个医院，甚至一个地区、一个国家的危重症急救水平。盐城市第一人民医院重症医学科于2019年4月引进ECMO设备，在副院长、重症医学科主任医师邓义军的带领下，由副主任医师陈建军等专家组建成立ECMO团队，团队成员进行多次的理论培训和模拟演练，确保安全有效地实施ECMO技术。救治过程中，ECMO功不可没，更离不开团队齐心协作、精心救治和日夜守护。该病例系我市首次独立开展应用ECMO技术救治呼吸衰竭患者，标志着盐城市一院乃至我市急诊急救医疗水平再上新台阶。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2073','2893','1','','','8月27日--9月7日，瑞士圣加仑州医院Gerhard  Hildebrandt教授在盐城市第一人民医院开展为期10天的会诊病人、指导手术、教学查房、学术研讨等中瑞神经外科学术交流活动。','192.168.162.110','192.168.162.110','1567872000','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">8月27日--9月7日，瑞士圣加仑州医院Gerhard  Hildebrandt教授在盐城市第一人民医院开展为期10天的会诊病人、指导手术、教学查房、学术研讨等中瑞神经外科学术交流活动。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2074','2894','1','','','城市因文明而美丽,人民因城市而自豪。新的时代，需要更高层次的文明实践;创建文明城市，呼唤每个人的积极参与。为深入推进文明城市创建工作，不断提升广大职工文明服务意识和水平，展现新时代我院医护人员的职业形象，在中华人民共和国成立70周年华诞来临之际，盐城市第','192.168.162.110','192.168.162.110','1567353600','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">城市因文明而美丽,人民因城市而自豪。新的时代，需要更高层次的文明实践;创建文明城市，呼唤每个人的积极参与。为深入推进文明城市创建工作，不断提升广大职工文明服务意识和水平，展现新时代我院医护人员的职业形象，在中华人民共和国成立70周年华诞来临之际，盐城市第一人民医院全国青年文明号——门诊药房、全国巾帼文明岗——消毒供应中心向全院职工郑重发出倡议书，倡议全院职工加强理论学习，提升道德修养；遵守社会公德，树立文明形象；着装规范得体，言行文明礼貌；弘扬雷锋精神，营造文明氛围。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2075','2895','1','','','近日，盐城市第一人民医院神经外科成功开展了全市首例神经导航下脑膜瘤切除术。市一院神经外科副主任郭俊博士等医务人员利用新引进的高端神经导航系统，成功切除了患者的脑部肿瘤。这种“精准治疗”新技术大大降低了对正常脑组织的干扰损伤，使手术更加微创化、精准化，','192.168.162.110','192.168.162.110','1567267200','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">近日，盐城市第一人民医院神经外科成功开展了全市首例神经导航下脑膜瘤切除术。市一院神经外科副主任郭俊博士等医务人员利用新引进的高端神经导航系统，成功切除了患者的脑部肿瘤。这种“精准治疗”新技术大大降低了对正常脑组织的干扰损伤，使手术更加微创化、精准化，标志着我市神经外科技术水平迈上新台阶。该技术改变了传统的开颅手术模式，实现了脑内尤其是脑深部病灶的准确定位，对于提高脑肿瘤的切除率，降低手术并发症与死亡率具有重要的临床价值。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2076','2896','1','','','为进一步推进盐城市全科医学的建设和全科医师的培养，改善基层医疗卫生服务体系，8月17日～18日，盐城市医学会全科医学会分会成立大会暨第一届全科医学学术会议隆重举办。此次活动由盐城市医学会主办，盐城市第一人民医院具体承办。会议邀请了江苏省医学会全科医学分会','192.168.162.110','192.168.162.110','1567267200','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为进一步推进盐城市全科医学的建设和全科医师的培养，改善基层医疗卫生服务体系，8月17日～18日，盐城市医学会全科医学会分会成立大会暨第一届全科医学学术会议隆重举办。此次活动由盐城市医学会主办，盐城市第一人民医院具体承办。会议邀请了江苏省医学会全科医学分会主任委员黄敏，江苏省人民医院老年医学科主任、全科医学科主任汤琪云，东南大学附属中大医院陈文姬教授，盐城市一院全科医学科李大林教授等著名专家出席会议并进行了精彩讲座，与工作在基层一线的全科医生们共同讨论、分享和交流。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会上,盐城市医学会秘书长杨益国宣布成立全科分会, 盐城市第一人民医院党委书记张慧娟与江苏省医学会全科医学分会主任委员黄敏为盐城市医学会全科医学分会揭牌，盐城市第一人民医院党委委员、副院长柏宏坚与副院长邓义军为盐城市第一人民医院全科医学研究所揭牌。盐城市第一人民医院党委书记张慧娟、江苏省医学会全科医学分会主任委员黄敏、江苏省医学会全科医学分会主任委员黄敏分别致辞。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2077','2897','1','','','近日，盐城市第一人民医院血液科首次成功采用单倍体造血干细胞移植技术救治一名ph阳性急性淋巴细胞白血病患者。患者于2019年8月21日顺利出舱，手术宣告成功。盐城首例单倍体造血干细胞移植成功，标志着我市治疗恶性血液病技术取得新的突破。患者的家人给市一院血液科、','192.168.162.110','192.168.162.110','1566403200','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">近日，盐城市第一人民医院血液科首次成功采用单倍体造血干细胞移植技术救治一名ph阳性急性淋巴细胞白血病患者。患者于2019年8月21日顺利出舱，手术宣告成功。盐城首例单倍体造血干细胞移植成功，标志着我市治疗恶性血液病技术取得新的突破。患者的家人给市一院血液科、药学部、输血科、麻醉手术科的医务人员送上锦旗表示感谢。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2078','2898','1','','','2019年8月19日是第二个中国医师节。为大力弘扬“敬佑生命、救死扶伤、甘于奉献、大爱无疆”的崇高精神，不断激发广大医务工作者的职业荣誉感和使命感，全心全意为病员服务，积极投身医疗、教学、科研工作之中，8月19日下午，盐城市第一人民医院召开2019年中国医师节庆祝','192.168.162.110','192.168.162.110','1566230400','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年8月19日是第二个中国医师节。为大力弘扬“敬佑生命、救死扶伤、甘于奉献、大爱无疆”的崇高精神，不断激发广大医务工作者的职业荣誉感和使命感，全心全意为病员服务，积极投身医疗、教学、科研工作之中，8月19日下午，盐城市第一人民医院召开2019年中国医师节庆祝大会，表彰了一批在医学技术进步发展中取得优异成绩的先进集体和先进个人。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">离退休的老前辈、老专家受邀参会。盐城市第一人民医院党委副书记、副院长朱向军宣读表彰决定，老、中、青三代医生代表发言，全体参会医师重温《中国医师宣言》。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院院长查文章在致辞中指出，希望广大医师继续弘扬“敬佑生命、救死扶伤、甘于奉献、大爱无疆”的崇高精神，强质量、重创新，鼓干劲、谋突破，全面推动医院高质量发展。一要有强烈的求知欲，练就过硬的技术和本领；二要有创新意识，在创新中学习，在创新中思考，在创新中发现和解决问题；三要德术并举，打造附医特色品牌，体现附医内涵和精神；四要加强团队协作，利用团队之力，更好地为患者服务。任重而道远，作为附医人，要共同携手、凝心聚力，写好高质量发展的新篇章，为人民的美好生活、百姓的健康而不懈努力，以优异的成绩迎接新中国成立70周年！</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">党委书记张慧娟在讲话中强调，“中国医师节”的设立，这意味着莫大的荣誉和重大的责任，同时也对医师们提出了新要求，充满了新期待，激励大家更加尊敬自己的职业，更加自律、自省、自重，为人民提供更好的健康服务。希望广大医师不忘初心、牢记使命，做好医院改革的促进者；严谨求精、追求卓越，做好人民健康的守护者；立德修身、弘扬医德，做好职业操守的捍卫者；实干苦干，勇于担当，做好医院发展的建设者。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2079','2899','1','','','为提升盐城地区早癌发现率和病变精准诊断率，进一步提高青年医师内镜技术水平，8月15日～16日，奥林巴斯-鼓楼医院青年内镜医师培训项目盐城站ESD第三期培训班在盐城市第一人民医院成功举办。南京鼓楼医院消化内科凌亭生教授、杨天博士等受邀，来院指导开展消化道早癌诊','192.168.162.110','192.168.162.110','1565971200','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为提升盐城地区早癌发现率和病变精准诊断率，进一步提高青年医师内镜技术水平，8月15日～16日，奥林巴斯-鼓楼医院青年内镜医师培训项目盐城站ESD第三期培训班在盐城市第一人民医院成功举办。南京鼓楼医院消化内科凌亭生教授、杨天博士等受邀，来院指导开展消化道早癌诊疗技术、病理诊断技术、ESD及其衍生技术等。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院消化内科主任杨红梅主持活动。南京鼓楼医院消化内科凌亭生教授、病理科孙琦、消化内科杨天博士、盐城市第一人民医院消化内科副主任吴旭东分别作了精彩的学术讲座。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">从传授知识要点，到手把手带教，凌亭生教授和杨天博士等专家对内镜诊断、ESD及衍生技术进行了指导和演示。从进镜的基本步骤，到观察要点，到注意寻找发白发红的部位，到开启NBI进行结构观察，每一步操作详细、精准、专业。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2080','2900','1','','','2019年7月，由盐城市一院牵头组建的中国第14期援圭亚那医疗队圆满完成一年援外任务载誉而归，他们以高度的责任感和使命感，发扬救死扶伤的人道主义精神，为受援国人民提供了专业的医疗服务，赢得了当地政府的赞誉和人民的信任，为国家赢得了荣誉。医疗队在圭亚那工作期','192.168.162.110','192.168.162.110','1565020800','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年7月，由盐城市一院牵头组建的中国第14期援圭亚那医疗队圆满完成一年援外任务载誉而归，他们以高度的责任感和使命感，发扬救死扶伤的人道主义精神，为受援国人民提供了专业的医疗服务，赢得了当地政府的赞誉和人民的信任，为国家赢得了荣誉。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">医疗队在圭亚那工作期间，医疗队门诊接诊16400人次； 病房查房28665人次；手术3920人次； 抢救危重病人560例； 针灸1142人次；麻醉1218例；出具病理和影像报告11349例；开展新技术和新项目43项；他们的医疗技术、医疗服务被圭亚那国家电视台、《Kaieteur News》《Times》等多家主流媒体宣传报道20余次，被人民日报、央视、健康报等国家级媒体报道30余次，国内网络媒体、省、市级电视台及报刊等报道100余次 。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2081','2901','1','','','母乳是婴儿最理想、最安全的天然食物，也是大自然赐予人类的珍贵礼物。为保护、促进和支持母乳喂养，自1992年起，国际母乳喂养行动联盟(WABA)将每年8月1日-7日确定为世界母乳喂养周，旨在呼吁世界各国重视母乳喂养对母婴、对家庭、对社会的重要意义，以促进儿童的健康生','192.168.162.110','192.168.162.110','1565020800','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">母乳是婴儿最理想、最安全的天然食物，也是大自然赐予人类的珍贵礼物。为保护、促进和支持母乳喂养，自1992年起，国际母乳喂养行动联盟(WABA)将每年8月1日-7日确定为世界母乳喂养周，旨在呼吁世界各国重视母乳喂养对母婴、对家庭、对社会的重要意义，以促进儿童的健康生存与发展。今年世界母乳喂养周主题是：“助力父母，成功母乳喂养”，强调以母亲和婴儿为核心，遵循生命最初1000天的时间轴，所有支持母乳喂养的医务人员及社会各界，需加强各环节协作，为母婴家庭传递一致信息并提供支持措施，帮助父母实现成功母乳喂养的愿望。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为了给新手爸妈们提供丰富的育儿知识，帮助准妈妈们掌握正确母乳喂养方式，促进社会和公众对母乳喂养重要性的正确认识和支持母乳喂养，8月5日上午，盐城市第一人民医院产科、营养科联合开展大型义诊活动，为孕产妇及家属宣传科普哺乳期乳房护理措施、指导孕期及哺乳期妇女膳食和不同阶段的婴幼儿科学喂养等知识。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2082','2902','1','','','8月1日下午，市委常委、市纪委书记、市监委主任王小红到盐城市第一人民医院调研全面从严治党、党风廉政建设工作情况，并主持召开座谈会。市纪委常委、秘书长张国锋等参加活动。座谈会前，王小红实地察看盐城市第一人民医院主院区门诊部、住院部等，了解近年来医院专科技','192.168.162.110','192.168.162.110','1564675200','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">8月1日下午，市委常委、市纪委书记、市监委主任王小红到盐城市第一人民医院调研全面从严治党、党风廉政建设工作情况，并主持召开座谈会。市纪委常委、秘书长张国锋等参加活动。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">座谈会前，王小红实地察看盐城市第一人民医院主院区门诊部、住院部等，了解近年来医院专科技术、人才培养、科研教学等情况以及主院区建设情况。盐城市卫生健康委员会主任徐杰介绍盐城市第一人民医院主院区二期工程建设情况，盐城市第一人民医院党委书记张慧娟介绍医院人才建设情况，盐城市第一人民医院院长查文章介绍国家标准化代谢性疾病管理中心盐城分中心——盐城市糖尿病防治中心，走访外科第一党支部党员活动室，调研基层党支部党建、党风廉政建设工作开展情况，观看盐城市第一人民医院《落实全面从严治党主体责任，切实提升医院党建工作水平》专题党建及党风廉政建设工作成果展示宣传展板。<br /> 座谈会上，市委常委、市纪委书记、市监委主任王小红听取有关部门和单位相关工作情况汇报并提工作要求。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2083','2903','1','','','为进一步加强医疗机构内镜清洗消毒工作，保障医疗质量和医疗安全，7月27日，“洗消有术”2019江苏消化内镜清洗消毒巡讲——盐城站在盐城市第一人民医院成功举办。盐城市第一人民医院护理部主任汤琪春、南京鼓楼医院消化内镜中心护士长李雯分别致辞。盐城市第一人民医院','192.168.162.110','192.168.162.110','1564588800','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为进一步加强医疗机构内镜清洗消毒工作，保障医疗质量和医疗安全，7月27日，“洗消有术”2019江苏消化内镜清洗消毒巡讲——盐城站在盐城市第一人民医院成功举办。盐城市第一人民医院护理部主任汤琪春、南京鼓楼医院消化内镜中心护士长李雯分别致辞。盐城市第一人民医院感染管理处主任谭思源、南京鼓楼医院消化内镜中心护士长李雯、南京医科大学第二附属医院消化医学中心护士长吴萍、江苏省人民医院内镜中心护士长丁静给学员们呈现了一场场知识盛宴，授课内容丰富、形式多样。精彩授课后，在南京鼓楼医院内镜中心蔡培、江苏省人民医院内镜中心张道权等讲师的带领下，学员们开展了规范流程操作演练，分享具有操作性的消化内镜清洗消毒经验。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2084','2904','1','','','7月22日，盐城市党政代表团到南京大学考察，江苏省政协副主席、南京大学党委书记胡金波会见盐城市委书记戴源、市长曹路宝一行。盐城市副市长葛启发、盐城市卫生健康委员会主任徐杰、盐城市第一人民医院党委书记张慧娟、院长查文章、副院长邓义军等随行到南京大学考察，','192.168.162.110','192.168.162.110','1563811200','<p style=\"margin-bottom: 0px; text-align: justify; background: white; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">7月22日，盐城市党政代表团到南京大学考察，江苏省政协副主席、南京大学党委书记胡金波会见盐城市委书记戴源、市长曹路宝一行。盐城市副市长葛启发、盐城市卫生健康委员会主任徐杰、盐城市第一人民医院党委书记张慧娟、院长查文章、副院长邓义军等随行到南京大学考察，学习借鉴南京大学在医疗卫生领域的科研成果和先进经验，进一步深化南京大学医学院与盐城市第一人民医院的交流合作。作为教育部直属的重点综合性大学，南京大学在教学、科研和社会服务等各个领域保持良好的发展态势，各项办学指标和综合实力均位居全国高校前列。此次，盐城市第一人民医院随盐城市党政代表团到南大考察，双方将在学科建设、医疗技术、人才培养、科研交流和医院管理等方面开展全面合作。</span></p><p style=\"margin-bottom: 0px; text-align: justify; background: white; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">会上，江苏省政协副主席、南京大学党委书记胡金波，盐城市委书记、市人大常委会主任戴源，盐城市委副书记、市长曹路宝分别作重要讲话。盐城市第一人民医院院长查文章汇报市一院教学科研的基本情况，南京大学科技处处长秦猛、南京大学医学院党委书记韩晓冬等介绍科研教学及与盐城市一院洽谈合作情况</span><span style=\"font-family: 宋体, SimSun; font-size: 13px; color: rgb(120, 124, 129); letter-spacing: 0px; background: white;\">。</span></span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2085','2905','1','','','为进一步规范肝癌患者的诊疗，为患者提供全方位、专业化、规范化、个体化的诊断和治疗，7月12日上午，盐城市第一人民医院肝癌多学科综合门诊（MDT）正式开诊，标志着市一院多学科诊疗工作向规范化、常态化迈出重要的一大步，4名患者在整个多学科诊疗服务过程中最大程度','192.168.162.110','192.168.162.110','1562947200','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为进一步规范肝癌患者的诊疗，为患者提供全方位、专业化、规范化、个体化的诊断和治疗，7月12日上午，盐城市第一人民医院肝癌多学科综合门诊（MDT）正式开诊，标志着市一院多学科诊疗工作向规范化、常态化迈出重要的一大步，4名患者在整个多学科诊疗服务过程中最大程度获益。肝癌多学科综合门诊依托三级甲等综合医院雄厚的技术实力和先进的医疗设备；依据国际国内最新的肝癌诊治指南和规范；整合普通外科、介入科、影像科、肿瘤科、感染科、消化内科、放疗科、病理科等多学科肝癌诊治专家；集手术、介入、放疗、化疗、中医药治疗、靶向治疗等多种手段；为肝癌患者制定最佳的治疗方案。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2086','2906','1','','','7月11日下午，全国巾帼文明岗、江苏省巾帼文明岗授牌暨巾帼消费助力脱贫攻坚活动在盐城市第一人民医院举办。“全国巾帼文明岗”是全国妇联授予优秀职业妇女团体的最高荣誉称号。1997年启动创建至今，盐城市第一人民医院共创建成功全国巾帼文明岗2个，全市仅此一家。会议','192.168.162.110','192.168.162.110','1562860800','<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">7月11日下午，全国巾帼文明岗、江苏省巾帼文明岗授牌暨巾帼消费助力脱贫攻坚活动在盐城市第一人民医院举办。“全国巾帼文明岗”是全国妇联授予优秀职业妇女团体的最高荣誉称号。1997年启动创建至今，盐城市第一人民医院共创建成功全国巾帼文明岗2个，全市仅此一家。会议由盐城市卫生健康委员会党委委员、副主任陈旭东主持，盐城市第一人民医院党委书记张慧娟致辞，盐城市妇联主席盛艳给“全国巾帼文明岗”——盐城市第一人民医院消毒供应中心、“江苏省巾帼文明岗”——重症医学科二病区授牌，盐城市妇联主席盛艳讲话，盐城市第一人民医院消毒供应中心护士长陈艳代表发言，盐城市妇联副主席胡忠秀宣读“巾帼有情 爱心消费”倡议书。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">会上，盐城市妇联主席盛艳指出，积极创建巾帼文明岗，大カ培树先进典型，对提升医务人员职业光荣感、责任感，树立医疗卫生行业良好社会形象具有重要意义。一要不断丰富争创内涵。融岗位技能、业务特色、服务质量、社会效益为一体，引导女职工既立足本职、争创佳绩，又参与社会公益和志愿服务，打造具有鲜明特色的文明岗、示范岗、公益岗。二要不断放大示范效应。做优爱心服务，做实便民服务，切实把全国、省巾帼文明岗建成我市卫健系统文明服务的窗口和行业形象的样板。三要不断提升争创水平。加强党建带妇建，搭建联谊、交流、学习平台，保护好、引导好女职工的积极性、创造性，为她们更好地施展才华和展示才干创造机会，为办好人民满意的医院贡献巾帼力量、展示巾帼风采。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2087','2907','1','','','为贯彻落实《国务院办公厅关于推进医疗联合体建设和发展的指导意见》《省医改办关于进一步推进医疗联合体建设和发展的实施意见》《关于印发江苏省医疗联合体建设规划（试行）的通知》和市委、市政府要求，推进我市医联体规范有序建设与发展，7月5号上午，在市卫健委和响','192.168.162.110','192.168.162.110','1562342400','<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为贯彻落实《国务院办公厅关于推进医疗联合体建设和发展的指导意见》《省医改办关于进一步推进医疗联合体建设和发展的实施意见》《关于印发江苏省医疗联合体建设规划（试行）的通知》和市委、市政府要求，推进我市医联体规范有序建设与发展，7月5号上午，在市卫健委和响水县委的组织下，盐城市第一人民医院、响水县人民医院、响水县港城医院医联体签约暨合作共建推进会在响水县人民医院正式召开。盐城市第一人民医院查文章院长、嵇友林副院长、闻国华副院长、邓义军副院长及相关职能部门负责人、省级重点临床专科学科带头人代表参加了此次会议。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px; text-indent: 37px;\">会议得到了市卫健委、响水县委、县政府、响水卫健委各级领导的高度重视，市卫健委徐杰主任、响水县委崔爱国书记、响水县人民政府桑良举副县长等领导做了重要讲话，并对盐城市第一人民医院和响水县人民医院共建医联体，促进响水县人民医院及响水县医疗卫生事业全面进步及造福当地百姓等表示了良好祝愿及充分肯定。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px; text-indent: 37px;\">响水县人民医院王洪华院长表达了对各级领导和盐城市第一人民医院领导和专家的到来表示了热烈的欢迎，同时介绍了响水县人民医院及响水县港城医院的建设、发展情况，并对通过医联体共建，促进响水县院各项工作有力推进表达了强烈的企盼。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px; text-indent: 37px;\">盐城市第一人民医院查文章院长表达了对各级领导支持医联体工作的衷心感谢，也代表盐城市第一人民医院领导班子和广大员工对建设好医联体的信心和决心。查院长介绍了医联体建设的具体方案和措施，重点提出了技术、质量、安全要达到两个医院间完全同质化管理的要求，并表示要通过专科共建、成立名医工作室、成立专科联盟、派驻医务人员等多种方式，达到提升响水县院综合服务能力的最终目标。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2088','2908','1','','','6月以来，盐城市第一人民医院按照市委统一部署，扎实开展系列特色党建活动，切实为即将开展的“不忘初心、牢记使命”主题教育夯实基础。开展特色党日活动。6月12日，盐城市第一人民医院机关第二党支部在新四军重建军部旧址——泰山庙开展党日活动，重温抗战时期的重大历','192.168.162.110','192.168.162.110','1561996800','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">6月以来，盐城市第一人民医院按照市委统一部署，扎实开展系列特色党建活动，切实为即将开展的“不忘初心、牢记使命”主题教育夯实基础。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">开展特色党日活动。6月12日，盐城市第一人民医院机关第二党支部在新四军重建军部旧址——泰山庙开展党日活动，重温抗战时期的重大历史事件，在党旗前庄重宣誓，回顾入党初心，并为5名党员过政治生日。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">开展党建目标责任制考核。6月下旬，由盐城市第一人民医院组织处牵头成立党建工作考核小组，对全院11个基层党支部2018年度党建工作进行综合检查考核，严格奖惩，充分体现党要管党、从严治党的要求。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">开展意识形态工作专题学习。6月19日下午，盐城市第一人民医院召开党委理论学习中心组学习（扩大）会议，院领导班子成员、党员代表共计300余人接受了意识形态专题教育。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">举办“不忘初心、牢记使命”主题教育培训班。6月20日，41名党员干部赴革命圣地河北省平山县，在西柏坡、白求恩柯棣华纪念馆、正定古城等地，通过两天的现场教学，完成了一次润泽心灵的红色之旅，感悟革命精神，缅怀革命先烈，传承红色基因。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">隆重集会纪念建党98周年。7月1日，盐城市一院召开纪念中国共产党成立98周年暨“七一”表彰大会。重温党的光辉历程，表彰先进典型，激励全院各级党组织和广大党员不忘初心、牢记使命，勇担推动医院高质量发展走在苏北苏中前列的历史使命。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">今年以来，盐城市第一人民医院党委全面贯彻落实习近平新时代中国特色社会主义思想和党的十九大精神，坚持以人民健康为中心，强化党建引领，切实将党建工作与医院改革发展同谋划、共部署、齐推进，按照市委要求，充分发挥基层党支部战斗堡垒作用和党员先锋模范带头作用，以坚强的党性、务实的作风、精湛的技术和优质的服务为全市人民的健康保驾护航，为推动医院高质量发展走在苏北、苏中前列不懈奋斗，努力用优异的业绩向新中国建国七十周年献上一份厚礼。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2089','2909','1','','','98载风雨征程，98载沧桑巨变。值此中国共产党建党98周年华诞，7月1日下午，盐城市第一人民医院召开纪念中国共产党成立九十八周年暨“七一”表彰大会。会上，盐城市一院党委副书记、院长查文章宣读国家、省、市级先进集体及先进个人名单; 盐城市一院党委副书记、副院长朱','192.168.162.110','192.168.162.110','1561996800','<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">98载风雨征程，98载沧桑巨变。值此中国共产党建党98周年华诞，7月1日下午，盐城市第一人民医院召开纪念中国共产党成立九十八周年暨“七一”表彰大会。会上，盐城市一院党委副书记、院长查文章宣读国家、省、市级先进集体及先进个人名单; 盐城市一院党委副书记、副院长朱向军宣读《关于表彰院级先进党支部、优秀党务工作者和优秀共产党员的决定》; 为先进基层党支部、优秀共产党员、优秀党务工作者等颁奖; 优秀党员代表、先进集体发言。会议最后，盐城市一院党委书记张慧娟提工作要求：一要守初心，担使命，在主题教育活动中走在前；二要强堡垒，当先锋，在医院高质量发展中作表率；三要找差距，抓落实，在迎评和完成全年目标任务上冲在先。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2090','2910','1','','','为加快盐城市疼痛学科的发展，提高各级医院疼痛科的科室建设和医疗技术水平，6月28日上午，盐城市第一人民医院疼痛专科紧密型医联体暨盐城市第一人民医院疼痛专科紧密型医联体亭湖区新洋疼痛诊疗服务中心揭牌成立！会上，中华医学会疼痛学分会主任委员刘延青教授致辞，','192.168.162.110','192.168.162.110','1561737600','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为加快盐城市疼痛学科的发展，提高各级医院疼痛科的科室建设和医疗技术水平，6月28日上午，盐城市第一人民医院疼痛专科紧密型医联体暨盐城市第一人民医院疼痛专科紧密型医联体亭湖区新洋疼痛诊疗服务中心揭牌成立！</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">会上，中华医学会疼痛学分会主任委员刘延青教授致辞，盐城市第一人民医院院长查文章教授、盐城市亭湖区政府副区长张洪国、盐城市卫健委基层处处长滕平、盐城市医学会副秘书长朱加祥讲话。张洪国副区长和查文章院长共同为盐城市第一人民医院疼痛专科紧密型医联体亭湖区新洋疼痛诊疗服务中心揭牌。盐城市第一人民医院疼痛科主任医师薛祥云和盐城市亭湖区新洋社区卫生服务中心院长吉联军签订医联体合作协议。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2091','2911','1','','','为搭建神经外科的交流平台，研讨与分享神经外科的治疗心得与手术技巧，促进盐城地区神经外科领域不断发展，6月28日下午，由江苏省临床重点专科——盐城市第一人民医院神经外科主办的2019年盐城地区神经外科学术沙龙会议隆重召开。会议多角度探讨神经学科“脑肿瘤、脑血','192.168.162.110','192.168.162.110','1561737600','<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为搭建神经外科的交流平台，研讨与分享神经外科的治疗心得与手术技巧，促进盐城地区神经外科领域不断发展，6月28日下午，由江苏省临床重点专科——盐城市第一人民医院神经外科主办的2019年盐城地区神经外科学术沙龙会议隆重召开。会议多角度探讨神经学科“脑肿瘤、脑血管、功能神经疾病”三个专题的发展方向，多名神经外科学专家分享了临床经验及科研方面的最新突破。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2092','2912','1','','','日前，共青团中央、最高人民法院、发展改革委等21家全国创建“青年文明号”活动组委会成员单位联合印发《关于命名2017-2018年度全国青年文明号的决定》（中青联发【2019】5号）, 命名在2017—2018 年度全国青年文明号创建周期中, 符合创建条件且表现突出的创建集体为“2','192.168.162.110','192.168.162.110','1561651200','<p style=\"text-indent: 47px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">日前，共青团中央、最高人民法院、发展改革委等21家全国创建“青年文明号”活动组委会成员单位联合印发《关于命名2017-2018年度全国青年文明号的决定》（中青联发【2019】5号）, 命名在2017—2018 年度全国青年文明号创建周期中, 符合创建条件且表现突出的创建集体为“2017—2018 年度全国青年文明号”，盐城市第一人民医院门诊药房上榜！</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2093','2913','1','','','为推动省、市卫健委“自省自警、拒腐防变”警示教育向深走实，结合医院“5·10”警示教育系列活动的有关要求，促进青年医务人员增强廉洁自律意识，牢固树立“不想腐”的思想自觉，强化青年医务人员“知敬畏、严纪律、尽职责”的廉洁从业观念，6月5日，盐城市一院召开青','192.168.162.110','192.168.162.110','1559750400','<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为推动省、市卫健委“自省自警、拒腐防变”警示教育向深走实，结合医院“5·10”警示教育系列活动的有关要求，促进青年医务人员增强廉洁自律意识，牢固树立“不想腐”的思想自觉，强化青年医务人员“知敬畏、严纪律、尽职责”的廉洁从业观念，6月5日，盐城市一院召开青年医务人员廉洁从业教育专题会议，医院500余名青年医务人员参加了会议。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px; text-indent: 37px;\">会前，全体参会人员在“不忘初心、牢记使命、廉洁行医、优质服务”为主题的承诺签名墙上郑重签名，彰显了医院青年医务工作者为人民健康服务的责任与担当。会上，与会人员观看了2018年度国家“最美医生”先进事迹宣传片、医疗卫生领域系列腐败案件《见利忘义 泯灭良心》警示教育片等。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px; text-indent: 37px;\">盐城市一院纪委书记卞学梅提工作要求：青年医务人员要加强品德修为，争做人民满意的好医生。一是志存高远，砥励前行，为人民的健康卫生事业建功立业；二是不忘初心，牢记使命，始终把人民的利益放在第一位；三是追求进步，政治坚定，在复杂的环境中不迷失方向；四是严以律己，遵纪守法，做一个清清白白、堂堂正正的好人。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2094','2914','1','','','为了庆祝“六一”国际儿童节，关爱医院职工子女成长，搭建才艺展示平台，6月2日，盐城市第一人民医院举办了“爱在一院 快乐成长”少儿才艺展示活动。本次活动共征集19幅书画作品，15个现场才艺展示节目。舞蹈、歌曲、乐器演奏、跆拳道表演等形式多样，丰富多彩。医院关','192.168.162.110','192.168.162.110','1559491200','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">为了庆祝“六一”国际儿童节，关爱医院职工子女成长，搭建才艺展示平台，6月2日，盐城市第一人民医院举办了“爱在一院 快乐成长”少儿才艺展示活动。本次活动共征集19幅书画作品，15个现场才艺展示节目。舞蹈、歌曲、乐器演奏、跆拳道表演等形式多样，丰富多彩。医院关工委驻会主任于洪蔚，市卫健委妇联主席、团委书记张健羽，为小朋友送去了节日的祝贺和殷切的希望。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2095','2915','1','','','6月1日上午，盐城市第一人民医院在南区举办了阿尔茨海默病大型义诊活动。此次义诊活动以“康复服务，健康中国”为主旨，由中国康复医学会举办，盐城市康复医学会、盐城市一院康复医学科具体承办。活动旨在筛查和早期发现轻度认知障碍患者，并通过专业视角给予患者及家属','192.168.162.110','192.168.162.110','1559404800','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">6月1日上午，盐城市第一人民医院在南区举办了阿尔茨海默病大型义诊活动。此次义诊活动以“康复服务，健康中国”为主旨，由中国康复医学会举办，盐城市康复医学会、盐城市一院康复医学科具体承办。活动旨在筛查和早期发现轻度认知障碍患者，并通过专业视角给予患者及家属诊治意见，解答疑惑，向就诊患者及家属讲解居家照护常识、沟通技巧、认知训练方法等，进一步提高广大群众对阿尔茨海默病的认识和了解，为预防疾病打下良好的基础。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2096','2916','1','','','为发展盐城市第一人民医院与韩国延世大学Severance医院之间的学术研究以及职员之间的交流合作，5月26日，盐城市第一人民医院与韩国延世大学Severance医院签署了友好姐妹医院合作协议（MOU）。市委书记、市人大常委会主任戴源，市卫健委主任徐杰等参加了签约仪式。市委书','192.168.162.110','192.168.162.110','1558886400','<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为发展盐城市第一人民医院与韩国延世大学Severance医院之间的学术研究以及职员之间的交流合作，5月26日，盐城市第一人民医院与韩国延世大学Severance医院签署了友好姐妹医院合作协议（MOU）。市委书记、市人大常委会主任戴源，市卫健委主任徐杰等参加了签约仪式。市委书记、市人大常委会主任戴源提出要求：希望双方进一步增进了解互信，在人员、技术等方面开展深层次合作。要按照协议要求，加快项目推进力度，为盐城健康医疗事业注入新的活力，不断提升我市健康医疗事业发展水平。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2097','2917','1','','','为搭建科研技术平台，不断提高临床科研水平，增强自主创新能力和核心竞争力，5月21日下午，我市卫生系统首个科研类专家团队工作室——“南通大学生命科学院刘东专家团队工作室”在盐城市第一人民医院揭牌成立。盐城市一院将充分整合中心实验室、转化医学中心、血液病实','192.168.162.110','192.168.162.110','1558454400','<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为搭建科研技术平台，不断提高临床科研水平，增强自主创新能力和核心竞争力，5月21日下午，我市卫生系统首个科研类专家团队工作室——“南通大学生命科学院刘东专家团队工作室”在盐城市第一人民医院揭牌成立。盐城市一院将充分整合中心实验室、转化医学中心、血液病实验室等，搭建科研技术平台，聘请刘东教授作为实验室建设和临床科研工作首席顾问，将进一步提升医院特色专科、重点学科的科研水平，切实为广大群众提供更加优质的医疗服务。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2098','2918','1','','','为加强党员理想信念教育，提醒党员不忘入党初心，铭记使命担当，5月21日下午，盐城市一院门诊党支部在党员活动室开展了5月特色党日活动。盐城市一院党委书记张慧娟，盐城市一院党委委员、副院长李奇志，门诊党支部全体党员，各党支部党建联络员，党委办公室，组织处等共','192.168.162.110','192.168.162.110','1558454400','<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为加强党员理想信念教育，提醒党员不忘入党初心，铭记使命担当，5月21日下午，盐城市一院门诊党支部在党员活动室开展了5月特色党日活动。盐城市一院党委书记张慧娟，盐城市一院党委委员、副院长李奇志，门诊党支部全体党员，各党支部党建联络员，党委办公室，组织处等共计60余人参加活动。院党委书记张慧娟寄语全体党员,要不忘初心，牢记使命；不忘本真、永葆本色，寄语各党支部：要“全面进步、全面过硬”。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2099','2919','1','','','5月9日下午，我院在北区急诊会议中心举行5·12国际护士节庆祝大会。出席大会的有院党委书记张慧娟、院长查文章、副院长朱向军等班子成员，全体护士长、优秀护士及护士代表。会上，护理部主任汤琪春作2018年护理工作总结；院领导宣读表彰决定并为优秀带教病区代表及优秀','192.168.162.110','192.168.162.110','1557417600','<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-size:20px;font-family:宋体\">5</span><span style=\"font-size:20px;font-family:宋体\">月9日下午，我院在北区急诊会议中心举行5·12国际护士节庆祝大会。出席大会的有院党委书记张慧娟、院长查文章、副院长朱向军等班子成员，全体护士长、优秀护士及护士代表。会上，护理部主任汤琪春作2018年护理工作总结；院领导宣读表彰决定并为优秀带教病区代表及优秀护士长代表颁奖；严晓鸥、陶皛珺等护士长代表发言；院党委书记张慧娟同志作重要讲话。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2100','2920','1','','','为庆祝“5.12”国际护士节，响应中华护理学会和江苏省护理学会的号召, 5月8日上午，我院认真组织泌尿外科、心血管、神经科、伤口造口、骨科五个护理专业学组的30多名护理专家和志愿者来到盐都康年颐养院，开展了主题为“科技促民生 科普惠健康——疾病预防 增进健康”护','192.168.162.110','192.168.162.110','1557331200','<p style=\"text-indent: 30px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">为庆祝“5.12”国际护士节，响应中华护理学会和江苏省护理学会的号召, 5月8日上午，我院认真组织泌尿外科、心血管、神经科、伤口造口、骨科五个护理专业学组的30多名护理专家和志愿者来到盐都康年颐养院，开展了主题为“科技促民生 科普惠健康——疾病预防 增进健康”护理科普活动。颐养院的60多位老人和30多名护理员积极参与，各项活动有条不紊的开展。 </span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">此次护理专家和志愿者走进养老院，关爱老人，是三级医院护理服务向社区延伸的一个体现，也让医务工作者进一步了解了老人的健康需求和困惑；护理专家通过零距离面对面的交流，不仅为老人提供了专业的健康指导，也进一步提升了护理员的理论水平和实践操作技能，受到了颐养院老人的一致好评。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2101','2921','1','','','为进一步增强职工的身体素质，丰富文化活动，营造良好氛围，激发广大职工为医院高质量发展做贡献的积极性、创造性，5月8日下午，盐城市第一人民医院职工趣味运动会在阳光明媚的春日里拉开了序幕。全院10支代表队共计500余名职工参加了此次比赛。比赛项目充满趣味性、挑','192.168.162.110','192.168.162.110','1557331200','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">为进一步增强职工的身体素质，丰富文化活动，营造良好氛围，激发广大职工为医院高质量发展做贡献的积极性、创造性，5月8日下午，盐城市第一人民医院职工趣味运动会在阳光明媚的春日里拉开了序幕。全院10支代表队共计500余名职工参加了此次比赛。比赛项目充满趣味性、挑战性和娱乐性，每轮角逐精彩激烈、妙趣横生，场上的队员奋勇争先，场下的队友鼓劲呐喊，欢声笑语将趣味运动会推向一个又一个高潮。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2102','2922','1','','','进一步弘扬五四精神，激发青年医务工作者创新创业激情，5月3日下午，盐城市第一人民医院召开优秀青年人才座谈会。来自临床一线和行政后勤的50名青年代表参会。会议由盐城市一院党委副书记、副院长朱向军主持。会上，盐城市一院团委负责人宋愚汇报医院青年工作。盐城市一','192.168.162.110','192.168.162.110','1556899200','<p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">进一步弘扬五四精神，激发青年医务工作者创新创业激情，5月3日下午，盐城市第一人民医院召开优秀青年人才座谈会。来自临床一线和行政后勤的50名青年代表参会。会议由盐城市一院党委副书记、副院长朱向军主持。会上，盐城市一院团委负责人宋愚汇报医院青年工作。盐城市一院纪委书记卞学梅就青年医务工作者党风廉政和行风建设方面提工作要求。盐城市卫健委团委副书记唐婷婷讲话。孙关、王维、等优秀青年代表交流发言。</span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">盐城市一院党委书记张慧娟讲话并提出工作要求。希望全院青年要以习近平总书记重要讲话精神为指导，继承和发扬五四精神，开拓创新，奋发有为，在新一轮发展中奉献青春，贡献力量。要坚定信念跟党走，爱岗敬业谱芳华，勤奋学习勇实践，锐意进取敢争先。院团委要以习近平总书记关于青年工作的重要思想为指引，发挥组织优势、加强自身建设，发挥共青团应有的、独特的作用。一要强化政治引领，二要打造品牌特色，三要优化队伍建设。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2103','2923','1','','','为进一步增强公众对胃癌的早筛早诊早治观念，提高消化道肿瘤的综合防治水平，4月27日，盐城市第一人民医院开展2019全国早期胃癌防治宣传月活动，免费为患者提供血清学胃功能筛查、现场义诊、胃癌防治知识讲座等医疗服务。','192.168.162.110','192.168.162.110','1556380800','<p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为进一步增强公众对胃癌的早筛早诊早治观念，提高消化道肿瘤的综合防治水平，4月27日，盐城市第一人民医院开展2019全国早期胃癌防治宣传月活动，免费为患者提供血清学胃功能筛查、现场义诊、胃癌防治知识讲座等医疗服务。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2104','2924','1','','','4月25日～26日，第24期中国重症医学机械通气规范化培训班在盐城市第一人民医院成功举办。此次培训班由东南大学附属中大医院重症医学科和盐城市第一人民医院重症医学科联合承办。为进一步规范治疗我国呼吸衰竭患者的机械通气技术，提高重症患者的救治效率，60名来自全国','192.168.162.110','192.168.162.110','1556294400','<p style=\"margin-bottom: 0px; text-align: justify; text-indent: 37px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4月25日～26日，第24期中国重症医学机械通气规范化培训班在盐城市第一人民医院成功举办。此次培训班由东南大学附属中大医院重症医学科和盐城市第一人民医院重症医学科联合承办。</span></p><p style=\"margin-bottom: 0px; text-align: justify; text-indent: 37px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为进一步规范治疗我国呼吸衰竭患者的机械通气技术，提高重症患者的救治效率，60名来自全国各地各级医院重症医学科和急诊科的学员参加了培训，东南大学附属中大医院、苏北人民医院、盐城市第一人民医院等8名专家教授的讲座为学员们奉上了一场场精彩的学术盛宴。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2105','2925','1','','','4月21日上午7时30分，2019大洋湾盐城马拉松将在盐城城南体育中心准时开赛。此次比赛，盐城市第一人民医院选派45名医护人员，助力1个医疗站、5个医疗点、6个移动医疗点（急救车），在南北2个院区组建急救团队，全力做好2019大洋湾盐城马拉松医疗保障工作。全体医护人员随','192.168.162.110','192.168.162.110','1555862400','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4月21日上午7时30分，2019大洋湾盐城马拉松将在盐城城南体育中心准时开赛。此次比赛，盐城市第一人民医院选派45名医护人员，助力1个医疗站、5个医疗点、6个移动医疗点（急救车），在南北2个院区组建急救团队，全力做好2019大洋湾盐城马拉松医疗保障工作。全体医护人员随时待命，为运动员保驾护航。为做好此次马拉松赛事的医疗保障工作，在市卫健委的组织领导下，4月17日～18日，盐城市一院开展了急诊急救演练，就马拉松比赛中最易发生且最危险的两个科目——心脏骤停、重度昏迷（中暑、脑外伤），进行了全流程急诊急救（绿色通道、急救、检查、住院、ICU病房的启用与病情报告）的演练。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2106','2926','1','','','欧洲帕金森病联合会从1997年开始，将每年的4月11日确定为“世界帕金森病日”。活动的宗旨在于：促使帕金森病患者、他们的家人、专业医疗人员共同努力，不仅让帕金森病家喻户晓，而且要提高公众的关注程度。4月11日上午，盐城市第一人民医院在南区和北区门诊大厅举办“医','192.168.162.110','192.168.162.110','1554998400','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">欧洲帕金森病联合会从1997年开始，将每年的4月11日确定为“世界帕金森病日”。活动的宗旨在于：促使帕金森病患者、他们的家人、专业医疗人员共同努力，不仅让帕金森病家喻户晓，而且要提高公众的关注程度。4月11日上午，盐城市第一人民医院在南区和北区门诊大厅举办“医患携手，健康自如”2019年世界帕金森病日大型义诊活动，为帕金森病患者提供免费诊疗、健康咨询及日常生活指导等医疗服务。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2107','2927','1','','','4月10日，副省长陈星莺来院看望慰问响水“3·21”爆炸事故受伤人员和医护人员。省政府副秘书长王思源，省卫健委、省体育局相关负责同志陪同。市领导葛启发、孙长春等参加活动。事故发生后，省内外专家组成医疗专家组，来盐进行伤员救治、康复等医学救援工作，广大医护人','192.168.162.110','192.168.162.110','1554912000','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">4月10日，副省长陈星莺来院看望慰问响水“3·21”爆炸事故受伤人员和医护人员。省政府副秘书长王思源，省卫健委、省体育局相关负责同志陪同。市领导葛启发、孙长春等参加活动。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">事故发生后，省内外专家组成医疗专家组，来盐进行伤员救治、康复等医学救援工作，广大医护人员日夜奋战，尽最大努力救治伤员。在市第一人民医院，陈星莺看望慰问了住院伤员和医护人员。在伤员病床前，陈星莺详细询问伤情和救治恢复情况，鼓励他们积极配合治疗，静心养伤。陈星莺向奋战在一线的医护人员表示感谢，叮嘱医护人员继续做好救治工作，根据每一位受伤人员的救治进展情况及时调整救治方案，确保每位伤员都得到精心治疗，能够早日康复。省、市有关部门要认真贯彻落实习近平总书记重要指示批示精神，按照省委、省政府部署要求，牢固树立以人民为中心的发展思想，耐心细致做好伤员及家属安抚和善后工作，统筹协调解决相关实际困难，真正把党和政府的关怀温暖送到群众心坎上。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2108','2928','1','','','为凝聚社会共识，进一步营造全社会共同关注并自觉维护医保基金安全的良好氛围，促进医疗保障事业稳步健康发展，4月8日上午，盐城市“打击欺诈骗保维护基金安全”集中宣传月活动启动仪式在盐城市第一人民医院南区举行。盐城市医疗保障局局长范建江宣读倡议书，盐城市第一','192.168.162.110','192.168.162.110','1554739200','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为凝聚社会共识，进一步营造全社会共同关注并自觉维护医保基金安全的良好氛围，促进医疗保障事业稳步健康发展，4月8日上午，盐城市“打击欺诈骗保维护基金安全”集中宣传月活动启动仪式在盐城市第一人民医院南区举行。盐城市医疗保障局局长范建江宣读倡议书，盐城市第一人民医院院长查文章宣读承诺书，盐城市人民政府副市长葛启发作重要讲话并宣布集中宣传月活动启动。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">活动中，通过展板宣传、发放宣传折页等形式，向群众宣传“打击欺诈骗保 维护基金安全”的相关政策，增加群众的知晓率。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2109','2929','1','','','4月3日下午，省委书记娄勤俭赴盐城市第一人民医院看望江苏响水“3·21”爆炸事故住院伤员和医疗专家。省委常委、常务副省长、省委秘书长樊金龙参加。市委书记戴源陪同。在盐城市第一人民医院，娄勤俭看望慰问了住院伤员和医疗专家。在伤员病床前，娄勤俭询问伤情和救治','192.168.162.110','192.168.162.110','1554307200','<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">4月3日下午，省委书记娄勤俭赴盐城市第一人民医院看望江苏响水“3·21”爆炸事故住院伤员和医疗专家。省委常委、常务副省长、省委秘书长樊金龙参加。市委书记戴源陪同。在盐城市第一人民医院，娄勤俭看望慰问了住院伤员和医疗专家。在伤员病床前，娄勤俭询问伤情和救治恢复情况，要求医院继续做好救治，尽全力减少伤残，让大家早日康复。事故发生后，许多省内外专家驰援过来，帮助救治事故伤员。娄勤俭对专家们不辞辛劳从外地赶来、连续作战救治伤员表示衷心感谢。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2110','2930','1','','','4月2日上午, 盐城市一院与北京华通国康公益基金会签订战略合作协议。会议由盐城市卫健委主任徐杰主持签约仪式, 盐城市一院党委书记张慧娟介绍医院发展情况, 北京华通国康公益基金会理事长陈冉致辞, 盐城市委常委、组织部长柏长岭讲话。柏部长指出，北京华通国康公益基金','192.168.162.110','192.168.162.110','1554220800','<p style=\"margin: 0px 0px 12px; background: white; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-weight: normal; font-size: 20px;\">4月2日上午, 盐城市一院与北京华通国康公益基金会签订战略合作协议。会议由盐城市卫健委主任徐杰主持签约仪式, 盐城市一院党委书记张慧娟介绍医院发展情况, 北京华通国康公益基金会理事长陈冉致辞, 盐城市委常委、组织部长柏长岭讲话。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">柏部长指出，北京华通国康公益基金会是一所富有国家情怀和战略愿景的慈善组织。此次签约，希望市一院着眼未来，把握机遇，立足新的起点，持续加大公立医院综合改革，扩大医院对外合作交流，充分用好优质国际资源和广阔交流平台，选派专业骨干和管理人员出国研修培训，学习先进管理经验和医疗技术，促进医、教、研协同发展，有效提升医院临床技能、科研水平和创新能力，努力开启“引领创新、合作共赢”的新局面，为医院可持续发展奠定坚实的人才基础，为盐城卫生健康事业的发展注入新动能，在健康盐城的建设上谋求更大作为，不断提升盐阜人民的幸福感、满意度！</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2111','2931','1','','','3月30日，中国创伤救治联盟创伤救治中心、直升机航空医疗救援定点医院、盐城市第一红十字医院在盐城市一院南区揭牌。近年来，盐城市一院以习近平新时代中国特色社会主义思想为指导，认真落实市委市政府决策部署和市卫健委工作要求，坚持“以人民健康为中心”，以“建设','192.168.162.110','192.168.162.110','1554048000','<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">3月30日，中国创伤救治联盟创伤救治中心、直升机航空医疗救援定点医院、盐城市第一红十字医院在盐城市一院南区揭牌。近年来，盐城市一院以习近平新时代中国特色社会主义思想为指导，认真落实市委市政府决策部署和市卫健委工作要求，坚持“以人民健康为中心”，以“建设高水平区域医疗中心”为目标，着力深化公立医院改革，完善医院管理体系，创新医院运行机制，锤炼优质医疗服务品牌，努力打造人民满意、政府满意、职工满意的现代化医院。此次揭牌活动为市一院的发展，为健康盐城的建设注入了新动能、新活力。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">会上，盐城市政协副主席、盐城市卫健委副主任孙长春讲话，中国创伤救治联盟常委兼秘书长王天兵讲话。揭牌仪式后，开展了助医惠民行动，向院内43名患有重大疾病的贫困、残疾、孤寡患者进行救助；盐城市一院院长查文章向建湖县人民医院等4家医疗机构发放盐城市创伤救治联盟会员单位证书。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2112','2932','1','','http://www1.ycsyy.com/upload/images/2019/11/t_be4cca6d8d755970.jpg','2021年5月30日，是一个普通的周末。烈日炎炎、骄阳似火，盐城市一院药学部静脉用药调配中心全体党员、科室业务骨干，来到泰山庙、铜马广场、新四军重建军部纪念馆，重温红色故事，寻找铁军足迹。时光荏苒，岁月峥嵘。想当年，“陕北有个延安，苏北有个盐城”。新四军抗','192.168.162.110','192.168.162.110','1622736000','<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2021年5月30日，是一个普通的周末。烈日炎炎、骄阳似火，盐城市一院药学部静脉用药调配中心全体党员、科室业务骨干，来到泰山庙、铜马广场、新四军重建军部纪念馆，重温红色故事，寻找铁军足迹。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">时光荏苒，岁月峥嵘。想当年，“陕北有个延安，苏北有个盐城”。新四军抗战的光辉历史，</span><a name=\"baidusnap3\"></a><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">是一部跟党举旗、听党指挥的奋斗史诗，是一曲坚定信念、忠贞不渝的英雄壮歌。泰山庙的一砖一瓦，仿佛都在诉说当年的峥嵘岁月；仰望新四军重建军部纪念塔，年轻英武的新四军号手，面向太阳、昂首挺胸，吹着前进的号角。那激昂的号声，至今令人情不自禁、热血沸腾。盐城新四军纪念馆门前，新四军小战士雕像的下方有陈毅同志手写的两个苍劲有力的大字——“东进”。在纪念馆各展厅，看着新四军打仗的路线图及战斗的老照片，听着前辈们从前浴血战斗的故事，把大家的思绪带回那血雨腥风、艰苦卓绝的年代。纪念馆里那些安静陈列的步枪履衣，生动记录了革命先辈在战场上浴血奋战、舍生忘死的光辉历史。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市一院诞生于硝烟弥漫的战争岁月，从十多人的“一肩挑”战地医院发展到目前“一院多区”的大型三级甲</span><a name=\"_GoBack\"></a><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">等综合性医院，离不开铁军精神的滋养，离不开红色基因的传承。追昔抚今，大家感慨万千，纷纷表示，将继续弘扬铁军精神、坚守为民情怀，努力建设高质量区域医疗中心，做盐阜人民的健康守护神，为健康盐城建设作出积极贡献。</span></p>\r\n<p style=\"text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www1.ycsyy.com/upload/images/2021/6/7581f43f57d4070f.jpg\" title=\"微信图片_20210604171210.jpg\" alt=\"微信图片_20210604171210.jpg\" width=\"392\" height=\"563\" style=\"width: 392px; height: 563px;\"></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2113','2933','1','','','盐城市第一人民医院创建于1948年，历经七十余年的发展，现已成为一所集医疗、教学、科研、康复、急救为一体的国家三级甲等综合性医院。医院同时为南京大学附属盐城第一医院、南通大学第四附属医院、徐州医科大学附属临床医院、江苏医药职业学院第一附属医院、第一临床学','192.168.162.110','192.168.162.110','1608134400','<br /><p style=\"font-size:15pt; line-height:150.83%; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\"><span style=\"background-color: rgb(255, 255, 255);\">盐城市第一人民医院创建于1948年，历经七十余年的发展，现已成为一所集医疗、教学、科研、康复、急救为一体的国家三级甲等综合性医院。医院同时为南京大学附属盐城第一医院、南通大学第四附属医院、徐州医科大学附属临床医院、江苏医药职业学院第一附属医院、第一临床学院、</span>中国创伤救治联盟建设单位和直升机航空救援定点医院、韩国延世大学医院合作医院<span style=\"background-color: rgb(255, 255, 255);\">。</span></span></p><p style=\"font-size:15pt; line-height:150.83%; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"background-color: rgb(255, 255, 255); font-size: 18px;\">为建立现代医院管理制度，根据《关于加强公立医院党的建设工作的意见》《国务院办公厅关于建立现代医院管理制度的指导意见》《医疗机构管理条例》和《关于开展制定医院章程试点工作的指导意见》等国家有关法律法规、规章和规范性文件，结合医院实际，制定本章程。</span></p><p style=\"font-size:10pt; line-height:5.83%; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; widows:0; \"><br /></p><p style=\"line-height:16.05pt; margin-bottom:0pt; margin-left:1pt; margin-right:0pt; margin-top:0pt; orphans:0; widows:0; \"><span style=\"font-size: 18px;\">- 7 -</span></p><h1 style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:15.05pt; margin-top:0pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-family: 黑体, SimHei; font-size: 18px;\">第一章      总则</span></h1><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:6pt; orphans:0; text-align:justify; text-indent:30.05pt; widows:0; \"><span style=\"font-size: 18px;\">第一条 医院举办主体：盐城市卫生健康委员会。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30.05pt; widows:0; \"><span style=\"font-size: 18px;\">第二条 医院名称：盐城市第一人民医院。中文简称：盐城一院。英文名称：YanCheng NO.1 People’s Hospital。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第三条 医院地址（共设四个院区）：<span style=\"font-size: 18px; background-color: rgb(255, 255, 255);\">主院区（人民南路66号）、北区（毓龙西路166号）、五官科院区（南城河路12号）、盐城市儿童医院院区（毓龙西路16号）。医院网址：</span></span><a href=\"http://www.ycsyy.com/\" style=\"font-size: 18px; text-decoration: underline;\"><span style=\"font-size: 18px;\">http://www.ycsyy.com/</span></a><span style=\"font-size: 18px;\">。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第四条 医院性质：公立非营利性医疗机构，具有独立法人资格， 依法履行相应的权利和义务，享有运营管理自主权，独立承担法律责任。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第五条 领导体制：实行党委领导下的院长负责制，院长是医院的法定代表人。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第六条 功能定位：集医疗、教学、科研、康复、急救、预防保健为一体的国家三级甲等综合性医院，能够为本市及周边区域内群众提供代表本区域高水平的综合性医疗服务，是盐城市区域医疗中心，南京大学附属盐城第一医院、南通大学第四附属医院、徐州医科大学附属临床医院、江苏医药职业学院第一附属医院、第一临床学院、中国创伤救治联盟建设单位和直升机航空救援定点医院、韩国延世大学医院合作医院，主要提供危急重症、疑难复杂疾病诊疗服务，接受下级医院转诊，并承担人才培养和一定的科研教学任务以及相应公共卫生和突发事件、紧急医疗救援等任务。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第七条 医院宗旨：贯彻落实新时期我国卫生与健康工作方针，坚持以人民健康为中心，以救死扶伤、防病治病、提高人民健康水平和促进医学事业发展为宗旨。。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第八条 核心价值观：<span style=\"font-size: 18px; background-color: rgb(255, 255, 255);\">以“治病救人，造福社会”为自身使命，秉承“生命所系，健康相托”的服务理念，用“严谨、博学、团结、奉献”的医院精神团结员工、引导员工，贯彻“病人至上”，始终如一的价值取向，牢守“厚德修身，精术济世”的院训，服务于人民、造福于社会。</span></span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第九条 发展目标：围绕布局合理、服务优质、质量过硬、技术领先的发展要求，坚持高质量的内涵式发展模式，建设成为高水平区域性医疗中心，高质量发展走在苏中、苏北前列。</span></p><p style=\"line-height:15pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; widows:0; \"><span style=\"font-size: 18px;\"></span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-size: 20px; font-family: 黑体, SimHei;\">第二章 医院外部治理体系</span></p><p style=\"line-height:24pt; margin-bottom:6pt; margin-left:0pt; margin-right:15.05pt; margin-top:11.05pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-size: 18px;\">第一节 举办主体的权利和义务</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第十条 盐城市卫生健康委员会按照党和政府赋予职责和法律法规规定，依法履行领导责任、保障责任、管理责任、监督责任，维持医院的公益性。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第十一条 盐城市卫生健康委员会行使医院的举办权、重大事项决策权、资产收益权等，行使涉外合作交流、与其他投资主体投资合作、注册举办新的机构、重大投资建设、大型医用设备配置等重大发展权。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第十二条 盐城市卫生健康委员会代表盐城市人民政府审定医院章程、发展规划、重大项目、收支预算等。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第十三条 建立以公益性为导向的考核评价机制，定期组织公立医院绩效考核和任期目标考核。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第十四条 上级党委和政府任免（聘任）医院党政领导人员，开展年度考核和任期目标考核，坚持考用结合，将考核结果与选拔任用、培养教育、管理监督、激励约束、问责追责等结合起来，推动能上能下，促进担当作为。建立容错纠错机制，激励医院领导人员不断推进工作创新。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第十五条 盐城市卫生健康委员会对医院财务收支和国有资产运营情况进行监管，监督医院保障国有资产安全和完整。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第十六条 盐城市卫生健康委员会为医院建立科学补偿机制提供条件， 理顺医疗服务价格，落实投入保障政策，保障医院可持续发展。</span></p><p style=\"line-height:24pt; margin-bottom:6pt; margin-left:0pt; margin-right:14.75pt; margin-top:9.85pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-size: 18px;\">第二节 医院的权利与义务</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第十七条 医院在盐城市卫生健康委员会的指导下，履行相关职责，承担相关义务：</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）贯彻落实新时期我国卫生与健康工作方针，坚持公益性，保障人民群众健康，推动医院各方面工作健康发展。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）为人民群众提供医疗保健、疾病预防、健康教育、健康科普等医疗和公共卫生服务。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（三）承担院校医学教育、毕业后医学教育和继续医学教育，不断提升医学人才能力素质和工作水平。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（四）开展临床医学和基础医学研究，推动医学科技成果转化。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（五）在上级领导部门批准的范围内依法、依规开展对外技术交流和国际合作。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（六）开展涉外医疗服务，承担重大活动医疗保障任务，承担突发公共事件的医疗卫生救助。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（七）开展党风廉政和行风职业道德建设。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（八）履行国际化援助任务，支援边远、边疆地区和基层医疗卫 生机构，做好医联体建设工作。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（九）承担上级党委和政府交办的其他事项。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30.2pt; widows:0; \"><span style=\"font-size: 18px;\">第十八条 医院按照国家有关法律、法规和部门规章，在登记的经营范围内从事相关活动，不受任何机关、团体、个人侵犯或非法干涉。坚持依法治院，建立医疗机构依法决策、依法管理、依法执业机制，健全医院法治工作制度、合规性审查制度和法律顾问制度，推动医院内部治理现代化。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30.2pt; widows:0; \"><span style=\"font-size: 18px;\">第十九条 医院享有内部人事管理、机构设置、中层干部聘任、人员招聘和人才引进、内部绩效考核与薪酬分配、年度预算执行、医院发展规划制定与实施、学科专科建设、人才培养、知识产权保护与成果转化等经营管理自主权。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第二十条 医院接受上级党委和政府有关部门的业务指导和监督管理，接受审计、财政、价格、医保、卫生健康等政府部门及举办主体的监督，保证医院日常执业行为及财务收支状况的健康运行。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第二十一条 医院主动接受社会监督和舆论监督。建立健全第三方满意度评价机制，建立完善的监督评价体系；依法实行院务公开，真实、完整、及时地公布服务信息，主动接受社会评价和监督。</span></p><h1 style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:15.05pt; margin-top:5.85pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-family: 黑体, SimHei; font-size: 20px;\">第三章 医院内部治理体系</span></h1><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:15.05pt; margin-top:11.05pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-size: 18px;\">第一节 党委、纪委</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32pt; widows:0; \"><span style=\"font-size: 18px;\">第二十二条 医院设立中国共产党盐城市第一人民医院委员会（以下简称医院党委）。医院党委发挥把方向、管大局、作决策、促改革、保落实的领导作用。主要职责如下：</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）贯彻落实党的基本理论、基本路线、基本方略，贯彻落实党的卫生与健康工作方针，贯彻落实深化医药卫生体制改革政策措施，坚持公立医院公益性，确保医院改革发展正确方向。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）依照有关规定讨论和决定医院改革发展、财务预决算、预算绩效、“三重一大”、内部组织机构设置，以及涉及医务人员权益保障等的重大问题。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（三）坚持党管干部原则，按照干部管理权限领导医院干部的选拔任用工作，认真做好离退休干部工作。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（四）坚持党管人才原则，讨论决定医院人才工作的政策措施，创新用人机制，优化人才成长环境。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（五）做好思想政治、意识形态和宣传工作，开展社会主义核心价值观教育，弘扬崇高精神，加强医德医风、精神文明和医院文化建设。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（六）完善医院党组织设置和工作机制，提升组织力，增强政治功能，严格党的组织生活，扩大党内基层民主，抓好发展党员和党员教育管理监督服务工作。严格执行“三会一课”、民主生活会和组织生活会、主题党日等制度。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（七）履行全面从严治党主体责任，支持纪检机构履行监督责任，加强医院党风廉政建设和反腐败工作。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（八）全面落实党的统一战线方针政策，做好统战工作。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（九）领导和支持工会、共青团等群团组织和职工代表大会开展工作。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32pt; widows:0; \"><span style=\"font-size: 18px;\">第二十三条 医院党委实行集体领导和个人分工负责相结合的制度。设党委书记1名，主持党委全面工作，是医院党建工作的第一责任人，医院党政领导班子其他党员成员严格落实“一岗双责”。医院党委委员数量、党委副书记职数以上级党委批复为准。党委书记、副书记、党委委员按照干部管理权限和基层党组织选举有关规定产生。任期按党内有关规定执行。医院党委履行党章等规定的各项职责，坚持正确的卫生与健康工作方针，确保医院改革发展正确方向，贯彻落实深化医药卫生体制改革政策措施，坚持公立医院公益性，决定医院重大问题，监督重大决议执行。医院党委设立工作机构：党委办公室、组织处、宣传处等。按职工总数的0.5%-1%的配齐配强专职党务工作人员，推动党务工作队伍职业化建设，医院党委下设党总支和党支部。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第二十四条 医院为党组织活动提供必要条件，保障组织机构、活动场所和活动经费，党组织活动经费纳入医院预算。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第二十五条 医院设立党的纪律检查委员会（以下简称“医院纪委”），设纪委书记一名。医院纪委在医院党委、派驻纪检组和上级纪委的领导下，全面履行党风廉政建设和监督执纪职责。主要职责如下：</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）检查医院贯彻落实党的路线方针政策和医院重大决策部署的情况。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）监督党员干部特别是关键岗位、重要人员履职和用权情况。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（三）开展党纪教育，推进廉政文化建设，筑牢党员干部拒腐防变的思想道德和法纪防线。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（四）开展作风督查，促进医院严格落实中央八项规定。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（五）完善反腐倡廉制度规范，构建系统化防治腐败工作制度体系。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（六）依纪依法查办案件，坚决惩治腐败行为。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第二十六条 医院纪委书记是履行医院党风廉政建设监督责任的第一责任人。医院纪委副书记职数和纪委委员数量以上级纪委批复为准。医院纪委书记、副书记、纪委委员按照有关规定和程序选举产生。医院纪委任期与医院党委任期相同。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:15.05pt; margin-top:9.9pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-size: 18px;\">第二节 医院领导班子</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:6pt; orphans:0; text-align:justify; text-indent:30.2pt; widows:0; \"><span style=\"font-size: 18px;\">第二十七条 医院设院长一名。院长是医院运营管理的第一责任人，在医院党委领导下，全面负责医院医疗、教学、科研、行政管理工作，为医院的法定代表人。副院长、总会计师协助院长分管医疗、教学、科研、财务及行政管理等各项工作。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第二十八条 医院行政领导班子成员由上级党委和政府按照干部管理权限，根据工作需要和领导班子建设实际，依照相关程序选拔任用或调整免职退出。领导班子成员定期述职，接受举办主体的考核和医院职工的评议。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32.2pt; widows:0; \"><span style=\"font-size: 18px;\">第二十九条 院长主要职责如下：</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）召集和主持院长办公会会议。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）根据规定和授权任免内设行政机构、业务部门和专业委员会负责人。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（三）组织开展医疗、教学和科研等业务工作；主持、指挥全院性的会诊、大型抢救、学术交流、新技术项目开展等医疗技术活动。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（四）签署医院有关文件和代表医院签署对外合作协议。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（五）发布或授权发布本单位相关信息。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（六）每年要向医院党委会全面报告工作，向职工代表大会报告工作，组织处理职工代表大会、专业委员会和工青妇有关行政工作的提案。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（七）法律、法规规定的其他职责。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30.2pt; widows:0; \"><span style=\"font-size: 18px;\">第三十条 医院行政领导班子实行任期目标责任制，具体依照医院实际和相关规定，要以上级主管部门目标要求为标准，注重工作实绩和社会效益，坚持党建工作和业务工作同步考核。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32.8pt; widows:0; \"><span style=\"font-size: 18px;\">第三十一条 医院贯彻全面从严治党的要求，完善院领导班子监督约束机制，构建严密有效的监督体系，发挥党内监督、民主监督、法律监督、审计监督和舆论监督等作用，督促领导班子认真履职尽责， 依法依规办事，保持清正廉洁。</span></p><p style=\"line-height:24pt; margin-bottom:6pt; margin-left:0pt; margin-right:15.05pt; margin-top:0pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-size: 18px;\">第三节 医院内部机构</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第三十二条 医院总院和分院实行统一法定代表人、统一领导班子管理、统一财务管理、统一人事管理、统一制度的模式。医院依据相关法律法规和规章，以及编制部门的规定，结合医院宗旨、发展目标、业务范围和实际需要，本着精简、高效、统一的原则，设立职能部门和临床医技部门。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">职能处室主要职责：执行医院管理决定；履行行政管理职能；执行、细化医院在医疗、教学、科研等方面的管理制度； 为医院业务发展及学科建设提供决策依据与管理支持。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">临床、医技科室主要职责：依法组织开展学科范围内的相关医疗执业活动，为患者提供诊疗、护理、康复和健康咨询等服务；推动学科发展和新技术开展，不断提高临床疗效；负责提高本科室质量安全管理和服务病人水平；开展医学教育、人才培养和科研工作；承担医院交办的其他工作。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第三十三条 医院内部机构负责人包括职能部门正副处长、正（副）主任、临床科室正（副）主任、护士长等在内的核心管理团队。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第三十四条 医院设立党委办公室和纪检监察机构，分别负责处理医院党委、纪委日常工作事务。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32.8pt; widows:0; \"><span style=\"font-size: 18px;\">第三十五条 医院实行院、科两级管理责任制。科主任是科室的行政负责人，医院建立健全科主任聘任管理、任期和任期目标责任管理、考核评价管理制度以及职业发展和激励保障、监督约束、退出等机制</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第三十六条 职能处室的设置经盐城市编制管理办公室核定产生。根据管理需要及时对职能处室的任务分工进行调整，并按照规定向上级有关部门报批。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第三十七条 职工代表大会制度是医院管理体制的重要组成部分， 是医院党委领导下职工行使民主权力，实行民主管理，民主监督的基本制度和形式。医院工会委员会是职工代表大会的工作机构，在职代会休会期间，负责职工代表大会的日常工作。医院职代会行使下列职权：</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）听取并审议医院章程及各项规章制度的制定和修订情况报告，提出意见和建议。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）听取并审议院长工作报告、总体发展规划、年度工作计划、重大改革方案、财务工作及其他专项工作报告等重大问题，提出意见和建议。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（三）审议并监督落实涉及职工切身利益的福利待遇、薪酬分配等有关的重大事项，维护职工合法权益。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（四）审议上一届（次）医院职代会提案的办理情况报告，检查监督职代会决议、代表提案的落实，听取和反映职工的意见和要求。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（五）按照有关规定对医院领导班子进行民主监督和评议。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（六）讨论其他需要经医院职代会审议、通过或决定的事项。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（七）医院职代会闭会期间，遇重大事项需要征求职代会代表意见时，可临时召集职代会代表对所议事项征求意见并进行符合职代会规定的有效表决。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第三十八条 职工代表大会每年至少召开一次。会议须到会代表数大于三分之二以上才能举行。职工代表大会实行民主集中制，职工代表大会代表由职工直接选举产生。职工代表大会实行任期制，每届任期与工会相同。届期满时，应按程序进行换届。提前或延迟换届须报请市总工会同意， 但提前或延迟均不得超过半年。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第三十九条 根据医院实际工作需要，设立医院质量与安全、医疗质量与安全、医院安全、学术、设备、药事、伦理等专业委员会，辅助医院行政领导班子对相应具体事务进行专业化决策与管理。各委员会主任和成员由院长提出人选，报医院党委会议审定，院长任命。各专业委员会依据相关规定开展工作。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30.2pt; widows:0; \"><span style=\"font-size: 18px;\">第四十条 专业委员会定期召开会议，会议议题由主任委员、副主任委员、秘书等讨论确定。根据会议议题，可邀请相关人员参加会议。</span></p><p style=\"line-height:24pt; margin-bottom:6pt; margin-left:2pt; margin-right:0pt; margin-top:6pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-size: 18px;\">第四节 群团组织和统一战线</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第四十一条 医院依法设置工会、妇委会、共青团等群众组织。各群众组织在党委的领导下，履行各自职责。工会依法组织员工参与医院的民主决策、民主管理和民主监督。民主党派基层组织依照法律和各自章程开展活动。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30.2pt; widows:0; \"><span style=\"font-size: 18px;\">第四十二条 民主党派是医院民主管理的重要力量。医院党委坚持党的统一战线有关政策和方针，把统战工作作为医院党委的一项重要工作。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32.8pt; widows:0; \"><span style=\"font-size: 18px;\">第四十三条 每年召开民主党派代表座谈会，听取民主党派代表的意见建议，鼓励民主党派为医院建设发展建言献策。充分发挥政协委员、人大代表参政议政的积极作用，结合医院实际情况撰写提案， 让医院更好地服务患者，进一步彰显公立医院公益性，履行社会责任。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32.8pt; widows:0; \"><span style=\"font-size: 18px;\"><br /></span></p><p style=\"text-align: center;\"> <span style=\"font-family: 黑体, SimHei; font-size: 20px;\">第四章  群团组织和统一战线</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32.6pt; widows:0; \"><span style=\"font-size: 18px;\">第四十四条 本章程所指医院员工包括全体在编职工和合同制员工等。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第四十五条 员工依法享有以下权利：</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）医院员工享有宪法、法律、法规及本章程制定的权利，同时必须履行宪法、法律、法规及本章程规定的义务。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）医院员工有权依据法律、法规、规章、医院相关规定和合同约定获得薪酬及其他福利待遇。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（三）医院员工在同等条件下公平享有职业发展、职称评定、职级晋升机会，公平获得各种奖励和荣誉。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（四）医院员工就职务聘用、福利待遇、评优奖励、纪律处分及其他关系自身利益等事项享有表达异议的权利。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第四十六条 医院员工履行以下义务：</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）贯彻国家卫生工作方针，遵守法律、法规、规章和员工守则，爱岗敬业，主动提高自身业务能力，践行医院宗旨和文化理念， 自觉维护医院声誉和利益。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）认同医院文化，坚守敬佑生命、救死扶伤、甘于奉献、大爱无疆的职业精神，为医院建设发展贡献力量。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（三）医院员工是医院的形象代言人和维护者，自觉遵守职业道德和学术规范，树立良好形象，尽职尽责，勤奋工作</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（四）有义务依法维护患者的健康权、人格权、知情权、民族习惯和宗教信仰。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（五）严格执行员工退休制度，按宪法、法律、法规、国家相关政策和医院规定享有相应权利，履行相应义务。</span><a name=\"_TOC_250007\"></a><a name=\"_TOC_250006\"></a></p><p style=\"line-height:12pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; widows:0; \"><span style=\"font-size: 18px;\"></span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-family: 黑体, SimHei; font-size: 20px;\">第五章 运行管理</span></p><p style=\"line-height:24pt; margin-bottom:6pt; margin-left:0pt; margin-right:15.05pt; margin-top:11.05pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-size: 18px;\">第一节 基本原则</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30.2pt; widows:0; \"><span style=\"font-size: 18px;\">第四十七条 医院运行管理坚持民主公开、公平公正、有效激励的原则，科学整合院内外各方资源，建立合理机制实现优质高效，促进医院可持续发展。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第四十八 医院实行医院和科室两级负责制，科室主任是科室各项工作的第一责任人。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第四十九条 医院运行管理实行目标责任制。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第五十条 各层级、各部门管理人员结合工作实际制定有机协调、切实可行、有据可考的发展目标和工作规划。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:29.2pt; widows:0; \"><span style=\"font-size: 18px;\">医院发展规划由党政领导班子集体讨论制订，由职工代表大会讨论通过；临床科室工作计划根据专科（学科）发展规划和医院目标责任管理要求制定，经主管院领导审核并备案；部门工作计划由本部门讨论制定，经主管院领导审核并备案。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">工作规划或计划要有落实保障机制，坚持任务到岗、责任到人， 明确考核奖惩机制。相应责任人对主管领导、院长和医院党委负责， 受全院员工监督。</span></p><p style=\"font-size:10pt; line-height:5.83%; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; widows:0; \"><br /></p><p style=\"line-height:16.05pt; margin-bottom:0pt; margin-left:1pt; margin-right:0pt; margin-top:0pt; orphans:0; widows:0; \"><span style=\"font-size: 18px;\">- 11 -</span></p><p style=\"line-height:24pt; margin-bottom:6pt; margin-left:0pt; margin-right:20.15pt; margin-top:0pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-size: 18px;\">第二节 决策机制</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第五十一条 明确议事决策范围（特别是“三重一大”事项）， 明确会议集体议事规则，实行主要领导末位发言制度，健全决策前论证与听取意见机制，坚持以会议形式集体决策重要事项。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第五十二条 实行集体领导和个人分工负责相结合的制度，凡属重大问题都要按照集体领导、民主集中、个别酝酿、会议决定的原则， 由党委集体讨论，作出决定，并按照分工抓好组织实施，支持院长依法依规独立负责地行使职权。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第五十三条 医院领导班子决策的形式有党委会、院长办公会。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第五十四条 党委会：</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">医院党委会议是研究和决定医院重大问题的会议，按照集体领导、民主集中、个别酝酿、会议决定的原则，由党委集体讨论作出决定。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）议事决策</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">1.全面贯彻落实党的民主集中制，充分发挥党委的核心作用，坚持集体领导、民主集中、个别酝酿、会议决定原则，提高科学决策、民主决策、依法决策水平。党委作出重大决策，一般应当在调查研究基础上提出方案，充分听取各方面意见，进行风险评估和合法合规性审查，经过集体讨论决定。重要行政、业务工作议题，先由院长办公会议提出意见方案， 再提交党委会议讨论决定。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">2.党委会决议的执行。党委会议作出的决议、决定，要按照集体领导、分工负责的原则，由分管领导认真组织实施。党委主要负责同志负责实施过程中的检查督促工作。党委办公室负责做好会议记录，整理会议纪要、决定，协助分管领导开展会议决议、决定落实情况的督办检查， 并将落实情况及时向院党委报告，确保党委决策落实。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">3.凡属“三重一大”事项，必须召开医院党委全体委员会议集体研究决定，除遇重大突发事件或紧急情况难以召开会议外，不得以传阅、会签或个别征求意见等形式代替集体决策，不允许用其他形式取代医院党委会讨论决定医院改革发展重大事项事决策。党委会主要讨论决定以下事项：</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（1）重大决策事项。执行党的卫生与健康工作方针，落实深化医药卫生体制改革政策措施；贯彻中央和省委关于全面从严治党的决策部署，推进医院党的建设重点工作；医院发展和建设、专（学）科技术进步与人才队伍建设等规划及年度工作计划，重大科技项目与人才项目；医院职工评先评优、出国（境）及人员外派交流；医院重要规章制度的立、改、废；内部组织机构（含行政部门、业务科室、党组织和其他医院党委明确的实体机构）、人员岗位的设置和重大调整，职称评聘、常规晋升晋级及日常人员招用、解聘、调动；职责权限内违纪违规案件的立案查处；职工收入分配及福利待遇、奖励和其他关系职工权益的重要事项；医院年度财务预算方案、决算情况的审定，预算执行与决算审计；医院重要资产处置、重要资源配置。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（2）重要人事任免事项。院管干部、内部组织机构负责人等人员的任免，推荐优秀干部、党代会代表、人大代表、政协委员等人选， 以及其他重要干部人事任免事项。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（3）重大项目安排事项。各类重点建设项目，国内、国（境） 外医疗技术交流与合作重要项目，大型医疗设备、药品、医用器械、检验试剂、高值耗材等大宗物资招标采购，基本建设和大额度基建修缮项目，以及其他重大项目安排事项。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（4）大额度资金使用事项。专（学）科建设、人才引进与培养等专项资金预算分配方案，医院党政领导干部调动、使用资金的限额及程序等资金管理制度。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（5）需要党委会讨论决定的其他重大问题或重要事项。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">4.医院党政班子和内设组织机构必须坚决执行党委会议决定。如有不同意见，可以保留或按照组织程序向上级党组织提出，但在决策改变前应当坚决执行，不得有任何与党委会议决议、决定相违背的言论和行为。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">5.实行党委会议决策失误责任追究制度，对违反决策程序造成重大失误或决策执行不力造成严重后果的，按有关规定追究责任。对于个人或少数人擅自决定本应由党委会议集体决策的行为，严肃追究相应责任人的责任。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）议事规则</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">1.党委会议由党委书记召集并主持，每月召开 1－2次，如遇特殊紧急情况可随时召开。会议由党委书记召集和主持，党委书记因故不能出席时，可委托班子成员召集并主持。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">2.会议议题、召开时间，由会议主持人确定。会议要严格按照预定议题进行，凡未经党委书记会前审定的议题，不列入会议议程，不得临时动议议题或表决事项。党委会议采取一题一议的方式进行，出席人员围绕相关议题内容充分发表意见，明确态度，按“少数服务多数”的原则作出决定。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">3.党委会议必须有半数以上党委委员到会方能召开，出席人员一般为党委委员，不是党委委员的院长、副院长可列席会议，其他列席人员由会议主持人根据会议议题需要确定。讨论决定干部任免等重要事项时，应有三分之二以上党委委员到会方能召开。讨论决定“三重一大”事项时，书记、院长一般应同时参加会议。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">4.在与会党委委员充分发表意见的基础上，根据讨论事项的不同内容， 分别采取口头、举手或无记名投票的方式进行表决。表决实行主持人末位 表态制。对于推荐、提名干部的讨论，干部任免和需要表决的重要事 项，会议主持人应当在出席人员充分发表意见的基础上进行归纳集中， 提出决策方案并进行表决。会议决定多个事项， 应逐项表决；赞成票超过应到会委员半数为通过，未到会委员的意见不得计入票数。党委 委员因故未出席会议的，由党委办公室负责会后通报相关情况。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">5.党委会议讨论决定的医院重大事项，应当进行充分酝酿后，再提交会议讨论决定。必要时应组织专家进行分析论证、开展民主协商。会议讨论时，每位成员都要充分发表意见，明确态度，按“少数服从多数”的原则作出决定，主要负责人最后发表意见。对少数不同意见， 应慎重考虑。如对决策事项有较大分歧意见，或持不同意见人数接近一半时，除紧急情况下必须按多数意见决定外，应暂缓作出决定，待进一步调查研究、分析论证、交换意见后，提交下次党委会议讨论决定或向上级党组织请示。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">6.党委会议执行回避制度。议题凡涉及本人及夫妻关系、直系血缘关系、三代以内旁系血亲关系、近姻亲关系的利益问题时，有关人员应回避。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">7.党委会议讨论和决定的事项等相关信息，经批准公开之前，出席人员和列席人员不得以任何形式泄露，对违反者严肃追究相应责任。 </span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">8.党委办公室负责做好会议记录，整理会议纪要、决定，协助分管院领导开展会议决议、决定落实情况的督办检查，并将落实情况及时向党委报告。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第五十五条 院长办公会：</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">院长办公会议是医院行政、业务议事决策会议，是院长行使职权、履行职责、研究和决定医院行政业务重要事项的工作会议。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）议事决策</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">1.院长办公会议贯彻民主集中制，坚持科学决策、民主决策、依法决策。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">2.议事决策一般采用院长办公会议形式，主要讨论决定以下事项：</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（1）讨论决定贯彻落实党委会决议的有关措施。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（2）讨论通过拟由党委会讨论决定的重大决策、重大项目安排和大额资金使用事项的方案。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（3）讨论拟由党委会讨论决定的职称评聘、常规晋升晋级及日常人员招用、解聘、调动等医院人事工作的事项。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（4）讨论决定医学生培养、住院医师和专科医师规范化培训、继续医学教育等招生培养工作，一线岗位人才引进培养等事项。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（5）讨论决定医院医疗、教学、科研和行政管理中其他需要集体决策的事项。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">3.如遇突发事件和紧急情况来不及提交院长办公会议进行集体讨论决定的重大事项，医院班子成员可按医院应急预案临机处置，事后向院长办公会议报告，属于“三重一大”的事项应同时向党委会议报告。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">4.院长办公会议作出的决议、决定，由分管行政班子成员按照分工负责的原则具体组织实施。会议决定的事项在执行过程中确需作出重大调整的，应当根据相关程序，再次召开院长办公会议讨论决定。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">5.医院行政班子成员和内设行政业务机构必须坚决执行院长办公会议决定。如有不同意见可以保留或按程序向上级部门提出，但在决定改变之前必须按决议执行。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">6.加强院长办公会议议决事项督查督办，明确具体督办部门，建立督办事项列督跟踪和督促落实机制，定期向院长办公会议通报议决事项推进落实情况。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）议事规则</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">1.院长办公会议定期召开，如遇特殊紧急情况可随时召开。院长办公会议由院长召集并主持，院长因故不能出席时，可委托班子其他成员召集并主持。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">2.院长办公会议出席人员一般为医院行政班子成员，须有半数以上的行政班子成员到会方可召开；讨论决定重要事项时，须有三分之二以上行政班子成员到会方可召开。纪委书记一般列席院长办公会议， 党委其他班子成员可视议题情况列席。院长可根据议题需要，确定其他相关人员列席会议。应出席人员因故不能出席的，应按规定请假， 其对会议所列议题的具体意见建议，可以书面形式表达。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">3.会议议题由医院行政领导班子成员提出，报院长确定。会议按照确定的议题进行，凡未经院长会前审定的议题，不列入会议议程。除因特殊紧急情况临时召开的之外，会议的议题、召开时间和召开地点应当至少提前 1 天通知岀席人员和列席人员。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">4.注重加强党政沟通，院长办公会议涉及“三重一大”等需要由党委会研究决定的议题，应当在会前充分听取党委书记意见并形成共识后，再提交会议研究审议，意见不一致的议题应暂缓上会。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">5.会议讨论决定医院重大行政事项，应当在会前进行充分研究且无重大分歧后再提交会议审议。注重加强会前协商，对于重大事项， 分管院领导应当在会前向院长专题汇报，经院长同意后提前向院长办公会议出席人员通报，经过充分沟通后再提交会议审议。如有重大异议， 经院长同意后可撤销相关议题。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">6.提交院长办公会议审议的议题，应当经过深入调查研究。按照规定须履行审核或审查程序的事项，应当在会前经由有关部门审核或审查。对于专业性、技术性较强的重要事项，应经过专家论证、风险评估，以及技术、政策、法律咨询，再提交院长办公会议审议；涉及员工重大切身利益的，应当采取座谈会、论证会等形式听取职工意见建议。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">7.院长办公会议采取一题一议的方式进行。行政班子成员应当充分发表意见，对不属于自己分管的工作，也应从全局出发关心支持，加强研究，提出意见建议。实行院长末位发言制，院长应当在出席人员全部发表意见后再作表态发言，并根据讨论情况提出决策意见。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">8.严格执行重大问题请示报告制度。对于应当报请党委会议讨论决定的议题，分管院领导应当根据院长办公会议讨论形成的意见修改完善后，按程序报请党委会议讨论决定。院长办公会议研究作出的重大政策决定、执行上级重要决定的情况，以及遇有重大突发事件和工作中重大问题，应当及时向上级部门和医院党委请示报告。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">9.院长办公会议执行回避制度。议题凡涉及本人及夫妻关系、直系血缘关系、三代以内旁系血亲关系、近姻亲关系的利益问题时，有关人员应回避。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">10.院长办公会议记录应当规范、准确，在此基础上编发院长办公会议纪要。院长办公会议讨论形成的决议、决定，由院长审核签发。 </span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">11.院长办公会议应当自觉接受上级部门和医院党委的领导和监督，接受上级和同级纪委的监督，接受下级部门和群众的监督，接受民主党派和无党派人士民主监督。</span></p><p style=\"line-height:25pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">12.实行院长办公会议决策失误责任追究制度，对违反决策程序造成重大失误或者决策执行不力造成严重后果的，按有关规定严肃追究责任。</span></p><p style=\"line-height:24pt; margin-bottom:6pt; margin-left:0pt; margin-right:15.05pt; margin-top:0pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-size: 18px;\">第三节 人力资源管理</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32.8pt; widows:0; \"><span style=\"font-size: 18px;\">第五十六条 深化医院人事制度改革，促进医院人事管理的科学化、规范化和制度化，依据国家、省、市岗位设置管理有关文件精神， 按照盐城市人力资源和社会保障局核准的岗位设置方案，结合医院实际情况，制定专业技术岗位聘用管理暂行办法，并严格按照专业技术职务竞聘上岗方案实施。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第五十七条 医院建立健全人力资源管理制度，实行全员聘任制， 与所有员工签订事业单位工作人员聘用合同，依法对员工进行聘任、解聘、考核、奖惩、培训等管理。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第五十八条 岗位设置基本原则：</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）按需设岗，优化结构</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">从医院的定位和目标出发，根据医疗、科研、教学和学科建设， 学术梯队配备、用人效益等实际需要，兼顾各类人员结构现状，优化结构比例，合理配置。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）总量控制，动态管理</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:29.2pt; widows:0; \"><span style=\"font-size: 18px;\">根据省市有关岗位设置工作的相关文件要求，结合医院实际需要， 确定岗位总量，并按照规定的岗位比例结构标准，设置各级各类岗位， 岗位数量一经核定，保持基本稳定，并根据医院的发展实行动态管理。（三）按岗聘任，规范管理</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:29pt; widows:0; \"><span style=\"font-size: 18px;\">以岗位设置为基础，深化用人制度改革，完善因事设岗、按岗用人、按岗定酬的制度，实行岗变薪变，把人员待遇和岗位职责、贡献大小结合起来。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第五十九条 结合医院工作和发展实际，分为管理岗位、专业技术岗位和工勤岗位三类。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）管理岗位。管理岗位是指承担领导职责或管理职能的工作岗位。管理岗位设置总数≤总岗位总量的10%。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）专业技术岗位。专业技术岗位是从事专业技术工作，具有相应的专业技术水平和能力要求的工作岗位，占岗位总量的85%。高、中、初等级结构比例为1：2：1。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（三）工勤技能岗位。工勤技能岗位承担操作和维护、后勤保障、服务等职责的工作岗位。工勤技能岗位总数为≤总岗位总量的5%。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32.8pt; widows:0; \"><span style=\"font-size: 18px;\">第六十条医院依照有关法律法规和规章，制定员工守则，对员工提出道德规范、组织纪律、考勤管理、学习培训、考核管理等规定。医院全体员工应遵守员工守则。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30.2pt; widows:0; \"><span style=\"font-size: 18px;\">第六十一条 建立健全岗位考核评价机制，对员工的思想政治表现、职业道德、业务水平和工作实绩进行考核，考核结果作为员工聘任或解聘、晋升、奖励或处分的依据。各临床科室建立考核、评价记录，医务、护理部门负责监管。人事部门牵头组织全院所有人员的年度考核工作，具体由各党支部落实实施，人事部门负责考核合格后的聘用、调资等工作。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第六十二条 医院根据上级政策要求，按国家公立医院改革及薪酬制度改革方向，从优化医院人力资源配置、提高医疗服务质量、加强成本管控、辅助医院科学运营决策、推动医院管理规范化、精细化、科学化目的出发，实施符合医院实际的绩效分配制度。绩效分配坚持多劳多得、优绩优酬的原则，坚持效率优先、兼顾公平的原则，坚持综合目标考核的原则，坚持向临床一线倾斜的原则，突出“技术、责任、风险、管理”等因素在绩效分配中的地位，充分调动职工的积极性、主动性和创造性，促进医院发展。根据医院改革发展实际情况，积极探索年薪制、项目工资、协议薪酬等多种分配方式，建立科学合理的薪酬制度。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第六十三条 人才招聘工作遵循“公开、平等、竞争、择优”的原则。严格执行招聘工作有关规定和招聘程序，严肃纪律，秉公办事，阳光操作，自觉接受纪检、监察、组织和社会各界的监督。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第六十四条 树立“人才是第一资源”的理念，注重各类人才的培养，不断丰富和完善有针对性、分层次、分阶段、分类型的人才培养体系。制定并不断完善医院住院医师及专科医师规范化培训制度。重视人才梯队建设，在制度上优先保障中青年业务和管理骨干人员的职业化培养。制定有效措施不断提高全员学历水平、能力素质。充分发挥老专家的传、帮、带作用，培育中青年业务骨干，促进学科发展。推动职称评审和人才评价改革，为培养学科带头人和领军人才创造条件。不断完善高端人才、优秀人才引入机制。</span></p><p style=\"line-height:24pt; margin-bottom:6pt; margin-left:0pt; margin-right:15.05pt; margin-top:8.75pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-size: 18px;\">第四节 医疗、教学和科研管理</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30.8pt; widows:0; \"><span style=\"font-size: 18px;\">第六十五条 医疗质量与安全是医院管理的基础工作，医疗质量管理委员会是医院医疗质量的管理机构，履行医疗质量安全管理职责， 对医院质量与安全专委会负责。在医院党委、院长和分管院长的领导 下进行工作，负责完成医院医疗质量与安全管理，对医院医疗质量与 安全进行综合评估，为医院在医疗质量与安全管理方面的决策提供信 息服务。临床医技科室主要负责人是本科室医疗质量管理的第一责任人，负责全面落实各项医疗规章制度。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第六十六条 医院坚持以病人为中心的服务理念，推行全面质量管理，实施改善医疗服务行动计划，全面提升患者满意度。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）参与分级诊疗制度建设，推进急慢分治，推广多学科诊疗模式，拓展药学服务新领域，延伸提供优质护理服务，推动预防保健、临床诊疗、康复照护等服务环节可连续。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）实施临床路径管理制度，推进检查检验结果互认，规范药品和耗材临床应用，做到合理检查、合理用药、合理治疗。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（三）创新急诊急救服务，提高重大疾病救治水平。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（四）建设智慧医院，建立健全预约诊疗制度、远程医疗制度， 全面优化医院服务流程。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（五）建立健全医务社工和志愿者制度。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第六十七条 医院建立医疗质量持续改进制度。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）严格按照核准登记的诊疗科目执业。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）严格执行医疗技术和手术管理有关规定，加强医疗技术临床应用管理。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（三）遵循临床诊疗指南、临床技术操作规范、行业标准和临床路径。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（四）严格落实首诊负责制度、三级查房制度、疑难病例讨论制度、死亡病例讨论制度、手术安全核查制度等18项医疗核心制度。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30.2pt; widows:0; \"><span style=\"font-size: 18px;\">第六十八条 医院以提高临床疗效为导向，以临床研究为重点， 开展科学研究，以科技创新推动临床学科和专科的发展，切实提升临床服务能力与水平。立足特色优势，引进国际先进技术方法，搭建高层次科技创新的技术平台。医疗、科研工作应符合伦理规范，接受伦理审查。医院不断完善鼓励创新的运行机制，加强科技创新、科学研究、科技成果转化与知识产权管理，鼓励各类人员积极参与科技成果转化活动，依法保护其合法权益，对做出突出贡献的人员予以表彰和奖励，并与其职称聘任、岗位聘用、薪酬待遇等挂钩。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30.2pt; widows:0; \"><span style=\"font-size: 18px;\">第六十九条 医院把培养具有系统而坚实的基础理论、专业知识和一定的科学实验技能的应用型医学人才及研究型医学人才作为教学目标。注重各级医学人才理想信念、健全人格、医德医风、思维体系、知识结构的养成。全院营造“乐教尚学”的氛围，视教学工作为义务。严格按照国家住院医师规范化培训基地评估和大学附属医院建设等教学管理规定的有关要求，落实经费投入、强化师资队伍、优化教学模式、完善教学条件、严控教学质量，教学工作与职称聘任、岗位聘用、薪酬待遇等挂钩，充分形成有效的奖惩机制，切实推动学科发展、专业提升，坚持遵循医学人才的成长规律，培养临床能力突出、创新能力强、综合素质高、满足社会需要的现代医学人才。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第七十条 医院建立医疗安全与风险管理体系。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）完善医疗安全管理相关工作制度、应急预案和工作流程。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）完善防范、处理医疗纠纷的预案，预防、减少医疗纠纷的发生，保障患者医疗安全。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（三）参加医疗机构责任保险，鼓励医师参加医师执业保险，引导患者参加医疗意外保险。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第七十一条 医院努力构建和谐医患关系。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）医院设立病人投诉与服务部门，开设医患纠纷处理窗口， 对外公布医患纠纷处理制度、服务流程和投诉电话。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）医院支持医患之间依法处理矛盾纠纷，支持医疗纠纷人民调解组织参与本院医患纠纷调解服务。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第七十二条 医院致力于推进平安医院建设。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）设立保卫工作部门，建立健全安全监控与巡防、应急处置与救助等制度，与辖区公安部门建立警医联动机制，维护医疗服务秩序，保护医院、医务人员和患者的合法权益与安全。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）通过宣传倡导，营造尊医重卫社会环境，引导患者及家属遵守医疗服务秩序，配合医疗机构和医务人员进行检查治疗，依法解决医疗纠纷。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（三）建立安全生产相关管理制度，指导、检查、督促全院员工的安全生产。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第七十三条 医院的医疗事故、医疗损害责任及其它医患争议的赔偿及处理应当按《中华人民共和国侵权责任法》《医疗事故处理条例》《医疗纠纷预防与处理条例》及其他相关法律、法规、规章规定执行。医院发生重大医疗事故和医疗纠纷案件，按规定及时向上级党委和政府有关部门报告。</span></p><p style=\"line-height:24pt; margin-bottom:6pt; margin-left:0pt; margin-right:15.05pt; margin-top:6pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-size: 18px;\">第五节 财务资产管理</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32.8pt; widows:0; \"><span style=\"font-size: 18px;\">第七十四条 医院的经费来源：医疗服务收入、其他收入、科教项目收入以及政府财政补助。医院资产为国家所有，医院对占有、使用的国有资产依法依规实施管理。任何个人不得侵占、挪用医院资产。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第七十五条 医院实施全面预算管理，建立资产管理与预算管理， 资产管理与财务管理相结合的工作机制。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32.2pt; widows:0; \"><span style=\"font-size: 18px;\">第七十六条 医院依照相关财经法律法规和制度，结合医院宗旨， 制定本院财务会计管理制度、财务会计内部控制制度、国有资产管理制度和对外投资合作制度等；依法按章进行会计核算，实行财务监督， 加强经济管理，提高经济效益，实现国有资产保值增值。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32.2pt; widows:0; \"><span style=\"font-size: 18px;\">第七十七条 医院设置总会计师，由总会计师全面负责医院财经管理和会计核算工作，强化医院财务风险管理，不断提高财务管理水平。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第七十八条 医院除法定的会计账簿外，不得另立会计账簿。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第七十九条 医院的人事、工资、保险、福利待遇按照国家有关规定执行。医院编制外聘（任）用员工工资、保险、福利待遇等按照国家对企事业单位的有关规定执行。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32.8pt; widows:0; \"><span style=\"font-size: 18px;\">第八十条 医院接受捐赠须严格遵守国家法律法规，坚持自愿无偿、非营利性、公益性和公开性原则。捐赠的使用须按照医院宗旨、捐赠协议约定和相关规定开展公益非营利活动；建立机制，保障捐赠管理公开透明，并接受有关部门监督。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第八十一条 医院院长变更时，须对前任院长进行任期内的经济责任审计。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30.2pt; widows:0; \"><span style=\"font-size: 18px;\">第八十二条 医院承诺按照国家法律、法规和《事业单位登记管理暂行条例》及其实施细则的规定，真实、完整、及时地披露相关资产信息，自觉接受财政、审计以及主管部门对医院进行的预算执行及其他财务收支情况等监督、审计。</span></p><p style=\"line-height:24pt; margin-bottom:6pt; margin-left:0pt; margin-right:15.05pt; margin-top:9.3pt; orphans:0; text-align:center; widows:0; \"><span style=\"font-size: 18px;\">第六节 后勤保障管理</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:33.4pt; widows:0; \"><span style=\"font-size: 18px;\">第八十三条 坚持“以病人为中心，后勤工作为临床一线服务”的原则，遵循“以人为本、服务临床”的服务理念，紧紧围绕医院中心工作， 认真落实等级医院评审标准，依据规范化、标准化、专业化、信息化的发展要求，充分发挥后勤工作保障性、服务性、经济性三大作用， 进一步解放思想，转变发展观念，创新发展模式，提高发展质量，把后勤建成机制灵活、管理科学、服务优良、环境优美、效益显著的适应社会主义市场经济，符合卫生发展规律的保障系统，为医院可持续发展提供安全、优质、高效、低耗的后勤保障。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第八十四条 以全面提升患者和职工满意度为工作目标。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第八十五条 后勤管理范围涉及基本建设、固定资产、物资保障、水电汽运行管理、外包业务考核与监管（绿化养护、电梯、食堂、弱电、污水处理站等）、生产安全等内容，遵守政策法规，严格内部控制，规范制度流程，严守廉政要求。</span></p><p style=\"line-height: 24pt; margin: 6pt 14.75pt 0pt 0pt; text-align: center; text-indent: 0em;\"><span style=\"font-size: 18px;\">第七节 信息化建设</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:33pt; widows:0; \"><span style=\"font-size: 18px;\">第八十六条 坚持“信息作为医院重要资源”理念，大力推进医院信息化建设，为服务患者、服务临床、服务科研、服务科学管理创造有力的信息支撑平台。</span></p><p><span style=\"font-size: 18px;\">第八十七条 借助信息化技术，推动医院管理向科学化、精细化发展，倡导技术革新和管理持续改进，拓展智慧医疗和远程医疗，确保医院的信息安全。</span></p><p><span style=\"font-size: 18px;\"><br /></span></p><p style=\"margin-bottom: 5px; text-align: center; text-indent: 2em;\" dir=\"rtl\"><span style=\"font-size: 18px;\">第八节        文化建设<br /></span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32.2pt; widows:0; \"><span style=\"font-size: 18px;\">第八十八条 医院以党建引领医院文化建设，始终将文化建设放在医院可持续健康发展的战略高度，以人民健康为中心，把人民健康放在优先发展的战略地位，把握正确导向，牢牢掌握意识形态主导权，梳理文化发展，将公平可及、群众受益作为出发点和立足点，挖掘医院文化历史沉淀，凝聚医院文化内核与特色，总结提炼特色文化， 结合时代需求，不断丰富文化内涵，形成医院文化架构体系。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:31.4pt; widows:0; \"><span style=\"font-size: 18px;\">第八十九条 通过传承弘扬优良传统，创新文化建设载体，将文化建设和基层党组织建设与社会主义核心价值观教育相结合，弘扬“敬佑生命、救死扶伤、甘于奉献、大爱无疆”的职业精神，激发职工爱国爱院、敬业奉献的热情，营造积极向上的良好氛围，塑造学术精湛、医德高尚、医风严谨的行业风范，不断增强医院凝聚力，推动医院高质量发展。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第九十条 文化载体建设：通过文化长廊，院史陈列和医院网站、新媒体、微信等多种文化载体，形成爱岗敬业、诚信服务、群体向善、奉献社会的良好氛围。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第九十一条 医院宗旨：治病救人，造福社会。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:109.5pt; widows:0; \"><span style=\"font-size: 18px;\">医院院训：厚德修身，精术济世。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:109.5pt; widows:0; \"><span style=\"font-size: 18px;\">医院精神：严谨 博学 团结 奉献。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:109.5pt; widows:0; \"><span style=\"font-size: 18px;\">服务理念：生命所系 健康相托<span style=\"font-family: 黑体, SimHei; font-size: 20px;\"><br /></span></span></p><p style=\"text-align: center; text-indent: 0em;\" dir=\"ltr\"><span style=\"font-family: 黑体, SimHei; font-size: 20px;\">第六章 章程的修改</span></p><p style=\"line-height: 24pt; margin: 0pt; text-align: center; text-indent: 2em;\" dir=\"ltr\"><span style=\"font-family: 黑体, SimHei; font-size: 20px;\"><br /></span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第九十二条 医院有下列情形之一的，应当修改章程：</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（一）章程规定的事项与法律、法规、规章和国家有关政策相冲突的。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（二）法律、法规、规章和国家有关政策发生变化，需要对章程进行相应调整的。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（三）医院名称、类别等级、办医宗旨、发展目标等实际情况发生变化的。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（四）章程内容与患者利益或员工整体利益不符的。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">（五）有权提议修改章程的机构认为应当修改章程的其他情形。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:32pt; widows:0; \"><span style=\"font-size: 18px;\">第九十三条 医院按照如下程序修订章程：成立章程修订工作小组，在广泛调研和征求意见的基础上，形成章程修订意见，经院长办公会、党委会审议，形成章程修订草案。通过后报请行政主管部门审查核准后发布。</span></p><p style=\"line-height: 24pt; margin: 0pt; text-align: center; text-indent: 0em;\"><span style=\"font-family: 黑体, SimHei; font-size: 18px;\"><span style=\"font-family: 黑体, SimHei; font-size: 20px;\">第七章  附则</span><br /></span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第九十四条 医院依据本章程制订完善相关规章制度和规范性文件，按照本章程实施管理和履行职能。凡与本章程不一致的，以本章程为准。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第九十五条 本章程未尽事宜，依照国家法律、行政法规及国家政策办理。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第九十六条 本章程解释权属于江苏省盐城市第一人民医院。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:30pt; widows:0; \"><span style=\"font-size: 18px;\">第九十七条 本章程自核准之日起施行。</span></p><p style=\"margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0.45pt; orphans:0; widows:0; \"><span style=\"font-size: 18px;\"></span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2114','2934','1','','http://www1.ycsyy.com/upload/images/2019/10/eccd807c10ba46ad.png','10月11日，在“不忘初心、牢记使命”主题教育扎实开展之际，我院举办了“智慧党建”信息平台启动仪式，“智慧党建”信息平台正式上线。近年来，我院党委积极贯彻落实中央、省、市和市卫健委各级党组织关于加强公立医院党建工作的部署，按照新时代党的建设总要求，坚持以','192.168.162.110','192.168.162.110','1570809600','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">10月11日，在“不忘初心、牢记使命”主题教育扎实开展之际，我院举办了“智慧党建”信息平台启动仪式，“智慧党建”信息平台正式上线。</span></p>\r\n<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">近年来，我院党委积极贯彻落实中央、省、市和市卫健委各级党组织关于加强公立医院党建工作的部署，按照新时代党的建设总要求，坚持以患者为中心，把加强公立医院党建工作作为当前医院改革发展工作的主线，不断创新党建工作方法，着力推动医院党的建设与业务工作相融合，努力建设患者放心、人民满意的医院，院党委获全省卫生健康行业“先进基层党组织”称号。</span></p>\r\n<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">“智慧党建”信息系统项目建设，就是我院党委深入贯彻落实习近平新时代中国特色社会主义思想，扎实开展“不忘初心、牢记使命”主题教育，进一步加强新时代公立医院党建工作，把全面管党治院向基层延伸的重要举措。该系统的上线标志着我院党建工作正式实现信息化和移动互联，构建起具备实时感知、互联互通、智能分析等功能的工作环境，推动党建工作从线下走向线上、从传统走向现代、从粗放走向精准。</span></p>\r\n<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">据介绍，“智慧党建”信息平台设置了组织管理、动态提醒、学习平台、个人中心四大模块，集党建地图、党委工作、党支部工作、党员教育、党建动态、党建考核等众多功能于一体，可以通过信息化手段，及时明确党建要求、细化党建工作、压实党建责任，成为我院党组织开展中心组学习、“三会一课”、日常党务工作的重要载体，成为发挥党员先锋模范作用的“激发器”和建设合格党员队伍的“助推器”。此外，“智慧党建”还开辟了专门的主题教育板块，包括中央精神、医院动态、学习资料、经验成效等模块，贯穿学习教育、调查研究、检视问题和整改落实全过程，力求充分发挥系统在推动党员干部加强学习、交流经验、展示成效的作用，打造医院主题教育特色亮点，推动主题教育扎实开展。 </span></p>\r\n<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">“我院智慧党建系统的上线启动，标志着医院党建工作迎来了一个新起点，将信息技术应用到党建工作中，既是信息化时代发展的客观要求，也是党建工作改革创新的必然要求。今后，我们将以更高的标准和要求深入推进党建各项工作，不断激发广大党员活力，努力为新时代加强公立医院党建工作探索新路径，为‘健康盐城战略’做出新的更大贡献。”院党委书记张慧娟介绍说。</span></p>\r\n<p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/eccd807c10ba46ad.png\" title=\"image.png\" alt=\"image.png\"></span></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2115','2935','1','','http://www1.ycsyy.com/upload/images/2019/10/24ebab6542ec62db.png','在全国上下喜迎中华人民共和国成立70周年和深入开展“不忘初心，牢记使命”主题教育之际， 9月26日下午，共青团盐城市第一人民医院第六次代表大会在主院区四楼会议室隆重举行，来自全院各支部团员代表参加本次会议。此次会议审议并通过了团委工作报告，选举产生了新一届','192.168.162.110','192.168.162.110','1569513600','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">在全国上下喜迎中华人民共和国成立70周年和深入开展“不忘初心，牢记使命”主题教育之际， 9月26日下午，共青团盐城市第一人民医院第六次代表大会在主院区四楼会议室隆重举行，来自全院各支部团员代表参加本次会议。此次会议审议并通过了团委工作报告，选举产生了新一届医院共青团委员会。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">市卫健委团委副书记唐婷婷，院党委委员、副院长、工会主席李明祥，院党委委员、副院长李奇志应邀出席大会。会议由院组织处处长、团委书记朱伟主持。</span></p><p style=\"text-align: center;\"><span style=\"font-size:21px;font-family: 宋体\"><span style=\"font-size:21px;font-family: 宋体\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/24ebab6542ec62db.png\" title=\"image.png\" alt=\"image.png\" /></span></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2116','2936','1','','http://www1.ycsyy.com/upload/images/2019/10/3f5aade77f5aa0f9.png','为了纪念中国共产党建党98周年，迎接新中国成立70周年华诞，6月19至20日，我院举办“不忘初心、牢记使命”理想信念主题教育第四期培训班。6月19日，在院主院区会议中心，召开党委理论学习中心组学习（扩大）会议，邀请市委宣传部蒋东仁副部长作意识形态工作专题辅导。20','192.168.162.110','192.168.162.110','1561046400','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为了纪念中国共产党建党98周年，迎接新中国成立70周年华诞，6月19至20日，我院举办“不忘初心、牢记使命”理想信念主题教育第四期培训班。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6月19日，在院主院区会议中心，召开党委理论学习中心组学习（扩大）会议，邀请市委宣传部蒋东仁副部长作意识形态工作专题辅导。20日，41名党员干部赴河北省平山县，在西北坡、白求恩柯棣华纪念馆、正定古城等地，开展现场教学，重温入党誓词，感悟革命精神，缅怀革命先烈，传承红色基因。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">大家一致表示要发扬党的优良传统，学习老一辈革命家艰苦奋斗的风范和革命精神，提升新时代责任感和求实精神，充分发挥党员的先锋模范作用，为推动医院高质量发展走在苏北苏中前列不懈奋斗。</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size:14px;font-family:宋体\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/3f5aade77f5aa0f9.png\" title=\"image.png\" alt=\"image.png\" /></span></span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2117','2937','1','','http://www1.ycsyy.com/upload/images/2019/10/aae70c960da61c4b.png','为认真学习贯彻习近平新时代中国特色社会主义思想，党的十九大和十九届二中、三中全会精神，扎实开展“不忘初心、牢记使命”主题教育，切实强化党员身份意识、宗旨意识和责任意识，进一步增强党员的党性观点和政治荣誉感。5月21日下午，我院门诊党支部在党员活动室开展','192.168.162.110','192.168.162.110','1558454400','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为认真学习贯彻习近平新时代中国特色社会主义思想，党的十九大和十九届二中、三中全会精神，扎实开展“不忘初心、牢记使命”主题教育，切实强化党员身份意识、宗旨意识和责任意识，进一步增强党员的党性观点和政治荣誉感。5月21日下午，我院门诊党支部在党员活动室开展了5月特色党日活动，活动内容包括给支部党员过生日、政治理论学习和党建联络员学习讨论交流。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">院党委书记张慧娟，院党委委员、副院长李奇志，门诊党支部全体党员，各党支部党建联络员，党委办公室，组织处等共计60余人参加活动。</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/aae70c960da61c4b.png\" title=\"image.png\" alt=\"image.png\" /></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">齐唱国歌</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/7465b1a83b013778.png\" title=\"image.png\" alt=\"image.png\" /><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"> </span><img src=\"http://www1.ycsyy.com/upload/images/2019/10/4e19ce4950cc7908.png\" title=\"image.png\" alt=\"image.png\" /></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">重温入党誓词 支部党员过生日 </span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/bb353a6cb44e24d9.png\" title=\"image.png\" alt=\"image.png\" /><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"> </span><img src=\"http://www1.ycsyy.com/upload/images/2019/10/6438b61e70d32a0f.png\" title=\"image.png\" alt=\"image.png\" /></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">开展政治理论学习</span></p><p><br /></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">院党委书记张慧娟寄语全体党员，不忘初心，牢记使命；不忘本真、永葆本色，始终牢记全心全意为人民服务的根本宗旨，心系群众、服务人民；注重思想道德修养，品德高尚、清正廉洁；时刻不忘肩上重任，以身作则、率先垂范。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">寄语各党支部，要“全面进步、全面过硬”，旗帜鲜明讲政治，全面增强“四个意识”，做好“两个维护”；持之以恒抓规范，加快提升支部组织力，牢固树立“一切工作到支部”的鲜明导向，以“标准化建设”为抓手，推动党内政治生活质量全面提升；融入中心勇担当，引导党员带头融入医院发展中心，勇于担当、积极作为；全面从严防控风险，坚持把党风廉政建设不断引向深入，防范各类风险；积极创新求实效，各党支部、各处室、各党小组等要坚持工作创新、服务党建。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2118','2938','1','','http://www1.ycsyy.com/upload/images/2019/10/9d0b7afd36b7361c.png','春意盎然，春风送暖。为深入学习贯彻习近平新时代中国特色社会主义思想和党的十九大精神，以“讴歌新时代，增添正能量，我看新中国成立70周年”主题教育活动开展为载体，进一步丰富我院离退休党员干部职工的精神文化生活。按照院党委要求，5月9日，老干部支部组织全院14','192.168.162.110','192.168.162.110','1557417600','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">春意盎然，春风送暖。为深入学习贯彻习近平新时代中国特色社会主义思想和党的十九大精神，以“讴歌新时代，增添正能量，我看新中国成立70周年”主题教育活动开展为载体，进一步丰富我院离退休党员干部职工的精神文化生活。按照院党委要求，5月9日，老干部支部组织全院140多名离退休党员同志赴淮安红色文化教育和历史文化教育基地，开展党日活动现场教学。</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">不忘初心跟党走，牢记使命勇担当。此次的党日活动，院离退休干部处在医院党委的指导下，精心准备、认真组织，整个活动受到了广大老党员同志们的交口称赞，大家纷纷表示要发挥余热，为医院的基层党建添砖加瓦，为推动医院高质量发展贡献力量。</span></p>\r\n<p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/9d0b7afd36b7361c.png\" title=\"image.png\" alt=\"image.png\"></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2119','2939','1','','http://www1.ycsyy.com/upload/images/2019/10/4c7c5ae136571c6c.png','春雷阵阵、阴雨绵绵。3月20日，我院100名党员干部来到射阳县千秋镇卫生院、三区居委会、滨东村、推广村开展第一季度精准扶贫、义诊讲学活动。在千秋镇卫生院，我院急诊科医护人员为200多名群众讲授急救知识，分发健康宣教图册。心内科鞠云枫主任、消化科王建华主任、CT','192.168.162.110','192.168.162.110','1553097600','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">春雷阵阵、阴雨绵绵。3月20日，我院100名党员干部来到射阳县千秋镇卫生院、三区居委会、滨东村、推广村开展第一季度精准扶贫、义诊讲学活动。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">在千秋镇卫生院，我院急诊科医护人员为200多名群众讲授急救知识，分发健康宣教图册。心内科鞠云枫主任、消化科王建华主任、CT室卢定友主任先后为100多名当地患者问诊把脉。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">在千秋镇三区居委会、滨东村、推广村，每一位党员干部与结对帮扶对象手牵手、心连心。20000元爱心款全部精准发放到每一位扶贫对象手中。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">我院全体党员干部始终以习近平新时代中国特色社会主义思想为指引，牢固树立“四个意识”，坚定“四个自信”，践行“两个维护”，全面落实“四个全面”战略部署，深入推进精准扶贫工作。医院人牢记党的宗旨，坚定理想信念，勇于创新、开拓进取，努力在自己的岗位上不断创造新业绩，为建设高水平区域医疗中心和建设“强富美高”新盐城作出应有的贡献。</span></p><p style=\"text-indent: 2em; text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size:19px;font-family: 宋体\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/4c7c5ae136571c6c.png\" title=\"image.png\" alt=\"image.png\" width=\"553\" height=\"338\" style=\"width: 553px; height: 338px;\" /><img src=\"http://www1.ycsyy.com/upload/images/2019/10/6fde0e8bdc5b8bdc.png\" title=\"image.png\" alt=\"image.png\" /></span></span></p><p style=\"text-align: center; line-height: 3em;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/b18de29de2827561.png\" title=\"image.png\" alt=\"image.png\" /><img src=\"http://www1.ycsyy.com/upload/images/2019/10/b0b760c03693ea5f.png\" title=\"image.png\" alt=\"image.png\" /></p><p style=\"text-align: center; line-height: 3em;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/44f25acc55238f21.png\" title=\"image.png\" alt=\"image.png\" /><img src=\"http://www1.ycsyy.com/upload/images/2019/10/c379b3cbf62b34f1.png\" title=\"image.png\" alt=\"image.png\" width=\"545\" height=\"360\" style=\"width: 545px; height: 360px;\" /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2120','2940','1','','http://www1.ycsyy.com/upload/images/2019/10/5babd6a6db7da82.png','在第109个“三八”国际劳动妇女节来临之际，按照院党委要求，医院将开展系列庆祝活动，以党建带团建，开展多种形式的庆祝活动。3月8日上午，为感谢在我们身边无私奉献和辛勤付出的“城市美容师”——环卫工人们，我院开展了“为了姐妹们的健康与幸福”——关爱“城市美','192.168.162.110','192.168.162.110','1552060800','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">在第109个“三八”国际劳动妇女节来临之际，按照院党委要求，医院将开展系列庆祝活动，以党建带团建，开展多种形式的庆祝活动。3月8日上午，为感谢在我们身边无私奉献和辛勤付出的“城市美容师”——环卫工人们，我院开展了“为了姐妹们的健康与幸福”——关爱“城市美容师”公益活动，定向为盐城市区100名环卫女工提供免费、专业的健康服务。</span></p>\r\n<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">在启动仪式上，院长查文章介绍了医院近年来的发展概况、南区近期建设情况和公益性活动的开展情况。多年来，我院始终坚持公立医院的公益性，常年举办义诊送医、对口帮扶、结对共建等公益性活动，营造了人人讲公益、时时做公益、自觉参与公益的良好氛围。</span></p>\r\n<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">仪式结束后，我院妇产科和普通外科（乳腺外科）多名专家提供义诊、健康咨询等服务，并举办防治宫颈癌和乳腺癌等科普知识讲座。</span></p>\r\n<p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/5babd6a6db7da82.png\" title=\"image.png\" alt=\"image.png\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/eccf4e867d2b4e87.png\" title=\"image.png\" alt=\"image.png\"></p>\r\n<p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/e7f2ca1fab359377.png\" title=\"image.png\" alt=\"image.png\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/3eebe4bdbfa50d90.png\" title=\"image.png\" alt=\"image.png\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/33e758b55f37176b.png\" title=\"image.png\" alt=\"image.png\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/539f1c676d7331ec.png\" title=\"image.png\" alt=\"image.png\"></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2121','2941','1','','http://www1.ycsyy.com/upload/images/2019/10/a63cfada8800e76e.png','为了进一步践行社会主义核心价值观，弘扬雷锋精神，做好党建带团建工作。3月5日上午，我院在南北两区分别开展了学雷锋志愿服务活动，组织来自普外科、内分泌科、肿瘤科、妇产科专家为患者提供免费健康咨询，免费测量血压和血糖。下午与越河路社区联合开展了为社区居民义','192.168.162.110','192.168.162.110','1551801600','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为了进一步践行社会主义核心价值观，弘扬雷锋精神，做好党建带团建工作。3月5日上午，我院在南北两区分别开展了学雷锋志愿服务活动，组织来自普外科、内分泌科、肿瘤科、妇产科专家为患者提供免费健康咨询，免费测量血压和血糖。下午与越河路社区联合开展了为社区居民义诊的活动，我院神经内科的医生和护士来到越河路社区，为社区群众进行义诊和健康咨询，并免费测量血压。志愿者们还走进行动不便和困难户家中为他们上门服务，收到社区群众的称赞。我院志愿者们用实际行动践行了奉献有爱互助进步的志愿服务精神！</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size:14px;font-family:宋体\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/a63cfada8800e76e.png\" title=\"image.png\" alt=\"image.png\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/83cab6fb61ffd277.png\" title=\"image.png\" alt=\"image.png\" width=\"253\" height=\"190\" style=\"width: 253px; height: 190px;\"></span></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2122','2942','1','','http://www1.ycsyy.com/upload/images/2019/10/1e597edb77dc7297.png','2019年3月1日下午，在我院南区会议室召开2018年抓基层党建、党风廉政建设、意识形态工作责任制述职评议会议。此次会议是按照市卫健委党委关于基层党建、党风廉政建设、意识形态工作的要求，进一步推进全面从严洽党。院党委书记张慧娟主持并讲话，院党委委员、副院长李奇','192.168.162.110','192.168.162.110','1551456000','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年3月1日下午，在我院南区会议室召开2018年抓基层党建、党风廉政建设、意识形态工作责任制述职评议会议。此次会议是按照市卫健委党委关于基层党建、党风廉政建设、意识形态工作的要求，进一步推进全面从严洽党。院党委书记张慧娟主持并讲话，院党委委员、副院长李奇志，院党委委员、副院长李自强出席会议，11个支部的支部书记、支委和党建工作联络员参加会议。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">首先，由李奇志院长介绍本次会议的议程，一是各支部书记作述职发言；二是张慧娟书记讲话；三是进行民主评议。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其次，各党支部书记围绕2018年度抓基层党建、党风廉政建设、意识形态工作责任制的主要经验做法、存在的突出问题和下一步工作计划进行了述职发言。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">述职发言结束后，张书记指出，各支部书记坚持以习近平新时代中国特色社会主义思想和党的十九大精神为指引，认真贯彻上级党委和院党委的决策部署，管党治党意识不断增强，基层党建工作不断提高。大家在述职中本着实事求是的原则，立足工作实际查找问题，针对自我剖析问题，提出下一步整改措施。但面上还存在一些普遍性问题，如责任落实还存在上热下冷、层层递减情况；各支部之间党建工作不太平衡；思想舆论引导还存在薄弱环节等。我院各党支部和广大党员干部必须始终以习近平新时代中国特色社会主义思想为指导，以党的政治建设为统领，牢固树立“四个意识”，自觉践行“两个维护”，更加有力有效地将党要管的党、全面从严治党落到实处，用过硬的党建引领医院改革发展稳定各项工作，加快推进健康盐城建设，为实现高水平建设区域医疗中心走在苏北苏中前列，提供坚强政治保证。</span></p><p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/10/1e597edb77dc7297.png\" title=\"image.png\" alt=\"image.png\" /><img src=\"http://www1.ycsyy.com/upload/images/2019/10/cf25f11ee1e47193.png\" title=\"image.png\" alt=\"image.png\" /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2123','2943','1','','http://www1.ycsyy.com/upload/images/2021/5/9a8129bd82451db.png','为促进医院行风建设，充分发挥社会各界的监督作用，广泛听取意见和建议，进一步改善医疗服务水平，2021年4月28日下午，我院在主院区四楼会议室召开社会行风监督员座谈会。市委宣传部副部长、市网信办主任董光威、市民族宗教事务局局长黄斌、盐阜大众报社副总编陆应铸等1','192.168.162.110','192.168.162.110','1619625600','<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为促进医院行风建设，充分发挥社会各界的监督作用，广泛听取意见和建议，进一步改善医疗服务水平，2021年4月28日下午，我院在主院区四楼会议室召开社会行风监督员座谈会。市委宣传部副部长、市网信办主任董光威、市民族宗教事务局局长黄斌、盐阜大众报社副总编陆应铸等15位行风监督员应邀出席。院长邓义军同志及相关职能处室负责人参会。会议由院纪委书记卞学梅主持。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www1.ycsyy.com/upload/images/2021/5/9a8129bd82451db.png\" title=\"777.png\" alt=\"777.png\" /></span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">卞学梅向行风监督员介绍了我院近几年行风建设工作情况。她指出，近年来，我院党政组织进一步强化责任担当，不断建立健全行风管理组织，努力加强医德医风教育，着力提高医务人员思想道德素质，狠抓行业突出问题的整治，营造风清气正行业环境，患者的满意度不断提升，在行风建设方面取得了一定的成效。同时，我院在行风建设方面依然存在不少问题。在今后的工作中，我们将以习近平新时期中国特色社会主义思想为指导，常抓不懈，在提升服务质量，营造良好行风方面拓展思路，扎实推进，为人民群众提供优质的医疗服务。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">与会的行风监督员对我院近年来行风建设工作取得的成效予以充分肯定。他们认为，领导重视，措施有力，市一院的行业风气较前发生了较大的变化。同时对医院进一步加强行风建设提出了宝贵的意见建议。一是提高医务人员专业技术能力，多换位思考，办人民满意医院；二是优化就诊流程，提供更加优质的服务；三是加强先进典型培树、新技术新项目等宣传报道，不断加强医院品牌建设，进一步提升医院在盐阜人民心中的美誉度。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2124','2944','1','','','为推动省、市卫健委“自省自警、拒腐防变”警示教育向深走实，结合医院“5·10”警示教育系列活动的有关要求，促进青年医务人员增强廉洁自律意识，牢固树立“不想腐”的思想自觉，强化青年医务人员“知敬畏、严纪律、尽职责”的廉洁从业观念，6月5日，盐城市一院召开青','192.168.162.110','192.168.162.110','1559664000','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为推动省、市卫健委“自省自警、拒腐防变”警示教育向深走实，结合医院“5·10”警示教育系列活动的有关要求，促进青年医务人员增强廉洁自律意识，牢固树立“不想腐”的思想自觉，强化青年医务人员“知敬畏、严纪律、尽职责”的廉洁从业观念，6月5日，盐城市一院召开青年医务人员廉洁从业教育专题会议，医院500余名青年医务人员参加了会议。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会议由盐城市一院监察室主任殷葵主持。会前，全体参会人员在“不忘初心、牢记使命、廉洁行医、优质服务”为主题的承诺签名墙上郑重签名，彰显了医院青年医务工作者为人民健康服务的责任与担当。会上，与会人员观看了2018年度国家“最美医生”先进事迹宣传片、医疗卫生领域系列腐败案件《见利忘义 泯灭良心》警示教育片等。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市一院纪委书记卞学梅提工作要求，要求青年医务人员要加强品德修为，争做人民满意的好医生。一是志存高远，砥励前行，为人民的健康卫生事业建功立业；二是不忘初心，牢记使命，始终把人民的利益放在第一位；三是追求进步，政治坚定，在复杂的环境中不迷失方向；四是严以律己，遵纪守法，做一个清清白白、堂堂正正的好人。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2125','2945','1','','','为进一步加强医院行风建设工作，近日，市一院药学部组织召开行风建设专题工作会议，开展党风廉政建设行风建设形势教育，明确工作要求。会议分析了近年来医疗卫生系统党风廉政建设行风建设的形势，结合医院工作实际，向与会人员提出了具体要求。要求所有同志认识到党风廉','192.168.162.110','192.168.162.110','1558972800','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为进一步加强医院行风建设工作，近日，市一院药学部组织召开行风建设专题工作会议，开展党风廉政建设行风建设形势教育，明确工作要求。</span><br /></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会议分析了近年来医疗卫生系统党风廉政建设行风建设的形势，结合医院工作实际，向与会人员提出了具体要求。要求所有同志认识到党风廉政建设行风建设的新形势、新要求，认识到自己在全面从严治党中所应当负起的责任。要认真践行“四种形态”，充分运用“第一种形态”，在“红脸出汗、咬耳扯袖”中得到警醒，时刻提醒自己，在严格自律的同时，管好部下，带好团队。要把握好和供应商的交往尺度，建立健康的关系，做到“亲”“清”适度，严格管理、热情服务，一清二白，不利用职务之便牟取不当利益，为不正当竞争提供方便。要认真学习法律法规和规章制度，要健全规章制度，规范权力使用，自觉接受监督。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">今年4到5月份期间，市一院根据市纪委、监委的部署安排，集中开展“讲医德、守法纪、颂清廉”为主题的“510”警示教育活动。本次药学部行风建设活动是医院开展重点部门“510”系列警示教育活动的重要内容之一，通过活动的开展，努力营造“不敢腐、不想腐”的思想氛围，促进党员干部知敬畏、存戒惧、守底线，廉洁行医，诚信服务，为健康盐城建设作出积极的贡献。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2126','2946','1','','','为进一步推进医院党建和党风廉政建设工作向纵深发展，4月23日，市一院组织召开了2019年医院党建暨党风廉政建设工作会议。院领导班子全体成员、党支部书记、中层干部、护士长、各诊疗组主诊、副主诊医师以及医技、行政后勤科室班组长等320人出席了会议。市卫生健康委员会','192.168.162.110','192.168.162.110','1555948800','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为进一步推进医院党建和党风廉政建设工作向纵深发展，4月23日，市一院组织召开了2019年医院党建暨党风廉政建设工作会议。院领导班子全体成员、党支部书记、中层干部、护士长、各诊疗组主诊、副主诊医师以及医技、行政后勤科室班组长等320人出席了会议。市卫生健康委员会党委书记、主任徐杰、市纪委监委第十五派驻纪检监察组组长陈友根到场指导，院长查文章主持会议。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会上，党委副书记朱向军同志和纪委书记卞学梅同志分别总结了2018年医院党建和党风廉政建设工作，对2019年的工作任务进行了安排部署。医院党委书记张慧娟同志与班子成员、各党支部书记、科室负责人分别签订了《201年党建暨党风廉政建设责任书》《意识形态工作责任书》和科室《廉政建设行风建设责任书》。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">院党委书记张慧娟同志指出，抓好医院党建和党风廉政建设工作，既是上级党组织的任务，也是保证医院健康快速发展的客观要求。作为全市最大的公立医院、盐城首家三等甲级综合性医院，不仅要在业务工作走在全市的前列，党建和党风廉政建设工作也要走在全市的前列。党委领导集体要认真履行主体责任，切实加强党的基层组织建设，加强干部队伍和人才队伍建设，加强精神文明建设、意识形态、统战和群团工作，不断加强党风廉政建设和与医德医风建设。要认真落实全省基层党建“五聚焦五落实”三年行动计划，以党建和党风廉政建设工作的高质量引领医院发展的高质量，用优异的成绩向祖国70华诞献礼。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会上，市第十五派驻纪检监察组组长陈友根同志分析了卫健系统党风廉政工作的新形势，介绍了无锡市人民医院智慧监管经验，要求一院广大干部职工提高政治站位，切实落实好党风廉政建设主体责任，运用信息化手段，提高监督成效，全力推动重点难点问题整改到位，以党风廉政建设的成效为医院各项事业的发展提供政治保障。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">市卫生健康委员会党委书记、主任徐杰同志肯定了市一院的党建和党风廉政建设工作。他要求市一院党委将全面从严治党管党的要求落实在医院工作的各个环节，注重标本兼治，强化对省纪委专责监督意见、省委对盐城巡视反馈意见及市委对一院延伸巡察发现问题的整改，完善制度、强化教育，一体推进“不敢腐、不能腐、不想腐”，促进医院各项工作不断取得新成效。（殷葵罗浩）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2127','2947','1','','','2019年3月6日，一季度住院患者工休会座谈会在南区门诊四楼会议室召开。住院患者（家属）代表及护理部、医务处、总务处、财务处、药学部、医保办等职能科室负责人参加了会议。首先，医院行风办负责人向与会人员介绍工休座谈会目的及医院目前情况。在两区深度融合发展情况','192.168.162.110','192.168.162.110','1551801600','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年3月6日，一季度住院患者工休会座谈会在南区门诊四楼会议室召开。住院患者（家属）代表及护理部、医务处、总务处、财务处、药学部、医保办等职能科室负责人参加了会议。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">首先，医院行风办负责人向与会人员介绍工休座谈会目的及医院目前情况。在两区深度融合发展情况下，医院将创新完善管理体系，着力打造市一院优质服务品牌。恳请大家根据住院就诊感受谏言献策，不断改进医院管理中存在的问题。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">与会患者就住院期间的切身感受纷纷发表自己意见，对医院好的地方给与肯定和赞扬，尤其医生和护士服务态度上。同时对存在不足的地方提出中肯的建议和意见，在场职能科室负责人现场解释，部分不能立刻解决的行风办梳理汇总，协调相关部门尽快落实整改并做好患者的反馈工作。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">最后行风办负责人感谢各位患者（家属）对医院的肯定与表扬，并表示与会患者提出的建议很宝贵，这也正是两区资源整合过程中我们后勤保障不足之处，会后将以问题为导向，改善患者就医感受，让人民群众对医院更满意。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2128','2948','1','','','1月3日下午，市一院召开改善医疗服务工作推进会，号召全院上下为进一步加强行业作风建设，提高医疗服务水平。在2017年省卫生计生委第三方机构患者满意度调查中，市一院综合满意度较前几年有明显提高，但在服务态度、膳食服务、医疗费用等方面仍有改善的空间。会议通报了','192.168.162.110','192.168.162.110','1546444800','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1月3日下午，市一院召开改善医疗服务工作推进会，号召全院上下为进一步加强行业作风建设，提高医疗服务水平。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">在2017年省卫生计生委第三方机构患者满意度调查中，市一院综合满意度较前几年有明显提高，但在服务态度、膳食服务、医疗费用等方面仍有改善的空间。会议通报了去年第三方机构出院患者满意度调查、服务性投诉情况以及上级部门明查暗访结果，对各科室、部门医疗服务中的存在问题进行了深入分析，深刻剖析医院在服务态度、医患沟通、规范服务、岗位纪律等方面的存在问题。要求全院上下重视患者投诉，妥善处理投诉，争取患者“二次满意”。同时把患者投诉作为查找自身存在问题，进行持续改进的动力。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会议指出，改善医疗服务行动的目标，是在群众安全、有效、便捷、明白就医上进行提升和改善。作为盐城市首家三级甲等综合性医院，一定要以习近平新时代中国特色社会主义思想为指导，深入贯彻党的十九大精神，坚持以病人为中心，围绕满足新时代人民群众对医疗卫生服务的新要求，切实改进工作作风，创新医疗服务模式，不断提升患者满意度，使人民群众的获得感、幸福感、安全感更加充实、更有保障、更可持续。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会议强调，各科室、部门要加强组织领导，落实工作责任。在抓科室管理、学科建设、业务发展的同时，认真履行“一岗双责”，重视医务人员的教育、培训和管理，践行服务承诺，坚决杜绝“生、硬、冷、顶、推”现象，营造优质服务的良好氛围。要坚持问题导向，狠抓持续改进。以国家卫生计生委考核标准为依据，进一步认真梳理排查存在问题，调整并明确今年的工作目标、任务和时序进度，加强多部门协作，明确具体承办人，逐一落实服务要求。要加强监督检查，实行责任追究。职能部门根据职能分工，经常性的督查分管范围内服务要求的落实情况。对于存在问题的科室和部门，要帮助科室查找原因，促进整改。对于有明确责任的问题，要追究责任到人，不能“和稀泥”、“和牌”。对于整改不力、同一问题反复发生的科室，不但要追究当事人的责任，还要上查一级，追究科室负责人的管理责任。各行政职能部门要改进工作作风，深入临床一线，及时发现问题、解决问题、服务临床，持续改进医疗服务，改善患者就医体验，为人民群众提供满意、放心的医疗服务，树立人民医院的良好形象。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2129','2949','1','','','为了进一步深化医院党风廉政建设和行风建设，近日，盐城市一院组织召开了行风建设工作会议，就纠正医药购销和医疗服务中的不正之风，推进医院行业作风建设工作进行布置，重申廉洁行医、诚信服务的要求。院领导班子成员、中层干部、护士长以及全院各诊疗组主诊医师近300','192.168.162.110','192.168.162.110','1545926400','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为了进一步深化医院党风廉政建设和行风建设，近日，盐城市一院组织召开了行风建设工作会议，就纠正医药购销和医疗服务中的不正之风，推进医院行业作风建设工作进行布置，重申廉洁行医、诚信服务的要求。院领导班子成员、中层干部、护士长以及全院各诊疗组主诊医师近300人出席了会议。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会上，纪委书记卞学梅同志向与会人员通报了12月24日央视《新闻30分》播出的《高回扣下的高价药》以及12月25日央视《焦点访谈》播出的《向高价医疗耗材说不》记者调查情况，再次通报了近几年来医疗卫生领域医务人员收受“回扣”的典型案例。对于《刑法修正案》（六）、（九），最高人民法院、最高人民检察院《关于办理贿赂刑事案件适用法律若干问题的意见》，国家卫生计生委《加强医疗卫生行风建设“九不准”》等国家法律法规，进行了详细的解读。卞书记对近年来医院在行风建设中存在的问题进行了通报和剖析，她指出，医院的行风建设工作始终是人民群众关注的焦点和热点问题，管行业必须管行风。要认真落实党风廉政建设责任制要求，坚持“管行业必须管行风”和“谁主管谁负责”的原则 ，落实“一岗双责”，努力形成预防腐败的长效管理机制。要坚持依法治理和以德治理相结合，加强反腐倡廉教育，努力内化医务人员“不想腐”的行动自觉。要坚持问题导向，集中开展整治活动。通过加强环节管理，坚决不给商业贿赂行为留有活动空间。纪检监察部门要加强监督执纪问责，重申纪律规定，加强监督检查，发现问题于早于小，采取有效措施，坚决遏制行业不正之风的蔓延。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">最后，院党委书记李大林同志提出了四点要求，一是对当前反腐的形势要有正确的认识。随着医改的不断深入，医保费用的控制会不断严格，政府对医院的监管、对医务人员的要求只会加强，不会减弱。二是在反商业贿赂的问题上要有正确的态度，不能有模糊的认识，更不能有法不责众和侥幸的心理。三是对目前的法律环境要有清醒的认识，要学法、懂法、知法、守法，要廉洁自律，规范从业行为。四是职能部门要扎紧制度的篱笆，完善管理机制，加强对医疗服务行为的监管。对于不听招呼、不知止、不收手的，要进行严肃的责任追究。如发生严重的行风问题，要实行“一案双查”，不仅要追究当事人的责任，还要追究管理部门及其负责人的责任。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2130','2950','1','','http://222.188.122.169/upload/images/2019/10/3d6090c71423c15e.jpeg','为认真落实全面从严治党要求，深入推进“三严三实”专题教育，进一步增强党员干部廉洁自律意识，筑牢拒腐防变的思想道德防线，11月24日下午，盐城市第一人民医院领导班子成员、党支部书记及重点岗位负责人30多人，前往市党风廉政教育中心，参观尚德昭廉教育馆，接受党风','192.168.162.110','192.168.162.110','1543161600','<p style=\"line-height: 3em; text-align: left; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">为认真落实全面从严治党要求，深入推进“三严三实”专题教育，进一步增强党员干部廉洁自律意识，筑牢拒腐防变的思想道德防线，11月24日下午，盐城市第一人民医院领导班子成员、党支部书记及重点岗位负责人30多人，前往市党风廉政教育中心，参观尚德昭廉教育馆，接受党风廉政专题教育。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">医院党委将参观教育活动作为“三严三实”专题教育的一项重要内容，认真组织，精心安排。院党委书记李大林同志亲自带队，明确要求领导班子成员以及医院所有涉及人、财、物、事管理的重点岗位负责人参加教育活动。大家通过参观图片展览、聆听工作人员讲解、观看反腐倡廉视频等形式，参观了教育馆的“勤廉楷模、德厚流光”、“前车之鉴、警钟长鸣”和“踏石留印、任重道远”三个展区。一个个鲜活的案例和一幅幅真实的画面，生动地展示了勤政为民、廉洁奉公的先进典型和贪污贿赂、腐化堕落的党员干部蜕化变质的轨迹，使党员干部受到一次深刻的思想教育。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">参观完市尚德昭廉教育馆，党员干部纷纷表态，要自觉加强党性修养，筑牢思想道德防线，以党章党纪为红线，自觉遵守《中国共产党廉洁自律准则》，认真对照《中国共产党纪律处分条例》开出的负面清单，明白哪些事能做，哪些事不能做。以先进典型为榜样，永葆政治本色；以反面案例为镜鉴，恪守清正廉洁，不触道德底线，不碰法律红线，做执行纪律、勤政廉政的模范。</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><img src=\"http://222.188.122.169/upload/images/2019/10/3d6090c71423c15e.jpeg\" width=\"600\" height=\"399\" alt=\"http://www.jsycjw.gov.cn/uploads/20151126.jpg\" border=\"0\" style=\"text-align: center; white-space: normal;\" /></span></p><p style=\"line-height: 3em; text-indent: 2em;\">　　</p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2131','2951','1','','','为贯彻落实市纪委派驻机构改革精神，推动主体责任和监督责任向基层延伸，10月16日下午，市纪委第十五派驻纪检组在盐城市第一人民医院召开了党风廉政建设调研工作会议，市一院领导班子全体成员和科级中层干部等出席，市卫计委党委书记、主任徐杰、市纪委第十五纪检组组长','192.168.162.110','192.168.162.110','1539619200','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为贯彻落实市纪委派驻机构改革精神，推动主体责任和监督责任向基层延伸，10月16日下午，市纪委第十五派驻纪检组在盐城市第一人民医院召开了党风廉政建设调研工作会议，市一院领导班子全体成员和科级中层干部等出席，市卫计委党委书记、主任徐杰、市纪委第十五纪检组组长陈友根在会上讲话。市一院党委书记、院长李大林汇报了医院党风廉政建设工作情况。</span><br /></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">市纪委第十五纪检组组长陈友根同志充分肯定了市一院的党风廉政建设工作，就下一步工作，提出四方面要求：一是认清新形势，适应新常态。派驻纪检组与驻在单位是监督与被监督的关系，大家要充分认清、主动适应新的形势，自觉接受监督。二是加强纪律教育，强化规矩意识。把加强思想政治建设放在首位，将“两学一做”学习教育常态化、制度化，认真组织学习十九大精神、学习党章及作风建设各项规定，强化纪律意识和规矩意识，提高思想自觉和行动自觉。三是落实两个责任，净化政治生态。各级领导干部要以上率下，坚持“谁主管，谁负责”，认真落实“两个责任”。加强党务院务公开，让权力在阳光下运行。纪检监察部门要聚焦主业，认真落实“三转”，严格监督执纪问责。四是加强作风建设，塑造良好形象。要坚持“管行业必须要管行风”，以抓铁留痕的决心抓作风建设，深入开展专项治理活动，对医药购销领域商业贿赂和收受“红包”行为“零容忍”，发现一起，查处一起，依法依规严肃处理，以一流的医疗水平，一流的医德医风，努力打造医院的新形象。　</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">市卫计委党委书记、主任徐杰就如何进一步加强医院的党风廉政建设，推进医院改革和发展，更好服务于市委中心工作和民生工程，结合市纪委的工作部署，提出要求。一是进一步强化“两个责任”，推动全面从严治党任务的落实。院党委要加强统一领导，各级领导干部要切实落实主体责任，层层传递压力，完善制度，落实责任，营造“不敢腐、不能腐、不想腐”的良好氛围。二是严肃党内政治生活，持续推进党风行风建设。严明党的政治纪律，严肃党内政治生活，严格执行民主集中制，认真落实“三会一课”制度，开展谈心谈话活动，坚持正确的用人导向。要转作风、正行风，纠建并举，建立长效机制，开展“红包”、回扣专项治理工作，巩固“远离回扣，廉洁从医”专项活动成果。三是注重廉洁教育，切实筑牢思想防线。强化党规党纪教育，加强职业道德建设。坚持抓早抓小，践行“四种形态”；加强廉洁心理建设，筑牢思想防线。认真落实党风廉政建设和反腐败工作要求，促进医院健康发展。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">最后，医院党委书记、院长李大林代表院党委表态，坚决服从派驻纪检组监督，自觉主动接受监督，支持纪检组工作，把党风廉政建设要求记在心上，落实在行动中，扎实推进医院的各项工作。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">调研活动中，第十五派驻纪检组向与会人员及职工代表发放了160份问卷，对医院党风廉政建设工作情况进行了现场调查。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2132','2952','1','','','2018年6月7日下午，市一院监察室、护理部在北区门诊八楼会议室组织召开了二季度工休座谈会。医院外科病区14名患者（家属）代表以及医务科、药剂科、财务科、总务科、医保办、物管办等行政职能科室负责人应邀出席了会议。会议由院监察室主任殷葵同志主持。她介绍，多年以','192.168.162.110','192.168.162.110','1528300800','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2018年6月7日下午，市一院监察室、护理部在北区门诊八楼会议室组织召开了二季度工休座谈会。医院外科病区14名患者（家属）代表以及医务科、药剂科、财务科、总务科、医保办、物管办等行政职能科室负责人应邀出席了会议。</span><br /></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会议由院监察室主任殷葵同志主持。她介绍，多年以来我院坚持通过多种途径，广泛收集社会群众的意见和建议。医院每季度、各病区每月召开一次工休会，通过面对面交流, 搭建了医患双方有效沟通的平台，及时了解患者诉求，查找整改存在问题，持续改进医疗服务，增进了医患之间的理解与信任。随着中国特色社会主义进入新时代，我国社会主要矛盾已经转化为人民日益增长的美好生活需要和不平衡不充分的发展之间的矛盾，患者对医疗质量、医疗服务等要求也不断的提高。因此，医院常性加强教育、监督和管理，不断改善软实力与硬实力，进而提高患者就医感受。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">患者及家属代表对医院召开工休会、征求意见、解决问题的做法给予充分的肯定，对医护人员精心的治疗、细致的护理和周到的服务给予充分的肯定和高度赞扬。与会人员还点名表扬了查文章、王跃琴、蔡为人、季丹、李倩、徐燕、王维、黄有伟、张龙慧、刘宁、项群、吴昊、陈华琴、朱敏、邓文华、杨威、夏莹莹、孙鹏飞、赵雯、严晓鸥、谷艳、柏珺、王四清、徐小卒、刘小兰、董晨欣、穆洪鑫、邵玉蓉、周广军、卞晓蓉、陈梦宁、钱苗苗、薛金玲、朱冬林等医务人员，</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">表扬他们待病人如亲人，态度和蔼可亲，服务耐心细致，关心无微不至。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">与会代表就医院、病区服务设施、医疗护理服务及后勤服务等方面工作提出了5条意见和建议。相关科室负责人对部分问题作了现场解答；对需要进一步调查、核实的问题，以及需多科室、部门协调解决的问题，监察室将在会后进行梳理汇总，下发书面通知到相关科室，要求整改并在一周内向建议人反馈。监察室将在近期对整改措施的落实情况进行回查，以促进存在问题的整改。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">最后，殷葵主任向各位患者（家属）代表诚恳致谢，座谈会在融洽、和谐的氛围中结束。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2133','2953','1','','','为进一步落实全面从严治党要求，总结回顾去年工作，部署2018年工作，市一院于4月26日召开2018年党风廉政建设工作会议。应邀参会的市纪委第十五派驻组组长陈友根对过去一年来市一院党委对派驻纪检组工作的支持和配合表示感谢，同时希望市一院党委与派驻纪检组加强协调、','192.168.162.110','192.168.162.110','1524672000','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为进一步落实全面从严治党要求，总结回顾去年工作，部署2018年工作，市一院于4月26日召开2018年党风廉政建设工作会议。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">应邀参会的市纪委第十五派驻组组长陈友根对过去一年来市一院党委对派驻纪检组工作的支持和配合表示感谢，同时希望市一院党委与派驻纪检组加强协调、增强联动、同向发力，积极推动该院党委主体责任和纪委监督责任落地生根，共同推进党风廉政建设和反腐败工作再展新气象、再作新作为和再出新成效。要求市一院强化担当意识，切实落实好党风廉政建设主体责任；敢于动真碰硬，求真务实地整改系统内存在的突出问题；加大问责力度，持续释放执纪监督越来越严的信号。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">市一院党委书记、院长李大林代表院党委作总结发言，指出2018年度医院各项工作已经全面展开，做好医院党建和党风廉政作风建设工作，压力大、任务重。全院要认清形势，主动作为，标本兼治，切实加强医院党风廉政建设行风建设工作。要不忘初心、砥砺奋进，强化责任担当，聚焦作风转变，奋力推进各项工作全面落实，不断开创党建和党风廉政建设工作的新局面。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会上，院主要领导、分管领导和各处（室）主要负责同志签订党风廉政建设责任书；市第三届医德之星、心内科博士、主任医师王志华，院监察室、行风办主任殷葵等作汇报发言。院党委班子全体成员，各党支部书记，中层干部，护士长，各科主诊、副主诊医师，医技、行政后勤科室班组长等出席会议。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2134','2954','1','','','为了进一步加强医院行风建设，深化“生命所系、健康相托”优质服务品牌创建活动，市一院于2017年09月27日在北区门诊八楼会议室召开了三季度住院患者工休座谈会。应邀出席会议的有外科片住院病员（家属）代表及院护理部、医务科、总务科、财务科、药剂科、医保办、物管办','192.168.162.110','192.168.162.110','1506441600','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为了进一步加强医院行风建设，深化“生命所系、健康相托”优质服务品牌创建活动，市一院于2017年09月27日在北区门诊八楼会议室召开了三季度住院患者工休座谈会。应邀出席会议的有外科片住院病员（家属）代表及院护理部、医务科、总务科、财务科、药剂科、医保办、物管办等职能科室负责人。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会议由行风办殷葵主任主持。殷主任强调，随着人民生活水平的不断提高，健康意识及维权意识的不断增强，群众就医时不仅注重医院的硬件设施，对医疗服务有了更高的要求。而优质医疗资源的不足，患者对医疗服务的期待值不断提高，加强一些社会因素的影响，致使医患矛盾频发。为了进一步满足人民群众的就医需求，提高患者对医院服务的满意度，我院着力加强行风建设，多途径、广泛征求社会群众对医院工作的意见和建议，持续改进医疗服务。召开工休会，是为了更好地建立医患沟通的桥梁，通过面对面的交流，倾听患者心声，查找医疗服务中的存在问题，不断改进工作，更好地服务群众。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会议上，各与会代表踊跃发言，对医院近几年在党风廉政建设行风服务方面工作给予了肯定，同时，对医院的工作提出了意见和建议。与会的患者、家属点名表扬了普外科三病区卞晓蓉、心胸外科病区戴萍、神经外科病区季丹、丁瑶，骨科三病区马永平、刘红兵、穆洪鑫、邓璐、周建，疼痛科病区薛祥云、武晶晶，泌尿外科病区瞿平、王华、张龙慧等医护人员及普外科二病区、妇产科病区、骨科二病区、儿科一病区等科室。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">最后，殷主任感谢各位病员（家属）对医院的关心、支持，欢迎大家多给医院提意见建议。她表示，医院将继续聆听来自病员的心声，从细节入手，改善医疗环境，提高服务水平，努力让群众看病更放心、更满意。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2135','2955','1','','http://www1.ycsyy.com/upload/images/2019/12/8be8b0e57704b8d6.png','为认真贯彻落实全面从严治党要求，营造医院风清气正的政治生态，2017年6月22日下午，盐城市一院组织召开2017年党风廉政建设工作会议，院党委全体成员，各党支部书记，中层干部，护士长，各诊疗组主诊、副主诊医师，医技、行政后勤科室班组长等310人出席会议。医院党委副','192.168.162.110','192.168.162.110','1498233600','<p style=\"margin: 5px 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为认真贯彻落实全面从严治党要求，营造医院风清气正的政治生态，2017年6月22日下午，盐城市一院组织召开2017年党风廉政建设工作会议，院党委全体成员，各党支部书记，中层干部，护士长，各诊疗组主诊、副主诊医师，医技、行政后勤科室班组长等310人出席会议。医院党委副书记、常务副院长查文章主持会议。</span></p><p style=\"margin: 5px 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">院纪委书记卞学梅代表院纪委对2016年党风廉政建设工作进行了回顾总结，对2017年工作进行了详细部署。她指出，党风廉政建设工作永远在路上，医院党风廉政建设面临严峻的形势，医药购销领域商业贿赂的风险依然存在，不敢腐、不能腐、不想腐的体制机制建设还需要进一步健全。2017年，院纪委将从严肃党内生活，营造良好的政治生态；保持政治定力，全面履行“两个责任”；加强制度建设，规范权力运行，防控廉洁风险；认真落实中央八项规定精神，持之以恒地抓好作风建设；扎实开展主题活动，使“远离回扣、廉洁从医”成为常态；不断提升医疗服务水平，着力从加强行风建设等方面入手，全面推进医院党风廉政建设工作，为医院的持续、快速、健康、稳定发展奠定良好的政治基础。</span></p><p style=\"margin: 5px 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会上，医院领导班子成员、各党支部书记及科室负责人分别向党委书记李大林同志递交党风廉政建设责任书。</span></p><p style=\"margin: 5px 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">李大林书记在代表院党委的讲话中指出，从严治党、高压反腐、改进作风绝不是一阵风，党风廉政建设永远在路上，将越来越严，越来越落实到具体工作措施上。本次会议是在全省卫生计生系统开展“远离回扣、廉洁从医”主题专项活动的背景下召开的。各级领导干部向院党委递交党风廉政责任书有其深刻的内涵。当前形势下，对于党风廉政建设方面出的问题，都要进行“一案双查”。因此，大家要在思想上高度重视，认真履行“一岗双责”，切实负起责任。</span></p><p style=\"margin: 5px 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2017年，医院党风廉政建设工作将从切实强化“两个责任”，推动全面从严治党任务落实；切实强化党内政治生活监督检查，持续推进党风、作风、行风建设；切实强化监督执纪问责，加大违纪案件查处力度；打铁还需自身硬，着力打造忠诚干净担当的纪检监察队伍等方面入手，以扎实的作风、创新的精神、有力的举措，不断开创医院反腐倡廉建设的新局面。</span></p><p style=\"margin: 5px 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">院党委要求，要通过开展以“远离回扣、廉洁从医”为主题的专项活动，认真贯彻落实省市卫生计生委、检察院的工作要求，认真组织开展自查自纠活动，对全体医务人员开展职业道德、法律法规教育；以案释法，开展警示教育，促进党员干部、医务人员知敬畏、存戒惧、守底线，廉洁从医，诚信服务。要注重整治效果、社会效果和法律效果的有机统一，通过活动的开展，确保医院的稳定和发展，确保广大人民群众能得到安全和稳定的医疗卫生服务。</span></p><p style=\"margin: 5px 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会后，播放了警示教育片《折翅的天使》。片中以2016年江苏省医疗卫生界的贪腐实例，深刻揭露了我省少数医疗机构的工作人员非法收受回扣、为医药代表统方，滥用职权谋取利益等腐败现象。片中还通过对“对大家都拿，不拿白不拿，法不责众”、“将回扣放在科研项目里，以为单位受贿不算罪”以及“以旅游、借钱取得高利息等方式变相得利，以为换个马甲不算受贿”等三个认识误区，分析受贿者自我遮蔽的侥幸心理。犯罪同行的现身说法，给大家强烈的视觉与思想冲击。与会人员表示，要从中汲取深刻教训，认清“罪与非罪”，做政治上的明白人，知法、懂法、守法。要加强道德修养，恪守职业道德，洁身自好，廉洁行医，坚决抵制医药购销和卫生医疗服务领域中的不正之风，用自己的实际行动，维护白衣天使的圣洁形象。</span></p><p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/12/8be8b0e57704b8d6.png\" title=\"image.png\" alt=\"image.png\" /></p>','','','','','');
