INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2518','3342','1','','','盐城市第一人民医院为满足临床使用需要，更好地为患者提供医疗服务，经医院研究同意，拟对医院《出生医学证明》档案整理服务项目进行招标，具体要求以招标文件为准。欢迎符合资格要求的供应商前来报名参与。 一、招标内容 《出生医学证明》档案整理服务项目，具体情况','172.16.1.2','172.16.1.2','1599753600','<p>&nbsp;&nbsp;<span style=\"font-size: 18px;\"> 盐城市第一人民医院为满足临床使用需要，更好地为患者提供医疗服务，经医院研究同意，拟对医院《出生医学证明》档案整理服务项目进行招标，具体要求以招标文件为准。欢迎符合资格要求的供应商前来报名参与。<br />&nbsp;&nbsp;&nbsp; 一、招标内容<br />&nbsp;&nbsp;&nbsp; 《出生医学证明》档案整理服务项目，具体情况详见“项目需求”等招标人要求。<br />&nbsp;&nbsp;&nbsp; 二、资质要求<br />&nbsp;&nbsp;&nbsp; 报名单位必须具备《中华人民共和国政府采购法》第22条所规定的条件外，还须具备如下条件：<br />&nbsp;&nbsp;&nbsp; 1、在国内市场监督管理部门注册，具有独立的法人资格且经营范围内容有档案服务或档案信息化或档案数字化内容（以营业执照或市场监督管理部门备案材料为准）；<br />&nbsp;&nbsp;&nbsp; 2、投标人至少四名项目成员具有档案资格上岗证（提供近半年社保证明，参与该项目人员需与投标文件提供人员保持一致）；<br />&nbsp;&nbsp;&nbsp; 3、投标人具有综合档案馆或同类项目案例；<br />&nbsp;&nbsp;&nbsp; 4、参加政府采购活动前三年内，没有重大违法记录；<br />&nbsp;&nbsp;&nbsp; 5、本项目不接受联合体投标。<br />&nbsp;&nbsp;&nbsp; 三、报名时间<br />&nbsp;&nbsp;&nbsp; 2020年9月11日至2020年9月16日；<br />&nbsp;&nbsp;&nbsp; 北京时间：上午08:30 — 11:30，下午14:30 — 18:00。<br />&nbsp;&nbsp;&nbsp; 四、报名地点<br />&nbsp;&nbsp;&nbsp; 盐城市青年西路8号810室（江苏仁禾中衡工程咨询房地产估价有限公司）。<br />&nbsp;&nbsp;&nbsp; 五、报名需提交材料<br />&nbsp;&nbsp;&nbsp; 1、企业营业执照(副本)的复印件；<br />&nbsp;&nbsp;&nbsp; 2、投标人提供至少四名项目成员档案资格上岗证及近半年社保证明（参与该项目人员需与投标文件提供人员保持一致）；<br />&nbsp;&nbsp;&nbsp; 3、投标人提供综合档案馆或同类项目案例资料，如合同复印件等；<br />&nbsp;&nbsp;&nbsp; 4、法定代表人直接参与报名的，需提供法定代表人身份证原件，同时提供复印件；委托代理人参与报名的，需提供法定代表人授权委托书、委托代理人身份证原件，&nbsp;&nbsp; 同时提供法定代表人以及委托代理人身份证复印件；<br />&nbsp;&nbsp;&nbsp; 5、提供信用查询记录（登录信用中国：www.creditchina.gov.cn，查询信用记录并截图）；<br />&nbsp;&nbsp;&nbsp; 6、封面注明投标公司名称、投标人电话、邮箱。<br />&nbsp;&nbsp;&nbsp; 注：以上报名材料请加盖投标单位红章、按顺序装订成册（概不退还）。<br />&nbsp;&nbsp;&nbsp; 六、招标文件的获取<br />&nbsp;&nbsp;&nbsp; 在报名后的3个工作日内，将招标文件通过邮件发送给投标报名人，注意查收（不另行电话通知），报名单位根据其做好标书等待开标。<br />&nbsp;&nbsp;&nbsp; 七、开标时间、地点：另行通知。<br />&nbsp;&nbsp;&nbsp; 八、联系方式<br />&nbsp;&nbsp;&nbsp; 联系科室：盐城市第一人民医院主院区（南区）门诊4楼招标采购中心<br />&nbsp;&nbsp;&nbsp; 联系人：张老师<br />&nbsp;&nbsp;&nbsp; 联系电话：0515-66696855<br />&nbsp;&nbsp;&nbsp; 联系地址：盐城市人民南路66号<br />&nbsp;&nbsp;&nbsp; 邮编：224000<br /><br />&nbsp;&nbsp;&nbsp; 招标代理公司：江苏仁禾中衡工程咨询房地产估价有限公司<br />&nbsp;&nbsp;&nbsp; 联系人：胡女士<br />&nbsp;&nbsp;&nbsp; 联系电话：13815581495<br />&nbsp;&nbsp;&nbsp; 联系地址：盐城市青年西路8号810室<br />&nbsp;&nbsp; 邮编：224006<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; 盐城市第一人民医院<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2020年9月11日<br /></span><br /></p>','','','','','');
