INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2539','3363','1','','','盐城市第一人民医院五官科医院改造工程设计服务项目已具备招标条件，拟对该项目进行公开招标，择优选择设计单位。1、项目概况1.1项目名称：盐城市第一人民医院五官科医院改造工程设计服务项目；1.2建设地点：盐城市第一人民医院五官科医院；1.3工程规模：盐城市第一人民','172.16.1.2','172.16.1.2','1594137600','<p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">盐城市第一人民医院五官科医院改造工程设计服务项目已具备招标条件，拟对该项目进行公开招标，择优选择设计单位。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1、项目概况</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1.1项目名称：盐城市第一人民医院五官科医院改造工程设计服务项目；</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1.2建设地点：盐城市第一人民医院五官科医院；</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1.3工程规模：盐城市第一人民医院五官科医院门诊大厅中庭、2-6层及检验科实验室升级改造，改造总面积约2600平方米，详见设计任务书。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1.4招标内容：盐城市第一人民医院五官科医院改造工程设计服务，包含但不限于方案设计、施工图设计以及施工和保修等阶段设计咨询服务等，具体以招标人的书面通知为准。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1.5质量标准：符合国家、省及市关于施工图设计深度的规范要求，并满足《建筑工程设计文件编制深度规定(2016年版)》，最终设计成果须确保通过相关部门审查。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1.6设计时限：满足招标人要求。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">2、投标人资格要求</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">2.1投标人具备独立企业法人资格；</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">2.2投标人须是具有建设主管部门核发的建筑装饰工程设计专项乙级及以上或工程设计综合甲级资质的独立法人；</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">2.3项目负责人须是本单位在职职工，并具有工程类高级职称。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">2.4本次招标不接受联合体投标。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">3、投标报名</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话及邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名，投标报名时间2020年7月8日8时至2020年7月13日18时。联系人：胡荣艳，0515-68037830；手机：13815581495。逾期不再接收报名。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">4、本项目采用资格后审,综合评估法。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">5、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">6、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">具体内容详见招标文件。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"margin-bottom: 0px; text-indent: 306px; line-height: 33px; background: white; text-align: right;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">招标人：盐城市第一人民医院</span></p><p style=\"margin-bottom: 0px; text-indent: 342px; line-height: 33px; background: white; text-align: right;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">时间：2020年7月8日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2540','3364','1','','','为了确保医院医用设备的安全运行，按照“公平，公正，公开”的原则，现我院对部分UPS电源购买维保等项目，进行市场调研，欢迎有资质的公司前来报名，现将有关事项公告如下：一、本次调研采购目录1、UPS电源购买维保（含电池组）主要机型及数量：主院区UPS-5KVA-120KAV','172.16.1.2','172.16.1.2','1594137600','<p style=\"text-indent:28px\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">为了确保医院医用设备的安全运行，按照“公平，公正，公开”的原则，现我院对部分UPS电源购买维保等项目，进行市场调研，欢迎有资质的公司前来报名，现将有关事项公告如下：</span></p><p><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">一、本次调研采购目录</span></p><p><span style=\"font-size: 16px; font-family: 宋体, SimSun;\">1<span style=\"font-size: 16px;\">、</span>UPS<span style=\"font-size: 16px;\">电源购买维保（含电池组）</span></span></p><p style=\"text-indent:17px\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\"><span style=\"font-family: 宋体, SimSun;\">主要机型及数量：主院区&nbsp;&nbsp;</span>UPS-5KVA-120KAV&nbsp; &nbsp; &nbsp; 68<span style=\"font-family: 宋体, SimSun;\">台</span></span></p><p style=\"text-indent:17px\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;EPS-FEPS-RNED-2.5-KVA&nbsp; 7<span style=\"font-size: 14px; font-family: 微软雅黑, sans-serif;\">台</span></span></p><p style=\"text-indent:17px\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;北 区&nbsp; UPS-5KVA-20KAV&nbsp; &nbsp; &nbsp; &nbsp;12<span style=\"font-size: 16px; font-family: 微软雅黑, sans-serif;\">台</span></span></p><p style=\"text-indent:101px\"><span style=\"font-size: 16px; font-family: 宋体, SimSun;\"><span style=\"font-size: 16px;\">注：其中有</span>39<span style=\"font-size: 16px;\">台设备中</span>56<span style=\"font-size: 16px;\">节电池组需要立即更换</span></span></p><p><span style=\"font-size: 16px; font-family: 宋体, SimSun;\">2<span style=\"font-size: 16px;\">、住院医师规范化培训管理系统</span>&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">3<span style=\"font-family: 宋体, SimSun;\">、全自动封口机</span></span></p><p><span style=\"font-size: 16px; font-family: 宋体, SimSun;\">4、手持式氧气吸入器检测仪</span></p><p><span style=\"font-size: 16px; font-family: 宋体, SimSun;\">5、生物阅读器</span></p><p><span style=\"font-size: 16px; font-family: 宋体, SimSun;\">二、供应商资格要求</span></p><p><span style=\"color: rgb(51, 51, 51); font-size: 16px; font-family: 宋体, SimSun;\">1、具备独立法人的资格</span></p><p><span style=\"color: rgb(51, 51, 51); font-size: 16px; font-family: 宋体, SimSun;\">2、具有良好的商业信誉</span></p><p><span style=\"color: rgb(51, 51, 51); font-size: 16px; font-family: 宋体, SimSun;\">3、具有独立承担民事责任的能力</span></p><p><span style=\"color: rgb(51, 51, 51); font-size: 16px; font-family: 宋体, SimSun;\">4、须符合信息化政府采购资质</span></p><p><span style=\"color: rgb(51, 51, 51); font-size: 16px; font-family: 宋体, SimSun;\">5、具有履行合同所必需的设备和专业技术能力</span></p><p><span style=\"color: rgb(51, 51, 51); font-size: 16px; font-family: 宋体, SimSun;\">6、近三年内在经营活动中无严重不良记录</span></p><p><span style=\"font-size: 16px; font-family: 宋体, SimSun;\">三、报名</span></p><p><span style=\"font-size: 16px; font-family: 宋体, SimSun;\">1、报名方式：邮箱报名</span></p><p><span style=\"font-size: 16px; font-family: 宋体, SimSun;\">2、截至时间：2020年07月 15 日&nbsp;&nbsp; 18：00时</span></p><p><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">3<span style=\"font-family: 宋体, SimSun;\">、报名邮箱：ycyysbc@qq.com</span></span></p><p><span style=\"font-size: 16px; font-family: 宋体, SimSun;\">四、报名须知</span></p><p><span style=\"font-size: 16px; font-family: 宋体, SimSun;\">凡具备资格要求的供应商报名时，须将企业营业执照正本、资质证书、法定代表人身份证明、授权委托书等原件扫描件发送邮箱。</span></p><p style=\";margin-bottom:0;text-align:justify;text-justify:inter-ideograph;text-indent:0\"><span style=\"font-size: 16px; font-family: 宋体, SimSun;\">五．<span style=\"font-size: 16px;\">本次招标项目联系人：李伟</span>&nbsp;&nbsp;&nbsp; <span style=\"font-size: 16px;\">联系电话：</span>0515-88508946&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 0515-83578946</span></p><p><span style=\"font-family: 宋体, SimSun; font-size: 16px;\"><span style=\"font-family: 宋体, SimSun;\">六．附件</span>1<span style=\"font-family: 宋体, SimSun;\">&nbsp;<a href=\"http://www.ycsyy.com/upload/files/2020/7/7b724dc00233ad99.docx\" title=\"盐城市第一人民医院设备咨询报名表.docx\" style=\"white-space: normal; color: rgb(0, 102, 204); text-decoration: underline; font-family: 宋体, SimSun; font-size: 16px;\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">盐城市第一人民医院设备咨询报名表.docx</span></a>（下载）</span></span><span style=\"font-family: 宋体, SimSun;\">&nbsp;&nbsp;</span></p><p><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">&nbsp;&nbsp;<span style=\"font-family: 宋体, SimSun; font-size: 16px;\"><span style=\"font-family: 宋体, SimSun;\">附件</span>2&nbsp;</span></span><a href=\"http://localhost/upload/files/2020/7/eb9ff6c2766ae6c7.xlsx\" title=\"住院医师规范化培训管理系统初步要求.xlsx\" style=\"color: rgb(0, 102, 204); font-family: 宋体, SimSun;\">住院医师规范化培训管理系统初步要求.xlsx</a><span style=\"font-family: 宋体, SimSun;\">（下载）</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2541','3365','1','','','1、盐城市第一人民医院主院区DSA改造工程，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2、项目概况与招标内容2.1项目名称：盐城市第一人民医院主院区DSA改造工程。2.2项目地点：盐城市第一人民医院主院区（人民南路66号）','172.16.1.2','172.16.1.2','1594137600','<p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1、盐城市第一人民医院主院区DSA改造工程，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">2、项目概况与招标内容</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">2.1项目名称：盐城市第一人民医院主院区DSA改造工程。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">2.2项目地点：盐城市第一人民医院主院区（人民南路66号）。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">2.3招标内容：盐城市第一人民医院主院区DSA改造工程,具体内容详见招标人提供的设计图纸和工程量清单。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">2.4发包方式：包工包料。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">3、投标人资格要求</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">3.1、投标人须具有有效的机电安装工程专业承包二级及以上资质（提供资质证书原件）；</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">3.2、投标人须具有有效的建筑智能化工程专业承包三级及以上资质，或建筑智能化工程设计与施工二级及以上资质（提供资质证书原件）；</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">3.3、投标人须具有有效的并安全生产许可证。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">3.4、拟派的项目经理必须具有贰级及以注册建造师证（专业为机电安装），并具有安全生产B类证，且从本工程投标文件递交截止之日起无在建工程。（提供项目经理建造师证书、安全生产B类证、无在建工程承诺书）。投标人需提供项目经理在本单位连续缴纳6个月及以上的社保证明（提供社保证明原件）。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">3.5、投标人自2015年7月1日以来承担过手术室、ICU等类似净化工程项目的业绩，时间以竣工验收时间为准。（提供合同、竣工验收报告原件）</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">4、投标报名及招标文件的获取</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2020年7月8日8时至2020年7月13日18时。联系人：刘永杰，0515-68037830；手机：13626222271。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">5、本项目采用竞争性谈判方式。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">其余内容详见招标文件。</span></p><p style=\";margin-bottom:0;text-align:right;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 招标人：盐城市第一人民医院</span></p><p style=\"text-align: right;\"><span style=\"font-family: 宋体, SimSun;\"><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51); background: white;\">时间：</span><span times new>2020</span><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51); background: white;\">年</span><span times new>7</span><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51); background: white;\">月</span><span times new>8</span><span style=\"font-size: 18px; font-family: 宋体; color: rgb(51, 51, 51); background: white;\">日</span></span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2542','3366','1','','','根据盐城市第一人民医院实际需求，我司拟对医院超市进行招租，采用公开招标的方式，选择承租单位，现诚邀请潜在的投标人参加投标。一、项目名称：盐城市第一人民医院超市招租服务项目二、项目综合说明1、拟出租地点：盐城市第一人民医院主院区2、出租规模：投标人须自行','172.16.1.2','172.16.1.2','1594137600','<p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">根据盐城市第一人民医院实际需求，我司拟对医院超市进行招租，采用公开招标的方式，选择承租单位，现诚邀请潜在的投标人参加投标。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">一、项目名称：盐城市第一人民医院超市招租服务项目</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">二、项目综合说明</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1、拟出租地点：盐城市第一人民医院主院区</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">2、出租规模：投标人须自行勘察现场以实际面积为准。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">3、拟出租场所经营范围：主要从事日用百货商品零售、销售，并与医院内的格调、氛围保持一致，但不得销售香烟、酒。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">4、出租期限：三年，具体开始时间以合同签订日期为准。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">5、标段划分：共分为二个标段，一标段室内影像科对面及三楼、二台自助机，面积约60平米；二标段室外住院楼与门诊楼间的炮楼一楼，面积约60平米。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">三、投标申请人的要求</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1．一标段资质要求：投标人须是在中华人民共和国境内注册的独立法人, 投标人营业执照经营范围须包含百货经营等相关资质，注册资金需大于100万元。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">2．二标段资质要求：投标人须是在中华人民共和国境内注册的独立法人，, 投标人营业执照经营范围须包含百货经营等相关资质，注册资金需大于100万元，同时须是连锁超市或加盟店，品牌布点不低于5家（不限地区，提供连锁或加盟资料）</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">3．投标人必须遵守国家法律法规和招标人的有关规定，具有良好的商业信誉。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">4．投标人具备必要的生产经营能力和专业技术人员等履行合同的能力，同时具备履行合同的良好记录。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">5．投标人具有健全的财务会计制度和良好的财务状况；出具至2020年6月的财务凭证（资产负债表、现金流量表、利润表）。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">6．投标人提供的服务符合国家、行业或企业的技术、安全和环境保护的标准；</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">7．投标人具有完善的质量保证体系和良好的服务能力；</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">8．投标人在以往服务经营中无任何信誉、食品卫生、消防安全等方面的不良记录；</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">9．未被列入“信用中国”、“信用江苏”网站失信被执行人名单、重大税收违法案件当事人名单和政府采购严重违法失信名单。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">10.本项目招标不接受联合体投标。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">11、投标单位不得同时投两个标段，</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">四、资格审查方式和评标办法</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">本项目采用“资格后审，综合评估法”。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">具体内容详见招标文件。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">五、投标报名及招标文件的获取</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2020年7月8日8时至2020年7月13日18时。联系人：刘永杰，0515-68037830；手机：13626222271。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">六、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">七、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">其余内容详见招标文件。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 14px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\";margin-bottom:0;text-align:right;text-indent:36px;line-height:33px;background: white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">招标人：盐城市第一人民医院</span></p><p style=\"margin-top:0;margin-right:12px;margin-bottom:0;margin-left:0;margin-bottom:0;text-align:right;text-indent:36px\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); background: white; font-family: 宋体, SimSun;\">时间：2020年7月8日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2543','3367','1','','','1、根据医院建设与发展的需要，对照相关法律法规规定，经医院研究同意，现对盐城市第一人民医院4台放射诊疗设备职业病危害放射防护预评价和控制效果评价等技术服务进行院内公开招标，诚邀潜在的投标人参加投标。2.项目概况与招标内容2.1项目概况：盐城市第一人民医院4台','172.16.1.2','172.16.1.2','1594051200','<p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">1</span><span style=\"font-family:宋体;color:black\">、根据医院建设与发展的需要，对照相关法律法规规定，经医院研究同意，现对盐城市第一人民医院4台放射诊疗设备职业病危害放射防护预评价和控制效果评价等技术服务进行院内公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">2.</span><span style=\"font-family:宋体;color:black\">项目概况与招标内容</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">2.1</span><span style=\"font-family:宋体;color:black\">项目概况：盐城市第一人民医院4台放射诊疗设备职业病危害放射防护预评价和控制效果评价等技术服务，共4台设备：1台牙片机、2台移动C臂机、1台移动DR。本项目最高限价：5万元</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">2.2</span><span style=\"font-family:宋体;color:black\">项目地点：盐城市人民南路66号、毓龙西路166号等</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">2.3</span><span style=\"font-family:宋体;color:black\">招标内容：盐城市第一人民医院4台放射诊疗设备预评价和控制效果评价等技术服务。包括但不限于：</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">（1）卫生：放射诊疗设备预评价和控制效果评价（含批复）、办理放射诊疗许可证；</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">（2）环保：办理环评备案及防护检测、办理辐射安全许可证；招标人保留对上述暂定招标内容适当调整的权利。</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">3.</span><span style=\"font-family:宋体;color:black\">投标人资格要求</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">3.1</span><span style=\"font-family:宋体;color:black\">投标人具有职业卫生（放射卫生）技术服务机构乙级及以上级资质；</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">3.2</span><span style=\"font-family:宋体;color:black\">投标人具有社会辐射环境检测机构乙级及以上级资质；</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">3.3</span><span style=\"font-family:宋体;color:black\">投标人资质需经省卫健委及省环保厅认可。</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">4.</span><span style=\"font-family:宋体;color:black\">投标报名</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:等线;color:black\">4.1 </span><span style=\"font-family:等线;color:black\">报名方式：邮箱报名；</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:等线;color:black\">4.2 </span><span style=\"font-family:等线;color:black\">截至时间：2020年07月<span style=\"text-decoration:underline;\"> 13 </span>日18：00时；</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:等线;color:black\">4.3 </span><span style=\"font-family:等线;color:black\">报名邮箱：<span style=\"text-decoration:underline;\">ycyycgzx @163.com </span>；</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:等线;color:black\">4.4 </span><span style=\"font-family:等线;color:black\">招标时间：另行通知；</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:等线;color:black\">4.5 </span><span style=\"font-family:等线;color:black\">报名须知：凡具备资格要求的供应商报名时，须将企业营业执照正本、符合资格条件的资质证书、法定代表人身份证明、报名表原件扫描件发送规定邮箱，并联系招标采购中心（电话<span style=\"text-decoration:underline;\">0515-66696855</span>）确认报名。</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">5. </span><span style=\"font-family:宋体;color:black\">本项目采用竞争性谈判方式。</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">6</span><span style=\"font-family:宋体;color:black\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">7</span><span style=\"font-family:宋体;color:black\">、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">其余内容详见招标文件。</span></p><p style=\";margin-bottom:0;text-align:right;text-indent:28px;line-height:42px\"><span style=\"font-family:宋体;color:black\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span><span style=\"font-family:宋体;color:black\">招标人：盐城市第一人民医院</span></p><p style=\";margin-bottom:0;text-align:right;text-indent:28px;line-height:42px\"><span style=\"font-family: 宋体;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-family: 宋体;\">时间：2020年7月7日</span></p><p style=\"margin-bottom: 0px; text-align: left; text-indent: 28px; line-height: 42px;\"><span style=\"font-family:宋体;color:black\"></span></p><p style=\"line-height: 16px;\"><a style=\"color: rgb(0, 102, 204); font-size: 16px; text-decoration: underline;\" href=\"http://localhost/upload/files/2020/7/9be049297b97e6cd.doc\" title=\"盐城市第一人民医院放射诊疗设备预评价和控制效果评价等服务报名表.doc\"><span style=\"font-size: 16px;\">盐城市第一人民医院放射诊疗设备预评价和控制效果评价等服务报名表.doc</span></a></p><p style=\"margin-bottom: 0px; text-align: left; text-indent: 28px; line-height: 42px;\"><span style=\"font-family:宋体;color:black\"><br /></span><br /></p><p><br /></p>','','','','','');
