INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2582','3406','1','','','为了提高医院医疗诊断水平，加强医疗质量管理。根据《医疗机构医用耗材管理办法》等政策要求，以江苏省阳光采购平台目录为基础，按照“公平，公正，公开”的原则，对我院部分急需医用物资进行招标采购，欢迎有资质的公司前来报名，现将有关事项公告如下：一、本次招标采','172.16.1.2','172.16.1.2','1587052800','<p style=\"text-indent: 193px;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\"><strong><strong><span style=\"font-family: 宋体,SimSun;\">&nbsp;</span></strong></strong></span></p><p style=\"text-indent: 28px;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\"><span style=\"font-family: 宋体; font-size: 16px;\">为了提高医院医疗诊断水平，加强医疗质量管理。根据《医疗机构医用耗材管理办法》等政策要求，以江苏省阳光采购平台目录为基础，按照“公平，公正，公开</span><span style=\"font-family: Calibri; font-size: 16px;\">”</span><span style=\"font-family: 宋体; font-size: 16px;\">的原则，对我院部分急需医用物资进行招标采购，欢迎有资质的公司前来报名，现将有关事项公告如下：<span style=\"font-family: 宋体,SimSun; font-size: 16px;\">&nbsp;</span></span></span></p><p style=\"text-indent: 28px;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\"><span style=\"font-family: 宋体; font-size: 16px;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\"></span></span></span></p><p><span style=\"font-family: 宋体,SimSun; font-size: 16px;\">一、本次招标采购目录</span></p><p><span style=\"font-family: 宋体,SimSun; font-size: 16px;\"><span style=\"font-family: Calibri; font-size: 16px;\">1 .</span><span style=\"font-family: 宋体; font-size: 16px;\">医用外科口罩</span><span style=\"font-family: Calibri; font-size: 16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family: 宋体; font-size: 16px;\">带量采购：</span><span style=\"font-family: Calibri; font-size: 16px;\">45</span><span style=\"font-family: 宋体; font-size: 16px;\">万只</span></span></p><p><span style=\"font-family: 宋体,SimSun; font-size: 16px;\"><span style=\"font-family: Calibri; font-size: 16px;\">2.</span><span style=\"font-family: 宋体; font-size: 16px;\">一次医用口罩（灭菌型）</span><span style=\"font-family: Calibri; font-size: 16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family: 宋体; font-size: 16px;\">带量采购：</span><span style=\"font-family: Calibri; font-size: 16px;\">15</span><span style=\"font-family: 宋体; font-size: 16px;\">万只</span></span></p><p><span style=\"font-family: 宋体,SimSun; font-size: 16px;\"><span style=\"font-family: Calibri; font-size: 16px;\">3.</span><span style=\"font-family: 宋体; font-size: 16px;\">检验科工作台</span><span style=\"font-family: Calibri; font-size: 16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family: 宋体; font-size: 16px;\">以现场实际尺寸为准</span></span></p><p><br /></p><p><span style=\"font-family: 宋体,SimSun; font-size: 16px;\">二、供应商资格要求</span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 16px;\">1.具备独立法人的资格</span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 16px;\">2.具有良好的商业信誉</span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 16px;\">3.具有独立承担民事责任的能力</span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 16px;\">4.具有履行合同所必需的设备和专业技术能力</span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 16px;\">5.近三年内在经营活动中无严重不良记录</span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\">&nbsp;</span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\">三、报名</span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\">1.<span style=\"font-family: 宋体; font-size: 16px;\">报名方式：邮箱报名</span></span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\">2.<span style=\"font-family: 宋体; font-size: 16px;\">截至时间：</span>2020<span style=\"font-family: 宋体; font-size: 16px;\">年</span>4<span style=\"font-family: 宋体; font-size: 16px;\">月</span>21<span style=\"font-family: 宋体; font-size: 16px;\">日</span>&nbsp;&nbsp; 24<span style=\"font-family: 宋体; font-size: 16px;\">：</span>00<span style=\"font-family: 宋体; font-size: 16px;\">时</span></span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\">3.<span style=\"font-family: 宋体; font-size: 16px;\">报名邮箱：</span>772909836@qq.com</span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\">&nbsp;</span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\">四、报名须知</span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\">凡具备资格要求的供应商报名时，须将企业营业执照正本、资质证书、法定代表人身份证明、生产企业资料、取得生产企业授权的委托书原件扫描件发送邮箱。</span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\"><br /></span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\">五、<span style=\"font-family: 宋体,SimSun; font-size: 16px;\"><span style=\"font-family: 宋体; font-size: 16px;\">本次招标项目联系人：唐志明</span>/<span style=\"font-family: 宋体; font-size: 16px;\">韩辉</span>&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-family: 宋体; font-size: 16px;\">联系电话：</span>0515-88508946&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 0515-83578946</span></span></p><p style=\"color: rgb(0, 0, 0); font-family:;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\"><br /></span></span></p><p><span style=\"font-family: 宋体,SimSun; font-size: 16px;\">六．盐城市第一人民医院咨询报名表（下载）</span></p><p style=\"line-height: 16px;\"><a title=\"盐城市第一人民医院咨询报名表.docx\" style=\"color: rgb(0, 102, 204); font-family: 宋体,SimSun; font-size: 16px; text-decoration: underline;\" href=\"http://localhost/upload/files/2020/4/a81836630470bfed.docx\"><span style=\"font-family: 宋体,SimSun; font-size: 16px;\">盐城市第一人民医院咨询报名表.docx</span></a></p><p>&nbsp;</p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2583','3407','1','','','因医院实际工作需要，于2020年4月11日进行招标的项目，经评标小组评审，中标结果如下：一、儿童医院一期病房木质柜和吧台采购项目中标单位：江苏同正家具有限公司中标价：37.2万元二、儿童医院一期低压电缆敷设工程中标单位：江苏建雄电气安装有限公司中标价：42.575788','172.16.1.2','172.16.1.2','1586707200','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">因医院实际工作需要，于2020年4月11日进行招标的项目，经评标小组评审，中标结果如下：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><strong><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、儿童医院一期病房木质柜和吧台采购项目</span></strong></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：江苏同正家具有限公司</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标价：37.2万元</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><strong><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、儿童医院一期低压电缆敷设工程</span></strong></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：江苏建雄电气安装有限公司</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标价：42.575788万元</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><strong><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、儿童医院医用隔帘、窗帘、遮阳卷帘及浴帘采购项目</span></strong></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：盐城市石头剪刀布软装有限公司</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标价：19万元</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><strong><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、儿童医院一期治疗柜和实验台采购项目</span></strong></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：苏州璟科金属科技有限公司</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标价：28.85万元</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><strong><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、儿童医院门诊楼及住院楼等建筑物安全鉴定项目</span></strong></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：江苏华科建设工程质量检测有限公司</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标价：9万元</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><strong><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、儿童医院食堂装修工程</span></strong></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：江苏力邦装饰有限公司</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标价：5.054万元</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监察室联系，电话0515-66696810。</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;盐城市第一人民医院</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2020年4月11日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2584','3408','1','','','1、盐城市第一人民医院PET—CT塑料袋采购项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2、项目概况与招标内容2.1项目名称：盐城市第一人民医院PET—CT塑料袋采购项目。2.2项目地点：盐城市人民南路66号。2.3招标内容','172.16.1.2','172.16.1.2','1586188800','<p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">1、盐城市第一人民医院PET—CT塑料袋采购项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2、项目概况与招标内容</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2.1项目名称：盐城市第一人民医院PET—CT塑料袋采购项目。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2.2项目地点：盐城市人民南路66号。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2.3招标内容：PET—CT塑料袋，<span style=\"font-size: 20px; font-family: 宋体, SimSun; color: black;\">具体内容详见招标人的要求和采购清单。</span></span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">3、投标人资格要求</span></p><p style=\"text-align: left; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">3.1符合《中华人民共和国政府采购法》第二十二条规定，是中国境内注册的独立法人企业。</span></p><p style=\"text-align: left; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">3.2供应商营业执照经营范围须包含本次招标的主要内容。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">4、投标报名</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">请符合上述资质条件的投标人携带单位介绍信原件到盐城市第一人民医院（主院区）四楼招标采购中心办公室投标报名，投标报名时间2020年 4月7日8时至2020年 4 月 13日17时。联系人：郑蓉；电话号码：66696855。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">5.、本项目采用竞争性谈判。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">其余内容详见招标文件。</span></p><p style=\"line-height: 3em; text-align: right;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 招标人：盐城市第一人民医院</span></p><p style=\"text-align: right; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 时间：2020年<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">&nbsp; 4 </span>月<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\"> 7</span>日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2585','3409','1','','','一、盐城市儿童医院门诊楼及住院楼等建筑物安全鉴定项目已具备招标条件，诚邀潜在的投标人参加本工程的投标工作。二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。三、项目概况3.1工程名称：盐城市儿童医院门诊楼及住院楼等建筑物安','172.16.1.2','172.16.1.2','1585843200','<p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、盐城市儿童医院门诊楼及住院楼等建筑物安全鉴定项目已具备招标条件，诚邀潜在的投标人参加本工程的投标工作。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、项目概况</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.1工程名称：盐城市儿童医院门诊楼及住院楼等建筑物安全鉴定项目</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.2工程地点：盐城市毓龙西路34号。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.3招标范围：对原市妇保院门诊楼、住院楼、原综合楼及原办公楼等建筑物进行司法鉴定，总建筑面积约16000㎡，具体以实际有效面积为准。招标人保留对招标范围调整的权利。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.4检测周期要求：15日历天，具体开工时间以招标人书面通知为准。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.5检测结果标准：提交具有法律效力的建筑安全鉴定报告。</span></p><p style=\"margin-left: 0px; text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、投标申请人应当具备的主要资格条件：</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.1投标人具有建设主管部门颁发的建设工程质量检测机构资质证书（核定项目须包括主体结构现场检测、地基基础工程和钢结构工程检测）。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.2投标人应具备国家认可委员会颁发的实验室认可证书(CNAS)和有效的计量认证证书(CMA)。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.3投标人应具备江苏省备案证书，必须为江苏省房地产业协会房屋安全管理和鉴定专业委员会会员单位。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.4投标人应具有省级及以上测绘地理信息局颁发的测绘资质证书。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.5投标人应根据现场勘查情况制定相关的检测鉴定方案，并组建项目组成员。项目组成员中国家一级注册结构工程师不少于2人，高级工程师不少于2人，中级工程师不少于5人（投标时须提供项目组成员的注册证书、职称证书）。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.6本次招标工程招标人一律不接受联合体投标报名。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、投标保证金：</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本项目投标保证金为人民币伍仟元整，具体交纳方式详见招标文件约定。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、本项目资格审查采用资格后审办法。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">七、本工程评标办法采用“合理低价法”。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">八、投标报名及招标文件领取</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合要求的潜在投标人于2020年4月3日至2020年 4 月 7日（每日上午8：30时至12：00时，下午14：30时至18：00时）携带单位介绍信（或授权委托书，注明联系人电话及邮箱）至江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号810室）报名并领取招标文件。&nbsp;&nbsp;</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标人（公章）：盐城市第一人民医院</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系人：金仁甫</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系电话：0515-88501096</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标代理机构（公章）：江苏仁禾中衡工程咨询房地产估价有限公司</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系人：胡荣艳</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系电话：0515-68037830</span></p><p style=\"text-align: right; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: right; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: right; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: right; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">日期：2020年4月3日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2586','3410','1','','','因医院实际工作需要，于2020年4月2日14时20分进行招标的项目，经评标小组评审，中标结果如下：一、防雷检测项目中标单位：吉林省北亚防雷装置检测咨询有限公司中标价：1.35万元二、北区水泵、风机维保项目中标单位：盐城市亭湖城西一分场电机维修部中 标 价：6.79万元三','172.16.1.2','172.16.1.2','1585843200','<p style=\"margin-bottom: 0px; text-indent: 32px; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-size: 20px; font-family: 宋体, SimSun;\">因医院实际工作需要，于2020年4月2日14时20分进行招标的项目，经评标小组评审，中标结果如下：</span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><strong>一、防雷检测项目</strong></span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-size: 20px; font-family: 宋体, SimSun;\">中标单位：吉林省北亚防雷装置检测咨询有限公司</span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-size: 20px; font-family: 宋体, SimSun;\">中标价：1.35万元</span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><strong>二、北区水泵、风机维保项目</strong></span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-size: 20px; font-family: 宋体, SimSun;\">中标单位：盐城市亭湖城西一分场电机维修部</span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-size: 20px; font-family: 宋体, SimSun;\">中 标 价：6.79万元</span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><strong><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51);\">三、</span></strong><strong><span style=\"font-family: 宋体;\">变配电设备维修保养、试验检测等项目&nbsp;</span></strong></span><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 19px; font-family: 宋体;\"> </span></strong></span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-size: 20px; font-family: 宋体, SimSun;\">中标单位：江苏夏阳电气有限公司</span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-size: 20px; font-family: 宋体, SimSun;\">中 标 价：14.6810万元</span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 20px; color: rgb(51, 51, 51);\">四、</span></strong><strong><span style=\"font-size: 20px;\">北区溴化锂中央空调机组维保项目</span></strong></span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-size: 20px; font-family: 宋体, SimSun;\">中标单位：蚌埠市艾克空调制冷科技有限公司</span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-size: 20px; font-family: 宋体, SimSun;\">中 标 价：20万元</span></p><p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-size: 20px; font-family: 宋体, SimSun;\">为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监察室联系，电话0515-66696810。</span></p><p style=\"margin-bottom: 0px; text-align: right; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-size: 20px; font-family: 宋体, SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;盐城市第一人民医院</span></p><p style=\"margin-bottom: 0px; text-align: right; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-size: 20px; font-family: 宋体, SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2020年4月3日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2587','3411','1','','','一、盐城市儿童医院一期钢制楼梯制作及安装工程已具备招标条件，诚邀潜在的投标人参加本工程的投标工作。二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。三、项目概况3.1工程名称：盐城市儿童医院一期钢制楼梯制作及安装工程。3.2工','172.16.1.2','172.16.1.2','1585756800','<p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、盐城市儿童医院一期钢制楼梯制作及安装工程已具备招标条件，诚邀潜在的投标人参加本工程的投标工作。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、项目概况</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.1工程名称：盐城市儿童医院一期钢制楼梯制作及安装工程。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.2工程地点：盐城市毓龙西路34号。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.3招标范围：盐城市儿童医院一期改造工程住院楼钢制楼梯制作及安装，具体以招标人提供的设计图纸和工程量清单为准。招标人保留对招标范围调整的权利。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.4工期要求：20日历天，具体开工时间以招标人书面通知为准。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.5质量标准：国家合格标准，并通过招标人及相关部门验收合格。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、投标申请人应当具备的主要资格条件：</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.1投标人资质条件：投标人须具有建设行政主管部门核发的钢结构工程专业承包三级及以上专业承包资质，并取得安全生产许可证。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.2项目负责人资格要求：具有建筑工程专业二级及以上注册建造师证资格，并具有安全生产考核证（B类）。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.3财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.4信誉要求：（1）投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。（2）投标人没有被盐城市城市建设投资集团有限公司暂停参加本司招投标活动且在处罚期内。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.5项目负责人、委托代理人为投标人本单位正式职工，从投标截止日当月向前连续6个月均在投标人本单位缴纳养老保险（例：如投标截止日期为2019年12月*日，则为2019年6-11月养老保险均在投标人本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.6本工程施工项目经理部主要管理人员包括项目负责人、技术负责人、施工员、安全员、质量员。投标时，仅需提供项目负责人相关资料，其他现场人员在中标签订施工合同备案时按苏建建管〔2017〕236号文要求配备到位。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.7本次招标工程招标人一律不接受联合体投标报名。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、投标保证金：</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本项目投标保证金为人民币壹万元整，具体交纳方式详见招标文件约定。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、本项目资格审查采用资格后审办法。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">七、本工程评标办法采用“合理低价法”。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">八、投标报名及招标文件领取</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合要求的投标人于2020年4月2日至2020年4月9日（每日上午8：30时至12：00时，下午14：30时至18：00时）携带单位介绍信（或授权委托书，注明联系人电话及邮箱）至江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号810室）报名并领取招标文件。&nbsp;</span><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 19px; font-family: 宋体;\">&nbsp;</span></span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标人（公章）：盐城市第一人民医院、盐城市城市建设投资集团有限公司</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系人：金仁甫、肖坤</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系电话：0515-88501016、0515-68011519</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标代理机构（公章）：江苏仁禾中衡工程咨询房地产估价有限公司</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系人：胡荣艳</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系电话：0515-68037830</span></p><p style=\"text-align: right; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;2020.4.2</span></p><p><br /></p>','','','','','');
