INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2602','3426','1','','','1、盐城市第一人民医院北区雨污管道测绘及雨污分流改造工程设计服务项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2、项目概况与招标内容2.1项目名称：盐城市第一人民医院北区雨污管道测绘及雨污分流改造工程设计服务','172.16.1.2','172.16.1.2','1583510400','<p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">1、盐城市第一人民医院北区雨污管道测绘及雨污分流改造工程设计服务项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2、项目概况与招标内容</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.1项目名称：盐城市第一人民医院北区雨污管道测绘及雨污分流改造工程设计服务项目。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.2项目地点：盐城市第一人民医院北区（毓龙西路166号）。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.3招标内容：1、北区医疗区及家属区（总占地面积约10万平方米）雨污管道及窨井勘察测绘；2、注明每个窨井和每段管道的深度、管径、流向、长度以及存在问题等信息，绘制一张详细的雨污管网现状图纸；3、根据雨污管网现状图纸，提出一个雨污分流改造工程初步设计方案，包含但不限于新增管道的位置、走向及长度、新增窨井的位置、深度及走向、应封堵窨井的位置和数量等；4、初步方案经招标人认可后，中标人绘制符合环保、城镇排水管理处等部门要求的施工图纸，并到相关部门办理审查合格手续。具体内容详见招标文件。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.4发包方式：包工包料。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">3、投标人资格要求：投标人须具有建设行政主管部门核发的市政（排水）设计乙级及以上资质的独立法人。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">4、投标报名及招标文件的获取</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2020年3月7日8时至2020年3月11日18时。联系人：胡荣艳，0515-68037830；手机：13815581495。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">5、本项目采用综合评估法。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: right;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;招标人：盐城市第一人民医院</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: right;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;时间：2020年3月7日&nbsp; </span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2603','3427','1','','','1、盐城市第一人民医院保健基地（主院区住院部19楼）装修项目已具备招标条件，招标人盐城市第一人民医院现对该项目的监理服务进行公开招标，诚邀潜在的投标人参加投标。2、项目概况与招标内容2.1项目名称：盐城市第一人民医院保健基地（主院区住院部19楼）装修工程监理','172.16.1.2','172.16.1.2','1583510400','<p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">1、盐城市第一人民医院保健基地（主院区住院部19楼）装修项目已具备招标条件，招标人盐城市第一人民医院现对该项目的监理服务进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2、项目概况与招标内容</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.1项目名称：盐城市第一人民医院保健基地（主院区住院部19楼）装修工程监理服务</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.2工程地点：盐城市第一人民医院主院区（人民南路66号）。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.3监理服务期：从监理合同签订之日起到竣工验收合格后监理资料移交为止。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.4质量目标：</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.4.1工期目标：确保承包人能够按照时间节点，完成每个阶段工作计划中的工作内容，最终按建设工程施工合同中要求的合同工期完成任务并及时交付招标人使用。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.4.2质量目标：确保所监理的工程能够达到该工程施工合同所约定的质量标准和质量目标。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.4.3安全目标：要求在整个项目实施阶段开展安全检查及督促工作，确保安全事故为零。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.4.4成本目标：要求在整个项目实施阶段开展管理活动，实现项目的实际投资不超过计划投资（以建设工程施工合同造价为主要控制指标）。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.4.5合同信息管理：要求全面监督、重点控制、专项管理。监理控制的重点是工程变更、工程签证、工程转让与分包、工程延期或索赔，对其实施专项管理。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">2.5招标内容包括（但不限于）：盐城市第一人民医院保健基地（主院区住院部19楼）装修工程监理服务，包括工程范围、阶段范围及监理服务内容。工程范围主要指装修拆除和安装（含一般水电安装、暖通空调、消防等）、室内装饰及其本工程所有配套工程等监理服务。阶段范围主要指装修拆除阶段和施工阶段的监理服务。监理服务内容：对建设工程质量、安全、成本、进度进行控制，对合同、信息进行管理，对工程建设相关方的关系进行协调，并履行建设工程安全生产管理法定职责的服务活动。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">3、投标人资格要求</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">3.1资质条件：投标人应具有建设行政主管部门核发的房屋建筑工程监理专业丙级及以上资质或监理综合资质的独立法人。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">3.2项目负责人（总监理工程师）的要求：年龄在55周岁以内且具有注册专业为 房屋建筑工程的《注册监理工程师注册执业证书》。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">注：项目监理机构主要管理人员配备须合理，人员配备不得低于苏建建管【2017】35号文件要求的最低配备标准。专业监理工程师和监理员必须符合在监工程监理规划监理人员进退场计划要求。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">4、投标报名及招标文件的获取</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2020年3月7日8时至2020年3月11日18时。联系人：胡荣艳，0515-68037830；手机：13815581495。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">5、本项目采用资格后审，综合评估法。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: right;\"><span style=\"color: black; background: white; font-family: 宋体