INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2607','3431','1','','','盐城市第一人民医院主院区净水设备维保及软水机采购安装项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标。1、项目概况与招标内容1.1项目名称：盐城市第一人民医院主院区净水设备维保及软水机采购安装项目1.2招标内容：大型生命源牌0.5T/H单极','172.16.1.2','172.16.1.2','1583510400','<p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院主院区净水设备维保及软水机采购安装项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、项目概况与招标内容</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.1项目名称：盐城市第一人民医院主院区净水设备维保及软水机采购安装项目</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">1.2招标内容：大型生命源牌0.5T/H单极反渗透净水设备维保2台，小型生命源牌全自动反渗透净水机维保4台，新生儿小型净水器维保1台。</span><span style=\"font-size: 20px; font-family: 宋体; color: red;\">净水设备维保、设备故障维修涉及报价清单外配件更换的，配件由甲方提供，乙方负责免费更换。</span><span style=\"font-size: 20px; font-family: 宋体;\">软水机采购安装4台，质保期内软水机使用所有耗材免费更换，定期加盐除外。具体内容详见招标人提供的工程量清单。</span></span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.3项目地点：盐城市第一人民医院主院区（人民南路66号）。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.4供货期要求： 15 日历天，具体开始日期以招标人的通知为准。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.5质量要求：符合国家“合格”标准，质保期2年。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.6发包方式：包工包料。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2 、投标人应当具备的主要资格条件：</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">投标人营业执照经营范围包含净水设备销售或安装等相关内容。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、本项目采用资格后审，竞争性谈判。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.3投标时须授权技术人员参加本项目谈判。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4、投标报名及招标文件的获取</span></p><p style=\"margin-bottom: 0px; text-indent: 13px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2020年3月7日8时至2020年3月11日18时。联系人：胡荣艳，0515-68037830；手机：13815581495。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"margin-bottom: 0px; text-indent: 13px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"margin-bottom: 0px; text-indent: 13px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"margin-bottom: 0px; text-align: right; text-indent: 13px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 招标人：盐城市第一人民医院</span></p><p style=\"line-height: 3em; text-align: right;\"><span style=\"background: white; font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 时间：2020年3月7日&nbsp;</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2608','3432','1','','','一、盐城市儿童医院DR、数字胃肠机房结构加固工程已具备招标条件，现邀请合格的潜在投标人参加本工程的投标工作。二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。三、项目概况3.1工程名称：盐城市儿童医院DR、数字胃肠机房结构加固','172.16.1.2','172.16.1.2','1582819200','<p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、盐城市儿童医院DR、数字胃肠机房结构加固工程已具备招标条件，现邀请合格的潜在投标人参加本工程的投标工作。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、项目概况</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.1工程名称：盐城市儿童医院DR、数字胃肠机房结构加固工程</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.2工程地点：盐城市毓龙西路34号。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.3招标范围：盐城市儿童医院DR、数字胃肠机房结构加固工程施工，具体以招标人提供的设计图纸和工程量清单为准。招标人保留对招标范围调整的权利。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.4工期要求：20日历天，并满足盐城市儿童医院一期整体建设周期要求。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.5质量标准：国家合格标准，并通过招标人及相关部门验收合格。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、投标申请人应当具备的主要资格条件：</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.1投标人资质条件：投标人须具有建设行政主管部门核发的特种工程专业（专业范围包含结构补强）承包资质，并取得安全生产许可证。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.2项目负责人资格要求：具有建筑工程装专业二级及以上注册建造师证资格，并具有安全生产考核证（B类）。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.3财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.4信誉要求：投标申请人近三年内没有被司法部门或行业管理部门查处；或虽有违反法律法规行为、被依法取消投标资格但期限已满的；或因招标投标活动中有违法违规等不良行为的、被招标投标管理部门公示限制但限制期限已满的；没有处于被责令停业、财产被接管、冻结、破产等状态。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.5项目负责人、委托代理人为投标人本单位正式职工，从投标截止日当月向前连续6个月均在投标人本单位缴纳养老保险（例：如投标截止日期为2019年12月*日，则为2019年6-11月养老保险均在投标人本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.6本工程施工项目经理部主要管理人员包括项目负责人、技术负责人、施工员、安全员、质量员。投标时，仅需提供项目负责人相关资料，其他现场人员在中标签订施工合同备案时按苏建建管〔2017〕236号文要求配备到位。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.7本次招标工程招标人一律不接受联合体投标报名。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、投标保证金：</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本项目投标保证金为人民币1000元整，具体交纳方式详见招标文件约定。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、本项目资格审查采用资格后审办法。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"background: aqua; font-family: 宋体, SimSun; font-size: 20px;\">七、本工程评标办法采用“竞争性谈判”。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">八、请具备主要资格条件的潜在投标人于2020年2月28日至2020年3月2日（每日上午8：30时至12：00时，下午14：30时至18：00时）携带单位介绍信（或授权委托书，注明联系人电话及邮箱）至盐城市儿童医院筹建处(毓龙西路34号）报名并领取招标文件。&nbsp;&nbsp;</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标人（公章）：盐城市第一人民医院、盐城市城市投资建设集团有限公司</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系人：金仁甫15366550018&nbsp;&nbsp;&nbsp;&nbsp; 周红军 18932299388</span></p><p style=\"text-align: left; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: right; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: right; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: right; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">日期：2020年2月28 日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2609','3433','1','','','一、盐城市儿童医院CT、DR及数字胃肠机房防辐射工程已具备招标条件，现邀请合格的潜在投标人参加本工程的投标工作。二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。三、项目概况3.1工程名称：盐城市儿童医院CT、DR及数字胃肠机房防','172.16.1.2','172.16.1.2','1582819200','<p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、盐城市儿童医院CT、DR及数字胃肠机房防辐射工程已具备招标条件，现邀请合格的潜在投标人参加本工程的投标工作。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、项目概况</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">3.1工程名称：盐城市儿童医院CT、DR及数字胃肠机房防辐射工程。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">3.2工程地点：盐城市毓龙西路34号。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">3.3招标范围：盐城市儿童医院CT、DR及数字胃肠机房防辐射工程施工，具体以招标人提供的设计图纸、技术要求和工程量清单为准。招标人保留对招标范围调整的权利。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">3.4工期要求：20日历天，并满足盐城市儿童医院一期整体建设周期要求。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">3.5质量标准：国家合格标准，并确保通过省、市卫生疾控、环保部门预评价、防护检测及竣工验收合格等环节。</span></p><p style=\"margin-left: 0px; text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">四、投标申请人应当具备的主要资格条件：</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">4.1投标人必须是中国境内注册的独立法人，且具有独立承担民事责任的能力、营业执照经营范围包含辐射防护等内容。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">4.2主要设备、材料（防辐射材料、防辐射门及铅玻璃）应具有卫生行政主管部门颁发的有效的《放射防护器材检测报告》。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">4.3财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">4.4信誉要求：（1）投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。（2）投标人没有被盐城市城市建设投资集团有限公司暂停参加本司招投标活动且在处罚期内。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">4.5项目负责人、委托代理人为投标人本单位正式职工，从投标截止日当月向前连续6个月均在投标人本单位缴纳养老保险（例：如投标截止日期为2019年12月*日，则为2019年6-11月养老保险均在投标人本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">4.6本次招标工程招标人一律不接受联合体投标报名。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、投标保证金：</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本项目投标保证金为人民币<strong>4000</strong>元整，具体交纳方式详见招标文件约定。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、本项目资格审查采用资格后审办法。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">七、本工程采用“竞争性谈判方式”。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">八、请具备主要资格条件的潜在投标人于2020年2月29日至2020年3月5日（每日上午8：30时至12：00时，下午14：30时至18：00时）携带单位介绍信（或授权委托书，注明联系人电话及邮箱）至江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号810室）报名并领取招标文件。&nbsp;&nbsp;</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标人（公章）：盐城市第一人民医院、盐城市城市建设投资集团有限公司</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系人：金仁甫、肖坤</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系电话：0515-88501016、0515-68011519</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标代理机构（公章）：江苏仁禾中衡工程咨询房地产估价有限公司</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系人：胡荣艳</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系电话：0515-68037830</span></p><p style=\"text-align: right; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2020年2月28日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2610','3434','1','','','一、盐城市儿童医院一期医用气体安装工程已具备招标条件，现邀请合格的潜在投标人参加本工程的投标工作。二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。三、项目概况3.1工程名称：盐城市儿童医院一期医用气体安装工程。3.2工程地点','172.16.1.2','172.16.1.2','1582819200','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、盐城市儿童医院一期医用气体安装工程已具备招标条件，现邀请合格的潜在投标人参加本工程的投标工作。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、项目概况</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.1工程名称：盐城市儿童医院一期医用气体安装工程。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.2工程地点：盐城市毓龙西路34号。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.3招标范围：盐城市儿童医院一期医用气体安装工程施工，具体以招标人提供的设计图纸、技术要求和工程量清单为准。招标人保留对招标范围调整的权利。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.4工期要求：30日历天，并满足盐城市儿童医院一期工程整体建设周期要求。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.5质量标准：国家合格标准，并通过招标人及相关部门验收合格。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、投标申请人应当具备的主要资格条件：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.1投标人资质条件（下列两项资质须同时具有）：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">(1)投标人须具有建设行政主管部门核发的建筑机电安装工程专业承包三级及以上资质，并取得安全生产许可证。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><strong><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">(2)投标人还须同时具有下列四种有效证书：（1）《特种设备安装改造维修许可证》；（2）《医疗器械经营许可证》（或《医疗器械生产许可证》；（3）《医疗器械注册证（中心供氧、中心吸引系统）》；（4）《医疗器械登记表》。</span></strong></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.2项目负责人资格要求：具有机电安装专业二级及以上注册建造师证资格，并具有安全生产考核证（B类）。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.3财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.4信誉要求：（1）投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。（2）投标人没有被盐城市城市建设投资集团有限公司暂停参加本司招投标活动且在处罚期内。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.5项目负责人、委托代理人为投标人本单位正式职工，从投标截止日当月向前连续6个月均在投标人本单位缴纳养老保险（例：如投标截止日期为2019年12月*日，则为2019年6-11月养老保险均在投标人本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.6本工程施工项目经理部主要管理人员包括项目负责人、技术负责人、施工员、安全员、质量员。投标时，仅需提供项目负责人相关资料，其他现场人员在中标签订施工合同备案时按苏建建管〔2017〕236号文要求配备到位。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.7本次招标工程招标人一律不接受联合体投标报名。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、投标保证金：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本项目投标保证金为人民币18000元整，具体交纳方式详见招标文件约定。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、本项目资格审查采用资格后审办法。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">七、本工程评标办法采用“合理低价法”。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">八、请具备主要资格条件的潜在投标人于2020年2月29日至2020年3月5日（每日上午8：30时至12：00时，下午14：30时至18：00时）携带单位介绍信（或授权委托书，注明联系人电话及邮箱）至江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号810室）报名并领取招标文件。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标人（公章）：盐城市第一人民医院、盐城市城市建设投资集团有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系人：金仁甫、肖坤</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系电话：0515-88501016、0515-68011519</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标代理机构（公章）：江苏仁禾中衡工程咨询房地产估价有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系人：胡荣艳</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系电话：0515-68037830&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2020年2月28日</span></p><p><br /></p>','','','','','');
