INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7049','0','cms','item','add','0','859','','宣传处','添加内容 | 盐城市第一人民医院售电侧改革配电优惠服务项目 竞争性谈判公告（重发','/index.php/cms/item-list-category-859-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院售电侧改革配电优惠服务项目 竞争性谈判公告（重发公告）\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-align:left;text-indent:21.0pt;\r\nline-height:23.0pt\" align=\"left\"><span style=\"font-family:宋体\">盐城市第一人民医院售电侧改革配电优惠服务项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-indent:21.1pt;line-height:23.0pt;layout-grid-mode:\r\nchar\"><b><span style=\"font-family:宋体\">1</span></b><b><span style=\"font-family:宋体\">、项目概况<span>&nbsp;&nbsp;&nbsp; </span></span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-family:宋体\">1.1</span><span style=\"font-family:\r\n宋体\">招标范围：盐城市第一人民医院售电侧改革配电优惠服务，包含但不限于主要包括购电价格优惠、对用电设施定期巡检、评估、优化、测试及技术支持。预计每年度用电量约为<span>3800</span>万千瓦时</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-family:宋体\">1.2 </span><span style=\"font-family:宋体\">项目地点：</span><span style=\"font-family:宋体\">盐城市第一人民医院主院区、北区、五官科医院、儿童医院</span><span style=\"font-family:宋体\">。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-family:宋体\">1.3 </span><span style=\"font-family:宋体\">服务时间要求</span><span style=\"font-family:宋体\">：</span><span style=\"font-family:宋体\">三年，自<span>2023</span>年<span>1</span>月<span>1</span>日—<span>2025</span>年<span>12</span>月<span>31</span>日止；合同按年签订，第一年合同到期后，经招标人考核合格续签下一年合同，否则招标人有权终止合同，重新组织招标，中标人须无条件退场。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-align:left;text-indent:21.1pt;\r\nline-height:23.0pt;layout-grid-mode:char\" align=\"left\"><b><span style=\"font-family:\r\n宋体\">2</span></b><b><span style=\"font-family:宋体\">、投标人应当具备的主要资格条件：</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-align:left;text-indent:21.0pt;\r\nline-height:23.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-family:\r\n宋体\">2.1 </span><span style=\"font-family:宋体\">投标人具有独立法人资格，且所在集团在江苏区域内应有取得电力业务许可证。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-align:left;text-indent:21.0pt;\r\nline-height:23.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-family:\r\n宋体\">2.2 </span><span style=\"font-family:宋体\">投标人须已在江苏省电力交易中心注册并通过公示。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-align:left;text-indent:21.0pt;\r\nline-height:23.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-family:\r\n宋体\">2.3 </span><span style=\"font-family:宋体\">投标人应有较强的电源保障能力，其相应母公司应为国家电网或大型发电集团，如为发电集团，在江苏省内控股发电装机容量不低于<span>600</span>万千瓦（投标时需提供本集团在江苏省内控股发电厂装机容量明细表）。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-align:left;text-indent:21.0pt;\r\nline-height:23.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-family:\r\n宋体\">2.4 </span><span style=\"font-family:宋体\">投标人应具备为招标人提供电能监测、综合能效分析、节能管理服务的能力。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-family:宋体\">2.5 </span><span style=\"font-family:宋体\">未被“信用中国”网站（<span>www.creditchina.gov.cn</span>）列入失信被执行人、重大税收违法案件当事人名单、政府采购严重失信行为记录名单。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-align:left;text-indent:21.0pt;\r\nline-height:23.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-family:\r\n宋体\">2.6 </span><span style=\"font-family:宋体\">本次招标工程招标人一律不接受联合体投标报名。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-align:left;text-indent:21.1pt;\r\nline-height:23.0pt;layout-grid-mode:char\" align=\"left\"><b><span style=\"font-family:\r\n宋体\">3</span></b><b><span style=\"font-family:宋体\">、投标报名及招标文件的获取</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-family:宋体\">3.1 </span><span style=\"font-family:宋体\">请符合资格条件的投标人于<span>2022</span>年<span>11</span>月<span>18</span>日至<span>2022</span>年<span>11</span>月<span>23</span>日，每日<span>8:30 </span>时至<span>18</span>：<span>00</span>时，携带投标申请人单位介绍信或授权委托书原件（注明联系人、联系方式及邮箱）及本人身份证原件获取招标文件等材料<span>,</span>逾期领取的不予接收。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-family:宋体\">3.2 </span><span style=\"font-family:宋体\">领取地点：盐城市招标代理有限公司（地址：盐城市钱江财富广场<span>A</span>座<span>4</span>楼行政服务部）。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-align:left;text-indent:21.0pt;\r\nline-height:23.0pt;layout-grid-mode:char;background:white\" align=\"left\"><span style=\"font-family:宋体\">3.3 </span><span style=\"font-family:宋体\">联系电话：张工<span>0515-89803566</span>、<span>13921809379</span>。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';text-align:left;text-indent:21.0pt;\r\nline-height:23.0pt;layout-grid-mode:char;background:white\" align=\"left\"><span style=\"font-family:宋体;background:white\">4</span><span style=\"font-family:宋体;\r\nbackground:white\">、</span><b><span style=\"font-family:宋体\">本项目采用“资格后审”，评标办法采用“竞争性谈判”</span></b><span style=\"font-family:宋体;background:white\">。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:char;\r\nbackground:white\"><span style=\"font-size:10.5pt;font-family:宋体;\r\nbackground:white\">5</span><span style=\"font-size:10.5pt;font-family:宋体;\r\nbackground:white\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:char;\r\nbackground:white\"><span style=\"font-size:10.5pt;font-family:宋体;\r\nbackground:white\">6</span><span style=\"font-size:10.5pt;font-family:宋体;\r\nbackground:white\">、招投标监督管理机构及投诉电话</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:char;\r\nbackground:white\"><span style=\"font-size:10.5pt;font-family:宋体;background:white\">盐城市第一人民医院监督审查室，联系电话<span>0515-66696810</span>。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';text-indent:21.1pt;line-height:23.0pt;layout-grid-mode:char;\r\nbackground:white\"><b><span style=\"font-size:10.5pt;font-family:宋体;\r\nbackground:white\">7</span></b><b><span style=\"font-size:10.5pt;font-family:\r\n宋体;background:white\">、联系方式</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:char;\r\nbackground:white\"><span style=\"font-size:10.5pt;font-family:宋体;color:black\">招标人：盐城市第一人民医院</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:char;\r\nbackground:white\"><span style=\"font-size:10.5pt;font-family:宋体;color:black\">地<span>&nbsp; </span>址：盐城市人民南路<span>66</span>号</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:char;\r\nbackground:white\"><span style=\"font-size:10.5pt;font-family:宋体;color:black\">联系人：凌先生</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\\\'嫤疓B2312\\\',\\\'serif\\\';text-indent:21.0p