INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7065','0','cms','item','add','0','859','','宣传处','添加内容(前台) | 盐城市儿童医院健康管理中心围墙项目招标公告（二次）','/index.php/cms/item-list-category-859-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/u.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市儿童医院健康管理中心围墙项目招标公告（二次）\',\n  \'title_color\' => \'\',\n  \'html\' => \'1\',\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'timestamp\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'summary\' => \'\',\n  \'sub_title\' => \'\',\n  \'verify_frame\' => \'\',\n  \'custom_a\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市儿童医院健康管理中心围墙项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">1</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、工程概况<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></b></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目名称：盐城市儿童医院健康管理中心围墙项目。</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目地点：盐城市儿童医院（毓龙西路<span>6</span>号）。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 22pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标内容：</span><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市儿童医院健康管理中心围墙项目，包含但不限于在儿童医院健康管理中心东侧场地与迎宾新村交界处，以儿童医院规划红线为基准建设长约<span>25</span>米的砖砌基础、铁艺围墙，分隔开居民区与医疗区。具体以招标人提供的施工图纸和工程量清单为准，招标人保留调整部分工程量的权利。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">质量标准：符合国家“合格”标准。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.5 </span><span style=\"font-family: 宋体; font-size: 12pt;\">工期要求：<span>15</span>日历天，具体开工日期以招标人的通知。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标规模：工程造价约<u><span>&nbsp;\r\n5.97 </span></u>万元。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.7</span><span style=\"font-family: 宋体; font-size: 12pt;\">发包方式：包工包料。</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">2 </span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">投标人应当具备的主要资格条件：</span></b></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人须具有建设行政主管部门颁发的建筑工程施工总承包三级及以上资质的独立法人，且取得有效的安全生产许可证。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目负责人的资格要求：项目负责人须具有建筑工程专业二级及以上建造师注册证书，同时具有《建筑施工企业项目负责人安全生产考核合格证》（<span>B</span>类）。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">&nbsp;&nbsp; 2.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">其他要求<span><br>\r\n&nbsp;&nbsp; 2.3.1</span>财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<span>&nbsp;<br>\r\n&nbsp;&nbsp; 2.3.2</span>信誉要求：①投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">本次招标不接受联合体投标。</span></p>\r\n<p align=\"left\" style=\\\'margin: 0cm 0cm 0pt; text-align: left; line-height: 21pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\\\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">3</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、投标报名及招标文件的获取</span></b></p>\r\n<p style=\\\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\\\'><span style=\"background: white;\">请符合上述资质条件的投标人携带单位介绍信到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">号</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">楼</span><span style=\"background: white; font-family: 宋体;\">805</span><span style=\"background: white;\">室）投标报名并领取招标文件，投标报名时间</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">11</span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">25</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">时</span><span style=\"background: white; font-family: 宋体;\">3