INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7105','0','cms','item','add','0','859','','宣传处','添加内容 | 农工党盐城市一院基层委与中健（盐城）康复医院 达成战略合作','/index.php/cms/item-list-category-859-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'农工党盐城市一院基层委与中健（盐城）康复医院 达成战略合作\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'2022-12-08 10:01:26\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">12</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">月<span>7</span>日下午，农工党盐城市第一人民医院基层委员会与中健（盐城）康复医院举行“战略合作”签约仪式。农工党市委会副主委胥江华、盐都区卫健委副主任朱海波应邀出席。农工党市一院基层委主委王军、中健（盐城）康复医院院长胡刚等参加。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">会上，王军主委与胡刚院长签署了战略合作协议。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">王军主委简要介绍了农工党市一院基层委的情况，并承诺今后将在医疗业务开展、教学人才培养、学科建设方面等给予大力支持，并通过战略合作，推进优质资源下沉，激发新思路，积累更多经验，共同为健康盐城以及大众健康发展发挥应有作用。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">胡刚院长表示，中健（盐城）康复医院将充分依托市一院的优质医疗资源，为群众提供更优质的医疗健康服务。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">大家还参观了中健（盐城）康复医院，并围绕医院管理、学科建设、医疗协作等方面进行深入交流。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\"><a href=\"/ewebeditor/uploadfile/image/2022/12/12/20221212100330770.jpg\" target=\"_blank\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;<img src=\"/ewebeditor/uploadfile/image/2022/12/12/20221212100330770_s.jpg\" border=\"0\"></a><br>\r\n</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;<a href=\"/ewebeditor/uploadfile/image/2022/12/12/20221212100500552.jpg\" target=\"_blank\"><img src=\"/ewebeditor/uploadfile/image/2022/12/12/20221212100500552_s.jpg\" border=\"0\"></a></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">&nbsp; </span></p>\',\n  ),\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'526a9465d5368736\',\n  \'verifier\' => \'宣传处\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1670810702');
