INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7133','0','cms','item','add','0','841','','宣传处','添加内容 | 盐城市一院召开“请党放心、抗疫有我” 抗击疫情专题动员大会','/index.php/cms/item-list-category-841-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市一院召开“请党放心、抗疫有我” 抗击疫情专题动员大会\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'2023-01-01 11:01:56\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-indent:28.0pt\"><span style=\"font-size:14.0pt;\r\nfont-family:仿宋,仿宋_GB2312\">随着国务院联防联控机制“新十条”抗疫政策的出台，当前抗疫进入了“分区分类，应治尽治”的新阶段，盐城市第一人民医院疫情防治工作已进入关键时刻。为充分发挥党组织战斗堡垒、党员先锋模范和团组织青年的主力军和突击队作用，<span>12</span>月<span>30</span>日，盐城市第一人民医院召开“请党放心、抗疫有我”抗击疫情专题动员大会，基层党支部书记、支委和团支部书记、支委参加会议。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-indent:28.0pt\"><span style=\"font-size:14.0pt;\r\nfont-family:仿宋,仿宋_GB2312\">盐城市第一人民医院党委书记查文章强调，我们要更加紧密团结在以习近平同志为核心的党中央周围，深刻领悟<span>“</span>两个确立<span>”</span>的决定性意义，增强<span>“</span>四个意识<span>”</span>、坚定<span>“</span>四个自信<span>”</span>、做到<span>“</span>两个维护<span>”</span>，把人民群众生命安全和身体健康放在第一位，全力做好医疗救治工作，全力扫除新冠疫情阴霾，全力促进经济社会恢复发展，在全面建设社会主义现代化国家的新征程上创造新的辉煌！</span></p>\r\n<a href=\"/ewebeditor/uploadfile/image/2023/01/02/20230102110311608.jpg\" target=\"_blank\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;<img src=\"/ewebeditor/uploadfile/image/2023/01/02/20230102110311608_s.jpg\" border=\"0\"></a>\',\n  ),\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'452113d4f0beef89\',\n  \'verifier\' => \'宣传处\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1672628599');
