INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7140','0','cms','item','update','2038','940','','宣传处','修改内容 | 卞伟钢','/index.php/cms/item-list-category-940-page-1','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-update?model=article&id=2038&verified=1\',\n  \'cid\' => \'940\',\n  \'title\' => \'卞伟钢\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'2021-07-13 17:43:43\',\n  \'sub_title\' => \'主任医师\',\n  \'author\' => \'\',\n  \'source\' => \'\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'https://www.ycsyy.com/attachment/cms/item/2021_07/13_17/032cba04642ac7df.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'硕士研究生，硕士生导师，南通大学兼职教授，盐城市一院肿瘤内科主任、主任医师。熟练掌握化疗、分子靶向治疗、生物免疫治疗等新技术。擅长在晚期肿瘤中应用化疗与肿瘤微创治疗、肿瘤生物免疫治疗等多种治疗手段相结合的综合治疗及基因指导下的个体化治疗。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\'\"><span style=\"font-family:宋体\">硕士研究生，硕士生导师，南通大学兼职教授，盐城市一院肿瘤内科主任、主任医师。江苏省免疫学会肿瘤免疫专业委员会委员、江苏省抗癌协会纵隔肿瘤专业委员会委员、江苏省生物技术协会肿瘤精准医学诊疗专业委员会常委、江苏省整合医学研究会肺癌专业委员会常委，盐城市抗癌协会胃肠肿瘤专业委员会主任委员、盐城市医学会肿瘤分会常委、盐城市抗癌协会肿瘤免疫治疗专委会副主任委、盐城市抗癌协会乳腺癌专业委员会副主任委员、盐城市肿瘤专业医疗质量控制中心主任，盐城市第</span><span>8</span><span style=\"font-family:宋体\">届、第</span><span>9</span><span style=\"font-family:宋体\">届政协委员，民建盐城市委委员，盐城市医学重点人才，盐城市医学创新团队带头人。熟练掌握化疗、分子靶向治疗、生物免疫治疗等新技术。擅长在晚期肿瘤中应用化疗与肿瘤微创治疗、肿瘤生物免疫治疗等多种治疗手段相结合的综合治疗及基因指导下的个体化治疗。近年来，科研项目获盐城市科学技术进步二等奖</span><span>1</span><span style=\"font-family:宋体\">项、盐城市科学技术一等奖</span><span>1</span><span style=\"font-family:宋体\">项、盐城市新技术引进二等奖</span><span>4</span><span style=\"font-family:宋体\">项；以第一作者或通讯作者发表专业论文</span><span>5</span><span style=\"font-family:宋体\">篇。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\'\"><span>&nbsp;</span></p>\r\n<p></p>\',\n  ),\n  \'editer\' => \'\',\n  \'list_order\' => \'2021-07-13 17:43:43\',\n  \'views\' => 599,\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'肺癌、乳腺癌、食道癌、结直肠癌、胃癌等肿瘤化疗、免疫治疗及分子靶向治疗，癌痛的规范化治疗。\',\n  \'model\' => \'article\',\n  \'verified\' => \'1\',\n  \'id\' => \'2038\',\n  \'iid\' => \'2038\',\n  \'page\' => \'1\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-list?model=article&cid=834\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'update\',\n  \'attachment_hash\' => \'6e2344e599b89457\',\n  \'verifier\' => \'admin5\',\n)','1672644134');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7141','0','cms','item','update','2041','940','','宣传处','修改内容 | 陈苏蓉','/index.php/cms/item-list-category-940-page-1','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-update?model=article&id=2041&verified=1\',\n  \'cid\' => \'940\',\n  \'title\' => \'陈苏蓉\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'2021-07-13 17:44:31\',\n  \'sub_title\' => \'主任医师\',\n  \'author\' => \'\',\n  \'source\' => \'\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'https://www.ycsyy.com/attachment/cms/item/2021_07/13_17/bff05637c71f96b7.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'盐城市一院肿瘤内科副主任、主任医师。对恶性肿瘤的化疗、靶向、免疫、内分泌、中医中药及支持营养、对症止痛等治疗方法及各种治疗并发症的处理有丰富的经验，特别对肺癌、乳腺癌、食管胃肠肝胆胰恶性肿瘤、妇科肿瘤的诊断与治疗有较深的研究。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\'\"><span style=\"font-family:宋体\">盐城市一院肿瘤内科副主任、主任医师。江苏省抗癌协会肿瘤精准治疗专业委员会委员、江苏省医师协会肿瘤多学科诊疗专业委员会委员，盐城市医学会生物与化疗分会副主任委员、盐城市抗癌协会乳腺癌专委会主任委员、盐城市抗癌协会肿瘤内科专委会副主任委员、盐城市抗癌协会胃肠肿瘤专委会副主任委员。对恶性肿瘤的化疗、靶向、免疫、内分泌、中医中药及支持营养、对症止痛等治疗方法及各种治疗并发症的处理有丰富的经验，特别对肺癌、乳腺癌、食管胃肠肝胆胰恶性肿瘤、妇科肿瘤的诊断与治疗有较深的研究。近年来，科研项目获盐城市科技进步一等奖</span><span>1</span><span style=\"font-family:宋体\">项、二等奖</span><span>2</span><span style=\"font-family:宋体\">项、三等奖</span><span>1</span><span style=\"font-family:宋体\">项，盐城市新技术引进奖</span><span>1</span><span style=\"font-family:宋体\">项。</span></p>\',\n  ),\n  \'editer\' => \'\',\n  \'list_order\' => \'2021-07-13 17:44:31\',\n  \'views\' => 356,\n  \'verify\' => 1,\n  \'html\' => \'1