INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7142','0','cms','item','update','2044','940','','宣传处','修改内容 | 陈平','/index.php/cms/item-list-category-940-page-1','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-update?model=article&id=2044&verified=1\',\n  \'cid\' => \'940\',\n  \'title\' => \'陈平\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'2021-07-13 17:46:26\',\n  \'sub_title\' => \'主任医师\',\n  \'author\' => \'\',\n  \'source\' => \'\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'https://www.ycsyy.com/attachment/cms/item/2021_07/13_17/c7681340565c939d.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'硕士研究生，盐城市一院肿瘤内科主任医师、肿瘤内科原主任。擅长肿瘤的规范化治疗，尤其是乳腺癌、肺癌的治疗，先后开展并参与乳腺癌、肺癌、胃癌等多项临床研究。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\'\"><span style=\"font-family:宋体\">硕士研究生，盐城市一院肿瘤内科主任医师、肿瘤内科原主任。中国抗癌协会食管癌专业委员会委员、中国抗癌协会原发灶不明专业委员会委员，江苏省抗癌协会肿瘤精准治疗专业委员会副主任委员、江苏省老年医学会肿瘤分会副主任委员、江苏省化疗与生物治疗专家委员会委员、江苏省医师协会肿瘤化疗与生物治疗医师分会常委、江苏省中西医结合学会肿瘤分会常委、江苏省康复医学会肿瘤康复专委会委员、江苏省淋巴瘤专家委员会委员，盐城市化疗与生物治疗专家委员会主任委员、盐城市抗癌协会肿瘤免疫治疗专业委员会主任委员。曾入选江苏省“六大人才高峰”高层次人才选拔培养对象，先后被盐城市人民政府授予“名医生”称号、被北京白求恩基金会授予“百家名医团专家”。擅长肿瘤的规范化治疗，尤其是乳腺癌、肺癌的治疗，先后开展并参与乳腺癌、肺癌、胃癌等多项临床研究。近年来，重点开展肿瘤的时辰化疗、同步放化疗及基因指导下的个体化治疗等基础性及临床研究工作，参与多项国内多中心肿瘤新药临床试验研究工作，参加完成国家自然科学青年科学基金项目</span><span>1</span><span style=\"font-family:宋体\">项，作为主要完成人（通讯作者）发表</span><span>SCI</span><span style=\"font-family:宋体\">论文</span><span>9</span><span style=\"font-family:宋体\">篇。</span></p>\',\n  ),\n  \'editer\' => \'\',\n  \'list_order\' => \'2021-07-13 17:46:26\',\n  \'views\' => 738,\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'肺癌，乳腺癌，胃癌、淋巴瘤等的诊治。\',\n  \'model\' => \'article\',\n  \'verified\' => \'1\',\n  \'id\' => \'2044\',\n  \'iid\' => \'2044\',\n  \'page\' => \'1\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-list?model=article&cid=834\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'update\',\n  \'attachment_hash\' => \'56d8a5f2c75b2d94\',\n  \'verifier\' => \'admin5\',\n)','1672644249');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7143','0','cms','item','update','2016','940','','宣传处','修改内容 | 戴夕超','/index.php/cms/item-list-category-940-page-1','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-update?model=article&id=2016&verified=1\',\n  \'cid\' => \'940\',\n  \'title\' => \'戴夕超\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'2021-07-13 17:24:48\',\n  \'sub_title\' => \'副主任医师\',\n  \'author\' => \'\',\n  \'source\' => \'\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'https://www.ycsyy.com/attachment/cms/item/2021_07/13_17/6f684387cae0ed8d.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'医学博士，硕士生导师，南通大学兼职副教授，盐城市一院肿瘤内科主任医师。擅长胸部肿瘤（肺癌、食管癌，纵隔肿瘤）、乳腺癌、消化系统肿瘤等疾病的诊治，对恶性肿瘤放疗、化疗、靶向、免疫治疗、微创治疗等综合治疗有丰富的临床经验。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\'\"><span style=\"font-family:宋体\">医学博士，硕士生导师，南通大学兼职副教授，盐城市一院肿瘤内科主任医师。意大利那不勒斯国家肿瘤中心访问学者，</span><span>IASLC</span><span style=\"font-family:宋体\">（国际肺癌联盟）会员，江苏省医疗质控中心肺癌质控专家委员会委员、江苏省医师协会精准医疗专委会姑息学组委员、江苏省免疫学会肿瘤多学科诊疗委员会委员、江苏省整合医学会肺癌专委会委员，盐城市化疗和生物治疗委员会委员、盐城市抗癌协会胰腺癌专委会副主任委员、盐城市抗癌协会淋巴瘤专委会常务委员、盐城市抗癌协会免疫治疗委员会常务委员、盐城市抗癌协会肺癌专委会委员，盐城市青年医学人才，盐城市医学创新团队带头人。擅长胸部肿瘤（肺癌、食管癌，纵隔肿瘤）、乳腺癌、消化系统肿瘤等疾病的诊治，对恶性肿瘤放疗、化疗、靶向、免疫治疗、微创治疗等综合治疗有丰富的临床经验。近年来，参与国家自然科学基金课题</span><span>1</span><span style=\"font-family:宋体\">项，主持江苏省卫健委课题</span><span>1</span><span style=\"font-family:宋体\">项、盐城市课题</span><span>1</span><span style=\"font-family:宋体\">项；科研成果获扬州市自然科学优秀学术论文三等奖、盐城市新技术引进二等奖、扬州市新技术引进二等奖；发表</span><span>SCI</span><span style=\"font-family:宋体\">文章</span><span>3</span><span style=\"font-family:宋体\">篇、中华文章</span><span>3</span><span style=\"font-family:宋体\">篇；成功申请国家专利</span><span>1</span><span style=\"font-family:宋体\">项。</span></p>\',\n  ),\n  \'editer\' => \'\',\n  \'list_order\' => \'2021-07-13 17:24:48\',\n  \'views\' => 1,\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'恶性肿瘤放疗、化疗、分子靶向治疗、免疫治疗等肿瘤综合治疗。\',\n  \'model\' => \'article\',\n  \'verified\' => \'1\',\n  \'id\' => \'2016\',\n  \'iid\' => \'2016\',\n  \'page\' => \'1\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-list?cid=940&model=article\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'update\',\n  \'attachment_hash\' => \'2b70a6fa221ed039\',\n  \'verifier\' => \'admin5\',\n)','1672644298');
