INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7145','0','cms','item','update','2043','940','','宣传处','修改内容 | 刘红旗','/index.php/cms/item-list-category-940-page-1','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-update?model=article&id=2043&verified=1\',\n  \'cid\' => \'940\',\n  \'title\' => \'刘红旗\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'2021-07-13 17:45:44\',\n  \'sub_title\' => \'主任医师\',\n  \'author\' => \'\',\n  \'source\' => \'\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'https://www.ycsyy.com/attachment/cms/item/2021_07/13_17/dc1eff0b835727d0.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，肿瘤科主任医师，本科学历。任《中华医药杂志》编委、国际生物闭合电路学会会员，CSCO会员，江苏省生物治疗委员会委员、盐城市肿瘤学会化疗委员会副主任委员。从事本专业30余年，对用中医扶正、西医化疗治疗中、晚期肿瘤有很深的研究。在中、外核心期刊发表论文20余篇，其中3篇SCI收录，主编肿瘤医学丛书1部，参编人卫出版社医学教材1部；获盐城市新科技进步二等奖2项、三等奖2项，获盐城市新技术引进..\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，肿瘤科主任医师，本科学历。任《中华医药杂志》编委、国际生物闭合电路学会会员，CSCO会员，江苏省生物治疗委员会委员、盐城市肿瘤学会化疗委员会副主任委员。从事本专业30余年，对用中医扶正、西医化疗治疗中、晚期肿瘤有很深的研究。在中、外核心期刊发表论文20余篇，其中3篇SCI收录，主编肿瘤医学丛书1部，参编人卫出版社医学教材1部；获盐城市新科技进步二等奖2项、三等奖2项，获盐城市新技术引进奖1项，获盐城市自然科学优秀学术论文奖2项。</p>\',\n  ),\n  \'editer\' => \'\',\n  \'list_order\' => \'2021-07-13 17:45:44\',\n  \'views\' => 436,\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'用中、西结合方式治疗中、晚期消化道肿瘤、呼吸系统肿瘤及妇科肿瘤。