INSERT INTO `p8_cms_item` (`id`,`model`,`title`,`title_color`,`title_bold`,`sub_title`,`cid`,`frame`,`url`,`uid`,`author`,`authority`,`editer`,`username`,`attributes`,`summary`,`pages`,`html_view_url_rule`,`views`,`score`,`level`,`level_time`,`source`,`comments`,`timestamp`,`verify_frame`,`create_time`,`update_time`,`list_order`,`verified`,`verifier`,`verify_time`,`ever_verified`,`allow_comment`,`allow_mood`,`credit`,`credit_type`,`digg`,`trample`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2130','article','杨林','','0','主任医师','953','<!--#p8_attach#-->/cms/item/2021_07/14_11/f6b2ac954a00aa75.JPG','','2','','','','admin5','6','男，主任中医师。任江苏省中医学会肺系分会委员，盐城市中医学会副会长，盐城市中医学会内科分会副主任委员，是盐城市名中医、盐城市名老中医专家学术经验继承指导老师。从医40余年，专攻中医内科，发明了“清胆合剂”、“残石净合剂”，对系统性红斑狼疮、慢性肾炎、紫癜性肾炎的治疗效果显著。获市科技进步一等奖1项；分别获盐城市政府、江苏省中医药科技进步二等奖3项。在省级以上杂志发表论文80余篇。','1','','501','0','0','0','','0','1626233318','','1626233318','1626233318','1626233318','1','admin5','0','1','1','1','1','0','0','0','','','','','','','','','','');
