INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1136','1918','1','','<!--#p8_attach#-->/cms/item/2021_07/13_09/0e5224318236c93a.jpg','男，主任医师，本科学历。从事呼吸内科工作20年，对呼吸系统疾病的诊治具有丰富的临床经验，熟练掌握呼吸内科各项常规操作及技能，特别擅长呼吸科急危重症，如慢性阻塞性肺病，支气管哮喘，呼吸衰竭的诊治，熟练运用机械通气，对肺部肿瘤的诊断治疗亦有丰富经验。在省级','192.168.162.110','192.168.162.110','1626140654','男，主任医师，本科学历。从事呼吸内科工作20年，对呼吸系统疾病的诊治具有丰富的临床经验，熟练掌握呼吸内科各项常规操作及技能，特别擅长呼吸科急危重症，如慢性阻塞性肺病，支气管哮喘，呼吸衰竭的诊治，熟练运用机械通气，对肺部肿瘤的诊断治疗亦有丰富经验。在省级以上杂志发表论文数篇。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1137','1919','1','','<!--#p8_attach#-->/cms/item/2021_07/13_09/fed72e378325da33.jpg.thumb.jpg','男，主任医师，硕士研究生导师。1978年毕业于南通医学院，长期从事临床诊疗工作，熟练掌握呼吸内科基础理论知识，临床各种疾病的诊治和手术操作，对各种呼吸系统疑难危重病人的诊治较有心得。在核心期刊发表论文20余篇；先后获得省科技进步三等奖1项、市科技进步一等奖2','192.168.162.110','192.168.162.110','1626140701','男，主任医师，硕士研究生导师。1978年毕业于南通医学院，长期从事临床诊疗工作，熟练掌握呼吸内科基础理论知识，临床各种疾病的诊治和手术操作，对各种呼吸系统疑难危重病人的诊治较有心得。在核心期刊发表论文20余篇；先后获得省科技进步三等奖1项、市科技进步一等奖2项、二等奖3项、市科技新技术新项目引进一等奖2项。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1138','1920','1','','<!--#p8_attach#-->/cms/item/2021_07/13_09/be4498f65768a50c.jpg','男，主任医师，硕士研究生导师。任省医学会呼吸病分会常务委员、盐城医学会呼吸内科专业主任委员、南通大学副教授。擅长诊断与治疗各种疑难、危重病和各种呼吸科疾病。发表SCI论文3篇（通讯作者）；获得省立项课题1项、省新科技引进奖二等奖1项、市新技术','192.168.162.110','172.16.1.2','1626140749','男，主任医师，硕士研究生导师。任省医学会呼吸病分会常务委员、盐城医学会呼吸内科专业主任委员、南通大学副教授。擅长诊断与治疗各种疑难、危重病和各种呼吸科疾病。发表SCI论文3篇（通讯作者）；获得省立项课题1项、省新科技引进奖二等奖1项、市新技术引进奖4项、市级立项课题4项；被评为盐城市有突出贡献的中青年专家。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1139','1922','1','','<!--#p8_attach#-->/cms/item/2021_07/13_09/d6530443dbf663a5.jpg','男，主任医师，硕士研究生学历，南通大学硕士研究生导师。现任市呼吸病学会名誉主委、省医疗技术管理委员。长期从事呼吸病学的临床、教学及科研工作，对呼吸系统的疑难和危重病的诊治具有丰富的临床经验和娴熟的技能，承担或参与卫生部、省市级科研课题多','192.168.162.110','172.16.1.2','1626140804','男，主任医师，硕士研究生学历，南通大学硕士研究生导师。现任市呼吸病学会名誉主委、省医疗技术管理委员。长期从事呼吸病学的临床、教学及科研工作，对呼吸系统的疑难和危重病的诊治具有丰富的临床经验和娴熟的技能，承担或参与卫生部、省市级科研课题多项。发表学术论文和专著20余篇，其中《中华结核和呼吸杂志》及SCI收录5篇。作为第一完成人和主要参与者的临床研究课题获江苏省科技进步奖、中华医学科技奖、上海市科技进步奖和盐城市政府科技进步奖的奖项。指导数名硕士研究生毕业。首批入选江苏省卫生系统百名医学重点人才计划；先后被评为江苏省有突出贡献的中青年专家、江苏省劳动模范、国家有突出贡献专家并享受国务院政府特殊津贴，为国务院特殊津贴专家。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1140','1923','1','','<!--#p8_attach#-->/cms/item/2021_07/13_09/8837870d46b6af72.jpg','男，呼吸与危重症医学科主任，主任医师，本科学历。任江苏省中西医结合呼吸分会委员、江苏省慢性阻塞性肺病学足委员、盐城市医学会呼吸分会副主任委员兼秘书长、南通大学兼职副教授。掌握呼吸内科基础理论知识，尤擅无创/有创机械通气抢救各种类型呼吸衰竭。近三年第一','192.168.162.110','172.16.1.2','1626140852','男，呼吸与危重症医学科主任，主任医师，本科学历。任江苏省中西医结合呼吸分会委员、江苏省慢性阻塞性肺病学足委员、盐城市医学会呼吸分会副主任委员兼秘书长、南通大学兼职副教授。掌握呼吸内科基础理论知识，尤擅无创/有创机械通气抢救各种类型呼吸衰竭。近三年第一作者发表SCI文章3篇，第一副主编参编医学专著2部。获市医学会新技术引进奖一等奖1项、省卫生厅医学新技术引进奖二等奖1项，参与市级科研立项4项，省级科研立项1项。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1143','1955','1','','http://www.ycsyy.com/upload/images/2019/10/3f108ce1bb1e806e.jpeg','举办地点：先锋社区莲花小区举办时间：2019年05月09日项目简介：为庆祝“5.12”国际护士节，将三级医院专业的护理服务向社区延伸，盐城市第一人民医院护理部积极响应江苏省护理学会的号召, 组织护理专家下社区，开展了主题为“科技促民生 科普惠健康——疾病预防 增进健','192.168.162.110','192.168.162.110','1557331200','<p style=\"text-align: left; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">举办地点：先锋社区莲花小区<br>\r\n</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">举办时间：2019年05月09日</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">项目简介：</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为庆祝“5.12”国际护士节，将三级医院专业的护理服务向社区延伸，盐城市第一人民医院护理部积极响应江苏省护理学会的号召, 组织护理专家下社区，开展了主题为“科技促民生 科普惠健康——疾病预防 增进健康”活动周系列活动。</span></p>\r\n<p style=\"line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span></p>\r\n<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/3f108ce1bb1e806e.jpeg\" width=\"554\" height=\"415\" alt=\"0000497e2e4072323f9dbb885d24ca0\" border=\"0\"></p>\r\n<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/48670e61ee0defa.jpeg\" width=\"554\" height=\"415\" alt=\"2f3ea61a9d90256a55386bb00160a9f\" border=\"0\"></p>\r\n<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">急诊急救专业学组在莲花社区进行CPR急救操作演示及培训</span></p>\r\n<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/18dc22f753bfb097.jpeg\" width=\"554\" height=\"415\" alt=\"\" border=\"0\"></p>\r\n<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　母婴专业学组在先锋街道莲花路社区现场宣传母乳喂养、孕期保健知识</span></p>\r\n<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/5563d93a3f55ca33.jpeg\" width=\"553\" height=\"415\" alt=\"5d8586cd5ef4ba633c90deb22cd76fd\" border=\"0\"></p>\r\n<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　中医科专业学组在先锋街道莲花路社区进行八段锦现场教学</span></p>\r\n<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/d4e2623bf1740be1.jpeg\" width=\"553\" height=\"415\" alt=\"de69e7d431dde9aef56bfaf6aca5b38\" border=\"0\"></p>\r\n<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中医科专业学组在先锋街道莲花路社区进行辨证施膳饮食指导</span></p>\r\n<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/b4eda79fa00f238e.jpeg\" width=\"553\" height=\"415\" alt=\"5d9180752faf424aff717e8cdd30dc4\" border=\"0\"></p>\r\n<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　消化科专业学组向先锋街道莲花路社区居民进行消化科相关知识的宣传</span><a name=\"_GoBack\"></a></p>\r\n<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/ab615031fbd4992a.jpeg\" width=\"512\" height=\"415\" alt=\"2\" border=\"0\"></p>\r\n<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/75766bcad06565d7.jpeg\" width=\"554\" height=\"415\" alt=\"6bd924e4306e72e61251af4ead1d7ea\" border=\"0\"></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1144','1956','1','','http://www.ycsyy.com/upload/images/2019/10/7b958a979f50bd89.jpeg','举办地点： 盐城盐都康年颐养院举办时间：2019年05月08日为庆祝“5.12”国际护士节，响应中华护理学会和江苏省护理学会的号召,我院护理部认真组织，安排了泌尿外科、心血管、神经科、伤口造口、骨科五个护理专业学组的30多名护理专家和志愿者，在护理部汤琪春主任和孙雯','192.168.162.110','192.168.162.110','1557244800','<p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">举办地点： 盐城盐都康年颐养院</span></p>\r\n<p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">举办时间：2019年05月08日</span></p>\r\n<p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为庆祝“5.12”国际护士节，响应中华护理学会和江苏省护理学会的号召,我院护理部认真组织，安排了泌尿外科、心血管、神经科、伤口造口、骨科五个护理专业学组的30多名护理专家和志愿者，在护理部汤琪春主任和孙雯敏副主任的带领下，于5月8日上午8时来到盐都康年颐养院，开展了主题为“科技促民生 科普惠健康——疾病预防 增进健康”护理科普活动。</span></p>\r\n<p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">1、泌尿外科护士长黄建英、心血管内科护士长程赟赟、神经内科护士长丁蓉、普外科护士长刘艳红分别为老人和护理员进行了«前列腺增生的诊疗和护理»、«冠心病的防治»、«脑卒中的识别和预防»、«失禁性皮炎的预防和护理»等专题知识讲座；</span></p>\r\n<p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/7b958a979f50bd89.jpeg\" width=\"553\" height=\"369\" alt=\"85ed00d3257c2af1ee68d4666250e76\" border=\"0\"></p>\r\n<p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/408f4e2c32fba36.jpeg\" width=\"553\" height=\"369\" alt=\"c40d01712e4fac569fb9d05696aedc3\" border=\"0\"></p>\r\n<p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/64c19ea945338fd3.jpeg\" width=\"554\" height=\"415\" alt=\"fb9e313a7185baee70fc855309f9026\" border=\"0\"></p>\r\n<p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><span style=\"text-indent: 2em; font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/8ecb1827b70fcc80.jpeg\" width=\"554\" height=\"415\" alt=\"a390d186dfe3820c587620ef511f84f\" border=\"0\" style=\"text-indent: 2em;\"></p>\r\n<p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/f728f20bf48cfb05.jpeg\" width=\"553\" height=\"415\" alt=\"c8ac22a220f8e56a2d501c7a178d11e\" border=\"0\" style=\"text-align: center; text-indent: 2em;\"></p>\r\n<p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/17cd90eb0479ceca.jpeg\" width=\"562\" height=\"424\" alt=\"c24bc5367c44ebaf3a7b5dfd2b401b8\" border=\"0\" style=\"text-align: center; text-indent: 2em; width: 562px; height: 424px;\"></p>\r\n<p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2、神经外科护士长张思敏和骨科陈静老师等为护理员进行了CPR、鼻饲、床上翻身、轮椅搬运法等现场模拟操作训练；</span></p>\r\n<p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/b1ea33641ba2c356.jpeg\" width=\"553\" height=\"369\" alt=\"a154014cb4123dff3e5f16dc0e16ba8\" border=\"0\" style=\"text-indent: 2em;\"></p>\r\n<p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/cfbc7327a81f4a03.jpeg\" width=\"553\" height=\"369\" alt=\"3b3e709851585c863b1b8c83a60dead\" border=\"0\" style=\"text-indent: 2em;\"></p>\r\n<p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/398cc501a4d52eb0.jpeg\" width=\"553\" height=\"369\" alt=\"1265199127d54029447e6547fd31396\" border=\"0\"></p>\r\n<p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/f41d4658dfcf9c4d.jpeg\" width=\"553\" height=\"369\" alt=\"4cd16ea84475369982f1b8a15233236\" border=\"0\"></p>\r\n<p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">3、护理专家现场为老人免费测血压、血糖等，对心血管、骨科、神经内科、泌尿外科等常见疾病提供咨询、健康指导、发放宣传材料等；</span></p>\r\n<p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/1130b6bc7bfa81e9.jpeg\" width=\"554\" height=\"362\" alt=\"52e4f233e6611064dfa21ec450598cf\" border=\"0\"></p>\r\n<p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/d5a70d5d17136e96.jpeg\" width=\"548\" height=\"730\" alt=\"6dfad6cbe807e4254aa5dca1cef7462\" border=\"0\"></p>\r\n<p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-align: left; text-indent: 2em;\">4、汤主任带领泌尿外科和伤口造口护理专业学组的护理专家深入病房，对一例膀胱造瘘术后的病人进行了床旁护理查房，耐心细致的指导了伤口造口护理要点和疾病相关知识健康教育。</span></p>\r\n<p style=\"text-align: center; text-indent: 2em; line-height: 3em;\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/38dc1edbabac80e0.jpeg\" width=\"553\" height=\"369\" alt=\"f2689a9c6aff25ee0472e57c23af087\" border=\"0\" style=\"text-indent: 2em;\"></p>\r\n<p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">此次护理专家和志愿者走进养老院，关爱老人，是三级医院护理服务向社区延伸的一个体现，也让我们进一步了解了老人的健康需求和困惑；护理专家通过零距离面对面的交流，不仅为老人提供了专业的健康指导，也进一步提升了护理员的理论水平和实践操作技能，受到了颐养院老人的一致好评。</span></p>\r\n<p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1145','1957','1','','http://www.ycsyy.com/upload/images/2019/10/93fb4567426af13.jpeg','为庆祝“5.12”国际护士节，将三级医院专业的护理服务向社区延伸，盐城市第一人民医院护理部积极响应江苏省护理学会的号召, 2017年05月06日组织护理专家下社区，开展了主题为“健康中国 科普助力--护理专家咨询、讲座大型公益活动”。急诊急救护理学组现场演示培训CPR　','192.168.162.110','192.168.162.110','1526054400','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">为庆祝“5.12”国际护士节，将三级医院专业的护理服务向社区延伸，盐城市第一人民医院护理部积极响应江苏省护理学会的号召, 2017年05月06日组织护理专家下社区，开展了主题为“健康中国 科普助力--护理专家咨询、讲座大型公益活动”。</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"text-align: center; font-size: 20px; font-family: 宋体, SimSun;\">急诊急救护理学组现</span><span style=\"text-align: center; font-size: 20px; font-family: 宋体, SimSun;\">场演示培训CPR</span><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　</span></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/93fb4567426af13.jpeg\" width=\"502\" height=\"376\" alt=\"786051953965130641\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">心血管护理学组为居民测量血压、进行护理咨询</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/9af75006c339e86d.jpeg\" width=\"553\" height=\"311\" alt=\"心血管\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　危重症等各护理专业学组专家开展专题讲座</span></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/41ab319e94d80209.jpeg\" width=\"537\" height=\"332\" alt=\"血糖讲座\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　糖尿病护理学组为居民测量血糖、用食物模型为居民讲解营养知识</span></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/b7ba07a133c67ab7.png\" width=\"553\" height=\"397\" alt=\"血糖2\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　护理学组现场发放教育处方</span></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/7228e210749a6943.jpeg\" width=\"564\" height=\"350\" alt=\"五官科\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　通过展板开展健康知识宣教</span></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/edf1fc38efd17edf.jpeg\" width=\"554\" height=\"311\" alt=\"20170506_092850.jpg\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　志愿者为居民送去温暖</span></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/3cf8924faf90db8c.jpeg\" width=\"553\" height=\"311\" alt=\"志愿者\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">现场活动合影留念</span></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/7a178b78a9091929.jpeg\" width=\"553\" height=\"346\" alt=\"合影\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　盐城晚报新闻报道</span></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/75ee949db40f34e7.png\" width=\"524\" height=\"469\" alt=\"EO%~{W)1[0A{F(2EE1~_}~G\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　盐阜大众报新闻报道</span></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/9d339f4b8fa784d3.png\" width=\"517\" height=\"362\" alt=\"%PQ`)OTK1N$`Z6IRWHZ3F}G\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　医院官方网站报道</span></p>\r\n<p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/b0bb54b7e35f390f.png\" width=\"553\" height=\"557\" alt=\"L_0V`VPG1Y0GV]4@XKW%)GG\" border=\"0\"></p>\r\n<p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p>\r\n<p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1146','1958','1','','http://www.ycsyy.com/upload/images/2019/10/ad3b5df9a7e42e5c.jpeg','　今年是第107个国际护士节，此次活动是我院根据盐城市护理学会的要求，举办的以“健康中国·科普助力”为主题的大型科普宣传系列活动之一。5月9日盐城市第一人民医院糖尿病、消化、急救、神经科、普外科等护理学组分别到先锋社区、东进社区、光明花苑开展“健康中国，','192.168.162.110','192.168.162.110','1525795200','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　今年是第107个国际护士节，此次活动是我院根据盐城市护理学会的要求，举办的以“健康中国·科普助力”为主题的大型科普宣传系列活动之一。5月9日盐城市第一人民医院糖尿病、消化、急救、神经科、普外科等护理学组分别到先锋社区、东进社区、光明花苑开展“健康中国，科普助力”活动。活动中，护理专家及医生热情为社区群众答疑解惑，开展多种形式的科普宣传，发放健康处方、测血糖、测血压、评估胰岛素注射部位并宣教，进行糖化血红蛋白筛查，现场急救培训，疾病PPT科普讲座等。活动既送去了医疗服务，增强了社区群众和老人们预防疾病的保健意识，也增进了我院和社区及养老中心的沟通联系，更好地为广大人民群众健康保驾护航。</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　 护理专科小组在先锋社区合影</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　 </span><img src=\"http://www.ycsyy.com/upload/images/2019/10/ad3b5df9a7e42e5c.jpeg\" width=\"518\" height=\"389\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　 糖尿病专业学组在先锋社区进行科普讲座</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/549399282f4343f8.jpeg\" width=\"238\" height=\"423\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　　内分泌科护士长武建霞在先锋社区进行糖尿病饮食宣教并现场答疑</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/9938244a8ac280a5.jpeg\" width=\"503\" height=\"353\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　医生及专科护士们为社区居民量血压、测血糖、发放健康宣传手册</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/69d11c4963c5abde.jpeg\" width=\"574\" height=\"380\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　　急诊急救专业学组为东进社区居民演示CPR急救操作</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/11f993b9b9bfe88b.jpeg\" width=\"323\" height=\"428\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　　急诊科宦燕护士长为东进社区居民讲解急救技能知识</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/7d8b68337130fe28.jpeg\" width=\"340\" height=\"453\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　　急诊急救专业学组在东进社区发放急救知识宣传单</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/9fd1dbdcbe3ad5.jpeg\" width=\"666\" height=\"500\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　急诊科李春娟护士长在莲花新村进行CPR操作讲解</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/b66b1de59d028072.jpeg\" width=\"582\" height=\"436\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">消化科专业学组进入光明花园小区</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/88ffe0314741f51f.jpeg\" width=\"637\" height=\"478\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　消化科专业学组进入光明花园小区为居民进行营养筛查并发放宣传单</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/ce11024cb24302bb.jpeg\" width=\"331\" height=\"442\" alt=\"\" border=\"0\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/a70d79e16ee30858.jpeg\" width=\"340\" height=\"453\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　　消化科专业学组为光明花苑居民提供健康咨询服务</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/3839a82f8e7af505.jpeg\" width=\"364\" height=\"485\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/cba8a7cc47f24aa2.jpeg\" width=\"738\" height=\"554\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　神经科专业学组健康咨询现场</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/b59502fc9ee1d54f.jpeg\" width=\"589\" height=\"442\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/45249d0cfec548eb.jpeg\" width=\"340\" height=\"453\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　专科护理小组千鹤湾老年医院合影</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/d50071cfdb555599.jpeg\" width=\"580\" height=\"435\" alt=\"\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><span style=\"text-indent: 2em; font-family: 宋体, SimSun; font-size: 20px;\">　　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">王跃琴护士长给千鹤湾养老中心的护理人员、护理员及部分能自理的老年人进行PPT授课</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/f97431031d55af35.jpeg\" width=\"467\" height=\"351\" alt=\"o_1cd2gcf2m1qrrtpp1tb28o7e9ga\" border=\"0\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/663a7f116da6ebe2.jpeg\" width=\"376\" height=\"353\" alt=\"o_1cd2gfrls1a6jk12fl0doq3iia\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">千鹤湾老年医院普外科学组现场咨询、发放宣传资料</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span><img src=\"http://www.ycsyy.com/upload/images/2019/10/ac0773f6f65ecd7f.jpeg\" width=\"244\" height=\"326\" alt=\"o_1cd2gprr619lkbn6rg915un183no\" border=\"0\"><img src=\"http://www.ycsyy.com/upload/images/2019/10/58458e2b7a6f3e2c.jpeg\" width=\"244\" height=\"326\" alt=\"o_1cd2gprqr1jo29nghd617ik1pttg\" border=\"0\"></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1147','1959','1','','http://www.ycsyy.com/upload/images/2021/5/c4a754bd0363029.png','为庆祝“5.12”国际护士节，将三级医院专业的护理服务向社区延伸，盐城市第一人民医院护理部积极响应江苏省护理学会的号召, 组织护理专家下社区，开展了主题为“护卫健康，未病先防”活动周系列活动。13日清晨，伴着五月温暖的微风，盐城市第一人民医院的护理专家及志愿','192.168.162.110','192.168.162.110','1621872000','<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为庆祝“5.12”国际护士节，将三级医院专业的护理服务向社区延伸，盐城市第一人民医院护理部积极响应江苏省护理学会的号召, 组织护理专家下社区，开展了主题为“护卫健康，未病先防”活动周系列活动。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">13日清晨，伴着五月温暖的微风，盐城市第一人民医院的护理专家及志愿者们陆续前往盐都康年颐养院、亭湖莲花社区、盐城市幼儿园开展护理专家义诊、小讲座、现场操作示教、护理查房指导等活动。我院护理部认真组织，共派出了伤口造口、营养、急诊急救、综合内科、消化、糖尿病、呼吸、泌尿外科、心血管、中医、骨科、神经、儿科、五官科、肿瘤17个护理专业学组的150多名护理专家和志愿者，此次活动受众达700余人。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2021年5月13日上午，我院临床营养、伤口造口、心血管护理、泌尿外科护理、中医护理、骨科护理、综合内科、急诊急救等八个护理专业学组联合盐都康年颐养院对在院老人及30多名护理员进行应急急救知识、慢病管理、营养管理及健康生活方式的现场宣教。经过前期精心的准备，各项活动有条不紊的开展。心血管护理学组为老人和护理员进行了«高血压自我管理»、综合内科学组为老人讲解老年人营养自我管理、骨科学组进行了骨质疏松等专题知识讲座；临床营养学组还为护理员进行了«老年患者喂养管的日常管理»的专题培训；中医护理学组成员李洁、夏剑云对中心护理人员及老人进行耳穴按摩操的现场培训工作，受教人群近百人次，开展耳穴贴压治疗42人次。护理专家现场为老人免费测血压、血糖等，对心血管、泌尿外科等常见疾病提供咨询、健康指导、发放宣传材料等；心肺复苏、AED使用、创伤止血包扎固定技术、气道异物梗阻急救海姆立克手法等急救常识宣教。伤口造口失禁护理、泌尿外科护理、骨科护理三个学组专家对护理院的伤造口患者、骨科患者等进行了床边护理查房指导，并相约定期回访。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">另外一组包括糖尿病、消化、呼吸、神经、母婴、妇科、肿瘤在内的专业学组来到莲花社区进行现场宣教、演示义诊等活动；分别开展脑卒中、消化系统疾病、内分泌、呼吸慢病管理以及社区疫情防控的健康咨询。活动开始，专家老师们对一些老年人常见病、多发病的预防和用药方法进行了耐心细致的指导。神经科学组张燕老师给社区群众讲解了脑卒中的危害，脑卒中预防等知识；讲座结束，护理人员免费为老人测量血压和血糖近50人次，发放“脑血管保健手册”、“脑卒中预防”、“高血压防治”等宣传材料10余种、120余份。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">儿科护理、五官科护理学组一行前往盐城市幼儿园开展了科普讲座、视频播放、现场操作演示等系列活动，本次活动共有200余名小朋友和30位老师参加。向小朋友讲解五官科展板的内容，深入浅出的演示了弱视、近视、牙齿的保健、气道异物的知识。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">此次活动不仅为老人、护理员、社区提供了专业的健康指导，也进一步提升了护理员的理论水平和实践操作技能，受到了颐养院老人、社区居民的一致好评。养老院的工作人员和老人们亲切的希望我们的白衣天使多多地看望他们。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">此次以“护卫健康，未病先防”为主题的科普周活动，各护理专业学组充分发挥专科优势，以PPT演示、发放健康宣传手册、发放健康宣传单、现场演示等方式向老人及社区护理员讲解各专业预防保健、饮食、急救等方面的知识。共计发放宣传手册600余份、测量血压300余人次、测量血糖86人次，提供专科咨询、宣教300余人次。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为了扩大本次活动的影响，提高健康宣传的效果，各护理专业学组邀请了相关专业医疗专家参与活动，专家现场坐诊，为居民提供疾病诊治、健康咨询。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">此次护理专家及志愿者们走进社区、贴近百姓，是三级医院护理服务向社区延伸的一个体现，也让我们进一步了</span><a name=\"_GoBack\"></a><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">解了普通居民的健康需求和困惑。护理专家面对面为广大社区群众提供了专业的健康指导，受到了广大居民的一致好评。</span></p>\r\n<p style=\"text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2021/5/c4a754bd0363029.png\" title=\"微信截图_20210525161746.png\" alt=\"微信截图_20210525161746.png\" width=\"701\" height=\"423\" style=\"text-align: center; white-space: normal; width: 701px; height: 423px;\"></span></p>\r\n<p style=\"text-indent: 2em;\"></p>\r\n<p style=\"text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2021/5/b9f301f14fd5259a.png\" title=\"微信截图_20210525161733.png\" alt=\"微信截图_20210525161733.png\" width=\"705\" height=\"424\" style=\"width: 705px; height: 424px;\"></span></p>\r\n<p style=\"text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www.ycsyy.com/upload/images/2021/5/9d0570ee991879a9.png\" title=\"微信截图_20210525161757.png\" alt=\"微信截图_20210525161757.png\" width=\"700\" height=\"439\" style=\"width: 700px; height: 439px;\"></span></p>\r\n<p style=\"text-align: center;\"><img src=\"http://www.ycsyy.com/upload/images/2021/5/bc2f9e1339da0865.png\" title=\"2d017522bb45d1edbc4ce6a9b930161.png\" alt=\"2d017522bb45d1edbc4ce6a9b930161.png\" width=\"686\" height=\"414\" style=\"width: 686px; height: 414px;\"></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1148','1960','1','','','各科室（病区）：根据《盐城市第一人民医院护理岗位管理实施方案》及《盐城市第一人民医院护士分层晋级管理办法》等文件要求，护理部决定于2019年7月组织N0—N4级护士岗位晋级申报及考评工作。具体要求通知如下：一、申报资质要求（一）各层级申报资质要求1、N0-N1：（1','192.168.162.110','192.168.162.110','1563811200','<p style=\"text-indent: 0em; line-height: normal;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">各科室（病区）：<br>\r\n</span></p>\r\n<p style=\"text-indent: 2em; line-height: normal;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">根据《盐城市第一人民医院护理岗位管理实施方案》及《盐城市第一人民医院护士分层晋级管理办法》等文件要求，护理部决定于2019年7月组织N0—N4级护士岗位晋级申报及考评工作。具体要求通知如下：</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">一、申报资质要求</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（一）各层级申报资质要求</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、N0-N1：</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）取得护士执业资格；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（2）从事临床护理工作1年以上的护士或护师；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（3）完成N0阶段分层培训计划，考核合格；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（4）年度考核合格。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、N1-N2：</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）从事临床护理工作3年以上，从事本专科半年以上；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（2）护师以上职称；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（3）完成规范化培训，取得结业证书；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（4）完成N1阶段分层培训计划，考核合格；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（5）N1阶段年度考核合格；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（6）达到所在专科能级对应的岗位准入条件。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、N2-N3：</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）从事临床护理工作8年以上；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（2）取得主管护师职称3年以上或取得省级专科护士资格；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（3）完成N2阶段分层培训计划，考核合格；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（4）N2阶段年度考核合格；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（5）达到所在专科能级对应的岗位准入条件。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4、N3-N4：</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）从事临床护理工作10年以上，从事本专科3年以上；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（2）取得副主任护师职称3年以上或取得主任护师职称；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（3）完成N3阶段分层培训计划，考核合格；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（4） N3阶段年度考核合格；</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（5）达到所在专科能级对应的岗位准入条件。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（二）其它</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、有工作经验刚进院人员，在完成岗前培训及满6个月的临床实践后，可根据其职称、工作年限等条件申报相应的能级。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、在现任能级任职期间离岗6个月至1年者，延迟1年晋级，离岗1年以上者，延迟2年晋级。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、在原层级工作满一年以上，硕士以上学历，已达到相应能级中的职称级别及能力要求，可提前1年申报高一能级。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4、在原层级工作满一年以上，任期内为医院做出重大贡献的人员可提前1年申报高一能级：参加援外医疗队者；对口支援≥6个月；在省级及以上护理竞赛中获得一等奖1次或二等奖2次；在市级护理竞赛中获得一等奖；在院外突发事件救治中做出突出贡献，得到表彰。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、申报程序：</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、申报时间：2019 年7月01日-03日上午</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、申报方法：申请人填写《盐城市第一人民医院护士能级晋级申请表》。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、资质审核：2019 年7月03日下午</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">由所在科室（病区）晋级考评小组初步审核→大科晋级考评组复审→护理部晋级考评组最终审核。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、晋级考评</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、考评时间：2019年7月4日—6日</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、考评要求详见《盐城市第一人民医院护士分层晋级管理办法》及附件。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、晋级公示及申诉</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">所有层级的晋级结果在医院内网公示3天，如对晋级过程或结果有疑问时，可在公示期间向护理部提出申诉，必要时可向监察部门申诉。公示无异议，晋级结果即生效。</span></p>\r\n<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　</span></p>\r\n<p style=\"text-indent: 2em; text-align: right;\" dir=\"rtl\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院 护理部</span></p>\r\n<p dir=\"rtl\" style=\"text-align: right; text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年6月28日</span></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1149','1961','1','','http://www.ycsyy.com/upload/images/2019/11/cfa148848463018e.png','各级医疗机构：为进一步加强护理专业技术发展和人才培养，为从事急、危重症护理的人员提供学习新知识、新技能的平台，提高急、危重症救治能力及专科护理质量，经江苏省继续医学教育委员会批准，由盐城市第一人民医院主办“急危重症护理管理及新知识培训班”（项目编号：','192.168.162.110','192.168.162.110','1538668800','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">各级医疗机构：</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为进一步加强护理专业技术发展和人才培养，为从事急、危重症护理的人员提供学习新知识、新技能的平台，提高急、危重症救治能力及专科护理质量，经江苏省继续医学教育委员会批准，由盐城市第一人民医院主办“急危重症护理管理及新知识培训班”（项目编号：2018091405026）。现将有关事宜通知如下：</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">一、培训内容</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">1、《江苏省三级综合医院评审标准实施细则（2017版）》（护理）解读</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">东南大学附属中大医院 李国宏</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2、临床课题选择与申报关键</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">江苏省中医院 芮庆林</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">3、常见急危重症的快速识别与处理</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">东南大学附属中大医院   惠晓芳</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">4、静脉用药安全与医用耗材</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">泰州市第一人民医院   郭素萍</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">5、护理安全管理</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院  汤琪春</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">6、液体复苏</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院  陈栋玉</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">7、常见侵入性诊疗相关感染预防控制</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院  徐 权</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">8、ICU常用评分工具及临床应用</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院 王念坚</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">9、氧疗指南解读及临床应用</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院 王 琦</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">二、出席对象</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">各级医院护理部主任、科护士长、ICU、专科监护病房、急诊科等相关科室护士长、专科护士及护理骨干。</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">三、授予学分</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">凡参会者完成规定的学时，考核合格后授予省级继续教育学分5分。</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">四、时间与地点</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">培训时间：2018年10月13日～14日</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">报到时间：2018年10月13日 13：00～13：45</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">报到地点：盐城市第一人民医院老干部楼十二楼会议室</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">培训地点：盐城市第一人民医院老干部楼十二楼会议室</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">住宿地点：宏派国际大酒店（盐城市亭湖区黄海西路135号）</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">五、收费标准</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">会务费：每人350元（含培训、材料、证书费）；会务费、差旅费、住宿费回原单位报销。</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">六、报名方式</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">请参加培训学习人员于2018年10月12日12:00前报名，报名回执统一发送至盐城市第一人民医院护理部邮箱:ycyyhlb@126.com。</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">联 系 人： 吴开勤</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">联系电话：0515-83578993</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　　　　　　　　　　　　　　盐城市第一人民医院 护理部</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　　　　　　　　　　　　　 　　　2018年10月5日</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">　　</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">　　附件1: “急、危重症护理管理及新知识培训班”参会回执</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><img src=\"http://www.ycsyy.com/upload/images/2019/11/cfa148848463018e.png\" title=\"image.png\" alt=\"image.png\"></span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"></p>\r\n<p>　　</p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1150','1962','1','','http://www.ycsyy.com/upload/images/2019/11/2b264cb7fa80b425.png','各级医疗机构：为了进一步加强医院护理管理，筑牢护理安全底线，提高护理管理水平，推进医院护理学科发展，经江苏省继续医学教育委员会批准，由盐城市第一人民医院主办“护理管理暨护理安全管理培训班”（项目编号：2017091405009/2018091405029）。现将有关培训事宜通','192.168.162.110','192.168.162.110','1538668800','<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">各级医疗机构：<br>\r\n</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为了进一步加强医院护理管理，筑牢护理安全底线，提高护理管理水平，推进医院护理学科发展，经江苏省继续医学教育委员会批准，由盐城市第一人民医院主办“护理管理暨护理安全管理培训班”（项目编号：2017091405009/2018091405029）。现将有关培训事宜通知如下：</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">一、培训内容</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">1、《江苏省三级综合医院评审标准实施细则（2017版）》（护理）解读</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">东南大学附属中大医院 李国宏</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2、临床课题选择与申报关键</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">江苏省中医院        芮庆林</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">3、护理安全管理</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院      汤琪春</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">4、护理质量敏感指标的闭环管理</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院      孙雯敏</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">5、护士长的管理艺术</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院      陈 艳</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">6、护士临床实践能力培养与考核</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院      薛晓明</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">7、常见侵入性诊疗相关感染预防控制</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院      徐 权</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">二、出席对象</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">各级医院护理部主任、科护士长、护士长、专科护士及护理骨干。</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">三、授发学分</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">凡参会者完成规定的学时，考核合格后授予省级继续教育学分5分。</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">四、时间与地点</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">培训时间：2018年10月13日～14日</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">报到时间：2018年10月13日 13：00～13：45</span></p>\r\n<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-