INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7255','0','cms','item','add','0','869','','宣传处','添加内容 | 【学雷锋 树新风】农工党盐城市一院基层委员会开展 “健康服务行暨学','/index.php/cms/item-list-category-869-page-2','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'869\',\n  \'title\' => \'【学雷锋 树新风】农工党盐城市一院基层委员会开展 “健康服务行暨学雷锋”宣教义诊活动\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">3</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">月<span>2</span>日，正值学雷锋纪念日即将到来之际，农工党盐城市一院基层委员会走进东进社区开展“健康服务行暨学雷锋”宣教义诊活动。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">活动现场，专家党员发挥专业特长，积极与前来就诊的患者沟通，详细了解他们的健康情况，认真解答患者问题，提出合理的治疗方案及建议，并耐心细致地讲解疾病防控要点。期间，专家党员还结合实例及课件，就“外伤应急处理”“<span>CPR</span>”等主题为项目安全管理人员及现场人员作了实操讲座。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">市一院基层委员会主委王军表示，此次义诊宣教活动是为弘扬雷锋精神，为广大患者提供的一次便民服务，今后基层委将持续发扬精神，进一步加强义诊、宣教活动力度，努力为提升基层医疗水平添砖加瓦，做良好社会风尚的创造者和传播者。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">&nbsp;</span></p>\r\n&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;<a href=\"/ewebeditor/uploadfile/image/2023/03/06/20230306093510889.jpg\" target=\"_blank\"><img src=\"/ewebeditor/uploadfile/image/2023/03/06/20230306093510889_s.jpg\" border=\"0\"></a>\',\n  ),\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'8b6f0aa1cdf27eb5\',\n  \'verifier\' => \'宣传处\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1678066512');
