INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7336','0','cms','item','add','0','859','','宣传处','添加内容 | 盐城市第一人民医院 项目结算审核服务招标公告','/index.php/cms/item-list-category-859-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院 项目结算审核服务招标公告\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:21.0pt;\r\nline-height:23.0pt\" align=\"left\"><span style=\"font-family:宋体\">盐城市第一人民医院项目结算审核服务已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:21.0pt;\r\nline-height:23.0pt;background:white\" align=\"left\"><span style=\"font-family:宋体\">一、盐城市招标代理有限公司受招标人委托具体负责本项目的招标事宜。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:21.0pt;\r\nline-height:23.0pt;background:white\" align=\"left\"><span style=\"font-family:宋体\">二、符合本项目招标公告资格要求的投标的申请人均可参加该项目投标。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:21.1pt;\r\nline-height:23.0pt;background:white\" align=\"left\"><b><span style=\"font-family:宋体\">三、项目概况：</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-family:宋体\">1</span><span style=\"font-family:\r\n宋体\">、</span><span style=\"font-family:宋体\">项目名称</span><span style=\"font-family:\r\n宋体\">：</span><span style=\"font-family:宋体\">盐城市第一人民医院项目结算审核服务</span><span style=\"font-family:宋体\">。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-family:宋体\">2</span><span style=\"font-family:\r\n宋体\">、</span><span style=\"font-family:宋体\">项目地点：</span><span style=\"font-family:\r\n宋体\">盐城市第一人民医院，具体项目服务地点根据招标人要求。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:21.0pt;\r\nline-height:23.0pt;background:white\" align=\"left\"><span style=\"font-family:宋体\">3</span><span style=\"font-family:宋体\">、审计服</span><span style=\"font-family:宋体\">务范围：</span><span style=\"font-family:宋体\">盐城市第一人民医院项目（含</span><span style=\"font-family:宋体\">工程、货物采购及服务</span><span style=\"font-family:宋体\">）结算审核服务。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-indent:21.0pt;line-height:23.0pt;layout-grid-mode:\r\nchar\"><span style=\"font-family:宋体\">4</span><span style=\"font-family:\r\n宋体\">、服务时间要求：</span><