INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7344','0','cms','item','add','0','933','','宣传处','添加内容 | “例”精图治 谁与争锋 —盐城市“谁与争锋”急诊绿色通道救护典型案','/index.php/cms/item-list-category-933-page-1','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'933\',\n  \'title\' => \'“例”精图治 谁与争锋 —盐城市“谁与争锋”急诊绿色通道救护典型案例竞赛活动圆满成功 盐城市第一人民医院护理组喜获一等奖\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">为提升急诊临床护理人员的专科服务能力，推进胸痛、脑卒中、创伤救治中心的建设，促进急诊护理学科的可持续发展，根据中华护理学会、江苏省护理学会及盐城市护理学会关于举办<span>“</span>谁与争锋<span>”</span>急诊绿色通道救护典型案例分享活动的通知精神，结合《江苏省十四五护理事业发展规划》的要求和《<span>“</span>健康中国<span>2030”</span>规划纲要》的精神，<span>2023</span>年<span>4</span>月<span>27</span>日，由盐城市护理学会急诊急救护理专业委员会主办，盐城市第三人民医院承办的盐城市<span>“</span>谁与争锋<span>”</span>急诊绿色通道救护典型案例竞赛决赛活动如期举行。盐城市第一人民医院荣获胸痛组一等奖，脑卒中组一等奖，创伤组二等奖。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;<img src=\"/ewebeditor/uploadfile/image/2023/05/06/20230506104418544.jpg\" border=\"0\"><br>\r\n</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\'\"><span>&nbsp;</span></p>\',\n  ),\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'9366391f419a2ee1\',\n  \'verifier\' => \'宣传处\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1683341065');
